    this_header = 0;
    message = NULL;
    while (!message && len) {
        loff_t saved_offset = this_header;
        if (*buf == '0' && !(this_header & 3)) {
            state = Start;
            written = write_buffer (buf, len);
            buf += written;
            len -= written;
            continue;
        }
        if (!*buf) {
            buf++;
            len--;
            this_header++;
            continue;
        }
        this_header = 0;
        insize = len;
        inbuf = buf;
        inptr = 0;
        outcnt = 0;
        bytes_out = 0;
        crc = (ulg) 0xffffffffL;
        makecrc ();
        gunzip ();
        if (state != Reset)
            error ("junk in gzipped archive");
        this_header = saved_offset + inptr;
        buf += inptr;
        len -= inptr;
    }
    kfree (window);
    kfree (name_buf);
    kfree (symlink_buf);
    kfree (header_buf);
    return message;
}

{
    loff_t saved_offset = this_header;
    if (*buf == '0' && !(this_header & 3)) {
        state = Start;
        written = write_buffer (buf, len);
        buf += written;
        len -= written;
        continue;
    }
    if (!*buf) {
        buf++;
        len--;
        this_header++;
        continue;
    }
    this_header = 0;
    insize = len;
    inbuf = buf;
    inptr = 0;
    outcnt = 0;
    bytes_out = 0;
    crc = (ulg) 0xffffffffL;
    makecrc ();
    gunzip ();
    if (state != Reset)
        error ("junk in gzipped archive");
    this_header = saved_offset + inptr;
    buf += inptr;
    len -= inptr;
}

{
    state = Start;
    written = write_buffer (buf, len);
    buf += written;
    len -= written;
    continue;
}

{
    buf++;
    len--;
    this_header++;
    continue;
}

{
    if (*str)
        return 0;
    do_retain_initrd = 1;
    return 1;
}

{
    if (do_retain_initrd)
        goto skip;
    free_initrd_mem (initrd_start, initrd_end);
skip :
    initrd_start = 0;
    initrd_end = 0;
}

{
    char *err = unpack_to_rootfs (__initramfs_start, __initramfs_end -__initramfs_start, 0);
    if (err)
        panic (err);
    if (initrd_start) {
        printk (KERN_INFO "Unpacking initramfs...");
        err = unpack_to_rootfs ((char *) initrd_start, initrd_end -initrd_start, 0);
        if (err)
            panic (err);
        printk (" done\n");
        free_initrd ();
    }
    return 0;
}

{
