{
    char *command_line;
    extern struct kernel_param __start___param [], __stop___param [];
    smp_setup_processor_id ();
    unwind_init ();
    lockdep_init ();
    debug_objects_early_init ();
    cgroup_init_early ();
    local_irq_disable ();
    early_boot_irqs_off ();
