{
    struct cpuid_regs *cmd = (struct cpuid_regs *) cmd_block;
    cpuid_count (cmd -> eax, cmd -> ecx, & cmd -> eax, & cmd -> ebx, & cmd -> ecx, & cmd -> edx);
}

{
    loff_t ret;
    struct inode *inode = file->f_mapping->host;
    mutex_lock (& inode -> i_mutex);
    switch (orig) {
    case 0 :
        file->f_pos = offset;
        ret = file->f_pos;
        break;
    case 1 :
        file->f_pos += offset;
        ret = file->f_pos;
        break;
    default :
        ret = -EINVAL;
    }
    mutex_unlock (& inode -> i_mutex);
    return ret;
}

{
case 0 :
    file->f_pos = offset;
    ret = file->f_pos;
    break;
case 1 :
    file->f_pos += offset;
    ret = file->f_pos;
    break;
default :
    ret = -EINVAL;
}

{
    char __user *tmp = buf;
    struct cpuid_regs cmd;
    int cpu = iminor (file->f_path.dentry->d_inode);
    u64 pos = *ppos;
    if (count % 16)
        return -EINVAL;
    for (; count; count -= 16) {
        cmd.eax = pos;
        cmd.ecx = pos >> 32;
        smp_call_function_single (cpu, cpuid_smp_cpuid, & cmd, 1, 1);
        if (copy_to_user (tmp, &cmd, 16))
            return -EFAULT;
        tmp += 16;
        *ppos = ++pos;
    }
    return tmp - buf;
}

{
    cmd.eax = pos;
    cmd.ecx = pos >> 32;
    smp_call_function_single (cpu, cpuid_smp_cpuid, & cmd, 1, 1);
    if (copy_to_user (tmp, &cmd, 16))
        return -EFAULT;
    tmp += 16;
    *ppos = ++pos;
}

{
    unsigned int cpu = iminor (file->f_path.dentry->d_inode);
    struct cpuinfo_x86 *c = &cpu_data (cpu);
    if (cpu >= NR_CPUS || !cpu_online (cpu))
        return -ENXIO;
    if (c->cpuid_level < 0)
        return -EIO;
    return 0;
}

{
    struct device *dev;
    dev = device_create (cpuid_class, NULL, MKDEV (CPUID_MAJOR, cpu), "cpu%d", cpu);
    return IS_ERR (dev) ? PTR_ERR (dev) : 0;
}

{
    device_destroy (cpuid_class, MKDEV (CPUID_MAJOR, cpu));
}

{
    unsigned int cpu = (unsigned long) hcpu;
    int err = 0;
    switch (action) {
    case CPU_UP_PREPARE :
        err = cpuid_device_create (cpu);
        break;
    case CPU_UP_CANCELED :
    case CPU_UP_CANCELED_FROZEN :
    case CPU_DEAD :
        cpuid_device_destroy (cpu);
        break;
    }
    return err ? NOTIFY_BAD : NOTIFY_OK;
}

{
case CPU_UP_PREPARE :
    err = cpuid_device_create (cpu);
    break;
case CPU_UP_CANCELED :
case CPU_UP_CANCELED_FROZEN :
case CPU_DEAD :
    cpuid_device_destroy (cpu);
    break;
}

{
    int i, err = 0;
    i = 0;
    if (register_chrdev (CPUID_MAJOR, "cpu/cpuid", &cpuid_fops)) {
        printk (KERN_ERR "cpuid: unable to get major %d for cpuid\n", CPUID_MAJOR);
        err = -EBUSY;
        goto out;
    }
    cpuid_class = class_create (THIS_MODULE, "cpuid");
    if (IS_ERR (cpuid_class)) {
        err = PTR_ERR (cpuid_class);
        goto out_chrdev;
    }

    for_each_online_cpu (i) {
        err = cpuid_device_create (i);
        if (err != 0)
            goto out_class;
    }

    register_hotcpu_notifier (& cpuid_class_cpu_notifier);
    err = 0;
    goto out;
out_class :
    i = 0;

    for_each_online_cpu (i) {
        cpuid_device_destroy (i);
    }

    class_destroy (cpuid_class);
out_chrdev :
    unregister_chrdev (CPUID_MAJOR, "cpu/cpuid");
out :
    return err;
}

{
    printk (KERN_ERR "cpuid: unable to get major %d for cpuid\n", CPUID_MAJOR);
    err = -EBUSY;
    goto out;
}

{
    err = PTR_ERR (cpuid_class);
    goto out_chrdev;
}

{
    err = cpuid_device_create (i);
    if (err != 0)
        goto out_class;
}

{
    cpuid_device_destroy (i);
}

{
    int cpu = 0;
    for_each_online_cpu (cpu)
    cpuid_device_destroy (cpu);
    class_destroy (cpuid_class);
    unregister_chrdev (CPUID_MAJOR, "cpu/cpuid");
    unregister_hotcpu_notifier (& cpuid_class_cpu_notifier);
}

{
    unsigned int i;
    for (i = base; i < base + extent; i++) {
        if (new_value)
            __set_bit (i, bitmap);
        else
            __clear_bit (i, bitmap);
    }
}

{
    if (new_value)
        __set_bit (i, bitmap);
    else
        __clear_bit (i, bitmap);
}

{
    struct thread_struct *t = &current->thread;
    struct tss_struct *tss;
    unsigned int i, max_long, bytes, bytes_updated;
    if ((from + num <= from) || (from + num > IO_BITMAP_BITS))
        return -EINVAL;
    if (turn_on && !capable (CAP_SYS_RAWIO))
        return -EPERM;
    if (!t->io_bitmap_ptr) {
        unsigned long *bitmap = kmalloc (IO_BITMAP_BYTES, GFP_KERNEL);
        if (!bitmap)
            return -ENOMEM;
        memset (bitmap, 0xff, IO_BITMAP_BYTES);
        t->io_bitmap_ptr = bitmap;
        set_thread_flag (TIF_IO_BITMAP);
    }
    tss = &per_cpu (init_tss, get_cpu ());
    set_bitmap (t -> io_bitmap_ptr, from, num, ! turn_on);
    max_long = 0;
    for (i = 0; i < IO_BITMAP_LONGS; i++)
        if (t->io_bitmap_ptr[i] != ~0UL)
            max_long = i;
    bytes = (max_long + 1) * sizeof (unsigned long);
    bytes_updated = max (bytes, t->io_bitmap_max);
    t->io_bitmap_max = bytes;
    memcpy (tss -> io_bitmap, t -> io_bitmap_ptr, bytes_updated);
    put_cpu ();
    return 0;
}

{
    unsigned long *bitmap = kmalloc (IO_BITMAP_BYTES, GFP_KERNEL);
    if (!bitmap)
        return -ENOMEM;
    memset (bitmap, 0xff, IO_BITMAP_BYTES);
    t->io_bitmap_ptr = bitmap;
    set_thread_flag (TIF_IO_BITMAP);
}

{
    unsigned int old = (regs->flags >> 12) & 3;
    if (level > 3)
        return -EINVAL;
    if (level > old) {
        if (!capable (CAP_SYS_RAWIO))
            return -EPERM;
    }
    regs->flags = (regs->flags & ~X86_EFLAGS_IOPL) | (level << 12);
    return 0;
}

{
    if (!capable (CAP_SYS_RAWIO))
        return -EPERM;
}

{
    return do_iopl (level, regs);
}

{
    const struct range *r1 = x1;
    const struct range *r2 = x2;
    int start1, start2;
    start1 = r1->start >> 32;
    start2 = r2->start >> 32;
    return start1 - start2;
}

{
    int i;
    unsigned bus;
    unsigned slot;
    int found;
    u64 val;
    u32 address;
    u64 tom2;
    u64 base = FAM10H_PCI_MMCONF_BASE;
    int hi_mmio_num;
    struct range range [8];
    if (fam10h_pci_mmconf_base_status)
        return;
    if (!early_pci_allowed ())
        goto fail;
    found = 0;
    for (i = 0; i < ARRAY_SIZE (pci_probes); i++) {
        u32 id;
        u16 device;
        u16 vendor;
        bus = pci_probes[i].bus;
        slot = pci_probes[i].slot;
        id = read_pci_config (bus, slot, 0, PCI_VENDOR_ID);
        vendor = id & 0xffff;
        device = (id >> 16) & 0xffff;
        if (pci_probes[i].vendor == vendor && pci_probes[i].device == device) {
            found = 1;
            break;
        }
    }
    if (!found)
        goto fail;
    address = MSR_K8_SYSCFG;
    rdmsrl (address, val);
    if (!(val & (1 << 21))) {
        tom2 = 0;
    }
    else {
        address = MSR_K8_TOP_MEM2;
        rdmsrl (address, val);
        tom2 = val & (0xffffULL << 32);
    }
    if (base <= tom2)
        base = tom2 + (1ULL << 32);
    hi_mmio_num = 0;
    for (i = 0; i < 8; i++) {
        u32 reg;
        u64 start;
        u64 end;
        reg = read_pci_config (bus, slot, 1, 0x80 + (i << 3));
        if (!(reg & 3))
            continue;
        start = (((u64) reg) << 8) & (0xffULL << 32);
        reg = read_pci_config (bus, slot, 1, 0x84 + (i << 3));
        end = (((u64) reg) << 8) & (0xffULL << 32);
        if (!end)
            continue;
        range[hi_mmio_num].start = start;
        range[hi_mmio_num].end = end;
        hi_mmio_num++;
    }
    if (!hi_mmio_num)
        goto out;
    sort (range, hi_mmio_num, sizeof (struct range), cmp_range, NULL);
    if (range[hi_mmio_num - 1].end < base)
        goto out;
    if (range[0].start > base)
        goto out;
    base = range[0].start - (1ULL << 32);
    if ((base > tom2) && BASE_VALID (base))
        goto out;
    base = range[hi_mmio_num - 1].end + (1ULL << 32);
    if ((base > tom2) && BASE_VALID (base))
        goto out;
    if (hi_mmio_num > 1)
        for (i = 0; i < hi_mmio_num - 1; i++) {
            if (range[i + 1].start > (range[i].end + (1ULL << 32))) {
                base = range[i].end + (1ULL << 32);
                if ((base > tom2) && BASE_VALID (base))
                    goto out;
            }
        }
fail :
    fam10h_pci_mmconf_base_status = -1;
    return;
out :
    fam10h_pci_mmconf_base = base;
    fam10h_pci_mmconf_base_status = 1;
}

{
    u32 id;
    u16 device;
    u16 vendor;
    bus = pci_probes[i].bus;
    slot = pci_probes[i].slot;
    id = read_pci_config (bus, slot, 0, PCI_VENDOR_ID);
    vendor = id & 0xffff;
    device = (id >> 16) & 0xffff;
    if (pci_probes[i].vendor == vendor && pci_probes[i].device == device) {
        found = 1;
        break;
    }
}

{
    found = 1;
    break;
}

{
    address = MSR_K8_TOP_MEM2;
    rdmsrl (address, val);
    tom2 = val & (0xffffULL << 32);
}

{
    u32 reg;
    u64 start;
    u64 end;
    reg = read_pci_config (bus, slot, 1, 0x80 + (i << 3));
    if (!(reg & 3))
        continue;
    start = (((u64) reg) << 8) & (0xffULL << 32);
    reg = read_pci_config (bus, slot, 1, 0x84 + (i << 3));
    end = (((u64) reg) << 8) & (0xffULL << 32);
    if (!end)
        continue;
    range[hi_mmio_num].start = start;
    range[hi_mmio_num].end = end;
    hi_mmio_num++;
}

{
    if (range[i + 1].start > (range[i].end + (1ULL << 32))) {
        base = range[i].end + (1ULL << 32);
        if ((base > tom2) && BASE_VALID (base))
            goto out;
    }
}

{
    base = range[i].end + (1ULL << 32);
    if ((base > tom2) && BASE_VALID (base))
        goto out;
}

{
    u64 val;
    u32 address;
    if (!(pci_probe & PCI_CHECK_ENABLE_AMD_MMCONF))
        return;
    address = MSR_FAM10H_MMIO_CONF_BASE;
    rdmsrl (address, val);
    if (val & FAM10H_MMIO_CONF_ENABLE) {
        unsigned busnbits;
        busnbits = (val >> FAM10H_MMIO_CONF_BUSRANGE_SHIFT) & FAM10H_MMIO_CONF_BUSRANGE_MASK;
        if (!acpi_pci_disabled || busnbits >= 8) {
            u64 base;
            base = val & (0xffffULL << 32);
            if (fam10h_pci_mmconf_base_status <= 0) {
                fam10h_pci_mmconf_base = base;
                fam10h_pci_mmconf_base_status = 1;
                return;
            }
            else if (fam10h_pci_mmconf_base == base)
                return;
        }
    }
    get_fam10h_pci_mmconf_base ();
    if (fam10h_pci_mmconf_base_status <= 0)
        return;
    printk (KERN_INFO "Enable MMCONFIG on AMD Family 10h\n");
    val &= ~((FAM10H_MMIO_CONF_BASE_MASK << FAM10H_MMIO_CONF_BASE_SHIFT) | (FAM10H_MMIO_CONF_BUSRANGE_MASK << FAM10H_MMIO_CONF_BUSRANGE_SHIFT));
    val |= fam10h_pci_mmconf_base | (8 << FAM10H_MMIO_CONF_BUSRANGE_SHIFT) | FAM10H_MMIO_CONF_ENABLE;
    wrmsrl (address, val);
}

{
    unsigned busnbits;
    busnbits = (val >> FAM10H_MMIO_CONF_BUSRANGE_SHIFT) & FAM10H_MMIO_CONF_BUSRANGE_MASK;
    if (!acpi_pci_disabled || busnbits >= 8) {
        u64 base;
        base = val & (0xffffULL << 32);
        if (fam10h_pci_mmconf_base_status <= 0) {
            fam10h_pci_mmconf_base = base;
            fam10h_pci_mmconf_base_status = 1;
            return;
        }
        else if (fam10h_pci_mmconf_base == base)
            return;
    }
}

{
    u64 base;
    base = val & (0xffffULL << 32);
    if (fam10h_pci_mmconf_base_status <= 0) {
        fam10h_pci_mmconf_base = base;
        fam10h_pci_mmconf_base_status = 1;
        return;
    }
    else if (fam10h_pci_mmconf_base == base)
        return;
}

{
    fam10h_pci_mmconf_base = base;
    fam10h_pci_mmconf_base_status = 1;
    return;
}

{
    pci_probe |= PCI_CHECK_ENABLE_AMD_MMCONF;
    return 0;
}

