    dmi_check_system (mmconf_dmi_table);
}

{
    debug_alternative = 1;
    return 1;
}

{
    noreplace_smp = 1;
    return 1;
}

{
    const unsigned char * const *noptable = intel_nops;
    int i;
    for (i = 0; noptypes[i].cpuid >= 0; i++) {
        if (boot_cpu_has (noptypes[i].cpuid)) {
            noptable = noptypes[i].noptable;
            break;
        }
    }
    return noptable;
}

{
    if (boot_cpu_has (noptypes[i].cpuid)) {
        noptable = noptypes[i].noptable;
