{
    int cpu;
    cpu = first_cpu (cpumask);
    if ((unsigned) cpu < NR_CPUS)
        return per_cpu (x86_cpu_to_apicid, cpu);
    else
        return BAD_APICID;
}

{
    int i;
    struct early_res *r;
    for (i = 0; i < MAX_EARLY_RES && early_res[i].end; i++) {
        r = &early_res[i];
        if (end > r->start && start < r->end)
            panic ("Overlapping early reservations %lx-%lx %s to %lx-%lx %s\n", start, end -1, name ? name : "", r->start, r->end - 1, r->name);
    }
    if (i >= MAX_EARLY_RES)
        panic ("Too many early reservations");
    r = &early_res[i];
    r->start = start;
    r->end = end;
    if (name)
        strncpy (r->name, name, sizeof (r->name) - 1);
}

{
    r = &early_res[i];
    if (end > r->start && start < r->end)
        panic ("Overlapping early reservations %lx-%lx %s to %lx-%lx %s\n", start, end -1, name ? name : "", r->start, r->end - 1, r->name);
}

{
    struct early_res *r;
    int i, j;
    for (i = 0; i < MAX_EARLY_RES && early_res[i].end; i++) {
        r = &early_res[i];
        if (start == r->start && end == r->end)
            break;
    }
    if (i >= MAX_EARLY_RES || !early_res[i].end)
        panic ("free_early on not reserved area: %lx-%lx!", start, end);
    for (j = i + 1; j < MAX_EARLY_RES && early_res[j].end; j++)
        ;
    memmove (& early_res [i], & early_res [i + 1], (j - 1 - i) * sizeof (struct early_res));
    early_res[j - 1].end = 0;
}

{
    r = &early_res[i];
    if (start == r->start && end == r->end)
        break;
}

{
    int i;
    unsigned long final_start, final_end;
    for (i = 0; i < MAX_EARLY_RES && early_res[i].end; i++) {
        struct early_res *r = &early_res[i];
        final_start = max (start, r->start);
        final_end = min (end, r->end);
        if (final_start >= final_end)
            continue;
        printk (KERN_INFO "  early res: %d [%lx-%lx] %s\n", i, final_start, final_end - 1, r -> name);
        reserve_bootmem_generic (final_start, final_end - final_start);
    }
}

{
    struct early_res *r = &early_res[i];
    final_start = max (start, r->start);
    final_end = min (end, r->end);
    if (final_start >= final_end)
        continue;
    printk (KERN_INFO "  early res: %d [%lx-%lx] %s\n", i, final_start, final_end - 1, r -> name);
    reserve_bootmem_generic (final_start, final_end - final_start);
}

{
    int i;
    unsigned long addr = *addrp, last;
    int changed = 0;
again :
    last = addr + size;
    for (i = 0; i < MAX_EARLY_RES && early_res[i].end; i++) {
        struct early_res *r = &early_res[i];
        if (last >= r->start && addr < r->end) {
            *addrp = addr = round_up (r->end, align);
            changed = 1;
            goto again;
        }
    }
    return changed;
}

{
    struct early_res *r = &early_res[i];
    if (last >= r->start && addr < r->end) {
        *addrp = addr = round_up (r->end, align);
        changed = 1;
        goto again;
    }
}

{
    *addrp = addr = round_up (r->end, align);
    changed = 1;
    goto again;
}

{
    int i;
    unsigned long addr = *addrp, last;
    unsigned long size = *sizep;
    int changed = 0;
again :
    last = addr + size;
    for (i = 0; i < MAX_EARLY_RES && early_res[i].end; i++) {
        struct early_res *r = &early_res[i];
        if (last > r->start && addr < r->start) {
            size = r->start - addr;
            changed = 1;
            goto again;
        }
        if (last > r->end && addr < r->end) {
            addr = round_up (r->end, align);
            size = last - addr;
            changed = 1;
            goto again;
        }
        if (last <= r->end && addr >= r->start) {
            (*sizep)++;
            return 0;
        }
    }
    if (changed) {
        *addrp = addr;
        *sizep = size;
    }
    return changed;
}

{
    struct early_res *r = &early_res[i];
    if (last > r->start && addr < r->start) {
        size = r->start - addr;
        changed = 1;
        goto again;
    }
    if (last > r->end && addr < r->end) {
        addr = round_up (r->end, align);
        size = last - addr;
        changed = 1;
        goto again;
    }
    if (last <= r->end && addr >= r->start) {
        (*sizep)++;
        return 0;
    }
}

{
    size = r->start - addr;
    changed = 1;
    goto again;
}

{
    addr = round_up (r->end, align);
    size = last - addr;
    changed = 1;
    goto again;
}

{
    (*sizep)++;
    return 0;
}

{
    *addrp = addr;
    *sizep = size;
}

{
    int i;
    for (i = 0; i < e820.nr_map; i++) {
        struct e820entry *ei = &e820.map[i];
        if (type && ei->type != type)
            continue;
        if (ei->addr >= end || ei->addr + ei->size <= start)
            continue;
        return 1;
    }
    return 0;
}

{
    struct e820entry *ei = &e820.map[i];
    if (type && ei->type != type)
        continue;
    if (ei->addr >= end || ei->addr + ei->size <= start)
        continue;
    return 1;
}

{
    int i;
    for (i = 0; i < e820.nr_map; i++) {
        struct e820entry *ei = &e820.map[i];
        if (type && ei->type != type)
            continue;
        if (ei->addr >= end || ei->addr + ei->size <= start)
            continue;
        if (ei->addr <= start)
            start = ei->addr + ei->size;
        if (start >= end)
            return 1;
    }
    return 0;
}

{
    struct e820entry *ei = &e820.map[i];
    if (type && ei->type != type)
        continue;
    if (ei->addr >= end || ei->addr + ei->size <= start)
        continue;
    if (ei->addr <= start)
        start = ei->addr + ei->size;
    if (start >= end)
        return 1;
}

{
    int i;
    for (i = 0; i < e820.nr_map; i++) {
        struct e820entry *ei = &e820.map[i];
        unsigned long addr, last;
        unsigned long ei_last;
        if (ei->type != E820_RAM)
            continue;
        addr = round_up (ei->addr, align);
        ei_last = ei->addr + ei->size;
        if (addr < start)
            addr = round_up (start, align);
        if (addr >= ei_last)
            continue;
        while (bad_addr (&addr, size, align) && addr + size <= ei_last)
            ;
        last = addr + size;
        if (last > ei_last)
            continue;
        if (last > end)
            continue;
        return addr;
    }
    return -1UL;
}

{
    struct e820entry *ei = &e820.map[i];
    unsigned long addr, last;
    unsigned long ei_last;
    if (ei->type != E820_RAM)
        continue;
    addr = round_up (ei->addr, align);
    ei_last = ei->addr + ei->size;
    if (addr < start)
        addr = round_up (start, align);
    if (addr >= ei_last)
        continue;
    while (bad_addr (&addr, size, align) && addr + size <= ei_last)
        ;
    last = addr + size;
    if (last > ei_last)
        continue;
    if (last > end)
        continue;
    return addr;
}

{
    int i;
    for (i = 0; i < e820.nr_map; i++) {
        struct e820entry *ei = &e820.map[i];
        unsigned long addr, last;
        unsigned long ei_last;
        if (ei->type != E820_RAM)
            continue;
        addr = round_up (ei->addr, align);
        ei_last = ei->addr + ei->size;
        if (addr < start)
            addr = round_up (start, align);
        if (addr >= ei_last)
            continue;
        *sizep = ei_last - addr;
        while (bad_addr_size (&addr, sizep, align) && addr + *sizep <= ei_last)
            ;
        last = addr + *sizep;
        if (last > ei_last)
            continue;
        return addr;
    }
    return -1UL;
}

{
    struct e820entry *ei = &e820.map[i];
    unsigned long addr, last;
    unsigned long ei_last;
    if (ei->type != E820_RAM)
        continue;
    addr = round_up (ei->addr, align);
    ei_last = ei->addr + ei->size;
    if (addr < start)
        addr = round_up (start, align);
    if (addr >= ei_last)
        continue;
    *sizep = ei_last - addr;
    while (bad_addr_size (&addr, sizep, align) && addr + *sizep <= ei_last)
        ;
    last = addr + *sizep;
    if (last > ei_last)
        continue;
    return addr;
}

{
    unsigned long end_pfn;
    end_pfn = find_max_pfn_with_active_regions ();
    if (end_pfn > max_pfn_mapped)
        max_pfn_mapped = end_pfn;
    if (max_pfn_mapped > MAXMEM >> PAGE_SHIFT)
        max_pfn_mapped = MAXMEM >> PAGE_SHIFT;
    if (end_pfn > end_user_pfn)
        end_pfn = end_user_pfn;
    if (end_pfn > max_pfn_mapped)
        end_pfn = max_pfn_mapped;
    printk (KERN_INFO "max_pfn_mapped = %lu\n", max_pfn_mapped);
    return end_pfn;
}

{
    int i;
    struct resource *res;
    res = alloc_bootmem_low (sizeof (struct resource) * e820.nr_map);
    for (i = 0; i < e820.nr_map; i++) {
        switch (e820.map[i].type) {
        case E820_RAM :
            res->name = "System RAM";
            break;
        case E820_ACPI :
            res->name = "ACPI Tables";
            break;
        case E820_NVS :
            res->name = "ACPI Non-volatile Storage";
            break;
        default :
            res->name = "reserved";
        }
        res->start = e820.map[i].addr;
        res->end = res->start + e820.map[i].size - 1;
        res->flags = IORESOURCE_MEM | IORESOURCE_BUSY;
        insert_resource (& iomem_resource, res);
        res++;
    }
}

{
    switch (e820.map[i].type) {
    case E820_RAM :
        res->name = "System RAM";
        break;
    case E820_ACPI :
        res->name = "ACPI Tables";
        break;
    case E820_NVS :
        res->name = "ACPI Non-volatile Storage";
        break;
    default :
        res->name = "reserved";
    }
    res->start = e820.map[i].addr;
    res->end = res->start + e820.map[i].size - 1;
    res->flags = IORESOURCE_MEM | IORESOURCE_BUSY;
    insert_resource (& iomem_resource, res);
    res++;
}

{
case E820_RAM :
    res->name = "System RAM";
    break;
case E820_ACPI :
    res->name = "ACPI Tables";
    break;
case E820_NVS :
    res->name = "ACPI Non-volatile Storage";
    break;
default :
    res->name = "reserved";
}

{
    int i;
    unsigned long paddr;
    paddr = round_down (e820.map[0].addr + e820.map[0].size, PAGE_SIZE);
    for (i = 1; i < e820.nr_map; i++) {
        struct e820entry *ei = &e820.map[i];
        if (paddr < ei->addr)
            register_nosave_region (PFN_DOWN (paddr), PFN_UP (ei->addr));
        paddr = round_down (ei->addr + ei->size, PAGE_SIZE);
        if (ei->type != E820_RAM)
            register_nosave_region (PFN_UP (ei->addr), PFN_DOWN (paddr));
        if (paddr >= (end_pfn << PAGE_SHIFT))
            break;
    }
}

{
    struct e820entry *ei = &e820.map[i];
    if (paddr < ei->addr)
        register_nosave_region (PFN_DOWN (paddr), PFN_UP (ei->addr));
    paddr = round_down (ei->addr + ei->size, PAGE_SIZE);
    if (ei->type != E820_RAM)
        register_nosave_region (PFN_UP (ei->addr), PFN_DOWN (paddr));
    if (paddr >= (end_pfn << PAGE_SHIFT))
        break;
}

{
    *ei_startpfn = round_up (ei->addr, PAGE_SIZE) >> PAGE_SHIFT;
    *ei_endpfn = round_down (ei->addr + ei->size, PAGE_SIZE) >> PAGE_SHIFT;
    if (*ei_startpfn >= *ei_endpfn)
        return 0;
    if (ei->type != E820_RAM && *ei_endpfn > max_pfn_mapped)
        max_pfn_mapped = *ei_endpfn;
    if (ei->type != E820_RAM || *ei_endpfn <= start_pfn || *ei_startpfn >= end_pfn)
        return 0;
    if (*ei_startpfn < start_pfn)
        *ei_startpfn = start_pfn;
    if (*ei_endpfn > end_pfn)
        *ei_endpfn = end_pfn;
    if (*ei_startpfn >= end_user_pfn)
        return 0;
    if (*ei_endpfn > end_user_pfn)
        *ei_endpfn = end_user_pfn;
    return 1;
}

{
    unsigned long ei_startpfn;
    unsigned long ei_endpfn;
    int i;
    for (i = 0; i < e820.nr_map; i++)
        if (e820_find_active_region (&e820.map[i], start_pfn, end_pfn, &ei_startpfn, &ei_endpfn))
            add_active_range (nid, ei_startpfn, ei_endpfn);
}

{
    int x = e820.nr_map;
    if (x == E820MAX) {
        printk (KERN_ERR "Ooops! Too many entries in the memory map!\n");
        return;
    }
    e820.map[x].addr = start;
    e820.map[x].size = size;
    e820.map[x].type = type;
    e820.nr_map++;
}

{
    printk (KERN_ERR "Ooops! Too many entries in the memory map!\n");
    return;
}

{
    unsigned long start_pfn = start >> PAGE_SHIFT;
    unsigned long end_pfn = end >> PAGE_SHIFT;
    unsigned long ei_startpfn, ei_endpfn, ram = 0;
    int i;
    for (i = 0; i < e820.nr_map; i++) {
        if (e820_find_active_region (&e820.map[i], start_pfn, end_pfn, &ei_startpfn, &ei_endpfn))
            ram += ei_endpfn - ei_startpfn;
    }
    return end - start - (ram << PAGE_SHIFT);
}

{
    if (e820_find_active_region (&e820.map[i], start_pfn, end_pfn, &ei_startpfn, &ei_endpfn))
        ram += ei_endpfn - ei_startpfn;
}

{
    int i;
    for (i = 0; i < e820.nr_map; i++) {
        printk (KERN_INFO " %s: %016Lx - %016Lx ", who, (unsigned long long) e820.map [i].addr, (unsigned long long) (e820.map [i].addr + e820.map [i].size));
        switch (e820.map[i].type) {
        case E820_RAM :
            printk (KERN_CONT "(usable)\n");
            break;
        case E820_RESERVED :
            printk (KERN_CONT "(reserved)\n");
            break;
        case E820_ACPI :
            printk (KERN_CONT "(ACPI data)\n");
            break;
        case E820_NVS :
            printk (KERN_CONT "(ACPI NVS)\n");
            break;
        default :
            printk (KERN_CONT "type %u\n", e820.map[i].type);
            break;
        }
    }
}

{
    printk (KERN_INFO " %s: %016Lx - %016Lx ", who, (unsigned long long) e820.map [i].addr, (unsigned long long) (e820.map [i].addr + e820.map [i].size));
    switch (e820.map[i].type) {
    case E820_RAM :
        printk (KERN_CONT "(usable)\n");
        break;
    case E820_RESERVED :
        printk (KERN_CONT "(reserved)\n");
        break;
    case E820_ACPI :
        printk (KERN_CONT "(ACPI data)\n");
        break;
    case E820_NVS :
        printk (KERN_CONT "(ACPI NVS)\n");
        break;
    default :
        printk (KERN_CONT "type %u\n", e820.map[i].type);
        break;
    }
}

{
case E820_RAM :
    printk (KERN_CONT "(usable)\n");
    break;
case E820_RESERVED :
    printk (KERN_CONT "(reserved)\n");
    break;
case E820_ACPI :
    printk (KERN_CONT "(ACPI data)\n");
    break;
case E820_NVS :
    printk (KERN_CONT "(ACPI NVS)\n");
    break;
default :
    printk (KERN_CONT "type %u\n", e820.map[i].type);
    break;
}

{
    struct change_member {
        struct e820entry *pbios;
        unsigned long long addr;
    };
    static struct change_member change_point_list [2 * E820MAX] __initdata;
    static struct change_member *change_point [2 * E820MAX] __initdata;
    static struct e820entry *overlap_list [E820MAX] __initdata;
    static struct e820entry new_bios [E820MAX] __initdata;
    struct change_member *change_tmp;
    unsigned long current_type, last_type;
    unsigned long long last_addr;
    int chgidx, still_changing;
    int overlap_entries;
    int new_bios_entry;
    int old_nr, new_nr, chg_nr;
    int i;
    if (*pnr_map < 2)
        return -1;
    old_nr = *pnr_map;
    for (i = 0; i < old_nr; i++)
        if (biosmap[i].addr + biosmap[i].size < biosmap[i].addr)
            return -1;
    for (i = 0; i < 2 * old_nr; i++)
        change_point[i] = &change_point_list[i];
    chgidx = 0;
    for (i = 0; i < old_nr; i++) {
        if (biosmap[i].size != 0) {
            change_point[chgidx]->addr = biosmap[i].addr;
            change_point[chgidx++]->pbios = &biosmap[i];
            change_point[chgidx]->addr = biosmap[i].addr + biosmap[i].size;
            change_point[chgidx++]->pbios = &biosmap[i];
        }
    }
    chg_nr = chgidx;
    still_changing = 1;
    while (still_changing) {
        still_changing = 0;
        for (i = 1; i < chg_nr; i++) {
            unsigned long long curaddr, lastaddr;
            unsigned long long curpbaddr, lastpbaddr;
            curaddr = change_point[i]->addr;
            lastaddr = change_point[i - 1]->addr;
            curpbaddr = change_point[i]->pbios->addr;
            lastpbaddr = change_point[i - 1]->pbios->addr;
            if (curaddr < lastaddr || (curaddr == lastaddr && curaddr == curpbaddr && lastaddr != lastpbaddr)) {
                change_tmp = change_point[i];
                change_point[i] = change_point[i - 1];
                change_point[i - 1] = change_tmp;
                still_changing = 1;
            }
        }
    }
    overlap_entries = 0;
    new_bios_entry = 0;
    last_type = 0;
    last_addr = 0;
    for (chgidx = 0; chgidx < chg_nr; chgidx++) {
        if (change_point[chgidx]->addr == change_point[chgidx]->pbios->addr) {
            overlap_list[overlap_entries++] = change_point[chgidx]->pbios;
        }
        else {
            for (i = 0; i < overlap_entries; i++) {
                if (overlap_list[i] == change_point[chgidx]->pbios)
                    overlap_list[i] = overlap_list[overlap_entries - 1];
            }
            overlap_entries--;
        }
        current_type = 0;
        for (i = 0; i < overlap_entries; i++)
            if (overlap_list[i]->type > current_type)
                current_type = overlap_list[i]->type;
        if (current_type != last_type) {
            if (last_type != 0) {
                new_bios[new_bios_entry].size = change_point[chgidx]->addr - last_addr;
                if (new_bios[new_bios_entry].size != 0)
                    if (++new_bios_entry >= E820MAX)
                        break;
            }
            if (current_type != 0) {
                new_bios[new_bios_entry].addr = change_point[chgidx]->addr;
                new_bios[new_bios_entry].type = current_type;
                last_addr = change_point[chgidx]->addr;
            }
            last_type = current_type;
        }
    }
    new_nr = new_bios_entry;
    memcpy (biosmap, new_bios, new_nr * sizeof (struct e820entry));
    *pnr_map = new_nr;
    return 0;
}

{
    if (biosmap[i].size != 0) {
        change_point[chgidx]->addr = biosmap[i].addr;
        change_point[chgidx++]->pbios = &biosmap[i];
        change_point[chgidx]->addr = biosmap[i].addr + biosmap[i].size;
        change_point[chgidx++]->pbios = &biosmap[i];
    }
}

{
    change_point[chgidx]->addr = biosmap[i].addr;
    change_point[chgidx++]->pbios = &biosmap[i];
    change_point[chgidx]->addr = biosmap[i].addr + biosmap[i].size;
    change_point[chgidx++]->pbios = &biosmap[i];
}

{
    still_changing = 0;
    for (i = 1; i < chg_nr; i++) {
        unsigned long long curaddr, lastaddr;
        unsigned long long curpbaddr, lastpbaddr;
        curaddr = change_point[i]->addr;
        lastaddr = change_point[i - 1]->addr;
        curpbaddr = change_point[i]->pbios->addr;
        lastpbaddr = change_point[i - 1]->pbios->addr;
        if (curaddr < lastaddr || (curaddr == lastaddr && curaddr == curpbaddr && lastaddr != lastpbaddr)) {
            change_tmp = change_point[i];
            change_point[i] = change_point[i - 1];
            change_point[i - 1] = change_tmp;
            still_changing = 1;
        }
    }
}

{
    unsigned long long curaddr, lastaddr;
    unsigned long long curpbaddr, lastpbaddr;
    curaddr = change_point[i]->addr;
    lastaddr = change_point[i - 1]->addr;
    curpbaddr = change_point[i]->pbios->addr;
    lastpbaddr = change_point[i - 1]->pbios->addr;
    if (curaddr < lastaddr || (curaddr == lastaddr && curaddr == curpbaddr && lastaddr != lastpbaddr)) {
        change_tmp = change_point[i];
        change_point[i] = change_point[i - 1];
        change_point[i - 1] = change_tmp;
        still_changing = 1;
    }
}

{
    change_tmp = change_point[i];
    change_point[i] = change_point[i - 1];
    change_point[i - 1] = change_tmp;
    still_changing = 1;
}

{
    if (change_point[chgidx]->addr == change_point[chgidx]->pbios->addr) {
        overlap_list[overlap_entries++] = change_point[chgidx]->pbios;
    }
    else {
        for (i = 0; i < overlap_entries; i++) {
            if (overlap_list[i] == change_point[chgidx]->pbios)
                overlap_list[i] = overlap_list[overlap_entries - 1];
        }
        overlap_entries--;
    }
    current_type = 0;
    for (i = 0; i < overlap_entries; i++)
        if (overlap_list[i]->type > current_type)
            current_type = overlap_list[i]->type;
    if (current_type != last_type) {
        if (last_type != 0) {
            new_bios[new_bios_entry].size = change_point[chgidx]->addr - last_addr;
            if (new_bios[new_bios_entry].size != 0)
                if (++new_bios_entry >= E820MAX)
                    break;
        }
        if (current_type != 0) {
            new_bios[new_bios_entry].addr = change_point[chgidx]->addr;
            new_bios[new_bios_entry].type = current_type;
            last_addr = change_point[chgidx]->addr;
        }
        last_type = current_type;
    }
}

{
    overlap_list[overlap_entries++] = change_point[chgidx]->pbios;
}

{
    for (i = 0; i < overlap_entries; i++) {
        if (overlap_list[i] == change_point[chgidx]->pbios)
            overlap_list[i] = overlap_list[overlap_entries - 1];
    }
    overlap_entries--;
}

{
    if (overlap_list[i] == change_point[chgidx]->pbios)
        overlap_list[i] = overlap_list[overlap_entries - 1];
}

{
    if (last_type != 0) {
        new_bios[new_bios_entry].size = change_point[chgidx]->addr - last_addr;
        if (new_bios[new_bios_entry].size != 0)
            if (++new_bios_entry >= E820MAX)
                break;
    }
    if (current_type != 0) {
        new_bios[new_bios_entry].addr = change_point[chgidx]->addr;
        new_bios[new_bios_entry].type = current_type;
        last_addr = change_point[chgidx]->addr;
    }
    last_type = current_type;
}

{
    new_bios[new_bios_entry].size = change_point[chgidx]->addr - last_addr;
    if (new_bios[new_bios_entry].size != 0)
        if (++new_bios_entry >= E820MAX)
            break;
}

{
    new_bios[new_bios_entry].addr = change_point[chgidx]->addr;
    new_bios[new_bios_entry].type = current_type;
    last_addr = change_point[chgidx]->addr;
}

{
    if (nr_map < 2)
        return -1;
    do {
        u64 start = biosmap->addr;
        u64 size = biosmap->size;
        u64 end = start + size;
        u32 type = biosmap->type;
        if (start > end)
            return -1;
        add_memory_region (start, size, type);
    }
    while (biosmap++, --nr_map);
    return 0;
}

{
    u64 start = biosmap->addr;
    u64 size = biosmap->size;
    u64 end = start + size;
    u32 type = biosmap->type;
    if (start > end)
        return -1;
    add_memory_region (start, size, type);
}

{
    early_printk (msg);
    panic (msg);
}

{
    char *who = "BIOS-e820";
    sanitize_e820_map (boot_params.e820_map, & boot_params.e820_entries);
    if (copy_e820_map (boot_params.e820_map, boot_params.e820_entries) < 0)
        early_panic ("Cannot find a valid memory map");
    printk (KERN_INFO "BIOS-provided physical RAM map:\n");
    e820_print_map (who);
    return who;
}

{
    if (!p)
        return -EINVAL;
    end_user_pfn = memparse (p, &p);
    end_user_pfn >>= PAGE_SHIFT;
    return 0;
}

{
    char *oldp;
    unsigned long long start_at, mem_size;
    if (!strcmp (p, "exactmap")) {
        max_pfn_mapped = 0;
        e820.nr_map = 0;
        userdef = 1;
        return 0;
    }
    oldp = p;
    mem_size = memparse (p, &p);
    if (p == oldp)
        return -EINVAL;
    userdef = 1;
    if (*p == '@') {
        start_at = memparse (p +1, &p);
        add_memory_region (start_at, mem_size, E820_RAM);
    }
    else if (*p == '#') {
        start_at = memparse (p +1, &p);
        add_memory_region (start_at, mem_size, E820_ACPI);
    }
    else if (*p == '$') {
        start_at = memparse (p +1, &p);
        add_memory_region (start_at, mem_size, E820_RESERVED);
    }
    else {
        end_user_pfn = (mem_size >> PAGE_SHIFT);
    }
    return *p == '\0' ? 0 : -EINVAL;
}

{
    max_pfn_mapped = 0;
    e820.nr_map = 0;
    userdef = 1;
    return 0;
}

{
    start_at = memparse (p +1, &p);
    add_memory_region (start_at, mem_size, E820_RAM);
}

{
    start_at = memparse (p +1, &p);
    add_memory_region (start_at, mem_size, E820_ACPI);
}

{
    start_at = memparse (p +1, &p);
    add_memory_region (start_at, mem_size, E820_RESERVED);
}

{
    end_user_pfn = (mem_size >> PAGE_SHIFT);
}

{
    if (userdef) {
        char nr = e820.nr_map;
        if (sanitize_e820_map (e820.map, &nr) < 0)
            early_panic ("Invalid user supplied memory map");
        e820.nr_map = nr;
        printk (KERN_INFO "user-defined physical RAM map:\n");
        e820_print_map ("user");
    }
}

{
    char nr = e820.nr_map;
    if (sanitize_e820_map (e820.map, &nr) < 0)
        early_panic ("Invalid user supplied memory map");
    e820.nr_map = nr;
    printk (KERN_INFO "user-defined physical RAM map:\n");
    e820_print_map ("user");
}

{
    int i;
    BUG_ON (old_type == new_type);
    for (i = 0; i < e820.nr_map; i++) {
        struct e820entry *ei = &e820.map[i];
        u64 final_start, final_end;
        if (ei->type != old_type)
            continue;
        if (ei->addr >= start && ei->size <= size) {
            ei->type = new_type;
            continue;
        }
        final_start = max (start, ei->addr);
        final_end = min (start +size, ei->addr + ei->size);
        if (final_start >= final_end)
            continue;
        add_memory_region (final_start, final_end - final_start, new_type);
    }
}

{
    struct e820entry *ei = &e820.map[i];
    u64 final_start, final_end;
    if (ei->type != old_type)
        continue;
    if (ei->addr >= start && ei->size <= size) {
        ei->type = new_type;
        continue;
    }
    final_start = max (start, ei->addr);
    final_end = min (start +size, ei->addr + ei->size);
    if (final_start >= final_end)
        continue;
    add_memory_region (final_start, final_end - final_start, new_type);
}

{
    ei->type = new_type;
    continue;
}

{
    u8 nr_map;
    nr_map = e820.nr_map;
    if (sanitize_e820_map (e820.map, &nr_map))
        return;
    e820.nr_map = nr_map;
    printk (KERN_INFO "modified physical RAM map:\n");
    e820_print_map ("modified");
}

{
    unsigned long gapstart, gapsize, round;
    unsigned long last;
    int i;
    int found = 0;
    last = 0x100000000ull;
    gapstart = 0x10000000;
    gapsize = 0x400000;
    i = e820.nr_map;
    while (--i >= 0) {
        unsigned long long start = e820.map[i].addr;
        unsigned long long end = start + e820.map[i].size;
        if (last > end) {
            unsigned long gap = last - end;
            if (gap > gapsize) {
                gapsize = gap;
                gapstart = end;
                found = 1;
            }
        }
        if (start < last)
            last = start;
    }
    if (!found) {
        gapstart = (end_pfn << PAGE_SHIFT) + 1024 * 1024;
        printk (KERN_ERR "PCI: Warning: Cannot find a gap in the 32bit " "address range\n" KERN_ERR "PCI: Unassigned devices with 32bit resource " "registers may break!\n");
    }
    round = 0x100000;
    while ((gapsize >> 4) > round)
        round += round;
    pci_mem_start = (gapstart + round) & -round;
    printk (KERN_INFO "Allocating PCI resources starting at %lx (gap: %lx:%lx)\n", pci_mem_start, gapstart, gapsize);
}

{
    unsigned long long start = e820.map[i].addr;
    unsigned long long end = start + e820.map[i].size;
    if (last > end) {
        unsigned long gap = last - end;
        if (gap > gapsize) {
            gapsize = gap;
            gapstart = end;
            found = 1;
        }
    }
    if (start < last)
        last = start;
}

{
    unsigned long gap = last - end;
    if (gap > gapsize) {
        gapsize = gap;
        gapstart = end;
        found = 1;
    }
}

{
    gapsize = gap;
    gapstart = end;
    found = 1;
}

{
    gapstart = (end_pfn << PAGE_SHIFT) + 1024 * 1024;
    printk (KERN_ERR "PCI: Warning: Cannot find a gap in the 32bit " "address range\n" KERN_ERR "PCI: Unassigned devices with 32bit resource " "registers may break!\n");
}

{
    int i;
    if (slot < 0 || slot >= e820.nr_map)
        return -1;
    for (i = slot; i < e820.nr_map; i++) {
        if (e820.map[i].type != E820_RAM)
            continue;
        break;
    }
    if (i == e820.nr_map || e820.map[i].addr > (max_pfn << PAGE_SHIFT))
        return -1;
    *addr = e820.map[i].addr;
    *size = min_t (u64, e820.map[i].size + e820.map[i].addr, max_pfn << PAGE_SHIFT) - *addr;
    return i + 1;
}

{
    if (e820.map[i].type != E820_RAM)
        continue;
    break;
}

{
    unsigned long long tsc_now, ns_now;
    unsigned long flags, *scale;
    local_irq_save (flags);
    sched_clock_idle_sleep_event ();
    scale = &per_cpu (cyc2ns, cpu);
    rdtscll (tsc_now);
    ns_now = __cycles_2_ns (tsc_now);
    if (cpu_khz)
        *scale = (NSEC_PER_MSEC << CYC2NS_SCALE_FACTOR) / cpu_khz;
    sched_clock_idle_wakeup_event (0);
    local_irq_restore (flags);
}

{
    unsigned long a = 0;
    rdtscll (a);
    return cycles_2_ns (a);
}

{
    return tsc_unstable;
}

{
    unsigned long t1, t2;
    int i;
    for (i = 0; i < MAX_RETRIES; i++) {
        t1 = get_cycles ();
        if (hpet)
            *hpet = hpet_readl (HPET_COUNTER) & 0xFFFFFFFF;
        else
            *pm = acpi_pm_read_early ();
        t2 = get_cycles ();
        if ((t2 - t1) < SMI_TRESHOLD)
            return t2;
    }
    return ULONG_MAX;
}

{
    t1 = get_cycles ();
    if (hpet)
        *hpet = hpet_readl (HPET_COUNTER) & 0xFFFFFFFF;
    else
        *pm = acpi_pm_read_early ();
    t2 = get_cycles ();
    if ((t2 - t1) < SMI_TRESHOLD)
        return t2;
}

{
    unsigned long flags, tsc1, tsc2, tr1, tr2, pm1, pm2, hpet1, hpet2;
    int hpet = is_hpet_enabled (), cpu;
    local_irq_save (flags);
    tsc1 = tsc_read_refs (&pm1, hpet ? &hpet1 : NULL);
    outb ((inb (0x61) & ~ 0x02) | 0x01, 0x61);
    outb (0xb0, 0x43);
    outb ((CLOCK_TICK_RATE / (1000 / 50)) & 0xff, 0x42);
    outb ((CLOCK_TICK_RATE / (1000 / 50)) >> 8, 0x42);
    tr1 = get_cycles ();
    while ((inb (0x61) & 0x20) == 0)
        ;
    tr2 = get_cycles ();
    tsc2 = tsc_read_refs (&pm2, hpet ? &hpet2 : NULL);
    local_irq_restore (flags);
    tsc_khz = (tr2 - tr1) / 50;
    if (!hpet && !pm1 && !pm2) {
        printk (KERN_INFO "TSC calibrated against PIT\n");
        goto out;
    }
    if (tsc1 == ULONG_MAX || tsc2 == ULONG_MAX) {
        printk (KERN_WARNING "TSC calibration disturbed by SMI, " "using PIT calibration result\n");
        goto out;
    }
    tsc2 = (tsc2 - tsc1) * 1000000L;
    if (hpet) {
        printk (KERN_INFO "TSC calibrated against HPET\n");
        if (hpet2 < hpet1)
            hpet2 += 0x100000000;
        hpet2 -= hpet1;
        tsc1 = (hpet2 * hpet_readl (HPET_PERIOD)) / 1000000;
    }
    else {
        printk (KERN_INFO "TSC calibrated against PM_TIMER\n");
        if (pm2 < pm1)
            pm2 += ACPI_PM_OVRRUN;
        pm2 -= pm1;
        tsc1 = (pm2 * 1000000000) / PMTMR_TICKS_PER_SEC;
    }
    tsc_khz = tsc2 / tsc1;
out :
    for_each_possible_cpu (cpu)
    set_cyc2ns_scale (tsc_khz, cpu);
}

{
    printk (KERN_INFO "TSC calibrated against PIT\n");
    goto out;
}

{
    printk (KERN_WARNING "TSC calibration disturbed by SMI, " "using PIT calibration result\n");
    goto out;
}

{
    printk (KERN_INFO "TSC calibrated against HPET\n");
    if (hpet2 < hpet1)
        hpet2 += 0x100000000;
    hpet2 -= hpet1;
    tsc1 = (hpet2 * hpet_readl (HPET_PERIOD)) / 1000000;
}

{
    printk (KERN_INFO "TSC calibrated against PM_TIMER\n");
    if (pm2 < pm1)
        pm2 += ACPI_PM_OVRRUN;
    pm2 -= pm1;
    tsc1 = (pm2 * 1000000000) / PMTMR_TICKS_PER_SEC;
}

{
    if (tsc_unstable)
        return 1;
    if (boot_cpu_has (X86_FEATURE_CONSTANT_TSC))
        return 0;
    return num_present_cpus () > 1;
}

