        clocksource_tsc.mult = clocksource_khz2mult (tsc_khz, clocksource_tsc.shift);
        if (check_tsc_unstable ())
            clocksource_tsc.rating = 0;
        clocksource_register (& clocksource_tsc);
    }
}

