{
    size_t bts_size_in_records;
    unsigned long bts;
    void *ds;
    if (!ds_cfg.sizeof_ds || !ds_cfg.sizeof_bts)
        return -EOPNOTSUPP;
    if (bts_size_in_bytes < 0)
        return -EINVAL;
    bts_size_in_records = bts_size_in_bytes / ds_cfg.sizeof_bts;
    bts_size_in_bytes = bts_size_in_records * ds_cfg.sizeof_bts;
    if (bts_size_in_bytes <= 0)
        return -EINVAL;
    bts = (unsigned long) kzalloc (bts_size_in_bytes, GFP_KERNEL);
    if (!bts)
        return -ENOMEM;
    ds = kzalloc (ds_cfg.sizeof_ds, GFP_KERNEL);
    if (!ds) {
        kfree ((void *) bts);
        return -ENOMEM;
    }
    set_bts_buffer_base (ds, bts);
    set_bts_index (ds, bts);
    set_bts_absolute_maximum (ds, bts + bts_size_in_bytes);
    set_bts_interrupt_threshold (ds, bts + bts_size_in_bytes + 1);
    *dsp = ds;
    return 0;
}

{
    kfree ((void *) bts);
    return -ENOMEM;
}

{
    if (*dsp) {
        kfree ((void *) get_bts_buffer_base (* dsp));
        kfree (*dsp);
        *dsp = NULL;
    }
    return 0;
}

{
    kfree ((void *) get_bts_buffer_base (* dsp));
    kfree (*dsp);
    *dsp = NULL;
}

{
    int size_in_bytes;
    if (!ds_cfg.sizeof_ds || !ds_cfg.sizeof_bts)
        return -EOPNOTSUPP;
    if (!ds)
        return 0;
    size_in_bytes = get_bts_absolute_maximum (ds) - get_bts_buffer_base (ds);
    return size_in_bytes;
}

{
    int size_in_bytes = ds_get_bts_size (ds);
    if (size_in_bytes <= 0)
        return size_in_bytes;
    return size_in_bytes / ds_cfg.sizeof_bts;
}

{
    int index_offset_in_bytes;
    if (!ds_cfg.sizeof_ds || !ds_cfg.sizeof_bts)
        return -EOPNOTSUPP;
    index_offset_in_bytes = get_bts_index (ds) - get_bts_buffer_base (ds);
    return index_offset_in_bytes / ds_cfg.sizeof_bts;
}

{
    switch (method) {
    case DS_O_SIGNAL :
        return -EOPNOTSUPP;
    case DS_O_WRAP :
        return 0;
    default :
        return -EINVAL;
    }
}

{
case DS_O_SIGNAL :
    return -EOPNOTSUPP;
case DS_O_WRAP :
    return 0;
default :
