{
    if (!iommu_detected && !no_iommu && end_pfn > MAX_DMA32_PFN)
        swiotlb = 1;
    if (swiotlb_force)
        swiotlb = 1;
    if (swiotlb) {
        printk (KERN_INFO "PCI-DMA: Using software bounce buffering for IO (SWIOTLB)\n");
        swiotlb_init ();
