{
    static char ids [] [8] = {
        [DEBUG_STACK - 1] = "#DB",
        [NMI_STACK - 1] = "NMI",
        [DOUBLEFAULT_STACK - 1] = "#DF",
        [STACKFAULT_STACK - 1] = "#SS",
        [MCE_STACK - 1] = "#MC",
    };
    unsigned k;
    for (k = 0; k < N_EXCEPTION_STACKS; k++) {
        unsigned long end = per_cpu (orig_ist, cpu).ist[k];
        if (stack >= end)
            continue;
        if (stack >= end - EXCEPTION_STKSZ) {
            if (*usedp & (1U << k))
                break;
            *usedp |= 1U << k;
            *idp = ids[k];
            return (unsigned long *) end;
        }
    }
    return NULL;
}

