            if ((unsigned long) stack == bp + 8) {
                ops->address (data, addr, 1);
                frame = frame->next_frame;
                bp = (unsigned long) frame;
            }
            else {
                ops->address (data, addr, bp == 0);
            }
        }
        stack++;
    }
    return bp;
}

