{
    int cpu;
    unsigned long flags;
    oops_enter ();
    raw_local_irq_save (flags);
    cpu = smp_processor_id ();
    if (!__raw_spin_trylock (&die_lock)) {
        if (cpu == die_owner)
            ;
        else
            __raw_spin_lock (&die_lock);
    }
    die_nest_count++;
    die_owner = cpu;
    console_verbose ();
    bust_spinlocks (1);
    return flags;
}

{
    if (cpu == die_owner)
        ;
    else
        __raw_spin_lock (&die_lock);
}

{
    die_owner = -1;
    bust_spinlocks (0);
    die_nest_count--;
    if (!die_nest_count)
        __raw_spin_unlock (&die_lock);
    raw_local_irq_restore (flags);
    if (!regs) {
        oops_exit ();
        return;
    }
    if (panic_on_oops)
        panic ("Fatal exception");
    oops_exit ();
    do_exit (signr);
}

{
    oops_exit ();
    return;
}

{
    static int die_counter;
    printk (KERN_EMERG "%s: %04lx [%u] ", str, err & 0xffff, ++ die_counter);
    printk ("\n");
    if (notify_die (DIE_OOPS, str, regs, err, current->thread.trap_no, SIGSEGV) == NOTIFY_STOP)
        return 1;
    show_registers (regs);
    add_taint (TAINT_DIE);
    printk (KERN_ALERT "RIP ");
    printk_address (regs -> ip, 1);
    printk (" RSP <%016lx>\n", regs -> sp);
    if (kexec_should_crash (current))
        crash_kexec (regs);
    return 0;
}

{
    unsigned long flags = oops_begin ();
    if (!user_mode (regs))
        report_bug (regs->ip, regs);
    if (__die (str, regs, err))
        regs = NULL;
    oops_end (flags, regs, SIGSEGV);
}

{
    unsigned long flags;
    if (notify_die (DIE_NMIWATCHDOG, str, regs, 0, 2, SIGINT) == NOTIFY_STOP)
        return;
    flags = oops_begin ();
    printk (str, smp_processor_id ());
    show_registers (regs);
    if (kexec_should_crash (current))
        crash_kexec (regs);
    if (do_panic || panic_on_oops)
        panic ("Non maskable interrupt");
    oops_end (flags, NULL, SIGBUS);
    nmi_exit ();
    local_irq_enable ();
    do_exit (SIGBUS);
}

{
    struct task_struct *tsk = current;
    if (user_mode (regs)) {
        tsk->thread.error_code = error_code;
        tsk->thread.trap_no = trapnr;
        if (show_unhandled_signals && unhandled_signal (tsk, signr) && printk_ratelimit ()) {
            printk (KERN_INFO "%s[%d] trap %s ip:%lx sp:%lx error:%lx", tsk -> comm, tsk -> pid, str, regs -> ip, regs -> sp, error_code);
            print_vma_addr (" in ", regs -> ip);
            printk ("\n");
        }
        if (info)
            force_sig_info (signr, info, tsk);
        else
            force_sig (signr, tsk);
        return;
    }
    if (!fixup_exception (regs)) {
        tsk->thread.error_code = error_code;
        tsk->thread.trap_no = trapnr;
        die (str, regs, error_code);
    }
    return;
}

{
    tsk->thread.error_code = error_code;
    tsk->thread.trap_no = trapnr;
    if (show_unhandled_signals && unhandled_signal (tsk, signr) && printk_ratelimit ()) {
        printk (KERN_INFO "%s[%d] trap %s ip:%lx sp:%lx error:%lx", tsk -> comm, tsk -> pid, str, regs -> ip, regs -> sp, error_code);
        print_vma_addr (" in ", regs -> ip);
        printk ("\n");
    }
    if (info)
        force_sig_info (signr, info, tsk);
    else
        force_sig (signr, tsk);
    return;
}

{
    printk (KERN_INFO "%s[%d] trap %s ip:%lx sp:%lx error:%lx", tsk -> comm, tsk -> pid, str, regs -> ip, regs -> sp, error_code);
    print_vma_addr (" in ", regs -> ip);
    printk ("\n");
}

{
    tsk->thread.error_code = error_code;
    tsk->thread.trap_no = trapnr;
    die (str, regs, error_code);
}

{
    if (notify_die (DIE_TRAP, "stack segment", regs, error_code, 12, SIGBUS) == NOTIFY_STOP)
        return;
    preempt_conditional_sti (regs);
    do_trap (12, SIGBUS, "stack segment", regs, error_code, NULL);
    preempt_conditional_cli (regs);
}

{
    static const char str [] = "double fault";
    struct task_struct *tsk = current;
    notify_die (DIE_TRAP, str, regs, error_code, 8, SIGSEGV);
    tsk->thread.error_code = error_code;
    tsk->thread.trap_no = 8;
    for (;;)
        die (str, regs, error_code);
}

{
    struct task_struct *tsk = current;
    conditional_sti (regs);
    if (user_mode (regs)) {
        tsk->thread.error_code = error_code;
        tsk->thread.trap_no = 13;
        if (show_unhandled_signals && unhandled_signal (tsk, SIGSEGV) && printk_ratelimit ()) {
            printk (KERN_INFO "%s[%d] general protection ip:%lx sp:%lx error:%lx", tsk -> comm, tsk -> pid, regs -> ip, regs -> sp, error_code);
            print_vma_addr (" in ", regs -> ip);
            printk ("\n");
        }
        force_sig (SIGSEGV, tsk);
        return;
    }
    if (fixup_exception (regs))
        return;
    tsk->thread.error_code = error_code;
    tsk->thread.trap_no = 13;
    if (notify_die (DIE_GPF, "general protection fault", regs, error_code, 13, SIGSEGV) == NOTIFY_STOP)
        return;
    die ("general protection fault", regs, error_code);
}

{
    tsk->thread.error_code = error_code;
    tsk->thread.trap_no = 13;
    if (show_unhandled_signals && unhandled_signal (tsk, SIGSEGV) && printk_ratelimit ()) {
        printk (KERN_INFO "%s[%d] general protection ip:%lx sp:%lx error:%lx", tsk -> comm, tsk -> pid, regs -> ip, regs -> sp, error_code);
        print_vma_addr (" in ", regs -> ip);
        printk ("\n");
    }
    force_sig (SIGSEGV, tsk);
    return;
}

{
    printk (KERN_INFO "%s[%d] general protection ip:%lx sp:%lx error:%lx", tsk -> comm, tsk -> pid, regs -> ip, regs -> sp, error_code);
    print_vma_addr (" in ", regs -> ip);
    printk ("\n");
}

{
    printk (KERN_EMERG "Uhhuh. NMI received for unknown reason %02x.\n", reason);
    printk (KERN_EMERG "You have some hardware problem, likely on the PCI bus.\n");
    if (panic_on_unrecovered_nmi)
        panic ("NMI: Not continuing");
    printk (KERN_EMERG "Dazed and confused, but trying to continue\n");
    reason = (reason & 0xf) | 4;
    outb (reason, 0x61);
}

{
    printk ("NMI: IOCK error (debug interrupt?)\n");
    show_registers (regs);
    reason = (reason & 0xf) | 8;
    outb (reason, 0x61);
    mdelay (2000);
    reason &= ~8;
    outb (reason, 0x61);
}

{
    if (notify_die (DIE_NMIUNKNOWN, "nmi", regs, reason, 2, SIGINT) == NOTIFY_STOP)
        return;
    printk (KERN_EMERG "Uhhuh. NMI received for unknown reason %02x.\n", reason);
    printk (KERN_EMERG "Do you have a strange power saving mode enabled?\n");
    if (panic_on_unrecovered_nmi)
        panic ("NMI: Not continuing");
    printk (KERN_EMERG "Dazed and confused, but trying to continue\n");
}

{
    unsigned char reason = 0;
    int cpu;
    cpu = smp_processor_id ();
    if (!cpu)
        reason = get_nmi_reason ();
    if (!(reason & 0xc0)) {
        if (notify_die (DIE_NMI_IPI, "nmi_ipi", regs, reason, 2, SIGINT) == NOTIFY_STOP)
            return;
        if (nmi_watchdog_tick (regs, reason))
            return;
        if (!do_nmi_callback (regs, cpu))
            unknown_nmi_error (reason, regs);
        return;
    }
    if (notify_die (DIE_NMI, "nmi", regs, reason, 2, SIGINT) == NOTIFY_STOP)
        return;
    if (reason & 0x80)
        mem_parity_error (reason, regs);
    if (reason & 0x40)
        io_check_error (reason, regs);
}

{
    if (notify_die (DIE_NMI_IPI, "nmi_ipi", regs, reason, 2, SIGINT) == NOTIFY_STOP)
        return;
    if (nmi_watchdog_tick (regs, reason))
        return;
    if (!do_nmi_callback (regs, cpu))
        unknown_nmi_error (reason, regs);
    return;
}

{
    trace_hardirqs_fixup ();
    if (notify_die (DIE_INT3, "int3", regs, error_code, 3, SIGTRAP) == NOTIFY_STOP) {
        return;
    }
    preempt_conditional_sti (regs);
    do_trap (3, SIGTRAP, "int3", regs, error_code, NULL);
    preempt_conditional_cli (regs);
}

{
    return;
}

{
    struct pt_regs *regs = eregs;
    if (eregs == (struct pt_regs *) eregs->sp)
        ;
    else if (user_mode (eregs))
        regs = task_pt_regs (current);
    else if (eregs->flags & X86_EFLAGS_IF)
        regs = (struct pt_regs *) (eregs->sp -= sizeof (struct pt_regs));
    if (eregs != regs)
        *regs = *eregs;
    return regs;
}

{
    unsigned long condition;
    struct task_struct *tsk = current;
    siginfo_t info;
    trace_hardirqs_fixup ();
    get_debugreg (condition, 6);
    clear_tsk_thread_flag (tsk, TIF_DEBUGCTLMSR);
    tsk->thread.debugctlmsr = 0;
    if (notify_die (DIE_DEBUG, "debug", regs, condition, error_code, SIGTRAP) == NOTIFY_STOP)
        return;
    preempt_conditional_sti (regs);
    if (condition & (DR_TRAP0 | DR_TRAP1 | DR_TRAP2 | DR_TRAP3)) {
        if (!tsk->thread.debugreg7) {
            goto clear_dr7;
        }
    }
    tsk->thread.debugreg6 = condition;
    if (condition & DR_STEP) {
        if (!user_mode (regs))
            goto clear_TF_reenable;
    }
    tsk->thread.trap_no = 1;
    tsk->thread.error_code = error_code;
    info.si_signo = SIGTRAP;
    info.si_errno = 0;
    info.si_code = TRAP_BRKPT;
    info.si_addr = user_mode (regs) ? (void __user *) regs->ip : NULL;
    force_sig_info (SIGTRAP, & info, tsk);
clear_dr7 :
    set_debugreg (0UL, 7);
    preempt_conditional_cli (regs);
    return;
clear_TF_reenable :
    set_tsk_thread_flag (tsk, TIF_SINGLESTEP);
    regs->flags &= ~X86_EFLAGS_TF;
    preempt_conditional_cli (regs);
}

{
    if (!tsk->thread.debugreg7) {
        goto clear_dr7;
    }
}

{
    goto clear_dr7;
}

{
    if (!user_mode (regs))
        goto clear_TF_reenable;
}

{
    if (fixup_exception (regs))
        return 1;
    notify_die (DIE_GPF, str, regs, 0, trapnr, SIGFPE);
    current->thread.trap_no = trapnr;
    die (str, regs, 0);
    return 0;
}

{
    void __user *ip = (void __user *) (regs->ip);
    struct task_struct *task;
    siginfo_t info;
    unsigned short cwd, swd;
    conditional_sti (regs);
    if (!user_mode (regs) && kernel_math_error (regs, "kernel x87 math error", 16))
        return;
    task = current;
    save_init_fpu (task);
    task->thread.trap_no = 16;
    task->thread.error_code = 0;
    info.si_signo = SIGFPE;
    info.si_errno = 0;
    info.si_code = __SI_FAULT;
    info.si_addr = ip;
    cwd = get_fpu_cwd (task);
    swd = get_fpu_swd (task);
    switch (swd & ~cwd & 0x3f) {
    case 0x000 :
    default :
        break;
    case 0x001 :
        info.si_code = FPE_FLTINV;
        break;
    case 0x002 :
    case 0x010 :
        info.si_code = FPE_FLTUND;
        break;
    case 0x004 :
        info.si_code = FPE_FLTDIV;
        break;
    case 0x008 :
        info.si_code = FPE_FLTOVF;
        break;
    case 0x020 :
        info.si_code = FPE_FLTRES;
        break;
    }
    force_sig_info (SIGFPE, & info, task);
}

{
case 0x000 :
default :
    break;
case 0x001 :
    info.si_code = FPE_FLTINV;
    break;
case 0x002 :
case 0x010 :
    info.si_code = FPE_FLTUND;
    break;
case 0x004 :
    info.si_code = FPE_FLTDIV;
    break;
case 0x008 :
    info.si_code = FPE_FLTOVF;
    break;
case 0x020 :
    info.si_code = FPE_FLTRES;
    break;
}

{
    printk ("bad interrupt");
}

{
    void __user *ip = (void __user *) (regs->ip);
    struct task_struct *task;
    siginfo_t info;
    unsigned short mxcsr;
    conditional_sti (regs);
    if (!user_mode (regs) && kernel_math_error (regs, "kernel simd math error", 19))
        return;
    task = current;
    save_init_fpu (task);
    task->thread.trap_no = 19;
    task->thread.error_code = 0;
    info.si_signo = SIGFPE;
    info.si_errno = 0;
    info.si_code = __SI_FAULT;
    info.si_addr = ip;
    mxcsr = get_fpu_mxcsr (task);
    switch (~((mxcsr & 0x1f80) >> 7) & (mxcsr & 0x3f)) {
    case 0x000 :
    default :
        break;
    case 0x001 :
        info.si_code = FPE_FLTINV;
        break;
    case 0x002 :
    case 0x010 :
        info.si_code = FPE_FLTUND;
        break;
    case 0x004 :
        info.si_code = FPE_FLTDIV;
        break;
    case 0x008 :
        info.si_code = FPE_FLTOVF;
        break;
    case 0x020 :
        info.si_code = FPE_FLTRES;
        break;
    }
    force_sig_info (SIGFPE, & info, task);
}

{
case 0x000 :
default :
    break;
case 0x001 :
    info.si_code = FPE_FLTINV;
    break;
case 0x002 :
case 0x010 :
    info.si_code = FPE_FLTUND;
    break;
case 0x004 :
    info.si_code = FPE_FLTDIV;
    break;
case 0x008 :
    info.si_code = FPE_FLTOVF;
    break;
case 0x020 :
    info.si_code = FPE_FLTRES;
    break;
}

{
    struct task_struct *me = current;
    if (!used_math ()) {
        local_irq_enable ();
        if (init_fpu (me)) {
            do_group_exit (SIGKILL);
            return;
        }
        local_irq_disable ();
    }
    clts ();
    restore_fpu_checking (& me -> thread.xstate -> fxsave);
    task_thread_info (me)->status |= TS_USEDFPU;
    me->fpu_counter++;
}

{
    local_irq_enable ();
    if (init_fpu (me)) {
        do_group_exit (SIGKILL);
        return;
    }
    local_irq_disable ();
}

{
    do_group_exit (SIGKILL);
    return;
}

{
    set_intr_gate (0, & divide_error);
    set_intr_gate_ist (1, & debug, DEBUG_STACK);
    set_intr_gate_ist (2, & nmi, NMI_STACK);
    set_system_gate_ist (3, & int3, DEBUG_STACK);
    set_system_gate (4, & overflow);
    set_intr_gate (5, & bounds);
    set_intr_gate (6, & invalid_op);
    set_intr_gate (7, & device_not_available);
    set_intr_gate_ist (8, & double_fault, DOUBLEFAULT_STACK);
    set_intr_gate (9, & coprocessor_segment_overrun);
    set_intr_gate (10, & invalid_TSS);
    set_intr_gate (11, & segment_not_present);
    set_intr_gate_ist (12, & stack_segment, STACKFAULT_STACK);
    set_intr_gate (13, & general_protection);
    set_intr_gate (14, & page_fault);
    set_intr_gate (15, & spurious_interrupt_bug);
    set_intr_gate (16, & coprocessor_error);
    set_intr_gate (17, & alignment_check);
    set_intr_gate (19, & simd_coprocessor_error);
    init_thread_xstate ();
    cpu_init ();
}

{
    if (!s)
        return -EINVAL;
    if (!strcmp (s, "panic"))
        panic_on_oops = 1;
    return 0;
}

{
    if (!s)
        return -EINVAL;
    kstack_depth_to_print = simple_strtoul (s, NULL, 0);
    return 0;
}

{
    code_bytes = simple_strtoul (s, NULL, 0);
    if (code_bytes > 8192)
        code_bytes = 8192;
    return 1;
}

{
    return cpumask_of_cpu (0);
}

{
    cpumask_t domain = CPU_MASK_NONE;
    cpu_set (cpu, domain);
    return domain;
}

{
    unsigned long val;
    int nasid;
    nasid = uv_apicid_to_nasid (phys_apicid);
    val = (1UL << UVH_IPI_INT_SEND_SHFT) | (phys_apicid << UVH_IPI_INT_APIC_ID_SHFT) | (((long) start_rip << UVH_IPI_INT_VECTOR_SHFT) >> 12) | APIC_DM_INIT;
    uv_write_global_mmr64 (nasid, UVH_IPI_INT, val);
    mdelay (10);
    val = (1UL << UVH_IPI_INT_SEND_SHFT) | (phys_apicid << UVH_IPI_INT_APIC_ID_SHFT) | (((long) start_rip << UVH_IPI_INT_VECTOR_SHFT) >> 12) | APIC_DM_STARTUP;
    uv_write_global_mmr64 (nasid, UVH_IPI_INT, val);
    return 0;
}

{
    unsigned long val, apicid, lapicid;
    int nasid;
    apicid = per_cpu (x86_cpu_to_apicid, cpu);
    lapicid = apicid & 0x3f;
    nasid = uv_apicid_to_nasid (apicid);
    val = (1UL << UVH_IPI_INT_SEND_SHFT) | (lapicid << UVH_IPI_INT_APIC_ID_SHFT) | (vector << UVH_IPI_INT_VECTOR_SHFT);
    uv_write_global_mmr64 (nasid, UVH_IPI_INT, val);
}

{
    unsigned int cpu;
    for (cpu = 0; cpu < NR_CPUS; ++cpu)
        if (cpu_isset (cpu, mask))
            uv_send_IPI_one (cpu, vector);
}

{
    cpumask_t mask = cpu_online_map;
    cpu_clear (smp_processor_id (), mask);
    if (!cpus_empty (mask))
        uv_send_IPI_mask (mask, vector);
}

{
    uv_send_IPI_mask (cpu_online_map, vector);
}

{
    return 1;
}

{
    int cpu;
    cpu = first_cpu (cpumask);
    if ((unsigned) cpu < NR_CPUS)
        return per_cpu (x86_cpu_to_apicid, cpu);
    else
        return BAD_APICID;
}

{
    return GET_APIC_ID (read_apic_id ()) >> index_msb;
}

{
    __get_cpu_var (x2apic_extra_bits) = ((nasid >> 1) << 6);
}

{
    union uvh_si_addr_map_config_u m_n_config;
    int bytes, nid, cpu, lcpu, nasid, last_nasid, blade;
    unsigned long mmr_base;
    m_n_config.v = uv_read_local_mmr (UVH_SI_ADDR_MAP_CONFIG);
    mmr_base = uv_read_local_mmr (UVH_RH_GAM_MMR_OVERLAY_CONFIG_MMR) & ~UV_MMR_ENABLE;
    printk (KERN_DEBUG "UV: global MMR base 0x%lx\n", mmr_base);
    last_nasid = -1;

    for_each_possible_cpu (cpu) {
        nid = cpu_to_node (cpu);
        nasid = uv_apicid_to_nasid (per_cpu (x86_cpu_to_apicid, cpu));
        if (nasid != last_nasid)
            uv_possible_blades++;
        last_nasid = nasid;
    }

    printk (KERN_DEBUG "UV: Found %d blades\n", uv_num_possible_blades ());
    bytes = sizeof (struct uv_blade_info) * uv_num_possible_blades ();
    uv_blade_info = alloc_bootmem_pages (bytes);
    bytes = sizeof (uv_node_to_blade[0]) * num_possible_nodes ();
    uv_node_to_blade = alloc_bootmem_pages (bytes);
    memset (uv_node_to_blade, 255, bytes);
    bytes = sizeof (uv_cpu_to_blade[0]) * num_possible_cpus ();
    uv_cpu_to_blade = alloc_bootmem_pages (bytes);
    memset (uv_cpu_to_blade, 255, bytes);
    last_nasid = -1;
    blade = -1;
    lcpu = -1;

    for_each_possible_cpu (cpu) {
        nid = cpu_to_node (cpu);
        nasid = uv_apicid_to_nasid (per_cpu (x86_cpu_to_apicid, cpu));
        if (nasid != last_nasid) {
            blade++;
            lcpu = -1;
            uv_blade_info[blade].nr_posible_cpus = 0;
            uv_blade_info[blade].nr_online_cpus = 0;
        }
        last_nasid = nasid;
        lcpu++;
        uv_cpu_hub_info (cpu)->m_val = m_n_config.s.m_skt;
        uv_cpu_hub_info (cpu)->n_val = m_n_config.s.n_skt;
        uv_cpu_hub_info (cpu)->numa_blade_id = blade;
        uv_cpu_hub_info (cpu)->blade_processor_id = lcpu;
        uv_cpu_hub_info (cpu)->local_nasid = nasid;
        uv_cpu_hub_info (cpu)->gnode_upper = nasid & ~((1 << uv_hub_info->n_val) - 1);
        uv_cpu_hub_info (cpu)->global_mmr_base = mmr_base;
        uv_cpu_hub_info (cpu)->coherency_domain_number = 0;
        uv_blade_info[blade].nasid = nasid;
        uv_blade_info[blade].nr_posible_cpus++;
        uv_node_to_blade[nid] = blade;
        uv_cpu_to_blade[cpu] = blade;
        printk (KERN_DEBUG "UV cpu %d, apicid 0x%x, nasid %d, nid %d\n", cpu, per_cpu (x86_cpu_to_apicid, cpu), nasid, nid);
        printk (KERN_DEBUG "UV   lcpu %d, blade %d\n", lcpu, blade);
    }

}

{
    nid = cpu_to_node (cpu);
    nasid = uv_apicid_to_nasid (per_cpu (x86_cpu_to_apicid, cpu));
    if (nasid != last_nasid)
        uv_possible_blades++;
    last_nasid = nasid;
}

{
    nid = cpu_to_node (cpu);
    nasid = uv_apicid_to_nasid (per_cpu (x86_cpu_to_apicid, cpu));
    if (nasid != last_nasid) {
        blade++;
        lcpu = -1;
        uv_blade_info[blade].nr_posible_cpus = 0;
        uv_blade_info[blade].nr_online_cpus = 0;
    }
    last_nasid = nasid;
    lcpu++;
    uv_cpu_hub_info (cpu)->m_val = m_n_config.s.m_skt;
    uv_cpu_hub_info (cpu)->n_val = m_n_config.s.n_skt;
    uv_cpu_hub_info (cpu)->numa_blade_id = blade;
    uv_cpu_hub_info (cpu)->blade_processor_id = lcpu;
    uv_cpu_hub_info (cpu)->local_nasid = nasid;
    uv_cpu_hub_info (cpu)->gnode_upper = nasid & ~((1 << uv_hub_info->n_val) - 1);
    uv_cpu_hub_info (cpu)->global_mmr_base = mmr_base;
    uv_cpu_hub_info (cpu)->coherency_domain_number = 0;
    uv_blade_info[blade].nasid = nasid;
    uv_blade_info[blade].nr_posible_cpus++;
    uv_node_to_blade[nid] = blade;
    uv_cpu_to_blade[cpu] = blade;
    printk (KERN_DEBUG "UV cpu %d, apicid 0x%x, nasid %d, nid %d\n", cpu, per_cpu (x86_cpu_to_apicid, cpu), nasid, nid);
    printk (KERN_DEBUG "UV   lcpu %d, blade %d\n", lcpu, blade);
}

{
    blade++;
    lcpu = -1;
    uv_blade_info[blade].nr_posible_cpus = 0;
    uv_blade_info[blade].nr_online_cpus = 0;
}

{
    if (!uv_node_to_blade)
        uv_system_init ();
    uv_blade_info[uv_numa_blade_id ()].nr_online_cpus++;
    if (get_uv_system_type () == UV_NON_UNIQUE_APIC)
        set_x2apic_extra_bits (uv_hub_info->local_nasid);
}

{
    struct wakeup_header *header;
    if (!acpi_realmode) {
        printk (KERN_ERR "Could not allocate memory during boot, " "S3 disabled\n");
        return -ENOMEM;
    }
    memcpy ((void *) acpi_realmode, & wakeup_code_start, WAKEUP_SIZE);
    header = (struct wakeup_header *) (acpi_realmode + HEADER_OFFSET);
    if (header->signature != 0x51ee1111) {
        printk (KERN_ERR "wakeup header does not match\n");
        return -EINVAL;
    }
    header->video_mode = saved_video_mode;
    header->wakeup_jmp_seg = acpi_wakeup_address >> 4;
    header->wakeup_gdt[0] = (u64) (sizeof (header->wakeup_gdt) - 1) + ((u64) (acpi_wakeup_address + ((char *) &header->wakeup_gdt - (char *) acpi_realmode)) << 16);
    header->wakeup_gdt[1] = (0x009bULL << 40) + ((u64) acpi_wakeup_address << 16) + 0xffff;
    header->wakeup_gdt[2] = (0x0093ULL << 40) + ((u64) acpi_wakeup_address << 16) + 0xffff;
    store_gdt ((struct desc_ptr *) & header -> pmode_gdt);
    header->pmode_efer_low = nx_enabled;
    if (header->pmode_efer_low & 1) {
        rdmsr (MSR_EFER, header -> pmode_efer_low, header -> pmode_efer_high);
    }
    header->pmode_cr0 = read_cr0 ();
    header->pmode_cr4 = read_cr4 ();
    header->realmode_flags = acpi_realmode_flags;
    header->real_magic = 0x12345678;
    header->pmode_entry = (u32) &wakeup_pmode_return;
    header->pmode_cr3 = (u32) (swsusp_pg_dir - __PAGE_OFFSET);
    saved_magic = 0x12345678;
    return 0;
}

{
    printk (KERN_ERR "Could not allocate memory during boot, " "S3 disabled\n");
    return -ENOMEM;
}

{
    printk (KERN_ERR "wakeup header does not match\n");
    return -EINVAL;
}

{
    rdmsr (MSR_EFER, header -> pmode_efer_low, header -> pmode_efer_high);
}

{
    if ((&wakeup_code_end - &wakeup_code_start) > WAKEUP_SIZE) {
        printk (KERN_ERR "ACPI: Wakeup code way too big, S3 disabled.\n");
        return;
    }
    acpi_realmode = (unsigned long) alloc_bootmem_low (WAKEUP_SIZE);
    if (!acpi_realmode) {
        printk (KERN_ERR "ACPI: Cannot allocate lowmem, S3 disabled.\n");
        return;
    }
    acpi_wakeup_address = virt_to_phys ((void *) acpi_realmode);
}

