{
    unsigned long flags;
    unsigned int v;
    if (evt->features & CLOCK_EVT_FEAT_DUMMY)
        return;
    local_irq_save (flags);
    switch (mode) {
    case CLOCK_EVT_MODE_PERIODIC :
    case CLOCK_EVT_MODE_ONESHOT :
        __setup_APIC_LVTT (calibration_result, mode != CLOCK_EVT_MODE_PERIODIC, 1);
        break;
    case CLOCK_EVT_MODE_UNUSED :
    case CLOCK_EVT_MODE_SHUTDOWN :
        v = apic_read (APIC_LVTT);
        v |= (APIC_LVT_MASKED | LOCAL_TIMER_VECTOR);
        apic_write (APIC_LVTT, v);
        break;
    case CLOCK_EVT_MODE_RESUME :
        break;
    }
    local_irq_restore (flags);
}

{
case CLOCK_EVT_MODE_PERIODIC :
case CLOCK_EVT_MODE_ONESHOT :
    __setup_APIC_LVTT (calibration_result, mode != CLOCK_EVT_MODE_PERIODIC, 1);
    break;
case CLOCK_EVT_MODE_UNUSED :
case CLOCK_EVT_MODE_SHUTDOWN :
    v = apic_read (APIC_LVTT);
    v |= (APIC_LVT_MASKED | LOCAL_TIMER_VECTOR);
    apic_write (APIC_LVTT, v);
    break;
case CLOCK_EVT_MODE_RESUME :
    break;
}

{
    struct clock_event_device *levt = &__get_cpu_var (lapic_events);
    memcpy (levt, & lapic_clockevent, sizeof (* levt));
    levt->cpumask = cpumask_of_cpu (smp_processor_id ());
    clockevents_register_device (levt);
}

{
    unsigned apic, apic_start;
    unsigned long tsc, tsc_start;
    int result;
    local_irq_disable ();
    __setup_APIC_LVTT (250000000, 0, 0);
    apic_start = apic_read (APIC_TMCCT);
    {
        rdtscll (tsc_start);
        do {
            apic = apic_read (APIC_TMCCT);
            rdtscll (tsc);
        }
        while ((tsc - tsc_start) < TICK_COUNT && (apic_start - apic) < TICK_COUNT);
        result = (apic_start - apic) * 1000L * tsc_khz / (tsc - tsc_start);
    }
    local_irq_enable ();
    printk (KERN_DEBUG "APIC timer calibration result %d\n", result);
    printk (KERN_INFO "Detected %d.%03d MHz APIC timer.\n", result / 1000 / 1000, result / 1000 % 1000);
    lapic_clockevent.mult = div_sc (result, NSEC_PER_SEC, lapic_clockevent.shift);
    lapic_clockevent.max_delta_ns = clockevent_delta2ns (0x7FFFFF, &lapic_clockevent);
    lapic_clockevent.min_delta_ns = clockevent_delta2ns (0xF, &lapic_clockevent);
    calibration_result = result / HZ;
}

{
    rdtscll (tsc_start);
    do {
        apic = apic_read (APIC_TMCCT);
        rdtscll (tsc);
    }
    while ((tsc - tsc_start) < TICK_COUNT && (apic_start - apic) < TICK_COUNT);
    result = (apic_start - apic) * 1000L * tsc_khz / (tsc - tsc_start);
}

{
    apic = apic_read (APIC_TMCCT);
    rdtscll (tsc);
}

{
    if (disable_apic_timer) {
        printk (KERN_INFO "Disabling APIC timer\n");
        if (num_possible_cpus () > 1) {
            lapic_clockevent.mult = 1;
            setup_APIC_timer ();
        }
        return;
    }
    printk (KERN_INFO "Using local APIC timer interrupts.\n");
    calibrate_APIC_clock ();
    if (calibration_result < (1000000 / HZ)) {
        printk (KERN_WARNING "APIC frequency too slow, disabling apic timer\n");
        if (num_possible_cpus () > 1)
            setup_APIC_timer ();
        return;
    }
    if (nmi_watchdog != NMI_IO_APIC)
        lapic_clockevent.features &= ~CLOCK_EVT_FEAT_DUMMY;
    else
        printk (KERN_WARNING "APIC timer registered as dummy," " due to nmi_watchdog=1!\n");
    setup_APIC_timer ();
}

{
    printk (KERN_INFO "Disabling APIC timer\n");
    if (num_possible_cpus () > 1) {
        lapic_clockevent.mult = 1;
        setup_APIC_timer ();
    }
    return;
}

{
    lapic_clockevent.mult = 1;
    setup_APIC_timer ();
}

{
    printk (KERN_WARNING "APIC frequency too slow, disabling apic timer\n");
    if (num_possible_cpus () > 1)
        setup_APIC_timer ();
    return;
}

{
    if (!disable_apic_timer || (lapic_clockevent.features & CLOCK_EVT_FEAT_DUMMY))
        return;
    printk (KERN_INFO "AMD C1E detected late. Force timer broadcast.\n");
    lapic_clockevent.features |= CLOCK_EVT_FEAT_DUMMY;
    local_irq_enable ();
    clockevents_notify (CLOCK_EVT_NOTIFY_BROADCAST_FORCE, & boot_cpu_physical_apicid);
    local_irq_disable ();
}

{
    check_boot_apic_timer_broadcast ();
    setup_APIC_timer ();
}

{
    int cpu = smp_processor_id ();
    struct clock_event_device *evt = &per_cpu (lapic_events, cpu);
    if (!evt->event_handler) {
        printk (KERN_WARNING "Spurious LAPIC timer interrupt on cpu %d\n", cpu);
        lapic_timer_setup (CLOCK_EVT_MODE_SHUTDOWN, evt);
        return;
    }
    add_pda (apic_timer_irqs, 1);
    evt->event_handler (evt);
}

{
    printk (KERN_WARNING "Spurious LAPIC timer interrupt on cpu %d\n", cpu);
    lapic_timer_setup (CLOCK_EVT_MODE_SHUTDOWN, evt);
    return;
}

{
    struct pt_regs *old_regs = set_irq_regs (regs);
    ack_APIC_irq ();
    exit_idle ();
    irq_enter ();
    local_apic_timer_interrupt ();
    irq_exit ();
    set_irq_regs (old_regs);
}

{
    return -EINVAL;
}

{
    int maxlvt;
    u32 v;
    if (!apic_phys)
        return;
    maxlvt = lapic_get_maxlvt ();
    if (maxlvt >= 3) {
        v = ERROR_APIC_VECTOR;
        apic_write (APIC_LVTERR, v | APIC_LVT_MASKED);
    }
    v = apic_read (APIC_LVTT);
    apic_write (APIC_LVTT, v | APIC_LVT_MASKED);
    v = apic_read (APIC_LVT0);
    apic_write (APIC_LVT0, v | APIC_LVT_MASKED);
    v = apic_read (APIC_LVT1);
    apic_write (APIC_LVT1, v | APIC_LVT_MASKED);
    if (maxlvt >= 4) {
        v = apic_read (APIC_LVTPC);
        apic_write (APIC_LVTPC, v | APIC_LVT_MASKED);
    }
    apic_write (APIC_LVTT, APIC_LVT_MASKED);
    apic_write (APIC_LVT0, APIC_LVT_MASKED);
    apic_write (APIC_LVT1, APIC_LVT_MASKED);
    if (maxlvt >= 3)
        apic_write (APIC_LVTERR, APIC_LVT_MASKED);
    if (maxlvt >= 4)
        apic_write (APIC_LVTPC, APIC_LVT_MASKED);
    apic_write (APIC_ESR, 0);
    apic_read (APIC_ESR);
}

{
    v = ERROR_APIC_VECTOR;
    apic_write (APIC_LVTERR, v | APIC_LVT_MASKED);
}

{
    v = apic_read (APIC_LVTPC);
    apic_write (APIC_LVTPC, v | APIC_LVT_MASKED);
}

{
    unsigned int value;
    clear_local_APIC ();
    value = apic_read (APIC_SPIV);
    value &= ~APIC_SPIV_APIC_ENABLED;
    apic_write (APIC_SPIV, value);
}

{
    unsigned long flags;
    if (!cpu_has_apic)
        return;
    local_irq_save (flags);
    disable_local_APIC ();
    local_irq_restore (flags);
}

{
    unsigned int reg0, reg1;
    reg0 = apic_read (APIC_LVR);
    apic_printk (APIC_DEBUG, "Getting VERSION: %x\n", reg0);
    apic_write (APIC_LVR, reg0 ^ APIC_LVR_MASK);
    reg1 = apic_read (APIC_LVR);
    apic_printk (APIC_DEBUG, "Getting VERSION: %x\n", reg1);
    if (reg1 != reg0)
        return 0;
    reg1 = GET_APIC_VERSION (reg0);
    if (reg1 == 0x00 || reg1 == 0xff)
        return 0;
    reg1 = lapic_get_maxlvt ();
    if (reg1 < 0x02 || reg1 == 0xff)
        return 0;
    reg0 = read_apic_id ();
    apic_printk (APIC_DEBUG, "Getting ID: %x\n", reg0);
    apic_write (APIC_ID, reg0 ^ APIC_ID_MASK);
    reg1 = read_apic_id ();
    apic_printk (APIC_DEBUG, "Getting ID: %x\n", reg1);
    apic_write (APIC_ID, reg0);
    if (reg1 != (reg0 ^ APIC_ID_MASK))
        return 0;
    reg0 = apic_read (APIC_LVT0);
    apic_printk (APIC_DEBUG, "Getting LVT0: %x\n", reg0);
    reg1 = apic_read (APIC_LVT1);
    apic_printk (APIC_DEBUG, "Getting LVT1: %x\n", reg1);
    return 1;
}

{
    if (modern_apic ())
        return;
    apic_wait_icr_idle ();
    apic_printk (APIC_DEBUG, "Synchronizing Arb IDs.\n");
    apic_write (APIC_ICR, APIC_DEST_ALLINC | APIC_INT_LEVELTRIG | APIC_DM_INIT);
}

{
    unsigned int value;
    if (smp_found_config || !cpu_has_apic)
        return;
    value = apic_read (APIC_LVR);
    clear_local_APIC ();
    value = apic_read (APIC_SPIV);
    value &= ~APIC_VECTOR_MASK;
    value |= APIC_SPIV_APIC_ENABLED;
    value |= APIC_SPIV_FOCUS_DISABLED;
    value |= SPURIOUS_APIC_VECTOR;
    apic_write (APIC_SPIV, value);
    apic_write (APIC_LVT0, APIC_DM_EXTINT);
    value = APIC_DM_NMI;
    apic_write (APIC_LVT1, value);
}

{
    unsigned int value;
    int i, j;
    preempt_disable ();
    value = apic_read (APIC_LVR);
    BUILD_BUG_ON ((SPURIOUS_APIC_VECTOR & 0x0f) != 0x0f);
    if (!apic_id_registered ())
        BUG ();
    init_apic_ldr ();
    value = apic_read (APIC_TASKPRI);
    value &= ~APIC_TPRI_MASK;
    apic_write (APIC_TASKPRI, value);
    for (i = APIC_ISR_NR - 1; i >= 0; i--) {
        value = apic_read (APIC_ISR +i * 0x10);
        for (j = 31; j >= 0; j--) {
            if (value & (1 << j))
                ack_APIC_irq ();
        }
    }
    value = apic_read (APIC_SPIV);
    value &= ~APIC_VECTOR_MASK;
    value |= APIC_SPIV_APIC_ENABLED;
    value |= SPURIOUS_APIC_VECTOR;
    apic_write (APIC_SPIV, value);
    value = apic_read (APIC_LVT0) & APIC_LVT_MASKED;
    if (!smp_processor_id () && !value) {
        value = APIC_DM_EXTINT;
        apic_printk (APIC_VERBOSE, "enabled ExtINT on CPU#%d\n", smp_processor_id ());
    }
    else {
        value = APIC_DM_EXTINT | APIC_LVT_MASKED;
        apic_printk (APIC_VERBOSE, "masked ExtINT on CPU#%d\n", smp_processor_id ());
    }
    apic_write (APIC_LVT0, value);
    if (!smp_processor_id ())
        value = APIC_DM_NMI;
    else
        value = APIC_DM_NMI | APIC_LVT_MASKED;
    apic_write (APIC_LVT1, value);
    preempt_enable ();
}

{
    value = apic_read (APIC_ISR +i * 0x10);
    for (j = 31; j >= 0; j--) {
        if (value & (1 << j))
            ack_APIC_irq ();
    }
}

{
    if (value & (1 << j))
        ack_APIC_irq ();
}

{
    value = APIC_DM_EXTINT;
    apic_printk (APIC_VERBOSE, "enabled ExtINT on CPU#%d\n", smp_processor_id ());
}

{
    value = APIC_DM_EXTINT | APIC_LVT_MASKED;
    apic_printk (APIC_VERBOSE, "masked ExtINT on CPU#%d\n", smp_processor_id ());
}

{
    unsigned maxlvt = lapic_get_maxlvt ();
    apic_write (APIC_LVTERR, ERROR_APIC_VECTOR);
    if (maxlvt > 3)
        apic_write (APIC_ESR, 0);
}

{
    lapic_setup_esr ();
    nmi_watchdog_default ();
    setup_apic_nmi_watchdog (NULL);
    apic_pm_activate ();
}

{
    if (!cpu_has_apic) {
        printk (KERN_INFO "No local APIC present\n");
        return -1;
    }
    mp_lapic_addr = APIC_DEFAULT_PHYS_BASE;
    boot_cpu_physical_apicid = 0;
    return 0;
}

{
    printk (KERN_INFO "No local APIC present\n");
    return -1;
}

{
    unsigned long apic_phys;
    if (!smp_found_config)
        return;
    apic_phys = mp_lapic_addr;
    set_fixmap_nocache (FIX_APIC_BASE, apic_phys);
    apic_printk (APIC_VERBOSE, "mapped APIC to %16lx (%16lx)\n", APIC_BASE, apic_phys);
    boot_cpu_physical_apicid = GET_APIC_ID (read_apic_id ());
}

{
    if (!smp_found_config && detect_init_APIC ()) {
        apic_phys = (unsigned long) alloc_bootmem_pages (PAGE_SIZE);
        apic_phys = __pa (apic_phys);
    }
    else
        apic_phys = mp_lapic_addr;
    set_fixmap_nocache (FIX_APIC_BASE, apic_phys);
    apic_printk (APIC_VERBOSE, "mapped APIC to %16lx (%16lx)\n", APIC_BASE, apic_phys);
    boot_cpu_physical_apicid = GET_APIC_ID (read_apic_id ());
}

{
    apic_phys = (unsigned long) alloc_bootmem_pages (PAGE_SIZE);
    apic_phys = __pa (apic_phys);
}

{
    if (disable_apic) {
        printk (KERN_INFO "Apic disabled\n");
        return -1;
    }
    if (!cpu_has_apic) {
        disable_apic = 1;
        printk (KERN_INFO "Apic disabled by BIOS\n");
        return -1;
    }
    verify_local_APIC ();
    phys_cpu_present_map = physid_mask_of_physid (boot_cpu_physical_apicid);
    apic_write (APIC_ID, SET_APIC_ID (boot_cpu_physical_apicid));
    setup_local_APIC ();
    if (!skip_ioapic_setup && nr_ioapics)
        enable_IO_APIC ();
    end_local_APIC_setup ();
    if (smp_found_config && !skip_ioapic_setup && nr_ioapics)
        setup_IO_APIC ();
    else
        nr_ioapics = 0;
    setup_boot_APIC_clock ();
    check_nmi_watchdog ();
    return 0;
}

{
    printk (KERN_INFO "Apic disabled\n");
    return -1;
}

{
    disable_apic = 1;
    printk (KERN_INFO "Apic disabled by BIOS\n");
    return -1;
}

{
    unsigned int v;
    exit_idle ();
    irq_enter ();
    v = apic_read (APIC_ISR +((SPURIOUS_APIC_VECTOR & ~0x1f) >> 1));
    if (v & (1 << (SPURIOUS_APIC_VECTOR & 0x1f)))
        ack_APIC_irq ();
    add_pda (irq_spurious_count, 1);
    irq_exit ();
}

{
    unsigned int v, v1;
    exit_idle ();
    irq_enter ();
    v = apic_read (APIC_ESR);
    apic_write (APIC_ESR, 0);
    v1 = apic_read (APIC_ESR);
    ack_APIC_irq ();
    atomic_inc (& irq_err_count);
    printk (KERN_DEBUG "APIC error on CPU%d: %02x(%02x)\n", smp_processor_id (), v, v1);
    irq_exit ();
}

{
    unsigned long value;
    value = apic_read (APIC_SPIV);
    value &= ~APIC_VECTOR_MASK;
    value |= APIC_SPIV_APIC_ENABLED;
    value |= 0xf;
    apic_write (APIC_SPIV, value);
    if (!virt_wire_setup) {
        value = apic_read (APIC_LVT0);
        value &= ~(APIC_MODE_MASK | APIC_SEND_PENDING | APIC_INPUT_POLARITY | APIC_LVT_REMOTE_IRR | APIC_LVT_LEVEL_TRIGGER | APIC_LVT_MASKED);
        value |= APIC_LVT_REMOTE_IRR | APIC_SEND_PENDING;
        value = SET_APIC_DELIVERY_MODE (value, APIC_MODE_EXTINT);
        apic_write (APIC_LVT0, value);
    }
    else {
        apic_write (APIC_LVT0, APIC_LVT_MASKED);
    }
    value = apic_read (APIC_LVT1);
    value &= ~(APIC_MODE_MASK | APIC_SEND_PENDING | APIC_INPUT_POLARITY | APIC_LVT_REMOTE_IRR | APIC_LVT_LEVEL_TRIGGER | APIC_LVT_MASKED);
    value |= APIC_LVT_REMOTE_IRR | APIC_SEND_PENDING;
    value = SET_APIC_DELIVERY_MODE (value, APIC_MODE_NMI);
    apic_write (APIC_LVT1, value);
}

{
    value = apic_read (APIC_LVT0);
    value &= ~(APIC_MODE_MASK | APIC_SEND_PENDING | APIC_INPUT_POLARITY | APIC_LVT_REMOTE_IRR | APIC_LVT_LEVEL_TRIGGER | APIC_LVT_MASKED);
    value |= APIC_LVT_REMOTE_IRR | APIC_SEND_PENDING;
    value = SET_APIC_DELIVERY_MODE (value, APIC_MODE_EXTINT);
    apic_write (APIC_LVT0, value);
}

{
    apic_write (APIC_LVT0, APIC_LVT_MASKED);
}

{
    int cpu;
    cpumask_t tmp_map;
    if (num_processors >= NR_CPUS) {
        printk (KERN_WARNING "WARNING: NR_CPUS limit of %i reached." " Processor ignored.\n", NR_CPUS);
        return;
    }
    if (num_processors >= maxcpus) {
        printk (KERN_WARNING "WARNING: maxcpus limit of %i reached." " Processor ignored.\n", maxcpus);
        return;
    }
    num_processors++;
    cpus_complement (tmp_map, cpu_present_map);
    cpu = first_cpu (tmp_map);
    physid_set (apicid, phys_cpu_present_map);
    if (apicid == boot_cpu_physical_apicid) {
        cpu = 0;
    }
    if (x86_cpu_to_apicid_early_ptr) {
        u16 *cpu_to_apicid = x86_cpu_to_apicid_early_ptr;
        u16 *bios_cpu_apicid = x86_bios_cpu_apicid_early_ptr;
        cpu_to_apicid[cpu] = apicid;
        bios_cpu_apicid[cpu] = apicid;
    }
    else {
        per_cpu (x86_cpu_to_apicid, cpu) = apicid;
        per_cpu (x86_bios_cpu_apicid, cpu) = apicid;
    }
    cpu_set (cpu, cpu_possible_map);
    cpu_set (cpu, cpu_present_map);
}

{
    printk (KERN_WARNING "WARNING: NR_CPUS limit of %i reached." " Processor ignored.\n", NR_CPUS);
    return;
}

{
    printk (KERN_WARNING "WARNING: maxcpus limit of %i reached." " Processor ignored.\n", maxcpus);
    return;
}

{
    u16 *cpu_to_apicid = x86_cpu_to_apicid_early_ptr;
    u16 *bios_cpu_apicid = x86_bios_cpu_apicid_early_ptr;
    cpu_to_apicid[cpu] = apicid;
    bios_cpu_apicid[cpu] = apicid;
}

{
    per_cpu (x86_cpu_to_apicid, cpu) = apicid;
    per_cpu (x86_bios_cpu_apicid, cpu) = apicid;
}

{
    int i, clusters, zeros;
    unsigned id;
    u16 *bios_cpu_apicid;
    DECLARE_BITMAP (clustermap, NUM_APIC_CLUSTERS);
    if ((boot_cpu_data.x86_vendor == X86_VENDOR_AMD) && !is_vsmp_box ())
        return 0;
    bios_cpu_apicid = x86_bios_cpu_apicid_early_ptr;
    bitmap_zero (clustermap, NUM_APIC_CLUSTERS);
    for (i = 0; i < NR_CPUS; i++) {
        if (bios_cpu_apicid) {
            id = bios_cpu_apicid[i];
        }
        else if (i < nr_cpu_ids) {
            if (cpu_present (i))
                id = per_cpu (x86_bios_cpu_apicid, i);
            else
                continue;
        }
        else
            break;
        if (id != BAD_APICID)
            __set_bit (APIC_CLUSTERID (id), clustermap);
    }
    clusters = 0;
    zeros = 0;
    for (i = 0; i < NUM_APIC_CLUSTERS; i++) {
        if (test_bit (i, clustermap)) {
            clusters += 1 + zeros;
            zeros = 0;
        }
        else
            ++zeros;
    }
    if (is_vsmp_box () && clusters > 1)
        return 1;
    return (clusters > 2);
}

{
    if (bios_cpu_apicid) {
        id = bios_cpu_apicid[i];
    }
    else if (i < nr_cpu_ids) {
        if (cpu_present (i))
            id = per_cpu (x86_bios_cpu_apicid, i);
        else
            continue;
    }
    else
        break;
    if (id != BAD_APICID)
        __set_bit (APIC_CLUSTERID (id), clustermap);
}

{
    id = bios_cpu_apicid[i];
}

{
    if (cpu_present (i))
        id = per_cpu (x86_bios_cpu_apicid, i);
    else
        continue;
}

{
    if (test_bit (i, clustermap)) {
        clusters += 1 + zeros;
        zeros = 0;
    }
    else
        ++zeros;
}

{
    clusters += 1 + zeros;
    zeros = 0;
}

{
    if (str == NULL) {
        skip_ioapic_setup = 0;
        ioapic_force = 1;
        return 0;
    }
    if (strcmp ("debug", str) == 0)
        apic_verbosity = APIC_DEBUG;
    else if (strcmp ("verbose", str) == 0)
        apic_verbosity = APIC_VERBOSE;
    else {
        printk (KERN_WARNING "APIC Verbosity level %s not recognised" " use apic=verbose or apic=debug\n", str);
        return -EINVAL;
    }
    return 0;
}

{
    skip_ioapic_setup = 0;
    ioapic_force = 1;
    return 0;
}

{
    printk (KERN_WARNING "APIC Verbosity level %s not recognised" " use apic=verbose or apic=debug\n", str);
    return -EINVAL;
}

{
    disable_apic = 1;
    clear_cpu_cap (& boot_cpu_data, X86_FEATURE_APIC);
    return 0;
}

{
    return setup_disableapic (str);
}

{
    local_apic_timer_c2_ok = 1;
    return 0;
}

{
    if (str[0] != ' ' && str[0] != 0)
        return 0;
    disable_apic_timer = 1;
    return 1;
}

{
    apic_calibrate_pmtmr = 1;
    notsc_setup (NULL);
    return 0;
}

{
    if (!apic_phys)
        return -1;
    lapic_resource.start = apic_phys;
    lapic_resource.end = lapic_resource.start + PAGE_SIZE - 1;
    insert_resource (& iomem_resource, & lapic_resource);
    return 0;
}

{
    vfree (module_region);
}

{
    struct vm_struct *area;
    if (!size)
        return NULL;
    size = PAGE_ALIGN (size);
    if (size > MODULES_LEN)
        return NULL;
    area = __get_vm_area (size, VM_ALLOC, MODULES_VADDR, MODULES_END);
    if (!area)
        return NULL;
    return __vmalloc_area (area, GFP_KERNEL, PAGE_KERNEL_EXEC);
}

{
    return 0;
}

{
    unsigned int i;
    Elf64_Rela *rel = (void *) sechdrs[relsec].sh_addr;
    Elf64_Sym *sym;
    void *loc;
    u64 val;
    DEBUGP ("Applying relocate section %u to %u\n", relsec, sechdrs [relsec].sh_info);
    for (i = 0; i < sechdrs[relsec].sh_size / sizeof (*rel); i++) {
        loc = (void *) sechdrs[sechdrs[relsec].sh_info].sh_addr + rel[i].r_offset;
        sym = (Elf64_Sym *) sechdrs[symindex].sh_addr + ELF64_R_SYM (rel[i].r_info);
        DEBUGP ("type %d st_value %Lx r_addend %Lx loc %Lx\n", (int) ELF64_R_TYPE (rel [i].r_info), sym -> st_value, rel [i].r_addend, (u64) loc);
        val = sym->st_value + rel[i].r_addend;
        switch (ELF64_R_TYPE (rel[i].r_info)) {
        case R_X86_64_NONE :
            break;
        case R_X86_64_64 :
            *(u64*) loc = val;
            break;
        case R_X86_64_32 :
            *(u32*) loc = val;
            if (val != *(u32*) loc)
                goto overflow;
            break;
        case R_X86_64_32S :
            *(s32*) loc = val;
            if ((s64) val != *(s32*) loc)
                goto overflow;
            break;
        case R_X86_64_PC32 :
            val -= (u64) loc;
            *(u32*) loc = val;
            break;
        default :
            printk (KERN_ERR "module %s: Unknown rela relocation: %Lu\n", me->name, ELF64_R_TYPE (rel[i].r_info));
            return -ENOEXEC;
        }
    }
    return 0;
overflow :
    printk (KERN_ERR "overflow in relocation type %d val %Lx\n", (int) ELF64_R_TYPE (rel[i].r_info), val);
    printk (KERN_ERR "`%s' likely not compiled with -mcmodel=kernel\n", me -> name);
    return -ENOEXEC;
}

{
    loc = (void *) sechdrs[sechdrs[relsec].sh_info].sh_addr + rel[i].r_offset;
    sym = (Elf64_Sym *) sechdrs[symindex].sh_addr + ELF64_R_SYM (rel[i].r_info);
    DEBUGP ("type %d st_value %Lx r_addend %Lx loc %Lx\n", (int) ELF64_R_TYPE (rel [i].r_info), sym -> st_value, rel [i].r_addend, (u64) loc);
    val = sym->st_value + rel[i].r_addend;
    switch (ELF64_R_TYPE (rel[i].r_info)) {
    case R_X86_64_NONE :
        break;
    case R_X86_64_64 :
        *(u64*) loc = val;
        break;
    case R_X86_64_32 :
        *(u32*) loc = val;
        if (val != *(u32*) loc)
            goto overflow;
        break;
    case R_X86_64_32S :
        *(s32*) loc = val;
        if ((s64) val != *(s32*) loc)
            goto overflow;
        break;
    case R_X86_64_PC32 :
        val -= (u64) loc;
        *(u32*) loc = val;
        break;
    default :
        printk (KERN_ERR "module %s: Unknown rela relocation: %Lu\n", me->name, ELF64_R_TYPE (rel[i].r_info));
        return -ENOEXEC;
    }
}

{
case R_X86_64_NONE :
    break;
case R_X86_64_64 :
    *(u64*) loc = val;
    break;
case R_X86_64_32 :
    *(u32*) loc = val;
    if (val != *(u32*) loc)
        goto overflow;
    break;
case R_X86_64_32S :
    *(s32*) loc = val;
    if ((s64) val != *(s32*) loc)
        goto overflow;
    break;
case R_X86_64_PC32 :
    val -= (u64) loc;
    *(u32*) loc = val;
    break;
default :
    printk (KERN_ERR "module %s: Unknown rela relocation: %Lu\n", me->name, ELF64_R_TYPE (rel[i].r_info));
    return -ENOEXEC;
}

{
    printk ("non add relocation not supported\n");
    return -ENOSYS;
}

{
    const Elf_Shdr *s, *text = NULL, *alt = NULL, *locks = NULL;
    char *secstrings = (void *) hdr + sechdrs[hdr->e_shstrndx].sh_offset;
    for (s = sechdrs; s < sechdrs + hdr->e_shnum; s++) {
        if (!strcmp (".text", secstrings +s->sh_name))
            text = s;
        if (!strcmp (".altinstructions", secstrings +s->sh_name))
            alt = s;
        if (!strcmp (".smp_locks", secstrings +s->sh_name))
            locks = s;
    }
    if (alt) {
        void *aseg = (void *) alt->sh_addr;
        apply_alternatives (aseg, aseg + alt -> sh_size);
    }
    if (locks && text) {
        void *lseg = (void *) locks->sh_addr;
        void *tseg = (void *) text->sh_addr;
        alternatives_smp_module_add (me, me -> name, lseg, lseg + locks -> sh_size, tseg, tseg + text -> sh_size);
    }
    return module_bug_finalize (hdr, sechdrs, me);
}

{
    if (!strcmp (".text", secstrings +s->sh_name))
        text = s;
    if (!strcmp (".altinstructions", secstrings +s->sh_name))
        alt = s;
    if (!strcmp (".smp_locks", secstrings +s->sh_name))
        locks = s;
}

{
    void *aseg = (void *) alt->sh_addr;
    apply_alternatives (aseg, aseg + alt -> sh_size);
}

{
    void *lseg = (void *) locks->sh_addr;
    void *tseg = (void *) text->sh_addr;
    alternatives_smp_module_add (me, me -> name, lseg, lseg + locks -> sh_size, tseg, tseg + text -> sh_size);
}

{
    alternatives_smp_module_del (mod);
    module_bug_cleanup (mod);
}

{
    pgd_t *pgd = pgd_offset_k (0UL);
    pgd_clear (pgd);
    __flush_tlb_all ();
}

{
    memset (__bss_start, 0, (unsigned long) __bss_stop - (unsigned long) __bss_start);
}

{
    char *command_line;
    memcpy (& boot_params, real_mode_data, sizeof boot_params);
    if (boot_params.hdr.cmd_line_ptr) {
        command_line = __va (boot_params.hdr.cmd_line_ptr);
        memcpy (boot_command_line, command_line, COMMAND_LINE_SIZE);
    }
}

{
    command_line = __va (boot_params.hdr.cmd_line_ptr);
    memcpy (boot_command_line, command_line, COMMAND_LINE_SIZE);
}

{
    unsigned int lowmem, ebda_addr;
    if (paravirt_enabled ())
        return;
    lowmem = *(unsignedshort*) __va (BIOS_LOWMEM_KILOBYTES);
    lowmem <<= 10;
    ebda_addr = get_bios_ebda ();
    if ((lowmem - ebda_addr) <= 0x10000)
        lowmem = ebda_addr;
    if ((ebda_addr == 0) && (lowmem >= 0x9f000))
        lowmem = 0x9f000;
    if ((lowmem == 0) || (lowmem >= 0x100000))
        lowmem = 0x9f000;
    reserve_early (lowmem, 0x100000, "BIOS reserved");
}

{
    struct setup_data *data;
    unsigned long pa_data;
    char buf [32];
    if (boot_params.hdr.version < 0x0209)
        return;
    pa_data = boot_params.hdr.setup_data;
    while (pa_data) {
        data = early_ioremap (pa_data, sizeof (*data));
        sprintf (buf, "setup data %x", data -> type);
        reserve_early (pa_data, pa_data + sizeof (* data) + data -> len, buf);
        pa_data = data->next;
        early_iounmap (data, sizeof (* data));
    }
}

{
    data = early_ioremap (pa_data, sizeof (*data));
    sprintf (buf, "setup data %x", data -> type);
    reserve_early (pa_data, pa_data + sizeof (* data) + data -> len, buf);
    pa_data = data->next;
    early_iounmap (data, sizeof (* data));
}

{
    int i;
    BUILD_BUG_ON (MODULES_VADDR < KERNEL_IMAGE_START);
    BUILD_BUG_ON (MODULES_VADDR - KERNEL_IMAGE_START < KERNEL_IMAGE_SIZE);
    BUILD_BUG_ON (MODULES_LEN + KERNEL_IMAGE_SIZE > 2 * PUD_SIZE);
    BUILD_BUG_ON ((KERNEL_IMAGE_START & ~ PMD_MASK) != 0);
    BUILD_BUG_ON ((MODULES_VADDR & ~ PMD_MASK) != 0);
    BUILD_BUG_ON (! (MODULES_VADDR > __START_KERNEL));
    BUILD_BUG_ON (! (((MODULES_END - 1) & PGDIR_MASK) == (__START_KERNEL & PGDIR_MASK)));
    clear_bss ();
    zap_identity_mappings ();
    cleanup_highmap ();
    for (i = 0; i < NUM_EXCEPTION_VECTORS; i++) {
        set_intr_gate (i, early_idt_handler);
    }
    load_idt ((const struct desc_ptr *) & idt_descr);
    early_printk ("Kernel alive\n");
    for (i = 0; i < NR_CPUS; i++)
        cpu_pda (i) = &boot_cpu_pda[i];
    pda_init (0);
    copy_bootdata (__va (real_mode_data));
    reserve_early (__pa_symbol (& _text), __pa_symbol (& _end), "TEXT DATA BSS");
    reserve_ebda_region ();
    reserve_setup_data ();
    start_kernel ();
}

