{
    printk (KERN_WARNING "unexpected IRQ trap at vector %02x\n", irq);
    if (!disable_apic)
        ack_APIC_irq ();
}

{
    int i = *(loff_t*) v, j;
    struct irqaction *action;
    unsigned long flags;
    if (i == 0) {
        seq_printf (p, "           ");
        for_each_online_cpu (j)
        seq_printf (p, "CPU%-8d", j);
        seq_putc (p, '\n');
    }
    if (i < NR_IRQS) {
        unsigned any_count = 0;
        spin_lock_irqsave (& irq_desc [i].lock, flags);
        any_count = kstat_irqs (i);
        action = irq_desc[i].action;
        if (!action && !any_count)
            goto skip;
        seq_printf (p, "%3d: ", i);
        seq_printf (p, "%10u ", kstat_irqs (i));
        seq_printf (p, " %8s", irq_desc [i].chip -> name);
        seq_printf (p, "-%-8s", irq_desc [i].name);
        if (action) {
            seq_printf (p, "  %s", action -> name);
            while ((action = action->next) != NULL)
                seq_printf (p, ", %s", action->name);
        }
        seq_putc (p, '\n');
    skip :
        spin_unlock_irqrestore (&irq_desc[i].lock, flags);
    }
    else if (i == NR_IRQS) {
        seq_printf (p, "NMI: ");
        for_each_online_cpu (j)
        seq_printf (p, "%10u ", cpu_pda (j) -> __nmi_count);
        seq_printf (p, "  Non-maskable interrupts\n");
        seq_printf (p, "LOC: ");
        for_each_online_cpu (j)
        seq_printf (p, "%10u ", cpu_pda (j) -> apic_timer_irqs);
        seq_printf (p, "  Local timer interrupts\n");
        seq_printf (p, "TRM: ");
        for_each_online_cpu (j)
        seq_printf (p, "%10u ", cpu_pda (j) -> irq_thermal_count);
        seq_printf (p, "  Thermal event interrupts\n");
        seq_printf (p, "THR: ");
        for_each_online_cpu (j)
        seq_printf (p, "%10u ", cpu_pda (j) -> irq_threshold_count);
        seq_printf (p, "  Threshold APIC interrupts\n");
        seq_printf (p, "SPU: ");
        for_each_online_cpu (j)
        seq_printf (p, "%10u ", cpu_pda (j) -> irq_spurious_count);
        seq_printf (p, "  Spurious interrupts\n");
        seq_printf (p, "ERR: %10u\n", atomic_read (& irq_err_count));
    }
    return 0;
}

{
    seq_printf (p, "           ");
    for_each_online_cpu (j)
    seq_printf (p, "CPU%-8d", j);
    seq_putc (p, '\n');
}

{
    unsigned any_count = 0;
    spin_lock_irqsave (& irq_desc [i].lock, flags);
    any_count = kstat_irqs (i);
    action = irq_desc[i].action;
    if (!action && !any_count)
        goto skip;
    seq_printf (p, "%3d: ", i);
    seq_printf (p, "%10u ", kstat_irqs (i));
    seq_printf (p, " %8s", irq_desc [i].chip -> name);
    seq_printf (p, "-%-8s", irq_desc [i].name);
    if (action) {
        seq_printf (p, "  %s", action -> name);
        while ((action = action->next) != NULL)
            seq_printf (p, ", %s", action->name);
    }
    seq_putc (p, '\n');
skip :
    spin_unlock_irqrestore (&irq_desc[i].lock, flags);
}

{
    seq_printf (p, "  %s", action -> name);
    while ((action = action->next) != NULL)
        seq_printf (p, ", %s", action->name);
}

{
    seq_printf (p, "NMI: ");
    for_each_online_cpu (j)
    seq_printf (p, "%10u ", cpu_pda (j) -> __nmi_count);
    seq_printf (p, "  Non-maskable interrupts\n");
    seq_printf (p, "LOC: ");
    for_each_online_cpu (j)
    seq_printf (p, "%10u ", cpu_pda (j) -> apic_timer_irqs);
    seq_printf (p, "  Local timer interrupts\n");
    seq_printf (p, "TRM: ");
    for_each_online_cpu (j)
    seq_printf (p, "%10u ", cpu_pda (j) -> irq_thermal_count);
    seq_printf (p, "  Thermal event interrupts\n");
    seq_printf (p, "THR: ");
    for_each_online_cpu (j)
    seq_printf (p, "%10u ", cpu_pda (j) -> irq_threshold_count);
    seq_printf (p, "  Threshold APIC interrupts\n");
    seq_printf (p, "SPU: ");
    for_each_online_cpu (j)
    seq_printf (p, "%10u ", cpu_pda (j) -> irq_spurious_count);
    seq_printf (p, "  Spurious interrupts\n");
    seq_printf (p, "ERR: %10u\n", atomic_read (& irq_err_count));
}

{
    struct pt_regs *old_regs = set_irq_regs (regs);
    unsigned vector = ~regs->orig_ax;
    unsigned irq;
    exit_idle ();
    irq_enter ();
    irq = __get_cpu_var (vector_irq)[vector];
    if (likely (irq < NR_IRQS))
        generic_handle_irq (irq);
    else {
        if (!disable_apic)
            ack_APIC_irq ();
        if (printk_ratelimit ())
            printk (KERN_EMERG "%s: %d.%d No irq handler for vector\n", __func__, smp_processor_id (), vector);
    }
    irq_exit ();
    set_irq_regs (old_regs);
    return 1;
}

{
    if (!disable_apic)
        ack_APIC_irq ();
    if (printk_ratelimit ())
        printk (KERN_EMERG "%s: %d.%d No irq handler for vector\n", __func__, smp_processor_id (), vector);
}

{
    __u32 pending;
    unsigned long flags;
    if (in_interrupt ())
        return;
    local_irq_save (flags);
    pending = local_softirq_pending ();
    if (pending) {
        call_softirq ();
        WARN_ON_ONCE (softirq_count ());
    }
    local_irq_restore (flags);
}

{
    call_softirq ();
    WARN_ON_ONCE (softirq_count ());
}

{
    for (;;) {
        switch (*str) {
        case 'w' :
            reboot_mode = 0x1234;
            break;
        case 'c' :
            reboot_mode = 0;
            break;
        case 'a' :
        case 'k' :
        case 't' :
        case 'e' :
            reboot_type = *str;
            break;
        case 'f' :
            reboot_force = 1;
            break;
        }
        str = strchr (str, ',');
        if (str)
            str++;
        else
            break;
    }
    return 1;
}

{
    switch (*str) {
    case 'w' :
        reboot_mode = 0x1234;
        break;
    case 'c' :
        reboot_mode = 0;
        break;
    case 'a' :
    case 'k' :
    case 't' :
    case 'e' :
        reboot_type = *str;
        break;
    case 'f' :
        reboot_force = 1;
        break;
    }
    str = strchr (str, ',');
    if (str)
        str++;
    else
        break;
}

{
case 'w' :
    reboot_mode = 0x1234;
    break;
case 'c' :
    reboot_mode = 0;
    break;
case 'a' :
case 'k' :
case 't' :
case 'e' :
    reboot_type = *str;
    break;
case 'f' :
    reboot_force = 1;
    break;
}

{
    int i;
    for (i = 0; i < 0x10000; i++) {
        if ((inb (0x64) & 0x02) == 0)
            break;
        udelay (2);
    }
}

{
    if ((inb (0x64) & 0x02) == 0)
        break;
    udelay (2);
}

{
    int i;
    *((unsigned short *) __va (0x472)) = reboot_mode;
    for (;;) {
        switch (reboot_type) {
        case BOOT_KBD :
            mach_reboot_fixups ();
            for (i = 0; i < 10; i++) {
                kb_wait ();
                udelay (50);
                outb (0xfe, 0x64);
                udelay (50);
            }
        case BOOT_TRIPLE :
            load_idt ((const struct desc_ptr *) &no_idt);
            __asm__ __volatile__ ("int3");
            reboot_type = BOOT_KBD;
            break;
        case BOOT_ACPI :
            acpi_reboot ();
            reboot_type = BOOT_KBD;
            break;
        case BOOT_EFI :
            if (efi_enabled)
                efi.reset_system (reboot_mode ? EFI_RESET_WARM : EFI_RESET_COLD, EFI_SUCCESS, 0, NULL);
            reboot_type = BOOT_KBD;
            break;
        }
    }
}

{
    switch (reboot_type) {
    case BOOT_KBD :
        mach_reboot_fixups ();
        for (i = 0; i < 10; i++) {
            kb_wait ();
            udelay (50);
            outb (0xfe, 0x64);
            udelay (50);
        }
    case BOOT_TRIPLE :
        load_idt ((const struct desc_ptr *) &no_idt);
        __asm__ __volatile__ ("int3");
        reboot_type = BOOT_KBD;
        break;
    case BOOT_ACPI :
        acpi_reboot ();
        reboot_type = BOOT_KBD;
        break;
    case BOOT_EFI :
        if (efi_enabled)
            efi.reset_system (reboot_mode ? EFI_RESET_WARM : EFI_RESET_COLD, EFI_SUCCESS, 0, NULL);
        reboot_type = BOOT_KBD;
        break;
    }
}

{
case BOOT_KBD :
    mach_reboot_fixups ();
    for (i = 0; i < 10; i++) {
        kb_wait ();
        udelay (50);
        outb (0xfe, 0x64);
        udelay (50);
    }
case BOOT_TRIPLE :
    load_idt ((const struct desc_ptr *) &no_idt);
    __asm__ __volatile__ ("int3");
    reboot_type = BOOT_KBD;
    break;
case BOOT_ACPI :
    acpi_reboot ();
    reboot_type = BOOT_KBD;
    break;
case BOOT_EFI :
    if (efi_enabled)
        efi.reset_system (reboot_mode ? EFI_RESET_WARM : EFI_RESET_COLD, EFI_SUCCESS, 0, NULL);
    reboot_type = BOOT_KBD;
    break;
}

{
    kb_wait ();
    udelay (50);
    outb (0xfe, 0x64);
    udelay (50);
}

{
    lapic_shutdown ();
}

