{
    switch (io_delay_type) {
    default :
    case CONFIG_IO_DELAY_TYPE_0X80 :
        asm volatile ("outb %al, $0x80"
        );
        break;
    case CONFIG_IO_DELAY_TYPE_0XED :
        asm volatile ("outb %al, $0xed"
        );
        break;
    case CONFIG_IO_DELAY_TYPE_UDELAY :
        udelay (2);
    case CONFIG_IO_DELAY_TYPE_NONE :
        break;
    }
}

{
default :
case CONFIG_IO_DELAY_TYPE_0X80 :
    asm volatile ("outb %al, $0x80"
    );
    break;
case CONFIG_IO_DELAY_TYPE_0XED :
    asm volatile ("outb %al, $0xed"
    );
    break;
case CONFIG_IO_DELAY_TYPE_UDELAY :
    udelay (2);
case CONFIG_IO_DELAY_TYPE_NONE :
    break;
}

{
    if (io_delay_type == CONFIG_IO_DELAY_TYPE_0X80) {
        printk (KERN_NOTICE "%s: using 0xed I/O delay port\n", id -> ident);
        io_delay_type = CONFIG_IO_DELAY_TYPE_0XED;
    }
    return 0;
}

{
    printk (KERN_NOTICE "%s: using 0xed I/O delay port\n", id -> ident);
    io_delay_type = CONFIG_IO_DELAY_TYPE_0XED;
}

{
    if (!io_delay_override)
        dmi_check_system (io_delay_0xed_port_dmi_table);
}

{
    if (!strcmp (s, "0x80"))
        io_delay_type = CONFIG_IO_DELAY_TYPE_0X80;
    else if (!strcmp (s, "0xed"))
        io_delay_type = CONFIG_IO_DELAY_TYPE_0XED;
    else if (!strcmp (s, "udelay"))
        io_delay_type = CONFIG_IO_DELAY_TYPE_UDELAY;
    else if (!strcmp (s, "none"))
        io_delay_type = CONFIG_IO_DELAY_TYPE_NONE;
    else
        return -EINVAL;
    io_delay_override = 1;
    return 0;
}

{
    if (unlikely (cpu_is_offline (cpu))) {
        WARN_ON (1);
        return;
    }
    send_IPI_mask (cpumask_of_cpu (cpu), RESCHEDULE_VECTOR);
}

{
    WARN_ON (1);
    return;
}

{
    spin_lock_irq (& call_lock);
}

{
    spin_unlock_irq (& call_lock);
}

{
    struct call_data_struct data;
    int cpus = num_online_cpus () - 1;
    if (!cpus)
        return;
    data.func = func;
    data.info = info;
    atomic_set (& data.started, 0);
    data.wait = wait;
    if (wait)
        atomic_set (&data.finished, 0);
    call_data = &data;
    mb ();
    send_IPI_allbutself (CALL_FUNCTION_VECTOR);
    while (atomic_read (&data.started) != cpus)
        cpu_relax ();
    if (wait)
        while (atomic_read (&data.finished) != cpus)
            cpu_relax ();
}

{
    struct call_data_struct data;
    cpumask_t allbutself;
    int cpus;
    WARN_ON (irqs_disabled ());
    spin_lock (& call_lock);
    allbutself = cpu_online_map;
    cpu_clear (smp_processor_id (), allbutself);
    cpus_and (mask, mask, allbutself);
    cpus = cpus_weight (mask);
    if (!cpus) {
        spin_unlock (& call_lock);
        return 0;
    }
    data.func = func;
    data.info = info;
    atomic_set (& data.started, 0);
    data.wait = wait;
    if (wait)
        atomic_set (&data.finished, 0);
    call_data = &data;
    wmb ();
    if (cpus_equal (mask, allbutself) && cpus_equal (cpu_online_map, cpu_callout_map))
        send_IPI_allbutself (CALL_FUNCTION_VECTOR);
    else
        send_IPI_mask (mask, CALL_FUNCTION_VECTOR);
    while (atomic_read (&data.started) != cpus)
        cpu_relax ();
    if (wait)
        while (atomic_read (&data.finished) != cpus)
            cpu_relax ();
    spin_unlock (& call_lock);
    return 0;
}

{
    spin_unlock (& call_lock);
    return 0;
}

{
    local_irq_disable ();
    cpu_clear (smp_processor_id (), cpu_online_map);
    disable_local_APIC ();
    if (hlt_works (smp_processor_id ()))
        for (;;)
            halt ();
    for (;;)
        ;
}

{
    int nolock;
    unsigned long flags;
    if (reboot_force)
        return;
    nolock = !spin_trylock (&call_lock);
    local_irq_save (flags);
    __smp_call_function (stop_this_cpu, NULL, 0, 0);
    if (!nolock)
        spin_unlock (&call_lock);
    disable_local_APIC ();
    local_irq_restore (flags);
}

{
    ack_APIC_irq ();
    add_pda (irq_resched_count, 1);
}

{
    void (*func) (void *info) = call_data->func;
    void *info = call_data->info;
    int wait = call_data->wait;
    ack_APIC_irq ();
    mb ();
    atomic_inc (& call_data -> started);
    irq_enter ();
    (*func) (info);
    add_pda (irq_call_count, 1);
    irq_exit ();
    if (wait) {
        mb ();
        atomic_inc (& call_data -> finished);
    }
}

{
    mb ();
    atomic_inc (& call_data -> finished);
}

{
    return readl (hpet_virt_address +a);
}

{
    writel (d, hpet_virt_address + a);
}

{
    hpet_virt_address = ioremap_nocache (hpet_address, HPET_MMAP_SIZE);
}

{
    iounmap (hpet_virt_address);
    hpet_virt_address = NULL;
}

{
    if (str) {
        if (!strncmp ("disable", str, 7))
            boot_hpet_disable = 1;
        if (!strncmp ("force", str, 5))
            hpet_force_user = 1;
    }
    return 1;
}

{
    if (!strncmp ("disable", str, 7))
        boot_hpet_disable = 1;
    if (!strncmp ("force", str, 5))
        hpet_force_user = 1;
}

{
    boot_hpet_disable = 1;
    return 1;
}

{
    return (!boot_hpet_disable && hpet_address);
}

{
    return is_hpet_capable () && hpet_legacy_int_enabled;
}

{
    unsigned long cfg = hpet_readl (HPET_CFG);
    cfg &= ~HPET_CFG_ENABLE;
    hpet_writel (cfg, HPET_CFG);
    hpet_writel (0, HPET_COUNTER);
    hpet_writel (0, HPET_COUNTER + 4);
    cfg |= HPET_CFG_ENABLE;
    hpet_writel (cfg, HPET_CFG);
}

{
    force_hpet_resume ();
}

{
    hpet_resume_device ();
    hpet_start_counter ();
}

{
    unsigned long cfg = hpet_readl (HPET_CFG);
    cfg |= HPET_CFG_LEGACY;
    hpet_writel (cfg, HPET_CFG);
    hpet_legacy_int_enabled = 1;
}

{
    uint64_t hpet_freq;
    hpet_enable_legacy_int ();
    hpet_freq = 1000000000000000ULL;
    do_div (hpet_freq, hpet_period);
    hpet_clockevent.mult = div_sc ((unsigned long) hpet_freq, NSEC_PER_SEC, hpet_clockevent.shift);
    hpet_clockevent.max_delta_ns = clockevent_delta2ns (0x7FFFFFFF, &hpet_clockevent);
    hpet_clockevent.min_delta_ns = clockevent_delta2ns (0x30, &hpet_clockevent);
    hpet_clockevent.cpumask = cpumask_of_cpu (smp_processor_id ());
    clockevents_register_device (& hpet_clockevent);
    global_clock_event = &hpet_clockevent;
    printk (KERN_DEBUG "hpet clockevent registered\n");
}

{
    unsigned long cfg, cmp, now;
    uint64_t delta;
    switch (mode) {
    case CLOCK_EVT_MODE_PERIODIC :
        delta = ((uint64_t) (NSEC_PER_SEC / HZ)) * hpet_clockevent.mult;
        delta >>= hpet_clockevent.shift;
        now = hpet_readl (HPET_COUNTER);
        cmp = now + (unsigned long) delta;
        cfg = hpet_readl (HPET_T0_CFG);
        cfg |= HPET_TN_ENABLE | HPET_TN_PERIODIC | HPET_TN_SETVAL | HPET_TN_32BIT;
        hpet_writel (cfg, HPET_T0_CFG);
        hpet_writel (cmp, HPET_T0_CMP);
        udelay (1);
        hpet_writel ((unsigned long) delta, HPET_T0_CMP);
        break;
    case CLOCK_EVT_MODE_ONESHOT :
        cfg = hpet_readl (HPET_T0_CFG);
        cfg &= ~HPET_TN_PERIODIC;
        cfg |= HPET_TN_ENABLE | HPET_TN_32BIT;
        hpet_writel (cfg, HPET_T0_CFG);
        break;
    case CLOCK_EVT_MODE_UNUSED :
    case CLOCK_EVT_MODE_SHUTDOWN :
        cfg = hpet_readl (HPET_T0_CFG);
        cfg &= ~HPET_TN_ENABLE;
        hpet_writel (cfg, HPET_T0_CFG);
        break;
    case CLOCK_EVT_MODE_RESUME :
        hpet_enable_legacy_int ();
        break;
    }
}

{
case CLOCK_EVT_MODE_PERIODIC :
    delta = ((uint64_t) (NSEC_PER_SEC / HZ)) * hpet_clockevent.mult;
    delta >>= hpet_clockevent.shift;
    now = hpet_readl (HPET_COUNTER);
    cmp = now + (unsigned long) delta;
    cfg = hpet_readl (HPET_T0_CFG);
    cfg |= HPET_TN_ENABLE | HPET_TN_PERIODIC | HPET_TN_SETVAL | HPET_TN_32BIT;
    hpet_writel (cfg, HPET_T0_CFG);
    hpet_writel (cmp, HPET_T0_CMP);
    udelay (1);
    hpet_writel ((unsigned long) delta, HPET_T0_CMP);
    break;
case CLOCK_EVT_MODE_ONESHOT :
    cfg = hpet_readl (HPET_T0_CFG);
    cfg &= ~HPET_TN_PERIODIC;
    cfg |= HPET_TN_ENABLE | HPET_TN_32BIT;
    hpet_writel (cfg, HPET_T0_CFG);
    break;
case CLOCK_EVT_MODE_UNUSED :
case CLOCK_EVT_MODE_SHUTDOWN :
    cfg = hpet_readl (HPET_T0_CFG);
    cfg &= ~HPET_TN_ENABLE;
    hpet_writel (cfg, HPET_T0_CFG);
    break;
case CLOCK_EVT_MODE_RESUME :
    hpet_enable_legacy_int ();
    break;
}

{
    unsigned long cnt;
    cnt = hpet_readl (HPET_COUNTER);
    cnt += delta;
    hpet_writel (cnt, HPET_T0_CMP);
    return ((long) (hpet_readl (HPET_COUNTER) - cnt) > 0) ? -ETIME : 0;
}

{
    return (cycle_t) hpet_readl (HPET_COUNTER);
}

{
    u64 tmp, start, now;
    cycle_t t1;
    hpet_start_counter ();
    t1 = read_hpet ();
    rdtscll (start);
    do {
        rep_nop ();
        rdtscll (now);
    }
    while ((now - start) < 200000UL);
    if (t1 == read_hpet ()) {
        printk (KERN_WARNING "HPET counter not counting. HPET disabled\n");
        return -ENODEV;
    }
    tmp = (u64) hpet_period << HPET_SHIFT;
    do_div (tmp, FSEC_PER_NSEC);
    clocksource_hpet.mult = (u32) tmp;
    clocksource_register (& clocksource_hpet);
    return 0;
}

{
    rep_nop ();
    rdtscll (now);
}

{
    printk (KERN_WARNING "HPET counter not counting. HPET disabled\n");
    return -ENODEV;
}

{
    unsigned long id;
    if (!is_hpet_capable ())
        return 0;
    hpet_set_mapping ();
    hpet_period = hpet_readl (HPET_PERIOD);
    if (hpet_period < HPET_MIN_PERIOD || hpet_period > HPET_MAX_PERIOD)
        goto out_nohpet;
    id = hpet_readl (HPET_ID);
    if (hpet_clocksource_register ())
        goto out_nohpet;
    if (id & HPET_ID_LEGSUP) {
        hpet_legacy_clockevent_register ();
        return 1;
    }
    return 0;
out_nohpet :
    hpet_clear_mapping ();
    boot_hpet_disable = 1;
    return 0;
}

{
    hpet_legacy_clockevent_register ();
    return 1;
}

{
    if (boot_hpet_disable)
        return -ENODEV;
    if (!hpet_address) {
        if (!force_hpet_address)
            return -ENODEV;
        hpet_address = force_hpet_address;
        hpet_enable ();
        if (!hpet_virt_address)
            return -ENODEV;
    }
    hpet_reserve_platform_timers (hpet_readl (HPET_ID));
    return 0;
}

{
    if (!force_hpet_address)
        return -ENODEV;
    hpet_address = force_hpet_address;
    hpet_enable ();
    if (!hpet_virt_address)
        return -ENODEV;
}

{
    if (is_hpet_capable ()) {
        unsigned long cfg = hpet_readl (HPET_CFG);
        if (hpet_legacy_int_enabled) {
            cfg &= ~HPET_CFG_LEGACY;
            hpet_legacy_int_enabled = 0;
        }
        cfg &= ~HPET_CFG_ENABLE;
        hpet_writel (cfg, HPET_CFG);
    }
}

{
    unsigned long cfg = hpet_readl (HPET_CFG);
    if (hpet_legacy_int_enabled) {
        cfg &= ~HPET_CFG_LEGACY;
        hpet_legacy_int_enabled = 0;
    }
    cfg &= ~HPET_CFG_ENABLE;
    hpet_writel (cfg, HPET_CFG);
}

{
    cfg &= ~HPET_CFG_LEGACY;
    hpet_legacy_int_enabled = 0;
}

{
    int cpuid, phys_id;
    unsigned long timeout;
    wait_for_init_deassert (& init_deasserted);
    phys_id = GET_APIC_ID (read_apic_id ());
    cpuid = smp_processor_id ();
    if (cpu_isset (cpuid, cpu_callin_map)) {
        panic ("%s: phys CPU#%d, CPU#%d already present??\n", __func__, phys_id, cpuid);
    }
    Dprintk ("CPU#%d (phys ID: %d) waiting for CALLOUT\n", cpuid, phys_id);
    timeout = jiffies + 2 * HZ;
    while (time_before (jiffies, timeout)) {
        if (cpu_isset (cpuid, cpu_callout_map))
            break;
        cpu_relax ();
    }
    if (!time_before (jiffies, timeout)) {
        panic ("%s: CPU%d started up but did not get a callout!\n", __func__, cpuid);
    }
    Dprintk ("CALLIN, before setup_local_APIC().\n");
    smp_callin_clear_local_apic ();
    setup_local_APIC ();
    end_local_APIC_setup ();
    map_cpu_to_logical_apicid ();
    local_irq_enable ();
    calibrate_delay ();
    local_irq_disable ();
    Dprintk ("Stack at about %p\n", & cpuid);
    smp_store_cpu_info (cpuid);
    cpu_set (cpuid, cpu_callin_map);
}

{
    panic ("%s: phys CPU#%d, CPU#%d already present??\n", __func__, phys_id, cpuid);
}

{
    if (cpu_isset (cpuid, cpu_callout_map))
        break;
    cpu_relax ();
}

{
    panic ("%s: CPU%d started up but did not get a callout!\n", __func__, cpuid);
}

{
    cpu_init ();
    preempt_disable ();
    smp_callin ();
    barrier ();
    check_tsc_sync_target ();
    if (nmi_watchdog == NMI_IO_APIC) {
        disable_8259A_irq (0);
        enable_NMI_through_LVT0 ();
        enable_8259A_irq (0);
    }
    set_cpu_sibling_map (raw_smp_processor_id ());
    wmb ();
    lock_ipi_call_lock ();
    cpu_set (smp_processor_id (), cpu_online_map);
    unlock_ipi_call_lock ();
    per_cpu (cpu_state, smp_processor_id ()) = CPU_ONLINE;
    setup_secondary_clock ();
    wmb ();
    cpu_idle ();
}

{
    disable_8259A_irq (0);
    enable_NMI_through_LVT0 ();
    enable_8259A_irq (0);
}

{
    if (smp_b_stepping)
        printk (KERN_WARNING "WARNING: SMP operation may be unreliable" "with B stepping processors.\n");
    if (tainted & TAINT_UNSAFE_SMP) {
        if (num_online_cpus ())
            printk (KERN_INFO "WARNING: This combination of AMD" "processors is not suitable for SMP.\n");
        else
            tainted &= ~TAINT_UNSAFE_SMP;
    }
}

{
    if (num_online_cpus ())
        printk (KERN_INFO "WARNING: This combination of AMD" "processors is not suitable for SMP.\n");
    else
        tainted &= ~TAINT_UNSAFE_SMP;
}

{
    struct cpuinfo_x86 *c = &cpu_data (id);
    *c = boot_cpu_data;
    c->cpu_index = id;
    if (id != 0)
        identify_secondary_cpu (c);
    smp_apply_quirks (c);
}

{
    int i;
    struct cpuinfo_x86 *c = &cpu_data (cpu);
    cpu_set (cpu, cpu_sibling_setup_map);
    if (smp_num_siblings > 1) {

        for_each_cpu_mask (i, cpu_sibling_setup_map) {
            if (c->phys_proc_id == cpu_data (i).phys_proc_id && c->cpu_core_id == cpu_data (i).cpu_core_id) {
                cpu_set (i, per_cpu (cpu_sibling_map, cpu));
                cpu_set (cpu, per_cpu (cpu_sibling_map, i));
                cpu_set (i, per_cpu (cpu_core_map, cpu));
                cpu_set (cpu, per_cpu (cpu_core_map, i));
                cpu_set (i, c -> llc_shared_map);
                cpu_set (cpu, cpu_data (i).llc_shared_map);
            }
        }

    }
    else {
        cpu_set (cpu, per_cpu (cpu_sibling_map, cpu));
    }
    cpu_set (cpu, c -> llc_shared_map);
    if (current_cpu_data.x86_max_cores == 1) {
        per_cpu (cpu_core_map, cpu) = per_cpu (cpu_sibling_map, cpu);
        c->booted_cores = 1;
        return;
    }

    for_each_cpu_mask (i, cpu_sibling_setup_map) {
        if (per_cpu (cpu_llc_id, cpu) != BAD_APICID && per_cpu (cpu_llc_id, cpu) == per_cpu (cpu_llc_id, i)) {
            cpu_set (i, c -> llc_shared_map);
            cpu_set (cpu, cpu_data (i).llc_shared_map);
        }
        if (c->phys_proc_id == cpu_data (i).phys_proc_id) {
            cpu_set (i, per_cpu (cpu_core_map, cpu));
            cpu_set (cpu, per_cpu (cpu_core_map, i));
            if (cpus_weight (per_cpu (cpu_sibling_map, cpu)) == 1) {
                if (first_cpu (per_cpu (cpu_sibling_map, i)) == i)
                    c->booted_cores++;
                if (i != cpu)
                    cpu_data (i).booted_cores++;
            }
            else if (i != cpu && !c->booted_cores)
                c->booted_cores = cpu_data (i).booted_cores;
        }
    }

}

{

    for_each_cpu_mask (i, cpu_sibling_setup_map) {
        if (c->phys_proc_id == cpu_data (i).phys_proc_id && c->cpu_core_id == cpu_data (i).cpu_core_id) {
            cpu_set (i, per_cpu (cpu_sibling_map, cpu));
            cpu_set (cpu, per_cpu (cpu_sibling_map, i));
            cpu_set (i, per_cpu (cpu_core_map, cpu));
            cpu_set (cpu, per_cpu (cpu_core_map, i));
            cpu_set (i, c -> llc_shared_map);
            cpu_set (cpu, cpu_data (i).llc_shared_map);
        }
    }

}

{
    if (c->phys_proc_id == cpu_data (i).phys_proc_id && c->cpu_core_id == cpu_data (i).cpu_core_id) {
        cpu_set (i, per_cpu (cpu_sibling_map, cpu));
        cpu_set (cpu, per_cpu (cpu_sibling_map, i));
        cpu_set (i, per_cpu (cpu_core_map, cpu));
        cpu_set (cpu, per_cpu (cpu_core_map, i));
        cpu_set (i, c -> llc_shared_map);
        cpu_set (cpu, cpu_data (i).llc_shared_map);
    }
}

{
    cpu_set (i, per_cpu (cpu_sibling_map, cpu));
    cpu_set (cpu, per_cpu (cpu_sibling_map, i));
    cpu_set (i, per_cpu (cpu_core_map, cpu));
    cpu_set (cpu, per_cpu (cpu_core_map, i));
    cpu_set (i, c -> llc_shared_map);
    cpu_set (cpu, cpu_data (i).llc_shared_map);
}

{
    cpu_set (cpu, per_cpu (cpu_sibling_map, cpu));
}

{
    per_cpu (cpu_core_map, cpu) = per_cpu (cpu_sibling_map, cpu);
    c->booted_cores = 1;
    return;
}

{
    if (per_cpu (cpu_llc_id, cpu) != BAD_APICID && per_cpu (cpu_llc_id, cpu) == per_cpu (cpu_llc_id, i)) {
        cpu_set (i, c -> llc_shared_map);
        cpu_set (cpu, cpu_data (i).llc_shared_map);
    }
    if (c->phys_proc_id == cpu_data (i).phys_proc_id) {
        cpu_set (i, per_cpu (cpu_core_map, cpu));
        cpu_set (cpu, per_cpu (cpu_core_map, i));
        if (cpus_weight (per_cpu (cpu_sibling_map, cpu)) == 1) {
            if (first_cpu (per_cpu (cpu_sibling_map, i)) == i)
                c->booted_cores++;
            if (i != cpu)
                cpu_data (i).booted_cores++;
        }
        else if (i != cpu && !c->booted_cores)
            c->booted_cores = cpu_data (i).booted_cores;
    }
}

{
    cpu_set (i, c -> llc_shared_map);
    cpu_set (cpu, cpu_data (i).llc_shared_map);
}

{
    cpu_set (i, per_cpu (cpu_core_map, cpu));
    cpu_set (cpu, per_cpu (cpu_core_map, i));
    if (cpus_weight (per_cpu (cpu_sibling_map, cpu)) == 1) {
        if (first_cpu (per_cpu (cpu_sibling_map, i)) == i)
            c->booted_cores++;
        if (i != cpu)
            cpu_data (i).booted_cores++;
    }
    else if (i != cpu && !c->booted_cores)
        c->booted_cores = cpu_data (i).booted_cores;
}

{
    if (first_cpu (per_cpu (cpu_sibling_map, i)) == i)
        c->booted_cores++;
    if (i != cpu)
        cpu_data (i).booted_cores++;
}

{
    struct cpuinfo_x86 *c = &cpu_data (cpu);
    if (sched_mc_power_savings || sched_smt_power_savings)
        return per_cpu (cpu_core_map, cpu);
    else
        return c->llc_shared_map;
}

{
    int cpu;
    unsigned long bogosum = 0;
    Dprintk ("Before bogomips.\n");
    for_each_possible_cpu (cpu)
    if (cpu_isset (cpu, cpu_callout_map))
        bogosum += cpu_data (cpu).loops_per_jiffy;
    printk (KERN_INFO "Total of %d processors activated (%lu.%02lu BogoMIPS).\n", num_online_cpus (), bogosum / (500000 / HZ), (bogosum / (5000 / HZ)) % 100);
    Dprintk ("Before bogocount - setting activated=1.\n");
}

{
    unsigned i, regs [] = {APIC_ID >> 4, APIC_LVR >> 4, APIC_SPIV >> 4};
    char *names [] = {"ID", "VERSION", "SPIV"};
    int timeout;
    u32 status;
    printk (KERN_INFO "Inquiring remote APIC #%d...\n", apicid);
    for (i = 0; i < ARRAY_SIZE (regs); i++) {
        printk (KERN_INFO "... APIC #%d %s: ", apicid, names [i]);
        status = safe_apic_wait_icr_idle ();
        if (status)
            printk (KERN_CONT "a previous APIC delivery may have failed\n");
        apic_write_around (APIC_ICR2, SET_APIC_DEST_FIELD (apicid));
        apic_write_around (APIC_ICR, APIC_DM_REMRD | regs [i]);
        timeout = 0;
        do {
            udelay (100);
            status = apic_read (APIC_ICR) & APIC_ICR_RR_MASK;
        }
        while (status == APIC_ICR_RR_INPROG && timeout++ < 1000);
        switch (status) {
        case APIC_ICR_RR_VALID :
            status = apic_read (APIC_RRR);
            printk (KERN_CONT "%08x\n", status);
            break;
        default :
            printk (KERN_CONT "failed\n");
        }
    }
}

{
    printk (KERN_INFO "... APIC #%d %s: ", apicid, names [i]);
    status = safe_apic_wait_icr_idle ();
    if (status)
        printk (KERN_CONT "a previous APIC delivery may have failed\n");
    apic_write_around (APIC_ICR2, SET_APIC_DEST_FIELD (apicid));
    apic_write_around (APIC_ICR, APIC_DM_REMRD | regs [i]);
    timeout = 0;
    do {
        udelay (100);
        status = apic_read (APIC_ICR) & APIC_ICR_RR_MASK;
    }
    while (status == APIC_ICR_RR_INPROG && timeout++ < 1000);
    switch (status) {
    case APIC_ICR_RR_VALID :
        status = apic_read (APIC_RRR);
        printk (KERN_CONT "%08x\n", status);
        break;
    default :
        printk (KERN_CONT "failed\n");
    }
}

{
    udelay (100);
    status = apic_read (APIC_ICR) & APIC_ICR_RR_MASK;
}

{
case APIC_ICR_RR_VALID :
    status = apic_read (APIC_RRR);
    printk (KERN_CONT "%08x\n", status);
    break;
default :
    printk (KERN_CONT "failed\n");
}

{
    struct create_idle *c_idle = container_of (work, struct create_idle, work);
    c_idle->idle = fork_idle (c_idle->cpu);
    complete (& c_idle -> done);
}

{
    unsigned long boot_error = 0;
    int timeout;
    unsigned long start_ip;
    unsigned short nmi_high = 0, nmi_low = 0;
    struct create_idle c_idle = {
        .cpu = cpu,
        .done = COMPLETION_INITIALIZER_ONSTACK (c_idle.done),
    };
    INIT_WORK (& c_idle.work, do_fork_idle);
    alternatives_smp_switch (1);
    c_idle.idle = get_idle_for_cpu (cpu);
    if (c_idle.idle) {
        c_idle.idle->thread.sp = (unsigned long) (((struct pt_regs *) (THREAD_SIZE + task_stack_page (c_idle.idle))) - 1);
        init_idle (c_idle.idle, cpu);
        goto do_rest;
    }
    if (!keventd_up () || current_is_keventd ())
        c_idle.work.func (&c_idle.work);
    else {
        schedule_work (& c_idle.work);
        wait_for_completion (& c_idle.done);
    }
    if (IS_ERR (c_idle.idle)) {
        printk ("failed fork for CPU %d\n", cpu);
        return PTR_ERR (c_idle.idle);
    }
    set_idle_for_cpu (cpu, c_idle.idle);
do_rest :
    cpu_pda (cpu)->pcurrent = c_idle.idle;
    init_rsp = c_idle.idle->thread.sp;
    load_sp0 (& per_cpu (init_tss, cpu), & c_idle.idle -> thread);
    initial_code = (unsigned long) start_secondary;
    clear_tsk_thread_flag (c_idle.idle, TIF_FORK);
    start_ip = setup_trampoline ();
    printk (KERN_INFO "Booting processor %d/%d ip %lx\n", cpu, apicid, start_ip);
    atomic_set (& init_deasserted, 0);
    if (get_uv_system_type () != UV_NON_UNIQUE_APIC) {
        Dprintk ("Setting warm reset code and vector.\n");
        store_NMI_vector (& nmi_high, & nmi_low);
        smpboot_setup_warm_reset_vector (start_ip);
        apic_write (APIC_ESR, 0);
        apic_read (APIC_ESR);
    }
    boot_error = wakeup_secondary_cpu (apicid, start_ip);
    if (!boot_error) {
        Dprintk ("Before Callout %d.\n", cpu);
        cpu_set (cpu, cpu_callout_map);
        Dprintk ("After Callout %d.\n", cpu);
        for (timeout = 0; timeout < 50000; timeout++) {
            if (cpu_isset (cpu, cpu_callin_map))
                break;
            udelay (100);
        }
        if (cpu_isset (cpu, cpu_callin_map)) {
            Dprintk ("OK.\n");
            printk (KERN_INFO "CPU%d: ", cpu);
            print_cpu_info (& cpu_data (cpu));
            Dprintk ("CPU has booted.\n");
        }
        else {
            boot_error = 1;
            if (*((volatile unsigned char *) trampoline_base) == 0xA5)
                printk (KERN_ERR "Stuck ??\n");
            else
                printk (KERN_ERR "Not responding.\n");
            if (get_uv_system_type () != UV_NON_UNIQUE_APIC)
                inquire_remote_apic (apicid);
        }
    }
    if (boot_error) {
        unmap_cpu_to_logical_apicid (cpu);
        cpu_clear (cpu, cpu_callout_map);
        cpu_clear (cpu, cpu_initialized);
        cpu_clear (cpu, cpu_present_map);
        per_cpu (x86_cpu_to_apicid, cpu) = BAD_APICID;
    }
    *((volatile unsigned long *) trampoline_base) = 0;
    smpboot_restore_warm_reset_vector ();
    return boot_error;
}

{
    c_idle.idle->thread.sp = (unsigned long) (((struct pt_regs *) (THREAD_SIZE + task_stack_page (c_idle.idle))) - 1);
    init_idle (c_idle.idle, cpu);
    goto do_rest;
}

{
    schedule_work (& c_idle.work);
    wait_for_completion (& c_idle.done);
}

{
    printk ("failed fork for CPU %d\n", cpu);
    return PTR_ERR (c_idle.idle);
}

{
    Dprintk ("Setting warm reset code and vector.\n");
    store_NMI_vector (& nmi_high, & nmi_low);
    smpboot_setup_warm_reset_vector (start_ip);
    apic_write (APIC_ESR, 0);
    apic_read (APIC_ESR);
}

{
    Dprintk ("Before Callout %d.\n", cpu);
    cpu_set (cpu, cpu_callout_map);
    Dprintk ("After Callout %d.\n", cpu);
    for (timeout = 0; timeout < 50000; timeout++) {
        if (cpu_isset (cpu, cpu_callin_map))
            break;
        udelay (100);
    }
    if (cpu_isset (cpu, cpu_callin_map)) {
        Dprintk ("OK.\n");
        printk (KERN_INFO "CPU%d: ", cpu);
        print_cpu_info (& cpu_data (cpu));
        Dprintk ("CPU has booted.\n");
    }
    else {
        boot_error = 1;
        if (*((volatile unsigned char *) trampoline_base) == 0xA5)
            printk (KERN_ERR "Stuck ??\n");
        else
            printk (KERN_ERR "Not responding.\n");
        if (get_uv_system_type () != UV_NON_UNIQUE_APIC)
            inquire_remote_apic (apicid);
    }
}

{
    if (cpu_isset (cpu, cpu_callin_map))
        break;
    udelay (100);
}

{
    Dprintk ("OK.\n");
    printk (KERN_INFO "CPU%d: ", cpu);
    print_cpu_info (& cpu_data (cpu));
    Dprintk ("CPU has booted.\n");
}

{
    boot_error = 1;
    if (*((volatile unsigned char *) trampoline_base) == 0xA5)
        printk (KERN_ERR "Stuck ??\n");
    else
        printk (KERN_ERR "Not responding.\n");
    if (get_uv_system_type () != UV_NON_UNIQUE_APIC)
        inquire_remote_apic (apicid);
}

{
    unmap_cpu_to_logical_apicid (cpu);
    cpu_clear (cpu, cpu_callout_map);
    cpu_clear (cpu, cpu_initialized);
    cpu_clear (cpu, cpu_present_map);
    per_cpu (x86_cpu_to_apicid, cpu) = BAD_APICID;
}

{
    int apicid = cpu_present_to_apicid (cpu);
    unsigned long flags;
    int err;
    WARN_ON (irqs_disabled ());
    Dprintk ("++++++++++++++++++++=_---CPU UP  %u\n", cpu);
    if (apicid == BAD_APICID || apicid == boot_cpu_physical_apicid || !physid_isset (apicid, phys_cpu_present_map)) {
        printk (KERN_ERR "%s: bad cpu %d\n", __func__, cpu);
        return -EINVAL;
    }
    if (cpu_isset (cpu, cpu_callin_map)) {
        Dprintk ("do_boot_cpu %d Already started\n", cpu);
        return -ENOSYS;
    }
    mtrr_save_state ();
    per_cpu (cpu_state, cpu) = CPU_UP_PREPARE;
    err = do_boot_cpu (apicid, cpu);
    if (err) {
        Dprintk ("do_boot_cpu failed %d\n", err);
        return -EIO;
    }
    local_irq_save (flags);
    check_tsc_sync_source (cpu);
    local_irq_restore (flags);
    while (!cpu_online (cpu)) {
        cpu_relax ();
        touch_nmi_watchdog ();
    }
    return 0;
}

{
    printk (KERN_ERR "%s: bad cpu %d\n", __func__, cpu);
    return -EINVAL;
}

{
    Dprintk ("do_boot_cpu %d Already started\n", cpu);
    return -ENOSYS;
}

{
    Dprintk ("do_boot_cpu failed %d\n", err);
    return -EIO;
}

{
    cpu_relax ();
    touch_nmi_watchdog ();
}

{
    cpu_present_map = cpumask_of_cpu (0);
    cpu_possible_map = cpumask_of_cpu (0);
    if (smp_found_config)
        phys_cpu_present_map = physid_mask_of_physid (boot_cpu_physical_apicid);
    else
        phys_cpu_present_map = physid_mask_of_physid (0);
    map_cpu_to_logical_apicid ();
    cpu_set (0, per_cpu (cpu_sibling_map, 0));
    cpu_set (0, per_cpu (cpu_core_map, 0));
}

{
    preempt_disable ();
    if (!physid_isset (hard_smp_processor_id (), phys_cpu_present_map)) {
        printk (KERN_WARNING "weird, boot CPU (#%d) not listed" "by the BIOS.\n", hard_smp_processor_id ());
        physid_set (hard_smp_processor_id (), phys_cpu_present_map);
    }
    if (!smp_found_config && !acpi_lapic) {
        preempt_enable ();
        printk (KERN_NOTICE "SMP motherboard not detected.\n");
        disable_smp ();
        if (APIC_init_uniprocessor ())
            printk (KERN_NOTICE "Local APIC not detected." " Using dummy APIC emulation.\n");
        return -1;
    }
    if (!check_phys_apicid_present (boot_cpu_physical_apicid)) {
        printk (KERN_NOTICE "weird, boot CPU (#%d) not listed by the BIOS.\n", boot_cpu_physical_apicid);
        physid_set (hard_smp_processor_id (), phys_cpu_present_map);
    }
    preempt_enable ();
    if (APIC_INTEGRATED (apic_version[boot_cpu_physical_apicid]) && !cpu_has_apic) {
        printk (KERN_ERR "BIOS bug, local APIC #%d not detected!...\n", boot_cpu_physical_apicid);
        printk (KERN_ERR "... forcing use of dummy APIC emulation." "(tell your hw vendor)\n");
        smpboot_clear_io_apic ();
        return -1;
    }
    verify_local_APIC ();
    if (!max_cpus) {
        printk (KERN_INFO "SMP mode deactivated," "forcing use of dummy APIC emulation.\n");
        smpboot_clear_io_apic ();
        setup_local_APIC ();
        end_local_APIC_setup ();
        return -1;
    }
    return 0;
}

{
    printk (KERN_WARNING "weird, boot CPU (#%d) not listed" "by the BIOS.\n", hard_smp_processor_id ());
    physid_set (hard_smp_processor_id (), phys_cpu_present_map);
}

{
    preempt_enable ();
    printk (KERN_NOTICE "SMP motherboard not detected.\n");
    disable_smp ();
    if (APIC_init_uniprocessor ())
        printk (KERN_NOTICE "Local APIC not detected." " Using dummy APIC emulation.\n");
    return -1;
}

{
    printk (KERN_NOTICE "weird, boot CPU (#%d) not listed by the BIOS.\n", boot_cpu_physical_apicid);
    physid_set (hard_smp_processor_id (), phys_cpu_present_map);
}

{
    printk (KERN_ERR "BIOS bug, local APIC #%d not detected!...\n", boot_cpu_physical_apicid);
    printk (KERN_ERR "... forcing use of dummy APIC emulation." "(tell your hw vendor)\n");
    smpboot_clear_io_apic ();
    return -1;
}

{
    printk (KERN_INFO "SMP mode deactivated," "forcing use of dummy APIC emulation.\n");
    smpboot_clear_io_apic ();
    setup_local_APIC ();
    end_local_APIC_setup ();
    return -1;
}

{
    int i;
    struct cpuinfo_x86 *c;

    for_each_possible_cpu (i) {
        c = &cpu_data (i);
        c->cpu_index = NR_CPUS;
    }

}

{
    c = &cpu_data (i);
    c->cpu_index = NR_CPUS;
}

{
    preempt_disable ();
    nmi_watchdog_default ();
    smp_cpu_index_default ();
    current_cpu_data = boot_cpu_data;
    cpu_callin_map = cpumask_of_cpu (0);
    mb ();
    smp_store_cpu_info (0);
    boot_cpu_logical_apicid = logical_smp_processor_id ();
    current_thread_info ()->cpu = 0;
    set_cpu_sibling_map (0);
    if (smp_sanity_check (max_cpus) < 0) {
        printk (KERN_INFO "SMP disabled\n");
        disable_smp ();
        goto out;
    }
    preempt_disable ();
    if (GET_APIC_ID (read_apic_id ()) != boot_cpu_physical_apicid) {
        panic ("Boot APIC ID in local APIC unexpected (%d vs %d)", GET_APIC_ID (read_apic_id ()), boot_cpu_physical_apicid);
    }
    preempt_enable ();
    setup_local_APIC ();
    end_local_APIC_setup ();
    map_cpu_to_logical_apicid ();
    setup_portio_remap ();
    smpboot_setup_io_apic ();
    printk (KERN_INFO "CPU%d: ", 0);
    print_cpu_info (& cpu_data (0));
    setup_boot_clock ();
out :
    preempt_enable ();
}

{
    printk (KERN_INFO "SMP disabled\n");
    disable_smp ();
    goto out;
}

{
    panic ("Boot APIC ID in local APIC unexpected (%d vs %d)", GET_APIC_ID (read_apic_id ()), boot_cpu_physical_apicid);
}

{
    int me = smp_processor_id ();
    cpu_set (me, cpu_callout_map);
    per_cpu (cpu_state, me) = CPU_ONLINE;
}

{
    Dprintk ("Boot done.\n");
    impress_friends ();
    smp_checks ();
    check_nmi_watchdog ();
}

{
    return -ENOSYS;
}

{
    extern unsigned int maxcpus;
    maxcpus = simple_strtoul (arg, NULL, 0);
    return 0;
}

{
    unsigned long bootmap_size, bootmap;
    bootmap_size = bootmem_bootmap_pages (end_pfn) << PAGE_SHIFT;
    bootmap = find_e820_area (0, end_pfn << PAGE_SHIFT, bootmap_size, PAGE_SIZE);
    if (bootmap == -1L)
        panic ("Cannot find bootmem map of size %ld\n", bootmap_size);
    bootmap_size = init_bootmem (bootmap >> PAGE_SHIFT, end_pfn);
    e820_register_active_regions (0, start_pfn, end_pfn);
    free_bootmem_with_active_regions (0, end_pfn);
    early_res_to_bootmem (0, end_pfn << PAGE_SHIFT);
    reserve_bootmem (bootmap, bootmap_size, BOOTMEM_DEFAULT);
}

{
    machine_specific_memory_setup ();
}

{
    struct setup_data *data;
    unsigned long pa_data;
    if (boot_params.hdr.version < 0x0209)
        return;
    pa_data = boot_params.hdr.setup_data;
    while (pa_data) {
        data = early_ioremap (pa_data, PAGE_SIZE);
        switch (data->type) {
        default :
            break;
        }
        free_early (pa_data, pa_data + sizeof (* data) + data -> len);
        pa_data = data->next;
        early_iounmap (data, PAGE_SIZE);
    }
}

{
    data = early_ioremap (pa_data, PAGE_SIZE);
    switch (data->type) {
    default :
        break;
    }
    free_early (pa_data, pa_data + sizeof (* data) + data -> len);
    pa_data = data->next;
    early_iounmap (data, PAGE_SIZE);
}

{
default :
    break;
}

{
    unsigned i;
    printk (KERN_INFO "Command line: %s\n", boot_command_line);
    ROOT_DEV = old_decode_dev (boot_params.hdr.root_dev);
    screen_info = boot_params.screen_info;
    edid_info = boot_params.edid_info;
    saved_video_mode = boot_params.hdr.vid_mode;
    bootloader_type = boot_params.hdr.type_of_loader;
    ARCH_SETUP memory_setup ();
    copy_edd ();
    if (!boot_params.hdr.root_flags)
        root_mountflags &= ~MS_RDONLY;
    init_mm.start_code = (unsigned long) &_text;
    init_mm.end_code = (unsigned long) &_etext;
    init_mm.end_data = (unsigned long) &_edata;
    init_mm.brk = (unsigned long) &_end;
    code_resource.start = virt_to_phys (&_text);
    code_resource.end = virt_to_phys (&_etext) - 1;
    data_resource.start = virt_to_phys (&_etext);
    data_resource.end = virt_to_phys (&_edata) - 1;
    bss_resource.start = virt_to_phys (&__bss_start);
    bss_resource.end = virt_to_phys (&__bss_stop) - 1;
    early_identify_cpu (& boot_cpu_data);
    strlcpy (command_line, boot_command_line, COMMAND_LINE_SIZE);
    *cmdline_p = command_line;
    parse_setup_data ();
    parse_early_param ();
    finish_e820_parsing ();
    insert_resource (& iomem_resource, & code_resource);
    insert_resource (& iomem_resource, & data_resource);
    insert_resource (& iomem_resource, & bss_resource);
    early_gart_iommu_check ();
    e820_register_active_regions (0, 0, - 1UL);
    end_pfn = e820_end_of_ram ();
    mtrr_bp_init ();
    if (mtrr_trim_uncached_memory (end_pfn)) {
        e820_register_active_regions (0, 0, - 1UL);
        end_pfn = e820_end_of_ram ();
    }
    num_physpages = end_pfn;
    check_efer ();
    max_pfn_mapped = init_memory_mapping (0, (max_pfn_mapped << PAGE_SHIFT));
    if (efi_enabled)
        efi_init ();
    vsmp_init ();
    dmi_scan_machine ();
    io_delay_init ();
    max_low_pfn = end_pfn;
    max_pfn = end_pfn;
    high_memory = (void *) __va (end_pfn *PAGE_SIZE - 1) + 1;
    remove_all_active_ranges ();
    contig_initmem_init (0, end_pfn);
    dma32_reserve_bootmem ();
    if (efi_enabled)
        efi_reserve_bootmem ();
    find_smp_config ();
    reserve_crashkernel ();
    reserve_ibft_region ();
    paging_init ();
    map_vsyscall ();
    early_quirks ();
    init_cpu_to_node ();
    if (smp_found_config)
        get_smp_config ();
    init_apic_mappings ();
    ioapic_init_mappings ();
    kvm_guest_init ();
    e820_reserve_resources ();
    e820_mark_nosave_regions ();
    for (i = 0; i < ARRAY_SIZE (standard_io_resources); i++)
        request_resource (&ioport_resource, &standard_io_resources[i]);
    e820_setup_gap ();
    check_enable_amd_mmconf_dmi ();
}

{
    e820_register_active_regions (0, 0, - 1UL);
    end_pfn = e820_end_of_ram ();
}

{
    unsigned int *v;
    if (c->extended_cpuid_level < 0x80000004)
        return 0;
    v = (unsigned int *) c->x86_model_id;
    cpuid (0x80000002, & v [0], & v [1], & v [2], & v [3]);
    cpuid (0x80000003, & v [4], & v [5], & v [6], & v [7]);
    cpuid (0x80000004, & v [8], & v [9], & v [10], & v [11]);
    c->x86_model_id[48] = 0;
    return 1;
}

{
    unsigned int n, dummy, eax, ebx, ecx, edx;
    n = c->extended_cpuid_level;
    if (n >= 0x80000005) {
        cpuid (0x80000005, & dummy, & ebx, & ecx, & edx);
        printk (KERN_INFO "CPU: L1 I Cache: %dK (%d bytes/line), " "D cache %dK (%d bytes/line)\n", edx >> 24, edx & 0xFF, ecx >> 24, ecx & 0xFF);
        c->x86_cache_size = (ecx >> 24) + (edx >> 24);
        c->x86_tlbsize = 0;
    }
    if (n >= 0x80000006) {
        cpuid (0x80000006, & dummy, & ebx, & ecx, & edx);
        ecx = cpuid_ecx (0x80000006);
        c->x86_cache_size = ecx >> 16;
        c->x86_tlbsize += ((ebx >> 16) & 0xfff) + (ebx & 0xfff);
        printk (KERN_INFO "CPU: L2 Cache: %dK (%d bytes/line)\n", c -> x86_cache_size, ecx & 0xFF);
    }
    if (n >= 0x80000008) {
        cpuid (0x80000008, & eax, & dummy, & dummy, & dummy);
        c->x86_virt_bits = (eax >> 8) & 0xff;
        c->x86_phys_bits = eax & 0xff;
    }
}

{
    cpuid (0x80000005, & dummy, & ebx, & ecx, & edx);
    printk (KERN_INFO "CPU: L1 I Cache: %dK (%d bytes/line), " "D cache %dK (%d bytes/line)\n", edx >> 24, edx & 0xFF, ecx >> 24, ecx & 0xFF);
    c->x86_cache_size = (ecx >> 24) + (edx >> 24);
    c->x86_tlbsize = 0;
}

{
    cpuid (0x80000006, & dummy, & ebx, & ecx, & edx);
    ecx = cpuid_ecx (0x80000006);
    c->x86_cache_size = ecx >> 16;
    c->x86_tlbsize += ((ebx >> 16) & 0xfff) + (ebx & 0xfff);
    printk (KERN_INFO "CPU: L2 Cache: %dK (%d bytes/line)\n", c -> x86_cache_size, ecx & 0xFF);
}

{
    cpuid (0x80000008, & eax, & dummy, & dummy, & dummy);
    c->x86_virt_bits = (eax >> 8) & 0xff;
    c->x86_phys_bits = eax & 0xff;
}

{
    u32 lo, hi, eax = cpuid_eax (CPUID_PROCESSOR_SIGNATURE);
    switch (eax & CPUID_XFAM) {
    case CPUID_XFAM_K8 :
        if ((eax & CPUID_XMOD) < CPUID_XMOD_REV_F)
            break;
    case CPUID_XFAM_10H :
    case CPUID_XFAM_11H :
        rdmsr (MSR_K8_ENABLE_C1E, lo, hi);
        if (lo & ENABLE_C1E_MASK)
            return 1;
        break;
    default :
        return 1;
    }
    return 0;
}

{
case CPUID_XFAM_K8 :
    if ((eax & CPUID_XMOD) < CPUID_XMOD_REV_F)
        break;
case CPUID_XFAM_10H :
case CPUID_XFAM_11H :
    rdmsr (MSR_K8_ENABLE_C1E, lo, hi);
    if (lo & ENABLE_C1E_MASK)
        return 1;
    break;
default :
    return 1;
}

{
    early_init_amd_mc (c);
    if (c->x86_power & (1 << 8))
        set_cpu_cap (c, X86_FEATURE_CONSTANT_TSC);
}

{
    unsigned level;
    clear_cpu_cap (c, 0 * 32 + 31);
    level = cpuid_eax (1);
    if (c->x86 == 15 && ((level >= 0x0f48 && level < 0x0f50) || level >= 0x0f58))
        set_cpu_cap (c, X86_FEATURE_REP_GOOD);
    if (c->x86 == 0x10 || c->x86 == 0x11)
        set_cpu_cap (c, X86_FEATURE_REP_GOOD);
    if (c->x86 >= 6)
        set_cpu_cap (c, X86_FEATURE_FXSAVE_LEAK);
    level = get_model_name (c);
    if (!level) {
        switch (c->x86) {
        case 15 :
            strcpy (c->x86_model_id, "Hammer");
            break;
        }
    }
    display_cacheinfo (c);
    if (c->extended_cpuid_level >= 0x80000008)
        amd_detect_cmp (c);
    if (c->extended_cpuid_level >= 0x80000006 && (cpuid_edx (0x80000006) & 0xf000))
        num_cache_leaves = 4;
    else
        num_cache_leaves = 3;
    if (c->x86 == 0xf || c->x86 == 0x10 || c->x86 == 0x11)
        set_cpu_cap (c, X86_FEATURE_K8);
    set_cpu_cap (c, X86_FEATURE_MFENCE_RDTSC);
    if (c->x86 == 0x10)
        fam10h_check_enable_mmcfg ();
    if (amd_apic_timer_broken ())
        disable_apic_timer = 1;
    if (c == &boot_cpu_data && c->x86 >= 0xf && c->x86 <= 0x11) {
        unsigned long long tseg;
        if (!rdmsrl_safe (MSR_K8_TSEG_ADDR, &tseg) && (tseg >> PMD_SHIFT) < (max_pfn_mapped >> (PMD_SHIFT - PAGE_SHIFT)))
            set_memory_4k ((unsigned long) __va (tseg), 1);
    }
}

{
    switch (c->x86) {
    case 15 :
        strcpy (c->x86_model_id, "Hammer");
        break;
    }
}

{
case 15 :
    strcpy (c->x86_model_id, "Hammer");
    break;
}

{
    unsigned long long tseg;
    if (!rdmsrl_safe (MSR_K8_TSEG_ADDR, &tseg) && (tseg >> PMD_SHIFT) < (max_pfn_mapped >> (PMD_SHIFT - PAGE_SHIFT)))
        set_memory_4k ((unsigned long) __va (tseg), 1);
}

{
    unsigned int eax, t;
    if (c->cpuid_level < 4)
        return 1;
    cpuid_count (4, 0, & eax, & t, & t, & t);
    if (eax & 0x1f)
        return ((eax >> 26) + 1);
    else
        return 1;
}

{
    if ((c->x86 == 0xf && c->x86_model >= 0x03) || (c->x86 == 0x6 && c->x86_model >= 0x0e))
        set_cpu_cap (c, X86_FEATURE_CONSTANT_TSC);
}

{
    unsigned n;
    init_intel_cacheinfo (c);
    if (c->cpuid_level > 9) {
        unsigned eax = cpuid_eax (10);
        if ((eax & 0xff) && (((eax >> 8) & 0xff) > 1))
            set_cpu_cap (c, X86_FEATURE_ARCH_PERFMON);
    }
    if (cpu_has_ds) {
        unsigned int l1, l2;
        rdmsr (MSR_IA32_MISC_ENABLE, l1, l2);
        if (!(l1 & (1 << 11)))
            set_cpu_cap (c, X86_FEATURE_BTS);
        if (!(l1 & (1 << 12)))
            set_cpu_cap (c, X86_FEATURE_PEBS);
    }
    if (cpu_has_bts)
        ds_init_intel (c);
    n = c->extended_cpuid_level;
    if (n >= 0x80000008) {
        unsigned eax = cpuid_eax (0x80000008);
        c->x86_virt_bits = (eax >> 8) & 0xff;
        c->x86_phys_bits = eax & 0xff;
        if (c->x86_vendor == X86_VENDOR_INTEL && c->x86 == 0xF && c->x86_model == 0x3 && c->x86_mask == 0x4)
            c->x86_phys_bits = 36;
    }
    if (c->x86 == 15)
        c->x86_cache_alignment = c->x86_clflush_size * 2;
    if (c->x86 == 6)
        set_cpu_cap (c, X86_FEATURE_REP_GOOD);
    set_cpu_cap (c, X86_FEATURE_LFENCE_RDTSC);
    c->x86_max_cores = intel_num_cpu_cores (c);
    srat_detect_node ();
}

{
    unsigned eax = cpuid_eax (10);
    if ((eax & 0xff) && (((eax >> 8) & 0xff) > 1))
        set_cpu_cap (c, X86_FEATURE_ARCH_PERFMON);
}

{
    unsigned int l1, l2;
    rdmsr (MSR_IA32_MISC_ENABLE, l1, l2);
    if (!(l1 & (1 << 11)))
        set_cpu_cap (c, X86_FEATURE_BTS);
    if (!(l1 & (1 << 12)))
        set_cpu_cap (c, X86_FEATURE_PEBS);
}

{
    unsigned eax = cpuid_eax (0x80000008);
    c->x86_virt_bits = (eax >> 8) & 0xff;
    c->x86_phys_bits = eax & 0xff;
    if (c->x86_vendor == X86_VENDOR_INTEL && c->x86 == 0xF && c->x86_model == 0x3 && c->x86_mask == 0x4)
        c->x86_phys_bits = 36;
}

{
    if (c->x86 == 0x6 && c->x86_model >= 0xf)
        set_cpu_cap (c, X86_FEATURE_CONSTANT_TSC);
}

{
    unsigned n;
    n = c->extended_cpuid_level;
    if (n >= 0x80000008) {
        unsigned eax = cpuid_eax (0x80000008);
        c->x86_virt_bits = (eax >> 8) & 0xff;
        c->x86_phys_bits = eax & 0xff;
    }
    if (c->x86 == 0x6 && c->x86_model >= 0xf) {
        c->x86_cache_alignment = c->x86_clflush_size * 2;
        set_cpu_cap (c, X86_FEATURE_CONSTANT_TSC);
        set_cpu_cap (c, X86_FEATURE_REP_GOOD);
    }
    set_cpu_cap (c, X86_FEATURE_LFENCE_RDTSC);
}

{
    unsigned eax = cpuid_eax (0x80000008);
    c->x86_virt_bits = (eax >> 8) & 0xff;
    c->x86_phys_bits = eax & 0xff;
}

{
    c->x86_cache_alignment = c->x86_clflush_size * 2;
    set_cpu_cap (c, X86_FEATURE_CONSTANT_TSC);
    set_cpu_cap (c, X86_FEATURE_REP_GOOD);
}

{
    char *v = c->x86_vendor_id;
    if (!strcmp (v, "AuthenticAMD"))
        c->x86_vendor = X86_VENDOR_AMD;
    else if (!strcmp (v, "GenuineIntel"))
        c->x86_vendor = X86_VENDOR_INTEL;
    else if (!strcmp (v, "CentaurHauls"))
        c->x86_vendor = X86_VENDOR_CENTAUR;
    else
        c->x86_vendor = X86_VENDOR_UNKNOWN;
}

{
    u32 tfms, xlvl;
    c->loops_per_jiffy = loops_per_jiffy;
    c->x86_cache_size = -1;
    c->x86_vendor = X86_VENDOR_UNKNOWN;
    c->x86_model = c->x86_mask = 0;
    c->x86_vendor_id[0] = '\0';
    c->x86_model_id[0] = '\0';
    c->x86_clflush_size = 64;
    c->x86_cache_alignment = c->x86_clflush_size;
    c->x86_max_cores = 1;
    c->x86_coreid_bits = 0;
    c->extended_cpuid_level = 0;
    memset (& c -> x86_capability, 0, sizeof c -> x86_capability);
    cpuid (0x00000000, (unsigned int *) & c -> cpuid_level, (unsigned int *) & c -> x86_vendor_id [0], (unsigned int *) & c -> x86_vendor_id [8], (unsigned int *) & c -> x86_vendor_id [4]);
    get_cpu_vendor (c);
    if (c->cpuid_level >= 0x00000001) {
        __u32 misc;
        cpuid (0x00000001, & tfms, & misc, & c -> x86_capability [4], & c -> x86_capability [0]);
        c->x86 = (tfms >> 8) & 0xf;
        c->x86_model = (tfms >> 4) & 0xf;
        c->x86_mask = tfms & 0xf;
        if (c->x86 == 0xf)
            c->x86 += (tfms >> 20) & 0xff;
        if (c->x86 >= 0x6)
            c->x86_model += ((tfms >> 16) & 0xF) << 4;
        if (test_cpu_cap (c, X86_FEATURE_CLFLSH))
            c->x86_clflush_size = ((misc >> 8) & 0xff) * 8;
    }
    else {
        c->x86 = 4;
    }
    c->initial_apicid = (cpuid_ebx (1) >> 24) & 0xff;
    xlvl = cpuid_eax (0x80000000);
    c->extended_cpuid_level = xlvl;
    if ((xlvl & 0xffff0000) == 0x80000000) {
        if (xlvl >= 0x80000001) {
            c->x86_capability[1] = cpuid_edx (0x80000001);
            c->x86_capability[6] = cpuid_ecx (0x80000001);
        }
        if (xlvl >= 0x80000004)
            get_model_name (c);
    }
    xlvl = cpuid_eax (0x80860000);
    if ((xlvl & 0xffff0000) == 0x80860000) {
        if (xlvl >= 0x80860001)
            c->x86_capability[2] = cpuid_edx (0x80860001);
    }
    c->extended_cpuid_level = cpuid_eax (0x80000000);
    if (c->extended_cpuid_level >= 0x80000007)
        c->x86_power = cpuid_edx (0x80000007);
    switch (c->x86_vendor) {
    case X86_VENDOR_AMD :
        early_init_amd (c);
        break;
    case X86_VENDOR_INTEL :
        early_init_intel (c);
        break;
    case X86_VENDOR_CENTAUR :
        early_init_centaur (c);
        break;
    }
    validate_pat_support (c);
}

{
    __u32 misc;
    cpuid (0x00000001, & tfms, & misc, & c -> x86_capability [4], & c -> x86_capability [0]);
    c->x86 = (tfms >> 8) & 0xf;
    c->x86_model = (tfms >> 4) & 0xf;
    c->x86_mask = tfms & 0xf;
    if (c->x86 == 0xf)
        c->x86 += (tfms >> 20) & 0xff;
    if (c->x86 >= 0x6)
        c->x86_model += ((tfms >> 16) & 0xF) << 4;
    if (test_cpu_cap (c, X86_FEATURE_CLFLSH))
        c->x86_clflush_size = ((misc >> 8) & 0xff) * 8;
}

{
    if (xlvl >= 0x80000001) {
        c->x86_capability[1] = cpuid_edx (0x80000001);
        c->x86_capability[6] = cpuid_ecx (0x80000001);
    }
    if (xlvl >= 0x80000004)
        get_model_name (c);
}

{
    c->x86_capability[1] = cpuid_edx (0x80000001);
    c->x86_capability[6] = cpuid_ecx (0x80000001);
}

{
    if (xlvl >= 0x80860001)
        c->x86_capability[2] = cpuid_edx (0x80860001);
}

{
case X86_VENDOR_AMD :
    early_init_amd (c);
    break;
case X86_VENDOR_INTEL :
    early_init_intel (c);
    break;
case X86_VENDOR_CENTAUR :
    early_init_centaur (c);
    break;
}

{
    int i;
    early_identify_cpu (c);
    init_scattered_cpuid_features (c);
    c->apicid = phys_pkg_id (0);
    switch (c->x86_vendor) {
    case X86_VENDOR_AMD :
        init_amd (c);
        break;
    case X86_VENDOR_INTEL :
        init_intel (c);
        break;
    case X86_VENDOR_CENTAUR :
        init_centaur (c);
        break;
    case X86_VENDOR_UNKNOWN :
    default :
        display_cacheinfo (c);
        break;
    }
    detect_ht (c);
    if (c != &boot_cpu_data) {
        for (i = 0; i < NCAPINTS; i++)
            boot_cpu_data.x86_capability[i] &= c->x86_capability[i];
    }
    for (i = 0; i < NCAPINTS; i++)
        c->x86_capability[i] &= ~cleared_cpu_caps[i];
    select_idle_routine (c);
}

{
case X86_VENDOR_AMD :
    init_amd (c);
    break;
case X86_VENDOR_INTEL :
    init_intel (c);
    break;
case X86_VENDOR_CENTAUR :
    init_centaur (c);
    break;
case X86_VENDOR_UNKNOWN :
default :
    display_cacheinfo (c);
    break;
}

{
    for (i = 0; i < NCAPINTS; i++)
        boot_cpu_data.x86_capability[i] &= c->x86_capability[i];
}

{
    identify_cpu (& boot_cpu_data);
}

{
    BUG_ON (c == & boot_cpu_data);
    identify_cpu (c);
    mtrr_ap_init ();
}

{
    setup_clear_cpu_cap (X86_FEATURE_CLFLSH);
    return 1;
}

{
    if (c->x86_model_id[0])
        printk (KERN_CONT "%s", c->x86_model_id);
    if (c->x86_mask || c->cpuid_level >= 0)
        printk (KERN_CONT " stepping %02x\n", c->x86_mask);
    else
        printk (KERN_CONT "\n");
}

{
    int bit;
    if (get_option (&arg, &bit) && bit < NCAPINTS * 32)
        setup_clear_cpu_cap (bit);
    else
        return 0;
    return 1;
}

{
    char c;
    int i, k, j;
    while ((c = *str++) != '\0' && n-- > 0) {
        if (current_ypos >= max_ypos) {
            for (k = 1, j = 0; k < max_ypos; k++, j++) {
                for (i = 0; i < max_xpos; i++) {
                    writew (readw (VGABASE + 2 * (max_xpos * k + i)), VGABASE + 2 * (max_xpos * j + i));
                }
            }
            for (i = 0; i < max_xpos; i++)
                writew (0x720, VGABASE +2 * (max_xpos * j + i));
            current_ypos = max_ypos - 1;
        }
        if (c == '\n') {
            current_xpos = 0;
            current_ypos++;
        }
        else if (c != '\r') {
            writew (((0x7 << 8) | (unsigned short) c), VGABASE + 2 * (max_xpos * current_ypos + current_xpos ++));
            if (current_xpos >= max_xpos) {
                current_xpos = 0;
                current_ypos++;
            }
        }
    }
}

{
    if (current_ypos >= max_ypos) {
        for (k = 1, j = 0; k < max_ypos; k++, j++) {
            for (i = 0; i < max_xpos; i++) {
                writew (readw (VGABASE + 2 * (max_xpos * k + i)), VGABASE + 2 * (max_xpos * j + i));
            }
        }
        for (i = 0; i < max_xpos; i++)
            writew (0x720, VGABASE +2 * (max_xpos * j + i));
        current_ypos = max_ypos - 1;
    }
    if (c == '\n') {
        current_xpos = 0;
        current_ypos++;
    }
    else if (c != '\r') {
        writew (((0x7 << 8) | (unsigned short) c), VGABASE + 2 * (max_xpos * current_ypos + current_xpos ++));
        if (current_xpos >= max_xpos) {
            current_xpos = 0;
            current_ypos++;
        }
    }
}

{
    for (k = 1, j = 0; k < max_ypos; k++, j++) {
        for (i = 0; i < max_xpos; i++) {
            writew (readw (VGABASE + 2 * (max_xpos * k + i)), VGABASE + 2 * (max_xpos * j + i));
        }
    }
    for (i = 0; i < max_xpos; i++)
        writew (0x720, VGABASE +2 * (max_xpos * j + i));
    current_ypos = max_ypos - 1;
}

{
    for (i = 0; i < max_xpos; i++) {
        writew (readw (VGABASE + 2 * (max_xpos * k + i)), VGABASE + 2 * (max_xpos * j + i));
    }
}

{
    writew (readw (VGABASE + 2 * (max_xpos * k + i)), VGABASE + 2 * (max_xpos * j + i));
}

{
    current_xpos = 0;
    current_ypos++;
}

{
    writew (((0x7 << 8) | (unsigned short) c), VGABASE + 2 * (max_xpos * current_ypos + current_xpos ++));
    if (current_xpos >= max_xpos) {
        current_xpos = 0;
        current_ypos++;
    }
}

{
    current_xpos = 0;
    current_ypos++;
}

{
    unsigned timeout = 0xffff;
    while ((inb (early_serial_base +LSR) & XMTRDY) == 0 && --timeout)
        cpu_relax ();
    outb (ch, early_serial_base + TXR);
    return timeout ? 0 : -1;
}

{
    while (*s && n-- > 0) {
        if (*s == '\n')
            early_serial_putc ('\r');
        early_serial_putc (*s);
        s++;
    }
}

{
    if (*s == '\n')
        early_serial_putc ('\r');
    early_serial_putc (*s);
    s++;
}

{
    unsigned char c;
    unsigned divisor;
    unsigned baud = DEFAULT_BAUD;
    char *e;
    if (*s == ',')
        ++s;
    if (*s) {
        unsigned port;
        if (!strncmp (s, "0x", 2)) {
            early_serial_base = simple_strtoul (s, &e, 16);
        }
        else {
            static int bases [] = {0x3f8, 0x2f8};
            if (!strncmp (s, "ttyS", 4))
                s += 4;
            port = simple_strtoul (s, &e, 10);
            if (port > 1 || s == e)
                port = 0;
            early_serial_base = bases[port];
        }
        s += strcspn (s, ",");
        if (*s == ',')
            s++;
    }
    outb (0x3, early_serial_base + LCR);
    outb (0, early_serial_base + IER);
    outb (0, early_serial_base + FCR);
    outb (0x3, early_serial_base + MCR);
    if (*s) {
        baud = simple_strtoul (s, &e, 0);
        if (baud == 0 || s == e)
            baud = DEFAULT_BAUD;
    }
    divisor = 115200 / baud;
    c = inb (early_serial_base +LCR);
    outb (c | DLAB, early_serial_base + LCR);
    outb (divisor & 0xff, early_serial_base + DLL);
    outb ((divisor >> 8) & 0xff, early_serial_base + DLH);
    outb (c & ~ DLAB, early_serial_base + LCR);
}

{
    unsigned port;
    if (!strncmp (s, "0x", 2)) {
        early_serial_base = simple_strtoul (s, &e, 16);
    }
    else {
        static int bases [] = {0x3f8, 0x2f8};
        if (!strncmp (s, "ttyS", 4))
            s += 4;
        port = simple_strtoul (s, &e, 10);
        if (port > 1 || s == e)
            port = 0;
        early_serial_base = bases[port];
    }
    s += strcspn (s, ",");
    if (*s == ',')
        s++;
}

{
    early_serial_base = simple_strtoul (s, &e, 16);
}

{
    static int bases [] = {0x3f8, 0x2f8};
    if (!strncmp (s, "ttyS", 4))
        s += 4;
    port = simple_strtoul (s, &e, 10);
    if (port > 1 || s == e)
        port = 0;
    early_serial_base = bases[port];
}

{
    baud = simple_strtoul (s, &e, 0);
    if (baud == 0 || s == e)
        baud = DEFAULT_BAUD;
}

{
    long ret;
    asm volatile ("cpuid"
        : "=a" (ret)
        : "b" (a), "c" (b), "d" (c), "0" (MAGIC1), "D" (cmd + MAGIC2)
    ) return ret;
}

{
    char *fn = "klog";
    if (*str == '=')
        fn = ++str;
    simnow_fd = simnow (XOPEN, (unsigned long) fn, O_WRONLY | O_APPEND | O_CREAT, 0644);
}

{
    simnow (XWRITE, simnow_fd, (unsigned long) s, n);
}

{
    char buf [512];
    int n;
    va_list ap;
    va_start (ap, fmt);
    n = vscnprintf (buf, 512, fmt, ap);
    early_console->write (early_console, buf, n);
    va_end (ap);
}

{
    if (!buf)
        return 0;
    if (early_console_initialized)
        return 0;
    early_console_initialized = 1;
    if (strstr (buf, "keep"))
        keep_early = 1;
    if (!strncmp (buf, "serial", 6)) {
        early_serial_init (buf + 6);
        early_console = &early_serial_console;
    }
    else if (!strncmp (buf, "ttyS", 4)) {
        early_serial_init (buf);
        early_console = &early_serial_console;
    }
    else if (!strncmp (buf, "vga", 3) && boot_params.screen_info.orig_video_isVGA == 1) {
        max_xpos = boot_params.screen_info.orig_video_cols;
        max_ypos = boot_params.screen_info.orig_video_lines;
        current_ypos = boot_params.screen_info.orig_y;
        early_console = &early_vga_console;
    }
    else if (!strncmp (buf, "simnow", 6)) {
        simnow_init (buf + 6);
        early_console = &simnow_console;
        keep_early = 1;
    }
    if (keep_early)
        early_console->flags &= ~CON_BOOT;
    else
        early_console->flags |= CON_BOOT;
    register_console (early_console);
    return 0;
}

{
    early_serial_init (buf + 6);
    early_console = &early_serial_console;
}

{
    early_serial_init (buf);
    early_console = &early_serial_console;
}

{
    max_xpos = boot_params.screen_info.orig_video_cols;
    max_ypos = boot_params.screen_info.orig_video_lines;
    current_ypos = boot_params.screen_info.orig_y;
    early_console = &early_vga_console;
}

{
    simnow_init (buf + 6);
    early_console = &simnow_console;
    keep_early = 1;
}

{
    unsigned long addr, seg;
    addr = regs->ip;
    seg = regs->cs & 0xffff;
    if (v8086_mode (regs)) {
        addr = (addr & 0xffff) + (seg << 4);
        return addr;
    }
    if ((seg & SEGMENT_TI_MASK) == SEGMENT_LDT) {
        u32 *desc;
        unsigned long base;
        seg &= ~7UL;
        mutex_lock (& child -> mm -> context.lock);
        if (unlikely ((seg >> 3) >= child->mm->context.size))
            addr = -1L;
        else {
            desc = child->mm->context.ldt + seg;
            base = ((desc[0] >> 16) | ((desc[1] & 0xff) << 16) | (desc[1] & 0xff000000));
            if (!((desc[1] >> 22) & 1))
                addr &= 0xffff;
            addr += base;
        }
        mutex_unlock (& child -> mm -> context.lock);
    }
    return addr;
}

{
    addr = (addr & 0xffff) + (seg << 4);
    return addr;
}

{
    u32 *desc;
    unsigned long base;
    seg &= ~7UL;
    mutex_lock (& child -> mm -> context.lock);
    if (unlikely ((seg >> 3) >= child->mm->context.size))
        addr = -1L;
    else {
        desc = child->mm->context.ldt + seg;
        base = ((desc[0] >> 16) | ((desc[1] & 0xff) << 16) | (desc[1] & 0xff000000));
        if (!((desc[1] >> 22) & 1))
            addr &= 0xffff;
        addr += base;
    }
    mutex_unlock (& child -> mm -> context.lock);
}

{
    desc = child->mm->context.ldt + seg;
    base = ((desc[0] >> 16) | ((desc[1] & 0xff) << 16) | (desc[1] & 0xff000000));
    if (!((desc[1] >> 22) & 1))
        addr &= 0xffff;
    addr += base;
}

{
    int i, copied;
    unsigned char opcode [15];
    unsigned long addr = convert_ip_to_linear (child, regs);
    copied = access_process_vm (child, addr, opcode, sizeof (opcode), 0);
    for (i = 0; i < copied; i++) {
        switch (opcode[i]) {
        case 0x9d :
        case 0xcf :
            return 1;
        case 0x66 :
        case 0x67 :
            continue;
        case 0x26 :
        case 0x2e :
        case 0x36 :
        case 0x3e :
        case 0x64 :
        case 0x65 :
        case 0xf0 :
        case 0xf2 :
        case 0xf3 :
            continue;
        case 0x9c :
        default :
            return 0;
        }
    }
    return 0;
}

{
    switch (opcode[i]) {
    case 0x9d :
    case 0xcf :
        return 1;
    case 0x66 :
    case 0x67 :
        continue;
    case 0x26 :
    case 0x2e :
    case 0x36 :
    case 0x3e :
    case 0x64 :
    case 0x65 :
    case 0xf0 :
    case 0xf2 :
    case 0xf3 :
        continue;
    case 0x9c :
    default :
        return 0;
    }
}

{
    struct pt_regs *regs = task_pt_regs (child);
    set_tsk_thread_flag (child, TIF_SINGLESTEP);
    if (regs->flags & X86_EFLAGS_TF)
        return 0;
    regs->flags |= X86_EFLAGS_TF;
    if (is_setting_trap_flag (child, regs))
        return 0;
    set_tsk_thread_flag (child, TIF_FORCED_TF);
    return 1;
}

{
    if (child->thread.debugctlmsr == val)
        return;
    child->thread.debugctlmsr = val;
    if (child != current)
        return;
    update_debugctlmsr (val);
}

{
    if (enable_single_step (child) && block) {
        set_tsk_thread_flag (child, TIF_DEBUGCTLMSR);
        write_debugctlmsr (child, child -> thread.debugctlmsr | DEBUGCTLMSR_BTF);
    }
    else {
        write_debugctlmsr (child, child -> thread.debugctlmsr & ~ DEBUGCTLMSR_BTF);
        if (!child->thread.debugctlmsr)
            clear_tsk_thread_flag (child, TIF_DEBUGCTLMSR);
    }
}

{
    set_tsk_thread_flag (child, TIF_DEBUGCTLMSR);
    write_debugctlmsr (child, child -> thread.debugctlmsr | DEBUGCTLMSR_BTF);
}

{
    write_debugctlmsr (child, child -> thread.debugctlmsr & ~ DEBUGCTLMSR_BTF);
    if (!child->thread.debugctlmsr)
        clear_tsk_thread_flag (child, TIF_DEBUGCTLMSR);
}

{
    enable_step (child, 0);
}

{
    enable_step (child, 1);
}

{
    write_debugctlmsr (child, child -> thread.debugctlmsr & ~ DEBUGCTLMSR_BTF);
    if (!child->thread.debugctlmsr)
        clear_tsk_thread_flag (child, TIF_DEBUGCTLMSR);
    clear_tsk_thread_flag (child, TIF_SINGLESTEP);
    if (test_and_clear_tsk_thread_flag (child, TIF_FORCED_TF))
        task_pt_regs (child)->flags &= ~X86_EFLAGS_TF;
}

{
    return (void __iomem *) __fix_to_virt (FIX_IO_APIC_BASE_0 +idx) + (mp_ioapics[idx].mpc_apicaddr & ~PAGE_MASK);
}

{
    struct io_apic __iomem *io_apic = io_apic_base (apic);
    writel (reg, & io_apic -> index);
    return readl (&io_apic->data);
}

{
    struct io_apic __iomem *io_apic = io_apic_base (apic);
    writel (reg, & io_apic -> index);
    writel (value, & io_apic -> data);
}

{
    struct io_apic __iomem *io_apic = io_apic_base (apic);
    writel (value, & io_apic -> data);
}

{
    struct irq_pin_list *entry;
    unsigned long flags;
    spin_lock_irqsave (& ioapic_lock, flags);
    entry = irq_2_pin + irq;
    for (;;) {
        unsigned int reg;
        int pin;
        pin = entry->pin;
        if (pin == -1)
            break;
        reg = io_apic_read (entry->apic, 0x10 + pin * 2);
        if ((reg >> 14) & 1) {
            spin_unlock_irqrestore (& ioapic_lock, flags);
            return true;
        }
        if (!entry->next)
            break;
        entry = irq_2_pin + entry->next;
    }
    spin_unlock_irqrestore (& ioapic_lock, flags);
    return false;
}

{
    unsigned int reg;
    int pin;
    pin = entry->pin;
    if (pin == -1)
        break;
    reg = io_apic_read (entry->apic, 0x10 + pin * 2);
    if ((reg >> 14) & 1) {
        spin_unlock_irqrestore (& ioapic_lock, flags);
        return true;
    }
    if (!entry->next)
        break;
    entry = irq_2_pin + entry->next;
}

{
    spin_unlock_irqrestore (& ioapic_lock, flags);
    return true;
}

{
    struct io_apic __iomem *io_apic = io_apic_base (apic);
    readl (& io_apic -> data);
}

{
    union entry_union eu;
    unsigned long flags;
    spin_lock_irqsave (& ioapic_lock, flags);
    eu.w1 = io_apic_read (apic, 0x10 + 2 * pin);
    eu.w2 = io_apic_read (apic, 0x11 + 2 * pin);
    spin_unlock_irqrestore (& ioapic_lock, flags);
    return eu.entry;
}

{
    union entry_union eu;
    eu.entry = e;
    io_apic_write (apic, 0x11 + 2 * pin, eu.w2);
    io_apic_write (apic, 0x10 + 2 * pin, eu.w1);
}

{
    unsigned long flags;
    spin_lock_irqsave (& ioapic_lock, flags);
    __ioapic_write_entry (apic, pin, e);
    spin_unlock_irqrestore (& ioapic_lock, flags);
}

{
    unsigned long flags;
    union entry_union eu = {
        .entry.mask = 1
    };
    spin_lock_irqsave (& ioapic_lock, flags);
    io_apic_write (apic, 0x10 + 2 * pin, eu.w1);
    io_apic_write (apic, 0x11 + 2 * pin, eu.w2);
    spin_unlock_irqrestore (& ioapic_lock, flags);
}

{
    static int first_free_entry = NR_IRQS;
    struct irq_pin_list *entry = irq_2_pin + irq;
    BUG_ON (irq >= NR_IRQS);
    while (entry->next)
        entry = irq_2_pin + entry->next;
    if (entry->pin != -1) {
        entry->next = first_free_entry;
        entry = irq_2_pin + entry->next;
        if (++first_free_entry >= PIN_MAP_SIZE)
            panic ("io_apic.c: ran out of irq_2_pin entries!");
    }
    entry->apic = apic;
    entry->pin = pin;
}

{
    entry->next = first_free_entry;
    entry = irq_2_pin + entry->next;
    if (++first_free_entry >= PIN_MAP_SIZE)
        panic ("io_apic.c: ran out of irq_2_pin entries!");
}

{
    unsigned long flags;
    spin_lock_irqsave (& ioapic_lock, flags);
    __mask_IO_APIC_irq (irq);
    spin_unlock_irqrestore (& ioapic_lock, flags);
}

{
    unsigned long flags;
    spin_lock_irqsave (& ioapic_lock, flags);
    __unmask_IO_APIC_irq (irq);
    spin_unlock_irqrestore (& ioapic_lock, flags);
}

{
    struct IO_APIC_route_entry entry;
    entry = ioapic_read_entry (apic, pin);
    if (entry.delivery_mode == dest_SMI)
        return;
    ioapic_mask_entry (apic, pin);
}

{
    int apic, pin;
    for (apic = 0; apic < nr_ioapics; apic++)
        for (pin = 0; pin < nr_ioapic_registers[apic]; pin++)
            clear_IO_APIC_pin (apic, pin);
}

{
    disable_ioapic_setup ();
    return 0;
}

{
    disable_timer_pin_1 = 1;
    return 1;
}

{
    timer_over_8254 = -1;
    return 1;
}

{
    timer_over_8254 = 2;
    return 1;
}

{
    int i;
    for (i = 0; i < mp_irq_entries; i++)
        if (mp_irqs[i].mpc_irqtype == type && (mp_irqs[i].mpc_dstapic == mp_ioapics[apic].mpc_apicid || mp_irqs[i].mpc_dstapic == MP_APIC_ALL) && mp_irqs[i].mpc_dstirq == pin)
            return i;
    return -1;
}

{
    int i;
    for (i = 0; i < mp_irq_entries; i++) {
        int lbus = mp_irqs[i].mpc_srcbus;
        if (test_bit (lbus, mp_bus_not_pci) && (mp_irqs[i].mpc_irqtype == type) && (mp_irqs[i].mpc_srcbusirq == irq))
            return mp_irqs[i].mpc_dstirq;
    }
    return -1;
}

{
    int lbus = mp_irqs[i].mpc_srcbus;
    if (test_bit (lbus, mp_bus_not_pci) && (mp_irqs[i].mpc_irqtype == type) && (mp_irqs[i].mpc_srcbusirq == irq))
        return mp_irqs[i].mpc_dstirq;
}

{
    int i;
    for (i = 0; i < mp_irq_entries; i++) {
        int lbus = mp_irqs[i].mpc_srcbus;
        if (test_bit (lbus, mp_bus_not_pci) && (mp_irqs[i].mpc_irqtype == type) && (mp_irqs[i].mpc_srcbusirq == irq))
            break;
    }
    if (i < mp_irq_entries) {
        int apic;
        for (apic = 0; apic < nr_ioapics; apic++) {
            if (mp_ioapics[apic].mpc_apicid == mp_irqs[i].mpc_dstapic)
                return apic;
        }
    }
    return -1;
}

{
    int lbus = mp_irqs[i].mpc_srcbus;
    if (test_bit (lbus, mp_bus_not_pci) && (mp_irqs[i].mpc_irqtype == type) && (mp_irqs[i].mpc_srcbusirq == irq))
        break;
}

{
    int apic;
    for (apic = 0; apic < nr_ioapics; apic++) {
        if (mp_ioapics[apic].mpc_apicid == mp_irqs[i].mpc_dstapic)
            return apic;
    }
}

{
    if (mp_ioapics[apic].mpc_apicid == mp_irqs[i].mpc_dstapic)
        return apic;
}

{
    int apic, i, best_guess = -1;
    apic_printk (APIC_DEBUG, "querying PCI -> IRQ mapping bus:%d, slot:%d, pin:%d.\n", bus, slot, pin);
    if (mp_bus_id_to_pci_bus[bus] == -1) {
        apic_printk (APIC_VERBOSE, "PCI BIOS passed nonexistent PCI bus %d!\n", bus);
        return -1;
    }
    for (i = 0; i < mp_irq_entries; i++) {
        int lbus = mp_irqs[i].mpc_srcbus;
        for (apic = 0; apic < nr_ioapics; apic++)
            if (mp_ioapics[apic].mpc_apicid == mp_irqs[i].mpc_dstapic || mp_irqs[i].mpc_dstapic == MP_APIC_ALL)
                break;
        if (!test_bit (lbus, mp_bus_not_pci) && !mp_irqs[i].mpc_irqtype && (bus == lbus) && (slot == ((mp_irqs[i].mpc_srcbusirq >> 2) & 0x1f))) {
            int irq = pin_2_irq (i, apic, mp_irqs[i].mpc_dstirq);
            if (!(apic || IO_APIC_IRQ (irq)))
                continue;
            if (pin == (mp_irqs[i].mpc_srcbusirq & 3))
                return irq;
            if (best_guess < 0)
                best_guess = irq;
        }
    }
    BUG_ON (best_guess >= NR_IRQS);
    return best_guess;
}

{
    apic_printk (APIC_VERBOSE, "PCI BIOS passed nonexistent PCI bus %d!\n", bus);
    return -1;
}

{
    int lbus = mp_irqs[i].mpc_srcbus;
    for (apic = 0; apic < nr_ioapics; apic++)
        if (mp_ioapics[apic].mpc_apicid == mp_irqs[i].mpc_dstapic || mp_irqs[i].mpc_dstapic == MP_APIC_ALL)
            break;
    if (!test_bit (lbus, mp_bus_not_pci) && !mp_irqs[i].mpc_irqtype && (bus == lbus) && (slot == ((mp_irqs[i].mpc_srcbusirq >> 2) & 0x1f))) {
        int irq = pin_2_irq (i, apic, mp_irqs[i].mpc_dstirq);
        if (!(apic || IO_APIC_IRQ (irq)))
            continue;
        if (pin == (mp_irqs[i].mpc_srcbusirq & 3))
            return irq;
        if (best_guess < 0)
            best_guess = irq;
    }
}

{
    int irq = pin_2_irq (i, apic, mp_irqs[i].mpc_dstirq);
    if (!(apic || IO_APIC_IRQ (irq)))
        continue;
    if (pin == (mp_irqs[i].mpc_srcbusirq & 3))
        return irq;
    if (best_guess < 0)
        best_guess = irq;
}

{
    int bus = mp_irqs[idx].mpc_srcbus;
    int polarity;
    switch (mp_irqs[idx].mpc_irqflag & 3) {
    case 0 :
        if (test_bit (bus, mp_bus_not_pci))
            polarity = default_ISA_polarity (idx);
        else
            polarity = default_PCI_polarity (idx);
        break;
    case 1 :
        {
            polarity = 0;
            break;
        }
    case 2 :
        {
            printk (KERN_WARNING "broken BIOS!!\n");
            polarity = 1;
            break;
        }
    case 3 :
        {
            polarity = 1;
            break;
        }
    default :
        {
            printk (KERN_WARNING "broken BIOS!!\n");
            polarity = 1;
            break;
        }
    }
    return polarity;
}

{
case 0 :
    if (test_bit (bus, mp_bus_not_pci))
        polarity = default_ISA_polarity (idx);
    else
        polarity = default_PCI_polarity (idx);
    break;
case 1 :
    {
        polarity = 0;
        break;
    }
case 2 :
    {
        printk (KERN_WARNING "broken BIOS!!\n");
        polarity = 1;
        break;
    }
case 3 :
    {
        polarity = 1;
        break;
    }
default :
    {
        printk (KERN_WARNING "broken BIOS!!\n");
        polarity = 1;
        break;
    }
}

{
    polarity = 0;
    break;
}

{
    printk (KERN_WARNING "broken BIOS!!\n");
    polarity = 1;
    break;
}

{
    polarity = 1;
    break;
}

{
    printk (KERN_WARNING "broken BIOS!!\n");
    polarity = 1;
    break;
}

{
    int bus = mp_irqs[idx].mpc_srcbus;
    int trigger;
    switch ((mp_irqs[idx].mpc_irqflag >> 2) & 3) {
    case 0 :
        if (test_bit (bus, mp_bus_not_pci))
            trigger = default_ISA_trigger (idx);
        else
            trigger = default_PCI_trigger (idx);
        break;
    case 1 :
        {
            trigger = 0;
            break;
        }
    case 2 :
        {
            printk (KERN_WARNING "broken BIOS!!\n");
            trigger = 1;
            break;
        }
    case 3 :
        {
            trigger = 1;
            break;
        }
    default :
        {
            printk (KERN_WARNING "broken BIOS!!\n");
            trigger = 0;
            break;
        }
    }
    return trigger;
}

{
case 0 :
    if (test_bit (bus, mp_bus_not_pci))
        trigger = default_ISA_trigger (idx);
    else
        trigger = default_PCI_trigger (idx);
    break;
case 1 :
    {
        trigger = 0;
        break;
    }
case 2 :
    {
        printk (KERN_WARNING "broken BIOS!!\n");
        trigger = 1;
        break;
    }
case 3 :
    {
        trigger = 1;
        break;
    }
default :
    {
        printk (KERN_WARNING "broken BIOS!!\n");
        trigger = 0;
        break;
    }
}

{
    trigger = 0;
    break;
}

{
    printk (KERN_WARNING "broken BIOS!!\n");
    trigger = 1;
    break;
}

{
    trigger = 1;
    break;
}

{
    printk (KERN_WARNING "broken BIOS!!\n");
    trigger = 0;
    break;
}

{
    return MPBIOS_polarity (idx);
}

{
    return MPBIOS_trigger (idx);
}

{
    int irq, i;
    int bus = mp_irqs[idx].mpc_srcbus;
    if (mp_irqs[idx].mpc_dstirq != pin)
        printk (KERN_ERR "broken BIOS or MPTABLE parser, ayiee!!\n");
    if (test_bit (bus, mp_bus_not_pci)) {
        irq = mp_irqs[idx].mpc_srcbusirq;
    }
    else {
        i = irq = 0;
        while (i < apic)
            irq += nr_ioapic_registers[i++];
        irq += pin;
    }
    BUG_ON (irq >= NR_IRQS);
    return irq;
}

{
    irq = mp_irqs[idx].mpc_srcbusirq;
}

{
    i = irq = 0;
    while (i < apic)
        irq += nr_ioapic_registers[i++];
    irq += pin;
}

{
    static int current_vector = FIRST_DEVICE_VECTOR, current_offset = 0;
    unsigned int old_vector;
    int cpu;
    struct irq_cfg *cfg;
    BUG_ON ((unsigned) irq >= NR_IRQS);
    cfg = &irq_cfg[irq];
    cpus_and (mask, mask, cpu_online_map);
    if ((cfg->move_in_progress) || cfg->move_cleanup_count)
        return -EBUSY;
    old_vector = cfg->vector;
    if (old_vector) {
        cpumask_t tmp;
        cpus_and (tmp, cfg -> domain, mask);
        if (!cpus_empty (tmp))
            return 0;
    }

    for_each_cpu_mask (cpu, mask) {
        cpumask_t domain, new_mask;
        int new_cpu;
        int vector, offset;
        domain = vector_allocation_domain (cpu);
        cpus_and (new_mask, domain, cpu_online_map);
        vector = current_vector;
        offset = current_offset;
    next :
        vector += 8;
        if (vector >= FIRST_SYSTEM_VECTOR) {
            offset = (offset + 1) % 8;
            vector = FIRST_DEVICE_VECTOR + offset;
        }
        if (unlikely (current_vector == vector))
            continue;
        if (vector == IA32_SYSCALL_VECTOR)
            goto next;
        for_each_cpu_mask (new_cpu, new_mask)
        if (per_cpu (vector_irq, new_cpu)[vector] != -1)
            goto next;
        current_vector = vector;
        current_offset = offset;
        if (old_vector) {
            cfg->move_in_progress = 1;
            cfg->old_domain = cfg->domain;
        }
        for_each_cpu_mask (new_cpu, new_mask)
        per_cpu (vector_irq, new_cpu) [vector] = irq;
        cfg->vector = vector;
        cfg->domain = domain;
        return 0;
    }

    return -ENOSPC;
}

{
    cpumask_t tmp;
    cpus_and (tmp, cfg -> domain, mask);
    if (!cpus_empty (tmp))
        return 0;
}

{
    cpumask_t domain, new_mask;
    int new_cpu;
    int vector, offset;
    domain = vector_allocation_domain (cpu);
    cpus_and (new_mask, domain, cpu_online_map);
    vector = current_vector;
    offset = current_offset;
next :
    vector += 8;
    if (vector >= FIRST_SYSTEM_VECTOR) {
        offset = (offset + 1) % 8;
        vector = FIRST_DEVICE_VECTOR + offset;
    }
    if (unlikely (current_vector == vector))
        continue;
    if (vector == IA32_SYSCALL_VECTOR)
        goto next;
    for_each_cpu_mask (new_cpu, new_mask)
    if (per_cpu (vector_irq, new_cpu)[vector] != -1)
        goto next;
    current_vector = vector;
    current_offset = offset;
    if (old_vector) {
        cfg->move_in_progress = 1;
        cfg->old_domain = cfg->domain;
    }
    for_each_cpu_mask (new_cpu, new_mask)
    per_cpu (vector_irq, new_cpu) [vector] = irq;
    cfg->vector = vector;
    cfg->domain = domain;
    return 0;
}

{
    offset = (offset + 1) % 8;
    vector = FIRST_DEVICE_VECTOR + offset;
}

{
    cfg->move_in_progress = 1;
    cfg->old_domain = cfg->domain;
}

{
    int err;
    unsigned long flags;
    spin_lock_irqsave (& vector_lock, flags);
    err = __assign_irq_vector (irq, mask);
    spin_unlock_irqrestore (& vector_lock, flags);
    return err;
}

{
    struct irq_cfg *cfg;
    cpumask_t mask;
    int cpu, vector;
    BUG_ON ((unsigned) irq >= NR_IRQS);
    cfg = &irq_cfg[irq];
    BUG_ON (! cfg -> vector);
    vector = cfg->vector;
    cpus_and (mask, cfg -> domain, cpu_online_map);
    for_each_cpu_mask (cpu, mask)
    per_cpu (vector_irq, cpu) [vector] = -1;
    cfg->vector = 0;
    cpus_clear (cfg -> domain);
}

{
    int irq, vector;
    for (irq = 0; irq < NR_IRQS; ++irq) {
        if (!cpu_isset (cpu, irq_cfg[irq].domain))
            continue;
        vector = irq_cfg[irq].vector;
        per_cpu (vector_irq, cpu)[vector] = irq;
    }
    for (vector = 0; vector < NR_VECTORS; ++vector) {
        irq = per_cpu (vector_irq, cpu)[vector];
        if (irq < 0)
            continue;
        if (!cpu_isset (cpu, irq_cfg[irq].domain))
            per_cpu (vector_irq, cpu)[vector] = -1;
    }
}

{
    if (!cpu_isset (cpu, irq_cfg[irq].domain))
        continue;
    vector = irq_cfg[irq].vector;
    per_cpu (vector_irq, cpu)[vector] = irq;
}

{
    irq = per_cpu (vector_irq, cpu)[vector];
    if (irq < 0)
        continue;
    if (!cpu_isset (cpu, irq_cfg[irq].domain))
        per_cpu (vector_irq, cpu)[vector] = -1;
}

{
    if (trigger) {
        irq_desc[irq].status |= IRQ_LEVEL;
        set_irq_chip_and_handler_name (irq, & ioapic_chip, handle_fasteoi_irq, "fasteoi");
    }
    else {
        irq_desc[irq].status &= ~IRQ_LEVEL;
        set_irq_chip_and_handler_name (irq, & ioapic_chip, handle_edge_irq, "edge");
    }
}

{
    irq_desc[irq].status |= IRQ_LEVEL;
    set_irq_chip_and_handler_name (irq, & ioapic_chip, handle_fasteoi_irq, "fasteoi");
}

{
    irq_desc[irq].status &= ~IRQ_LEVEL;
    set_irq_chip_and_handler_name (irq, & ioapic_chip, handle_edge_irq, "edge");
}

{
    struct irq_cfg *cfg = irq_cfg + irq;
    struct IO_APIC_route_entry entry;
    cpumask_t mask;
    if (!IO_APIC_IRQ (irq))
        return;
    mask = TARGET_CPUS;
    if (assign_irq_vector (irq, mask))
        return;
    cpus_and (mask, cfg -> domain, mask);
    apic_printk (APIC_VERBOSE, KERN_DEBUG "IOAPIC[%d]: Set routing entry (%d-%d -> 0x%x -> " "IRQ %d Mode:%i Active:%i)\n", apic, mp_ioapics [apic].mpc_apicid, pin, cfg -> vector, irq, trigger, polarity);
    memset (& entry, 0, sizeof (entry));
    entry.delivery_mode = INT_DELIVERY_MODE;
    entry.dest_mode = INT_DEST_MODE;
    entry.dest = cpu_mask_to_apicid (mask);
    entry.mask = 0;
    entry.trigger = trigger;
    entry.polarity = polarity;
    entry.vector = cfg->vector;
    if (trigger)
        entry.mask = 1;
    ioapic_register_intr (irq, trigger);
    if (irq < 16)
        disable_8259A_irq (irq);
    ioapic_write_entry (apic, pin, entry);
}

{
    int apic, pin, idx, irq, first_notcon = 1;
    apic_printk (APIC_VERBOSE, KERN_DEBUG "init IO_APIC IRQs\n");
    for (apic = 0; apic < nr_ioapics; apic++) {
        for (pin = 0; pin < nr_ioapic_registers[apic]; pin++) {
            idx = find_irq_entry (apic, pin, mp_INT);
            if (idx == -1) {
                if (first_notcon) {
                    apic_printk (APIC_VERBOSE, KERN_DEBUG " IO-APIC (apicid-pin) %d-%d", mp_ioapics [apic].mpc_apicid, pin);
                    first_notcon = 0;
                }
                else
                    apic_printk (APIC_VERBOSE, ", %d-%d", mp_ioapics[apic].mpc_apicid, pin);
                continue;
            }
            if (!first_notcon) {
                apic_printk (APIC_VERBOSE, " not connected.\n");
                first_notcon = 1;
            }
            irq = pin_2_irq (idx, apic, pin);
            add_pin_to_irq (irq, apic, pin);
            setup_IO_APIC_irq (apic, pin, irq, irq_trigger (idx), irq_polarity (idx));
        }
    }
    if (!first_notcon)
        apic_printk (APIC_VERBOSE, " not connected.\n");
}

{
    for (pin = 0; pin < nr_ioapic_registers[apic]; pin++) {
        idx = find_irq_entry (apic, pin, mp_INT);
        if (idx == -1) {
            if (first_notcon) {
                apic_printk (APIC_VERBOSE, KERN_DEBUG " IO-APIC (apicid-pin) %d-%d", mp_ioapics [apic].mpc_apicid, pin);
                first_notcon = 0;
            }
            else
                apic_printk (APIC_VERBOSE, ", %d-%d", mp_ioapics[apic].mpc_apicid, pin);
            continue;
        }
        if (!first_notcon) {
            apic_printk (APIC_VERBOSE, " not connected.\n");
            first_notcon = 1;
        }
        irq = pin_2_irq (idx, apic, pin);
        add_pin_to_irq (irq, apic, pin);
        setup_IO_APIC_irq (apic, pin, irq, irq_trigger (idx), irq_polarity (idx));
    }
}

{
    idx = find_irq_entry (apic, pin, mp_INT);
    if (idx == -1) {
        if (first_notcon) {
            apic_printk (APIC_VERBOSE, KERN_DEBUG " IO-APIC (apicid-pin) %d-%d", mp_ioapics [apic].mpc_apicid, pin);
            first_notcon = 0;
        }
        else
            apic_printk (APIC_VERBOSE, ", %d-%d", mp_ioapics[apic].mpc_apicid, pin);
        continue;
    }
    if (!first_notcon) {
        apic_printk (APIC_VERBOSE, " not connected.\n");
        first_notcon = 1;
    }
    irq = pin_2_irq (idx, apic, pin);
    add_pin_to_irq (irq, apic, pin);
    setup_IO_APIC_irq (apic, pin, irq, irq_trigger (idx), irq_polarity (idx));
}

{
    if (first_notcon) {
        apic_printk (APIC_VERBOSE, KERN_DEBUG " IO-APIC (apicid-pin) %d-%d", mp_ioapics [apic].mpc_apicid, pin);
        first_notcon = 0;
    }
    else
        apic_printk (APIC_VERBOSE, ", %d-%d", mp_ioapics[apic].mpc_apicid, pin);
    continue;
}

{
    apic_printk (APIC_VERBOSE, KERN_DEBUG " IO-APIC (apicid-pin) %d-%d", mp_ioapics [apic].mpc_apicid, pin);
    first_notcon = 0;
}

{
    apic_printk (APIC_VERBOSE, " not connected.\n");
    first_notcon = 1;
}

{
    struct IO_APIC_route_entry entry;
    memset (& entry, 0, sizeof (entry));
    disable_8259A_irq (0);
    apic_write (APIC_LVT0, APIC_LVT_MASKED | APIC_DM_EXTINT);
    entry.dest_mode = INT_DEST_MODE;
    entry.mask = 0;
    entry.dest = cpu_mask_to_apicid (TARGET_CPUS);
    entry.delivery_mode = INT_DELIVERY_MODE;
    entry.polarity = 0;
    entry.trigger = 0;
    entry.vector = vector;
    set_irq_chip_and_handler_name (0, & ioapic_chip, handle_edge_irq, "edge");
    ioapic_write_entry (apic, pin, entry);
    enable_8259A_irq (0);
}

{
    int apic, i;
    union IO_APIC_reg_00 reg_00;
    union IO_APIC_reg_01 reg_01;
    union IO_APIC_reg_02 reg_02;
    unsigned long flags;
    if (apic_verbosity == APIC_QUIET)
        return;
    printk (KERN_DEBUG "number of MP IRQ sources: %d.\n", mp_irq_entries);
    for (i = 0; i < nr_ioapics; i++)
        printk (KERN_DEBUG "number of IO-APIC #%d registers: %d.\n", mp_ioapics[i].mpc_apicid, nr_ioapic_registers[i]);
    printk (KERN_INFO "testing the IO APIC.......................\n");
    for (apic = 0; apic < nr_ioapics; apic++) {
        spin_lock_irqsave (& ioapic_lock, flags);
        reg_00.raw = io_apic_read (apic, 0);
        reg_01.raw = io_apic_read (apic, 1);
        if (reg_01.bits.version >= 0x10)
            reg_02.raw = io_apic_read (apic, 2);
        spin_unlock_irqrestore (& ioapic_lock, flags);
        printk ("\n");
        printk (KERN_DEBUG "IO APIC #%d......\n", mp_ioapics [apic].mpc_apicid);
        printk (KERN_DEBUG ".... register #00: %08X\n", reg_00.raw);
        printk (KERN_DEBUG ".......    : physical APIC id: %02X\n", reg_00.bits.ID);
        printk (KERN_DEBUG ".... register #01: %08X\n", * (int *) & reg_01);
        printk (KERN_DEBUG ".......     : max redirection entries: %04X\n", reg_01.bits.entries);
        printk (KERN_DEBUG ".......     : PRQ implemented: %X\n", reg_01.bits.PRQ);
        printk (KERN_DEBUG ".......     : IO APIC version: %04X\n", reg_01.bits.version);
        if (reg_01.bits.version >= 0x10) {
            printk (KERN_DEBUG ".... register #02: %08X\n", reg_02.raw);
            printk (KERN_DEBUG ".......     : arbitration: %02X\n", reg_02.bits.arbitration);
        }
        printk (KERN_DEBUG ".... IRQ redirection table:\n");
        printk (KERN_DEBUG " NR Dst Mask Trig IRR Pol" " Stat Dmod Deli Vect:   \n");
        for (i = 0; i <= reg_01.bits.entries; i++) {
            struct IO_APIC_route_entry entry;
            entry = ioapic_read_entry (apic, i);
            printk (KERN_DEBUG " %02x %03X ", i, entry.dest);
            printk ("%1d    %1d    %1d   %1d   %1d    %1d    %1d    %02X\n", entry.mask, entry.trigger, entry.irr, entry.polarity, entry.delivery_status, entry.dest_mode, entry.delivery_mode, entry.vector);
        }
    }
    printk (KERN_DEBUG "IRQ to pin mappings:\n");
    for (i = 0; i < NR_IRQS; i++) {
        struct irq_pin_list *entry = irq_2_pin + i;
        if (entry->pin < 0)
            continue;
        printk (KERN_DEBUG "IRQ%d ", i);
        for (;;) {
            printk ("-> %d:%d", entry -> apic, entry -> pin);
            if (!entry->next)
                break;
            entry = irq_2_pin + entry->next;
        }
        printk ("\n");
    }
    printk (KERN_INFO ".................................... done.\n");
    return;
}

{
