{
    printk (KERN_INFO "activating NMI Watchdog ...");
    enable_NMI_through_LVT0 ();
    printk (" done.\n");
}

{
    int apic, pin, i;
    struct IO_APIC_route_entry entry0, entry1;
    unsigned char save_control, save_freq_select;
    pin = find_isa_irq_pin (8, mp_INT);
    apic = find_isa_irq_apic (8, mp_INT);
    if (pin == -1)
        return;
    entry0 = ioapic_read_entry (apic, pin);
    clear_IO_APIC_pin (apic, pin);
    memset (& entry1, 0, sizeof (entry1));
    entry1.dest_mode = 0;
    entry1.mask = 0;
    entry1.dest = hard_smp_processor_id ();
    entry1.delivery_mode = dest_ExtINT;
    entry1.polarity = entry0.polarity;
    entry1.trigger = 0;
    entry1.vector = 0;
    ioapic_write_entry (apic, pin, entry1);
    save_control = CMOS_READ (RTC_CONTROL);
    save_freq_select = CMOS_READ (RTC_FREQ_SELECT);
    CMOS_WRITE ((save_freq_select & ~ RTC_RATE_SELECT) | 0x6, RTC_FREQ_SELECT);
    CMOS_WRITE (save_control | RTC_PIE, RTC_CONTROL);
    i = 100;
    while (i-- > 0) {
        mdelay (10);
        if ((CMOS_READ (RTC_INTR_FLAGS) & RTC_PF) == RTC_PF)
            i -= 10;
    }
    CMOS_WRITE (save_control, RTC_CONTROL);
    CMOS_WRITE (save_freq_select, RTC_FREQ_SELECT);
    clear_IO_APIC_pin (apic, pin);
    ioapic_write_entry (apic, pin, entry0);
}

{
    mdelay (10);
    if ((CMOS_READ (RTC_INTR_FLAGS) & RTC_PF) == RTC_PF)
        i -= 10;
}

{
    struct irq_cfg *cfg = irq_cfg + 0;
    int apic1, pin1, apic2, pin2;
    unsigned long flags;
    local_irq_save (flags);
    disable_8259A_irq (0);
    assign_irq_vector (0, TARGET_CPUS);
    apic_write (APIC_LVT0, APIC_LVT_MASKED | APIC_DM_EXTINT);
    init_8259A (1);
    if (timer_over_8254 > 0)
        enable_8259A_irq (0);
    pin1 = find_isa_irq_pin (0, mp_INT);
    apic1 = find_isa_irq_apic (0, mp_INT);
    pin2 = ioapic_i8259.pin;
    apic2 = ioapic_i8259.apic;
    apic_printk (APIC_VERBOSE, KERN_INFO "..TIMER: vector=0x%02X apic1=%d pin1=%d apic2=%d pin2=%d\n", cfg -> vector, apic1, pin1, apic2, pin2);
    if (pin1 != -1) {
        unmask_IO_APIC_irq (0);
        if (!no_timer_check && timer_irq_works ()) {
            nmi_watchdog_default ();
            if (nmi_watchdog == NMI_IO_APIC) {
                disable_8259A_irq (0);
                setup_nmi ();
                enable_8259A_irq (0);
            }
            if (disable_timer_pin_1 > 0)
                clear_IO_APIC_pin (0, pin1);
            goto out;
        }
        clear_IO_APIC_pin (apic1, pin1);
        apic_printk (APIC_QUIET, KERN_ERR "..MP-BIOS bug: 8254 timer not " "connected to IO-APIC\n");
    }
    apic_printk (APIC_VERBOSE, KERN_INFO "...trying to set up timer (IRQ0) " "through the 8259A ... ");
    if (pin2 != -1) {
        apic_printk (APIC_VERBOSE, "\n..... (found apic %d pin %d) ...", apic2, pin2);
        setup_ExtINT_IRQ0_pin (apic2, pin2, cfg -> vector);
        if (timer_irq_works ()) {
            apic_printk (APIC_VERBOSE, " works.\n");
            nmi_watchdog_default ();
            if (nmi_watchdog == NMI_IO_APIC) {
                setup_nmi ();
            }
            goto out;
        }
        clear_IO_APIC_pin (apic2, pin2);
    }
    apic_printk (APIC_VERBOSE, " failed.\n");
    if (nmi_watchdog == NMI_IO_APIC) {
        printk (KERN_WARNING "timer doesn't work through the IO-APIC - disabling NMI Watchdog!\n");
        nmi_watchdog = 0;
    }
    apic_printk (APIC_VERBOSE, KERN_INFO "...trying to set up timer as Virtual Wire IRQ...");
    disable_8259A_irq (0);
    irq_desc[0].chip = &lapic_irq_type;
    apic_write (APIC_LVT0, APIC_DM_FIXED | cfg -> vector);
    enable_8259A_irq (0);
    if (timer_irq_works ()) {
        apic_printk (APIC_VERBOSE, " works.\n");
        goto out;
    }
    apic_write (APIC_LVT0, APIC_LVT_MASKED | APIC_DM_FIXED | cfg -> vector);
    apic_printk (APIC_VERBOSE, " failed.\n");
    apic_printk (APIC_VERBOSE, KERN_INFO "...trying to set up timer as ExtINT IRQ...");
    init_8259A (0);
    make_8259A_irq (0);
    apic_write (APIC_LVT0, APIC_DM_EXTINT);
    unlock_ExtINT_logic ();
    if (timer_irq_works ()) {
        apic_printk (APIC_VERBOSE, " works.\n");
        goto out;
    }
    apic_printk (APIC_VERBOSE, " failed :(.\n");
    panic ("IO-APIC + timer doesn't work! Try using the 'noapic' kernel parameter\n");
out :
    local_irq_restore (flags);
}

{
    unmask_IO_APIC_irq (0);
    if (!no_timer_check && timer_irq_works ()) {
        nmi_watchdog_default ();
        if (nmi_watchdog == NMI_IO_APIC) {
            disable_8259A_irq (0);
            setup_nmi ();
            enable_8259A_irq (0);
        }
        if (disable_timer_pin_1 > 0)
            clear_IO_APIC_pin (0, pin1);
        goto out;
    }
    clear_IO_APIC_pin (apic1, pin1);
    apic_printk (APIC_QUIET, KERN_ERR "..MP-BIOS bug: 8254 timer not " "connected to IO-APIC\n");
}

{
    nmi_watchdog_default ();
    if (nmi_watchdog == NMI_IO_APIC) {
        disable_8259A_irq (0);
        setup_nmi ();
        enable_8259A_irq (0);
    }
    if (disable_timer_pin_1 > 0)
        clear_IO_APIC_pin (0, pin1);
    goto out;
}

{
    disable_8259A_irq (0);
    setup_nmi ();
    enable_8259A_irq (0);
}

{
    apic_printk (APIC_VERBOSE, "\n..... (found apic %d pin %d) ...", apic2, pin2);
    setup_ExtINT_IRQ0_pin (apic2, pin2, cfg -> vector);
    if (timer_irq_works ()) {
        apic_printk (APIC_VERBOSE, " works.\n");
        nmi_watchdog_default ();
        if (nmi_watchdog == NMI_IO_APIC) {
            setup_nmi ();
        }
        goto out;
    }
    clear_IO_APIC_pin (apic2, pin2);
}

{
    apic_printk (APIC_VERBOSE, " works.\n");
    nmi_watchdog_default ();
    if (nmi_watchdog == NMI_IO_APIC) {
        setup_nmi ();
    }
    goto out;
}

{
    setup_nmi ();
}

{
    printk (KERN_WARNING "timer doesn't work through the IO-APIC - disabling NMI Watchdog!\n");
    nmi_watchdog = 0;
}

{
    apic_printk (APIC_VERBOSE, " works.\n");
    goto out;
}

{
    apic_printk (APIC_VERBOSE, " works.\n");
    goto out;
}

{
    no_timer_check = 1;
    return 1;
}

{
    if (acpi_ioapic)
        io_apic_irqs = ~0;
    else
        io_apic_irqs = ~PIC_IRQS;
    apic_printk (APIC_VERBOSE, "ENABLING IO-APIC IRQs\n");
    sync_Arb_IDs ();
    setup_IO_APIC_irqs ();
    init_IO_APIC_traps ();
    check_timer ();
    if (!acpi_ioapic)
        print_IO_APIC ();
}

{
    struct IO_APIC_route_entry *entry;
    struct sysfs_ioapic_data *data;
    int i;
    data = container_of (dev, struct sysfs_ioapic_data, dev);
    entry = data->entry;
    for (i = 0; i < nr_ioapic_registers[dev->id]; i++, entry++)
        *entry = ioapic_read_entry (dev->id, i);
    return 0;
}

{
    struct IO_APIC_route_entry *entry;
    struct sysfs_ioapic_data *data;
    unsigned long flags;
    union IO_APIC_reg_00 reg_00;
    int i;
    data = container_of (dev, struct sysfs_ioapic_data, dev);
    entry = data->entry;
    spin_lock_irqsave (& ioapic_lock, flags);
    reg_00.raw = io_apic_read (dev->id, 0);
    if (reg_00.bits.ID != mp_ioapics[dev->id].mpc_apicid) {
        reg_00.bits.ID = mp_ioapics[dev->id].mpc_apicid;
        io_apic_write (dev -> id, 0, reg_00.raw);
    }
    spin_unlock_irqrestore (& ioapic_lock, flags);
    for (i = 0; i < nr_ioapic_registers[dev->id]; i++)
        ioapic_write_entry (dev->id, i, entry[i]);
    return 0;
}

{
    reg_00.bits.ID = mp_ioapics[dev->id].mpc_apicid;
    io_apic_write (dev -> id, 0, reg_00.raw);
}

{
    struct sys_device *dev;
    int i, size, error;
    error = sysdev_class_register (&ioapic_sysdev_class);
    if (error)
        return error;
    for (i = 0; i < nr_ioapics; i++) {
        size = sizeof (struct sys_device) + nr_ioapic_registers[i] * sizeof (struct IO_APIC_route_entry);
        mp_ioapic_data[i] = kzalloc (size, GFP_KERNEL);
        if (!mp_ioapic_data[i]) {
            printk (KERN_ERR "Can't suspend/resume IOAPIC %d\n", i);
            continue;
        }
        dev = &mp_ioapic_data[i]->dev;
        dev->id = i;
        dev->cls = &ioapic_sysdev_class;
        error = sysdev_register (dev);
        if (error) {
            kfree (mp_ioapic_data [i]);
            mp_ioapic_data[i] = NULL;
            printk (KERN_ERR "Can't suspend/resume IOAPIC %d\n", i);
            continue;
        }
    }
    return 0;
}

{
    size = sizeof (struct sys_device) + nr_ioapic_registers[i] * sizeof (struct IO_APIC_route_entry);
    mp_ioapic_data[i] = kzalloc (size, GFP_KERNEL);
    if (!mp_ioapic_data[i]) {
        printk (KERN_ERR "Can't suspend/resume IOAPIC %d\n", i);
        continue;
    }
    dev = &mp_ioapic_data[i]->dev;
    dev->id = i;
    dev->cls = &ioapic_sysdev_class;
    error = sysdev_register (dev);
    if (error) {
        kfree (mp_ioapic_data [i]);
        mp_ioapic_data[i] = NULL;
        printk (KERN_ERR "Can't suspend/resume IOAPIC %d\n", i);
        continue;
    }
}

{
    printk (KERN_ERR "Can't suspend/resume IOAPIC %d\n", i);
    continue;
}

{
    kfree (mp_ioapic_data [i]);
    mp_ioapic_data[i] = NULL;
    printk (KERN_ERR "Can't suspend/resume IOAPIC %d\n", i);
    continue;
}

{
    int irq;
    int new;
    unsigned long flags;
    irq = -ENOSPC;
    spin_lock_irqsave (& vector_lock, flags);
    for (new = (NR_IRQS - 1); new >= 0; new--) {
        if (platform_legacy_irq (new))
            continue;
        if (irq_cfg[new].vector != 0)
            continue;
        if (__assign_irq_vector (new, TARGET_CPUS) == 0)
            irq = new;
        break;
    }
    spin_unlock_irqrestore (& vector_lock, flags);
    if (irq >= 0) {
        dynamic_irq_init (irq);
    }
    return irq;
}

{
    if (platform_legacy_irq (new))
        continue;
    if (irq_cfg[new].vector != 0)
        continue;
    if (__assign_irq_vector (new, TARGET_CPUS) == 0)
        irq = new;
    break;
}

{
    dynamic_irq_init (irq);
}

{
    unsigned long flags;
    dynamic_irq_cleanup (irq);
    spin_lock_irqsave (& vector_lock, flags);
    __clear_irq_vector (irq);
    spin_unlock_irqrestore (& vector_lock, flags);
}

{
    unsigned long n;
    struct resource *res;
    char *mem;
    int i;
    if (nr_ioapics <= 0)
        return NULL;
    n = IOAPIC_RESOURCE_NAME_SIZE + sizeof (struct resource);
    n *= nr_ioapics;
    mem = alloc_bootmem (n);
    res = (void *) mem;
    if (mem != NULL) {
        mem += sizeof (struct resource) * nr_ioapics;
        for (i = 0; i < nr_ioapics; i++) {
            res[i].name = mem;
            res[i].flags = IORESOURCE_MEM | IORESOURCE_BUSY;
            sprintf (mem, "IOAPIC %u", i);
            mem += IOAPIC_RESOURCE_NAME_SIZE;
        }
    }
    ioapic_resources = res;
    return res;
}

{
    mem += sizeof (struct resource) * nr_ioapics;
    for (i = 0; i < nr_ioapics; i++) {
        res[i].name = mem;
        res[i].flags = IORESOURCE_MEM | IORESOURCE_BUSY;
        sprintf (mem, "IOAPIC %u", i);
        mem += IOAPIC_RESOURCE_NAME_SIZE;
    }
}

{
    res[i].name = mem;
    res[i].flags = IORESOURCE_MEM | IORESOURCE_BUSY;
    sprintf (mem, "IOAPIC %u", i);
    mem += IOAPIC_RESOURCE_NAME_SIZE;
}

{
    unsigned long ioapic_phys, idx = FIX_IO_APIC_BASE_0;
    struct resource *ioapic_res;
    int i;
    ioapic_res = ioapic_setup_resources ();
    for (i = 0; i < nr_ioapics; i++) {
        if (smp_found_config) {
            ioapic_phys = mp_ioapics[i].mpc_apicaddr;
        }
        else {
            ioapic_phys = (unsigned long) alloc_bootmem_pages (PAGE_SIZE);
            ioapic_phys = __pa (ioapic_phys);
        }
        set_fixmap_nocache (idx, ioapic_phys);
        apic_printk (APIC_VERBOSE, "mapped IOAPIC to %016lx (%016lx)\n", __fix_to_virt (idx), ioapic_phys);
        idx++;
        if (ioapic_res != NULL) {
            ioapic_res->start = ioapic_phys;
            ioapic_res->end = ioapic_phys + (4 * 1024) - 1;
            ioapic_res++;
        }
    }
}

{
    if (smp_found_config) {
        ioapic_phys = mp_ioapics[i].mpc_apicaddr;
    }
    else {
        ioapic_phys = (unsigned long) alloc_bootmem_pages (PAGE_SIZE);
        ioapic_phys = __pa (ioapic_phys);
    }
    set_fixmap_nocache (idx, ioapic_phys);
    apic_printk (APIC_VERBOSE, "mapped IOAPIC to %016lx (%016lx)\n", __fix_to_virt (idx), ioapic_phys);
    idx++;
    if (ioapic_res != NULL) {
        ioapic_res->start = ioapic_phys;
        ioapic_res->end = ioapic_phys + (4 * 1024) - 1;
        ioapic_res++;
    }
}

{
    ioapic_phys = mp_ioapics[i].mpc_apicaddr;
}

{
    ioapic_phys = (unsigned long) alloc_bootmem_pages (PAGE_SIZE);
    ioapic_phys = __pa (ioapic_phys);
}

{
    ioapic_res->start = ioapic_phys;
    ioapic_res->end = ioapic_phys + (4 * 1024) - 1;
    ioapic_res++;
}

{
    int i;
    struct resource *r = ioapic_resources;
    if (!r) {
        printk (KERN_ERR "IO APIC resources could be not be allocated.\n");
        return -1;
    }
    for (i = 0; i < nr_ioapics; i++) {
        insert_resource (& iomem_resource, r);
        r++;
    }
    return 0;
}

{
    printk (KERN_ERR "IO APIC resources could be not be allocated.\n");
    return -1;
}

{
    insert_resource (& iomem_resource, r);
    r++;
}

{
    atomic_notifier_chain_register (& idle_notifier, n);
}

{
    write_pda (isidle, 1);
    atomic_notifier_call_chain (& idle_notifier, IDLE_START, NULL);
}

{
    if (test_and_clear_bit_pda (0, isidle) == 0)
        return;
    atomic_notifier_call_chain (& idle_notifier, IDLE_END, NULL);
}

{
    if (current->pid)
        return;
    __exit_idle ();
}

{
    current_thread_info ()->status &= ~TS_POLLING;
    smp_mb ();
    if (!need_resched ())
        safe_halt ();
    else
        local_irq_enable ();
    current_thread_info ()->status |= TS_POLLING;
}

{
    current_thread_info ()->status |= TS_POLLING;
    while (1) {
        tick_nohz_stop_sched_tick ();
        while (!need_resched ()) {
            void (*idle) (void);
            rmb ();
            idle = pm_idle;
            if (!idle)
                idle = default_idle;
            if (cpu_is_offline (smp_processor_id ()))
                play_dead ();
            local_irq_disable ();
            enter_idle ();
            idle ();
            __exit_idle ();
        }
        tick_nohz_restart_sched_tick ();
        preempt_enable_no_resched ();
        schedule ();
        preempt_disable ();
    }
}

{
    tick_nohz_stop_sched_tick ();
    while (!need_resched ()) {
        void (*idle) (void);
        rmb ();
        idle = pm_idle;
        if (!idle)
            idle = default_idle;
        if (cpu_is_offline (smp_processor_id ()))
            play_dead ();
        local_irq_disable ();
        enter_idle ();
        idle ();
        __exit_idle ();
    }
    tick_nohz_restart_sched_tick ();
    preempt_enable_no_resched ();
    schedule ();
    preempt_disable ();
}

{
    void (*idle) (void);
    rmb ();
    idle = pm_idle;
    if (!idle)
        idle = default_idle;
    if (cpu_is_offline (smp_processor_id ()))
        play_dead ();
    local_irq_disable ();
    enter_idle ();
    idle ();
    __exit_idle ();
}

{
    unsigned long cr0 = 0L, cr2 = 0L, cr3 = 0L, cr4 = 0L, fs, gs, shadowgs;
    unsigned long d0, d1, d2, d3, d6, d7;
    unsigned int fsindex, gsindex;
    unsigned int ds, cs, es;
    printk ("\n");
    print_modules ();
    printk ("Pid: %d, comm: %.20s %s %s %.*s\n", current -> pid, current -> comm, print_tainted (), init_utsname () -> release, (int) strcspn (init_utsname () -> version, " "), init_utsname () -> version);
    printk ("RIP: %04lx:[<%016lx>] ", regs -> cs & 0xffff, regs -> ip);
    printk_address (regs -> ip, 1);
    printk ("RSP: %04lx:%016lx  EFLAGS: %08lx\n", regs -> ss, regs -> sp, regs -> flags);
    printk ("RAX: %016lx RBX: %016lx RCX: %016lx\n", regs -> ax, regs -> bx, regs -> cx);
    printk ("RDX: %016lx RSI: %016lx RDI: %016lx\n", regs -> dx, regs -> si, regs -> di);
    printk ("RBP: %016lx R08: %016lx R09: %016lx\n", regs -> bp, regs -> r8, regs -> r9);
    printk ("R10: %016lx R11: %016lx R12: %016lx\n", regs -> r10, regs -> r11, regs -> r12);
    printk ("R13: %016lx R14: %016lx R15: %016lx\n", regs -> r13, regs -> r14, regs -> r15);
    asm ("movl %%ds,%0"
        : "=r" (ds)
    ) asm ("movl %%cs,%0"
        : "=r" (cs)
    ) asm ("movl %%es,%0"
        : "=r" (es)
    ) asm ("movl %%fs,%0"
        : "=r" (fsindex)
    ) asm ("movl %%gs,%0"
        : "=r" (gsindex)
    ) rdmsrl (MSR_FS_BASE, fs);
    rdmsrl (MSR_GS_BASE, gs);
    rdmsrl (MSR_KERNEL_GS_BASE, shadowgs);
    cr0 = read_cr0 ();
    cr2 = read_cr2 ();
    cr3 = read_cr3 ();
    cr4 = read_cr4 ();
    printk ("FS:  %016lx(%04x) GS:%016lx(%04x) knlGS:%016lx\n", fs, fsindex, gs, gsindex, shadowgs);
    printk ("CS:  %04x DS: %04x ES: %04x CR0: %016lx\n", cs, ds, es, cr0);
    printk ("CR2: %016lx CR3: %016lx CR4: %016lx\n", cr2, cr3, cr4);
    get_debugreg (d0, 0);
    get_debugreg (d1, 1);
    get_debugreg (d2, 2);
    printk ("DR0: %016lx DR1: %016lx DR2: %016lx\n", d0, d1, d2);
    get_debugreg (d3, 3);
    get_debugreg (d6, 6);
    get_debugreg (d7, 7);
    printk ("DR3: %016lx DR6: %016lx DR7: %016lx\n", d3, d6, d7);
}

{
    printk ("CPU %d:", smp_processor_id ());
    __show_regs (regs);
    show_trace (NULL, regs, (void *) (regs + 1), regs -> bp);
}

{
    struct task_struct *me = current;
    struct thread_struct *t = &me->thread;
    if (me->thread.io_bitmap_ptr) {
        struct tss_struct *tss = &per_cpu (init_tss, get_cpu ());
        kfree (t -> io_bitmap_ptr);
        t->io_bitmap_ptr = NULL;
        clear_thread_flag (TIF_IO_BITMAP);
        memset (tss -> io_bitmap, 0xff, t -> io_bitmap_max);
        t->io_bitmap_max = 0;
        put_cpu ();
    }
}

{
    struct tss_struct *tss = &per_cpu (init_tss, get_cpu ());
    kfree (t -> io_bitmap_ptr);
    t->io_bitmap_ptr = NULL;
    clear_thread_flag (TIF_IO_BITMAP);
    memset (tss -> io_bitmap, 0xff, t -> io_bitmap_max);
    t->io_bitmap_max = 0;
    put_cpu ();
}

{
    struct task_struct *tsk = current;
    if (test_tsk_thread_flag (tsk, TIF_ABI_PENDING)) {
        clear_tsk_thread_flag (tsk, TIF_ABI_PENDING);
        if (test_tsk_thread_flag (tsk, TIF_IA32)) {
            clear_tsk_thread_flag (tsk, TIF_IA32);
        }
        else {
            set_tsk_thread_flag (tsk, TIF_IA32);
            current_thread_info ()->status |= TS_COMPAT;
        }
    }
    clear_tsk_thread_flag (tsk, TIF_DEBUG);
    tsk->thread.debugreg0 = 0;
    tsk->thread.debugreg1 = 0;
    tsk->thread.debugreg2 = 0;
    tsk->thread.debugreg3 = 0;
    tsk->thread.debugreg6 = 0;
    tsk->thread.debugreg7 = 0;
    memset (tsk -> thread.tls_array, 0, sizeof (tsk -> thread.tls_array));
    tsk->fpu_counter = 0;
    clear_fpu (tsk);
    clear_used_math ();
}

{
    clear_tsk_thread_flag (tsk, TIF_ABI_PENDING);
    if (test_tsk_thread_flag (tsk, TIF_IA32)) {
        clear_tsk_thread_flag (tsk, TIF_IA32);
    }
    else {
        set_tsk_thread_flag (tsk, TIF_IA32);
        current_thread_info ()->status |= TS_COMPAT;
    }
}

{
    clear_tsk_thread_flag (tsk, TIF_IA32);
}

{
    set_tsk_thread_flag (tsk, TIF_IA32);
    current_thread_info ()->status |= TS_COMPAT;
}

{
    if (dead_task->mm) {
        if (dead_task->mm->context.size) {
            printk ("WARNING: dead process %8s still has LDT? <%p/%d>\n", dead_task -> comm, dead_task -> mm -> context.ldt, dead_task -> mm -> context.size);
            BUG ();
        }
    }
}

{
    if (dead_task->mm->context.size) {
        printk ("WARNING: dead process %8s still has LDT? <%p/%d>\n", dead_task -> comm, dead_task -> mm -> context.ldt, dead_task -> mm -> context.size);
        BUG ();
    }
}

{
    printk ("WARNING: dead process %8s still has LDT? <%p/%d>\n", dead_task -> comm, dead_task -> mm -> context.ldt, dead_task -> mm -> context.size);
    BUG ();
}

{
    struct user_desc ud = {
        .base_addr = addr,
        .limit = 0xfffff,
        .seg_32bit = 1,
        .limit_in_pages = 1,
        .useable = 1,
    };
    struct desc_struct *desc = t->thread.tls_array;
    desc += tls;
    fill_ldt (desc, & ud);
}

{
    return get_desc_base (&t->thread.tls_array[tls]);
}

{
    unlazy_fpu (tsk);
}

{
    int err;
    struct pt_regs *childregs;
    struct task_struct *me = current;
    childregs = ((struct pt_regs *) (THREAD_SIZE + task_stack_page (p))) - 1;
    *childregs = *regs;
    childregs->ax = 0;
    childregs->sp = sp;
    if (sp == ~0UL)
        childregs->sp = (unsigned long) childregs;
    p->thread.sp = (unsigned long) childregs;
    p->thread.sp0 = (unsigned long) (childregs + 1);
    p->thread.usersp = me->thread.usersp;
    set_tsk_thread_flag (p, TIF_FORK);
    p->thread.fs = me->thread.fs;
    p->thread.gs = me->thread.gs;
    asm ("mov %%gs,%0"
        : "=m" (p->thread.gsindex)
    ) asm ("mov %%fs,%0"
        : "=m" (p->thread.fsindex)
    ) asm ("mov %%es,%0"
        : "=m" (p->thread.es)
    ) asm ("mov %%ds,%0"
        : "=m" (p->thread.ds)
    ) if (unlikely (test_tsk_thread_flag (me, TIF_IO_BITMAP))) {
        p->thread.io_bitmap_ptr = kmalloc (IO_BITMAP_BYTES, GFP_KERNEL);
        if (!p->thread.io_bitmap_ptr) {
            p->thread.io_bitmap_max = 0;
            return -ENOMEM;
        }
        memcpy (p -> thread.io_bitmap_ptr, me -> thread.io_bitmap_ptr, IO_BITMAP_BYTES);
        set_tsk_thread_flag (p, TIF_IO_BITMAP);
    }
    if (clone_flags & CLONE_SETTLS) {
        err = do_arch_prctl (p, ARCH_SET_FS, childregs->r8);
        if (err)
            goto out;
    }
    err = 0;
out :
    if (err && p->thread.io_bitmap_ptr) {
        kfree (p -> thread.io_bitmap_ptr);
        p->thread.io_bitmap_max = 0;
    }
    return err;
}

{
    p->thread.io_bitmap_ptr = kmalloc (IO_BITMAP_BYTES, GFP_KERNEL);
    if (!p->thread.io_bitmap_ptr) {
        p->thread.io_bitmap_max = 0;
        return -ENOMEM;
    }
    memcpy (p -> thread.io_bitmap_ptr, me -> thread.io_bitmap_ptr, IO_BITMAP_BYTES);
    set_tsk_thread_flag (p, TIF_IO_BITMAP);
}

{
    p->thread.io_bitmap_max = 0;
    return -ENOMEM;
}

{
    err = do_arch_prctl (p, ARCH_SET_FS, childregs->r8);
    if (err)
        goto out;
}

{
    kfree (p -> thread.io_bitmap_ptr);
    p->thread.io_bitmap_max = 0;
}

{
    asm volatile ("movl %0, %%fs; movl %0, %%es; movl %0, %%ds"
        :
        : "r" (0)
    ) load_gs_index (0);
    regs->ip = new_ip;
    regs->sp = new_sp;
    write_pda (oldrsp, new_sp);
    regs->cs = __USER_CS;
    regs->ss = __USER_DS;
    regs->flags = 0x200;
    set_fs (USER_DS);
    free_thread_xstate (current);
}

{
    write_cr4 (read_cr4 () | X86_CR4_TSD);
}

{
    preempt_disable ();
    if (!test_and_set_thread_flag (TIF_NOTSC))
        hard_disable_TSC ();
    preempt_enable ();
}

{
    write_cr4 (read_cr4 () & ~ X86_CR4_TSD);
}

{
    preempt_disable ();
    if (test_and_clear_thread_flag (TIF_NOTSC))
        hard_enable_TSC ();
    preempt_enable ();
}

{
    unsigned int val;
    if (test_thread_flag (TIF_NOTSC))
        val = PR_TSC_SIGSEGV;
    else
        val = PR_TSC_ENABLE;
    return put_user (val, (unsigned int __user *) adr);
}

{
    if (val == PR_TSC_SIGSEGV)
        disable_TSC ();
    else if (val == PR_TSC_ENABLE)
        enable_TSC ();
    else
        return -EINVAL;
    return 0;
}

{
    struct thread_struct *prev, *next;
    unsigned long debugctl;
    prev = &prev_p->thread, next = &next_p->thread;
    debugctl = prev->debugctlmsr;
    if (next->ds_area_msr != prev->ds_area_msr) {
        debugctl = 0;
        update_debugctlmsr (0);
        wrmsrl (MSR_IA32_DS_AREA, next -> ds_area_msr);
    }
    if (next->debugctlmsr != debugctl)
        update_debugctlmsr (next->debugctlmsr);
    if (test_tsk_thread_flag (next_p, TIF_DEBUG)) {
        loaddebug (next, 0);
        loaddebug (next, 1);
        loaddebug (next, 2);
        loaddebug (next, 3);
        loaddebug (next, 6);
        loaddebug (next, 7);
    }
    if (test_tsk_thread_flag (prev_p, TIF_NOTSC) ^ test_tsk_thread_flag (next_p, TIF_NOTSC)) {
        if (test_tsk_thread_flag (next_p, TIF_NOTSC))
            hard_disable_TSC ();
        else
            hard_enable_TSC ();
    }
    if (test_tsk_thread_flag (next_p, TIF_IO_BITMAP)) {
        memcpy (tss -> io_bitmap, next -> io_bitmap_ptr, max (prev -> io_bitmap_max, next -> io_bitmap_max));
    }
    else if (test_tsk_thread_flag (prev_p, TIF_IO_BITMAP)) {
        memset (tss -> io_bitmap, 0xff, prev -> io_bitmap_max);
    }
}

{
    debugctl = 0;
    update_debugctlmsr (0);
    wrmsrl (MSR_IA32_DS_AREA, next -> ds_area_msr);
}

{
    loaddebug (next, 0);
    loaddebug (next, 1);
    loaddebug (next, 2);
    loaddebug (next, 3);
    loaddebug (next, 6);
    loaddebug (next, 7);
}

{
    if (test_tsk_thread_flag (next_p, TIF_NOTSC))
        hard_disable_TSC ();
    else
        hard_enable_TSC ();
}

{
    memcpy (tss -> io_bitmap, next -> io_bitmap_ptr, max (prev -> io_bitmap_max, next -> io_bitmap_max));
}

{
    memset (tss -> io_bitmap, 0xff, prev -> io_bitmap_max);
}

{
    struct thread_struct *prev = &prev_p->thread, *next = &next_p->thread;
    int cpu = smp_processor_id ();
    struct tss_struct *tss = &per_cpu (init_tss, cpu);
    if (next_p->fpu_counter > 5)
        prefetch (next->xstate);
    load_sp0 (tss, next);
    asm volatile ("mov %%es,%0"
        : "=m" (prev->es)
    ) if (unlikely (next->es | prev->es))
        loadsegment (es, next->es);
    asm volatile ("mov %%ds,%0"
        : "=m" (prev->ds)
    ) if (unlikely (next->ds | prev->ds))
        loadsegment (ds, next->ds);
    load_TLS (next, cpu);
    {
        unsigned fsindex;
        asm volatile ("movl %%fs,%0"
            : "=r" (fsindex)
        ) if (unlikely (fsindex | next->fsindex | prev->fs)) {
            loadsegment (fs, next -> fsindex);
            if (fsindex)
                prev->fs = 0;
        }
        if (next->fs)
            wrmsrl (MSR_FS_BASE, next->fs);
        prev->fsindex = fsindex;
    }
    {
        unsigned gsindex;
        asm volatile ("movl %%gs,%0"
            : "=r" (gsindex)
        ) if (unlikely (gsindex | next->gsindex | prev->gs)) {
            load_gs_index (next -> gsindex);
            if (gsindex)
                prev->gs = 0;
        }
        if (next->gs)
            wrmsrl (MSR_KERNEL_GS_BASE, next->gs);
        prev->gsindex = gsindex;
    }
    unlazy_fpu (prev_p);
    prev->usersp = read_pda (oldrsp);
    write_pda (oldrsp, next -> usersp);
    write_pda (pcurrent, next_p);
    write_pda (kernelstack, (unsigned long) task_stack_page (next_p) + THREAD_SIZE - PDA_STACKOFFSET);
    if (unlikely (task_thread_info (next_p)->flags & _TIF_WORK_CTXSW_NEXT || task_thread_info (prev_p)->flags & _TIF_WORK_CTXSW_PREV))
        __switch_to_xtra (prev_p, next_p, tss);
    if (tsk_used_math (next_p) && next_p->fpu_counter > 5)
        math_state_restore ();
    return prev_p;
}

{
    unsigned fsindex;
    asm volatile ("movl %%fs,%0"
        : "=r" (fsindex)
    ) if (unlikely (fsindex | next->fsindex | prev->fs)) {
        loadsegment (fs, next -> fsindex);
        if (fsindex)
            prev->fs = 0;
    }
    if (next->fs)
        wrmsrl (MSR_FS_BASE, next->fs);
    prev->fsindex = fsindex;
}

{
    loadsegment (fs, next -> fsindex);
    if (fsindex)
        prev->fs = 0;
}

{
    unsigned gsindex;
    asm volatile ("movl %%gs,%0"
        : "=r" (gsindex)
    ) if (unlikely (gsindex | next->gsindex | prev->gs)) {
        load_gs_index (next -> gsindex);
        if (gsindex)
            prev->gs = 0;
    }
    if (next->gs)
        wrmsrl (MSR_KERNEL_GS_BASE, next->gs);
    prev->gsindex = gsindex;
}

{
    load_gs_index (next -> gsindex);
    if (gsindex)
        prev->gs = 0;
}

{
    long error;
    char *filename;
    filename = getname (name);
    error = PTR_ERR (filename);
    if (IS_ERR (filename))
        return error;
    error = do_execve (filename, argv, envp, regs);
    putname (filename);
    return error;
}

{
    clear_thread_flag (TIF_IA32);
    current->personality &= ~READ_IMPLIES_EXEC;
}

{
    return do_fork (SIGCHLD, regs->sp, regs, 0, NULL, NULL);
}

{
    if (!newsp)
        newsp = regs->sp;
    return do_fork (clone_flags, newsp, regs, 0, parent_tid, child_tid);
}

{
    return do_fork (CLONE_VFORK | CLONE_VM | SIGCHLD, regs->sp, regs, 0, NULL, NULL);
}

{
    unsigned long stack;
    u64 fp, ip;
    int count = 0;
    if (!p || p == current || p->state == TASK_RUNNING)
        return 0;
    stack = (unsigned long) task_stack_page (p);
    if (p->thread.sp < stack || p->thread.sp > stack + THREAD_SIZE)
        return 0;
    fp = *(u64*) (p->thread.sp);
    do {
        if (fp < (unsigned long) stack || fp > (unsigned long) stack + THREAD_SIZE)
            return 0;
        ip = *(u64*) (fp + 8);
        if (!in_sched_functions (ip))
            return ip;
        fp = *(u64*) fp;
    }
    while (count++ < 16);
    return 0;
}

{
    if (fp < (unsigned long) stack || fp > (unsigned long) stack + THREAD_SIZE)
        return 0;
    ip = *(u64*) (fp + 8);
    if (!in_sched_functions (ip))
        return ip;
    fp = *(u64*) fp;
}

{
    int ret = 0;
    int doit = task == current;
    int cpu;
    switch (code) {
    case ARCH_SET_GS :
        if (addr >= TASK_SIZE_OF (task))
            return -EPERM;
        cpu = get_cpu ();
        if (addr <= 0xffffffff) {
            set_32bit_tls (task, GS_TLS, addr);
            if (doit) {
                load_TLS (& task -> thread, cpu);
                load_gs_index (GS_TLS_SEL);
            }
            task->thread.gsindex = GS_TLS_SEL;
            task->thread.gs = 0;
        }
        else {
            task->thread.gsindex = 0;
            task->thread.gs = addr;
            if (doit) {
                load_gs_index (0);
                ret = checking_wrmsrl (MSR_KERNEL_GS_BASE, addr);
            }
        }
        put_cpu ();
        break;
    case ARCH_SET_FS :
        if (addr >= TASK_SIZE_OF (task))
            return -EPERM;
        cpu = get_cpu ();
        if (addr <= 0xffffffff) {
            set_32bit_tls (task, FS_TLS, addr);
            if (doit) {
                load_TLS (& task -> thread, cpu);
                asm volatile ("movl %0,%%fs"
                    :
                    : "r" (FS_TLS_SEL)
                )}
            task->thread.fsindex = FS_TLS_SEL;
            task->thread.fs = 0;
        }
        else {
            task->thread.fsindex = 0;
            task->thread.fs = addr;
            if (doit) {
                asm volatile ("movl %0,%%fs"
                    :
                    : "r" (0)
                ) ret = checking_wrmsrl (MSR_FS_BASE, addr);
            }
        }
        put_cpu ();
        break;
    case ARCH_GET_FS :
        {
            unsigned long base;
            if (task->thread.fsindex == FS_TLS_SEL)
                base = read_32bit_tls (task, FS_TLS);
            else if (doit)
                rdmsrl (MSR_FS_BASE, base);
            else
                base = task->thread.fs;
            ret = put_user (base, (unsigned long __user *) addr);
            break;
        }
    case ARCH_GET_GS :
        {
            unsigned long base;
            unsigned gsindex;
            if (task->thread.gsindex == GS_TLS_SEL)
                base = read_32bit_tls (task, GS_TLS);
            else if (doit) {
                asm ("movl %%gs,%0"
                    : "=r" (gsindex)
                ) if (gsindex)
                    rdmsrl (MSR_KERNEL_GS_BASE, base);
                else
                    base = task->thread.gs;
            }
            else
                base = task->thread.gs;
            ret = put_user (base, (unsigned long __user *) addr);
            break;
        }
    default :
        ret = -EINVAL;
        break;
    }
    return ret;
}

{
case ARCH_SET_GS :
    if (addr >= TASK_SIZE_OF (task))
        return -EPERM;
    cpu = get_cpu ();
    if (addr <= 0xffffffff) {
        set_32bit_tls (task, GS_TLS, addr);
        if (doit) {
            load_TLS (& task -> thread, cpu);
            load_gs_index (GS_TLS_SEL);
        }
        task->thread.gsindex = GS_TLS_SEL;
        task->thread.gs = 0;
    }
    else {
        task->thread.gsindex = 0;
        task->thread.gs = addr;
        if (doit) {
            load_gs_index (0);
            ret = checking_wrmsrl (MSR_KERNEL_GS_BASE, addr);
        }
    }
    put_cpu ();
    break;
case ARCH_SET_FS :
    if (addr >= TASK_SIZE_OF (task))
        return -EPERM;
    cpu = get_cpu ();
    if (addr <= 0xffffffff) {
