{
    if (!(current->personality & ADDR_NO_RANDOMIZE) && randomize_va_space)
        sp -= get_random_int () % 8192;
    return sp & ~0xf;
}

{
    unsigned long range_end = mm->brk + 0x02000000;
    return randomize_range (mm->brk, range_end, 0) ? : mm->brk;
}

{
    cycles_t start, now, prev, end;
    int i;
    start = get_cycles ();
    end = start + tsc_khz * 20ULL;
    now = start;
    for (i = 0;; i++) {
        __raw_spin_lock (& sync_lock);
        prev = last_tsc;
        now = get_cycles ();
        last_tsc = now;
        __raw_spin_unlock (& sync_lock);
        if (unlikely (!(i & 7))) {
            if (now > end || i > 10000000)
                break;
            cpu_relax ();
            touch_nmi_watchdog ();
        }
        if (unlikely (prev > now)) {
            __raw_spin_lock (& sync_lock);
            max_warp = max (max_warp, prev -now);
            nr_warps++;
            __raw_spin_unlock (& sync_lock);
        }
    }
    if (!(now - start)) {
        printk ("Warning: zero tsc calibration delta: %Ld [max: %Ld]\n", now - start, end - start);
        WARN_ON (1);
    }
}

{
    __raw_spin_lock (& sync_lock);
    prev = last_tsc;
    now = get_cycles ();
    last_tsc = now;
    __raw_spin_unlock (& sync_lock);
    if (unlikely (!(i & 7))) {
        if (now > end || i > 10000000)
            break;
        cpu_relax ();
        touch_nmi_watchdog ();
    }
    if (unlikely (prev > now)) {
        __raw_spin_lock (& sync_lock);
        max_warp = max (max_warp, prev -now);
        nr_warps++;
        __raw_spin_unlock (& sync_lock);
    }
}

{
    if (now > end || i > 10000000)
        break;
    cpu_relax ();
    touch_nmi_watchdog ();
}

{
    __raw_spin_lock (& sync_lock);
    max_warp = max (max_warp, prev -now);
    nr_warps++;
    __raw_spin_unlock (& sync_lock);
}

{
    printk ("Warning: zero tsc calibration delta: %Ld [max: %Ld]\n", now - start, end - start);
    WARN_ON (1);
}

{
    int cpus = 2;
    if (unsynchronized_tsc ())
        return;
    printk (KERN_INFO "checking TSC synchronization [CPU#%d -> CPU#%d]:", smp_processor_id (), cpu);
    atomic_set (& stop_count, 0);
    while (atomic_read (&start_count) != cpus - 1)
        cpu_relax ();
    atomic_inc (& start_count);
    check_tsc_warp ();
    while (atomic_read (&stop_count) != cpus - 1)
        cpu_relax ();
    if (nr_warps) {
        printk ("\n");
        printk (KERN_WARNING "Measured %Ld cycles TSC warp between CPUs," " turning off TSC clock.\n", max_warp);
        mark_tsc_unstable ("check_tsc_sync_source failed");
    }
    else {
        printk (" passed.\n");
    }
    atomic_set (& start_count, 0);
    nr_warps = 0;
    max_warp = 0;
    last_tsc = 0;
    atomic_inc (& stop_count);
}

{
    printk ("\n");
    printk (KERN_WARNING "Measured %Ld cycles TSC warp between CPUs," " turning off TSC clock.\n", max_warp);
    mark_tsc_unstable ("check_tsc_sync_source failed");
}

{
    printk (" passed.\n");
}

{
    int cpus = 2;
    if (unsynchronized_tsc ())
        return;
    atomic_inc (& start_count);
    while (atomic_read (&start_count) != cpus)
        cpu_relax ();
    check_tsc_warp ();
    atomic_inc (& stop_count);
    while (atomic_read (&stop_count) != cpus)
        cpu_relax ();
}

{
    loff_t ret;
    struct inode *inode = file->f_mapping->host;
    mutex_lock (& inode -> i_mutex);
    switch (orig) {
    case 0 :
        file->f_pos = offset;
        ret = file->f_pos;
        break;
    case 1 :
        file->f_pos += offset;
        ret = file->f_pos;
        break;
    default :
        ret = -EINVAL;
    }
    mutex_unlock (& inode -> i_mutex);
    return ret;
}

{
case 0 :
    file->f_pos = offset;
    ret = file->f_pos;
    break;
case 1 :
    file->f_pos += offset;
    ret = file->f_pos;
    break;
default :
    ret = -EINVAL;
}

{
    u32 __user *tmp = (u32 __user *) buf;
    u32 data [2];
    u32 reg = *ppos;
    int cpu = iminor (file->f_path.dentry->d_inode);
    int err;
    if (count % 8)
        return -EINVAL;
    for (; count; count -= 8) {
        err = rdmsr_safe_on_cpu (cpu, reg, &data[0], &data[1]);
        if (err)
            return -EIO;
        if (copy_to_user (tmp, &data, 8))
            return -EFAULT;
        tmp += 2;
    }
    return ((char __user *) tmp) - buf;
}

{
    err = rdmsr_safe_on_cpu (cpu, reg, &data[0], &data[1]);
    if (err)
        return -EIO;
    if (copy_to_user (tmp, &data, 8))
        return -EFAULT;
    tmp += 2;
}

{
    const u32 __user *tmp = (const u32 __user *) buf;
    u32 data [2];
    u32 reg = *ppos;
    int cpu = iminor (file->f_path.dentry->d_inode);
    int err;
    if (count % 8)
        return -EINVAL;
    for (; count; count -= 8) {
        if (copy_from_user (&data, tmp, 8))
            return -EFAULT;
        err = wrmsr_safe_on_cpu (cpu, reg, data[0], data[1]);
        if (err)
            return -EIO;
        tmp += 2;
    }
    return ((char __user *) tmp) - buf;
}

{
    if (copy_from_user (&data, tmp, 8))
        return -EFAULT;
    err = wrmsr_safe_on_cpu (cpu, reg, data[0], data[1]);
    if (err)
        return -EIO;
    tmp += 2;
}

{
    unsigned int cpu = iminor (file->f_path.dentry->d_inode);
    struct cpuinfo_x86 *c = &cpu_data (cpu);
    if (cpu >= NR_CPUS || !cpu_online (cpu))
        return -ENXIO;
    if (!cpu_has (c, X86_FEATURE_MSR))
        return -EIO;
    return 0;
}

{
    struct device *dev;
    dev = device_create (msr_class, NULL, MKDEV (MSR_MAJOR, cpu), "msr%d", cpu);
    return IS_ERR (dev) ? PTR_ERR (dev) : 0;
}

{
    device_destroy (msr_class, MKDEV (MSR_MAJOR, cpu));
}

{
    unsigned int cpu = (unsigned long) hcpu;
    int err = 0;
    switch (action) {
    case CPU_UP_PREPARE :
        err = msr_device_create (cpu);
        break;
    case CPU_UP_CANCELED :
    case CPU_UP_CANCELED_FROZEN :
    case CPU_DEAD :
        msr_device_destroy (cpu);
        break;
    }
    return err ? NOTIFY_BAD : NOTIFY_OK;
}

{
case CPU_UP_PREPARE :
    err = msr_device_create (cpu);
    break;
case CPU_UP_CANCELED :
case CPU_UP_CANCELED_FROZEN :
case CPU_DEAD :
    msr_device_destroy (cpu);
    break;
}

{
    int i, err = 0;
    i = 0;
    if (register_chrdev (MSR_MAJOR, "cpu/msr", &msr_fops)) {
        printk (KERN_ERR "msr: unable to get major %d for msr\n", MSR_MAJOR);
        err = -EBUSY;
        goto out;
    }
    msr_class = class_create (THIS_MODULE, "msr");
    if (IS_ERR (msr_class)) {
        err = PTR_ERR (msr_class);
        goto out_chrdev;
    }

    for_each_online_cpu (i) {
        err = msr_device_create (i);
        if (err != 0)
            goto out_class;
    }

    register_hotcpu_notifier (& msr_class_cpu_notifier);
    err = 0;
    goto out;
out_class :
    i = 0;
    for_each_online_cpu (i)
    msr_device_destroy (i);
    class_destroy (msr_class);
out_chrdev :
    unregister_chrdev (MSR_MAJOR, "cpu/msr");
out :
    return err;
}

{
    printk (KERN_ERR "msr: unable to get major %d for msr\n", MSR_MAJOR);
    err = -EBUSY;
    goto out;
}

{
    err = PTR_ERR (msr_class);
    goto out_chrdev;
}

{
    err = msr_device_create (i);
    if (err != 0)
        goto out_class;
}

{
    int cpu = 0;
    for_each_online_cpu (cpu)
    msr_device_destroy (cpu);
    class_destroy (msr_class);
    unregister_chrdev (MSR_MAJOR, "cpu/msr");
    unregister_hotcpu_notifier (& msr_class_cpu_notifier);
}

{
    if (nmi_watchdog != NMI_DEFAULT)
        return;
    nmi_watchdog = NMI_NONE;
}

{
    int *prev_nmi_count;
    int cpu;
    if ((nmi_watchdog == NMI_NONE) || (nmi_watchdog == NMI_DISABLED))
        return 0;
    if (!atomic_read (&nmi_active))
        return 0;
    prev_nmi_count = kmalloc (NR_CPUS * sizeof (int), GFP_KERNEL);
    if (!prev_nmi_count)
        return -1;
    printk (KERN_INFO "Testing NMI watchdog ... ");
    for (cpu = 0; cpu < NR_CPUS; cpu++)
        prev_nmi_count[cpu] = cpu_pda (cpu)->__nmi_count;
    local_irq_enable ();
    mdelay ((20 * 1000) / nmi_hz);

    for_each_online_cpu (cpu) {
        if (!per_cpu (wd_enabled, cpu))
            continue;
        if (cpu_pda (cpu)->__nmi_count - prev_nmi_count[cpu] <= 5) {
            printk (KERN_WARNING "WARNING: CPU#%d: NMI " "appears to be stuck (%d->%d)!\n", cpu, prev_nmi_count [cpu], cpu_pda (cpu) -> __nmi_count);
            per_cpu (wd_enabled, cpu) = 0;
            atomic_dec (& nmi_active);
        }
    }

    endflag = 1;
    if (!atomic_read (&nmi_active)) {
        kfree (prev_nmi_count);
        atomic_set (& nmi_active, - 1);
        return -1;
    }
    printk ("OK.\n");
    if (nmi_watchdog == NMI_LOCAL_APIC)
        nmi_hz = lapic_adjust_nmi_hz (1);
    kfree (prev_nmi_count);
    return 0;
}

{
    if (!per_cpu (wd_enabled, cpu))
        continue;
    if (cpu_pda (cpu)->__nmi_count - prev_nmi_count[cpu] <= 5) {
        printk (KERN_WARNING "WARNING: CPU#%d: NMI " "appears to be stuck (%d->%d)!\n", cpu, prev_nmi_count [cpu], cpu_pda (cpu) -> __nmi_count);
        per_cpu (wd_enabled, cpu) = 0;
        atomic_dec (& nmi_active);
    }
}

{
    printk (KERN_WARNING "WARNING: CPU#%d: NMI " "appears to be stuck (%d->%d)!\n", cpu, prev_nmi_count [cpu], cpu_pda (cpu) -> __nmi_count);
    per_cpu (wd_enabled, cpu) = 0;
    atomic_dec (& nmi_active);
}

{
    kfree (prev_nmi_count);
    atomic_set (& nmi_active, - 1);
    return -1;
}

{
    int nmi;
    if (!strncmp (str, "panic", 5)) {
        panic_on_timeout = 1;
        str = strchr (str, ',');
        if (!str)
            return 1;
        ++str;
    }
    get_option (& str, & nmi);
    if ((nmi >= NMI_INVALID) || (nmi < NMI_NONE))
        return 0;
    nmi_watchdog = nmi;
    return 1;
}

{
    panic_on_timeout = 1;
    str = strchr (str, ',');
    if (!str)
        return 1;
    ++str;
}

{
    apic_write (APIC_LVT0, APIC_DM_NMI);
}

{
    if (atomic_read (&nmi_active) && nmi_watchdog == NMI_IO_APIC)
        on_each_cpu (__acpi_nmi_enable, NULL, 0, 1);
}

{
    apic_write (APIC_LVT0, APIC_DM_NMI | APIC_LVT_MASKED);
}

{
    if (atomic_read (&nmi_active) && nmi_watchdog == NMI_IO_APIC)
        on_each_cpu (__acpi_nmi_disable, NULL, 0, 1);
}

{
    if (__get_cpu_var (wd_enabled))
        return;
    if ((smp_processor_id () != 0) && (atomic_read (&nmi_active) <= 0))
        return;
    switch (nmi_watchdog) {
    case NMI_LOCAL_APIC :
        __get_cpu_var (wd_enabled) = 1;
        if (lapic_watchdog_init (nmi_hz) < 0) {
            __get_cpu_var (wd_enabled) = 0;
            return;
        }
    case NMI_IO_APIC :
        __get_cpu_var (wd_enabled) = 1;
        atomic_inc (& nmi_active);
    }
}

{
case NMI_LOCAL_APIC :
    __get_cpu_var (wd_enabled) = 1;
    if (lapic_watchdog_init (nmi_hz) < 0) {
        __get_cpu_var (wd_enabled) = 0;
        return;
    }
case NMI_IO_APIC :
    __get_cpu_var (wd_enabled) = 1;
    atomic_inc (& nmi_active);
}

{
    __get_cpu_var (wd_enabled) = 0;
    return;
}

{
    if ((nmi_watchdog != NMI_LOCAL_APIC) && (nmi_watchdog != NMI_IO_APIC))
        return;
    if (__get_cpu_var (wd_enabled) == 0)
        return;
    if (nmi_watchdog == NMI_LOCAL_APIC)
        lapic_watchdog_stop ();
    __get_cpu_var (wd_enabled) = 0;
    atomic_dec (& nmi_active);
}

{
    if (nmi_watchdog > 0) {
        unsigned cpu;

        for_each_present_cpu (cpu) {
            if (per_cpu (nmi_touch, cpu) != 1)
                per_cpu (nmi_touch, cpu) = 1;
        }

    }
    touch_softlockup_watchdog ();
}

{
    unsigned cpu;

    for_each_present_cpu (cpu) {
        if (per_cpu (nmi_touch, cpu) != 1)
            per_cpu (nmi_touch, cpu) = 1;
    }

}

{
    if (per_cpu (nmi_touch, cpu) != 1)
        per_cpu (nmi_touch, cpu) = 1;
}

{
    int sum;
    int touched = 0;
    int cpu = smp_processor_id ();
    int rc = 0;
    if (notify_die (DIE_NMI, "nmi", regs, reason, 2, SIGINT) == NOTIFY_STOP) {
        rc = 1;
        touched = 1;
    }
    sum = read_pda (apic_timer_irqs) + read_pda (irq0_irqs);
    if (__get_cpu_var (nmi_touch)) {
        __get_cpu_var (nmi_touch) = 0;
        touched = 1;
    }
    if (cpu_isset (cpu, backtrace_mask)) {
        static DEFINE_SPINLOCK (lock);
        spin_lock (& lock);
        printk ("NMI backtrace for cpu %d\n", cpu);
        dump_stack ();
        spin_unlock (& lock);
        cpu_clear (cpu, backtrace_mask);
    }
    if (!touched && __get_cpu_var (last_irq_sum) == sum) {
        local_inc (& __get_cpu_var (alert_counter));
        if (local_read (&__get_cpu_var (alert_counter)) == 5 * nmi_hz)
            die_nmi ("NMI Watchdog detected LOCKUP on CPU %d\n", regs, panic_on_timeout);
    }
    else {
        __get_cpu_var (last_irq_sum) = sum;
        local_set (& __get_cpu_var (alert_counter), 0);
    }
    if (!__get_cpu_var (wd_enabled))
        return rc;
    switch (nmi_watchdog) {
    case NMI_LOCAL_APIC :
        rc |= lapic_wd_event (nmi_hz);
        break;
    case NMI_IO_APIC :
        rc = 1;
        break;
    }
    return rc;
}

{
    rc = 1;
    touched = 1;
}

{
    __get_cpu_var (nmi_touch) = 0;
    touched = 1;
}

{
    static DEFINE_SPINLOCK (lock);
    spin_lock (& lock);
    printk ("NMI backtrace for cpu %d\n", cpu);
    dump_stack ();
    spin_unlock (& lock);
    cpu_clear (cpu, backtrace_mask);
}

{
    local_inc (& __get_cpu_var (alert_counter));
    if (local_read (&__get_cpu_var (alert_counter)) == 5 * nmi_hz)
        die_nmi ("NMI Watchdog detected LOCKUP on CPU %d\n", regs, panic_on_timeout);
}

{
    __get_cpu_var (last_irq_sum) = sum;
    local_set (& __get_cpu_var (alert_counter), 0);
}

{
case NMI_LOCAL_APIC :
    rc |= lapic_wd_event (nmi_hz);
    break;
case NMI_IO_APIC :
    rc = 1;
    break;
}

{
    nmi_enter ();
    add_pda (__nmi_count, 1);
    if (!ignore_nmis)
        default_do_nmi (regs);
    nmi_exit ();
}

{
    acpi_nmi_disable ();
    ignore_nmis++;
}

{
    ignore_nmis--;
    acpi_nmi_enable ();
}

