case PCI_CLASS_BRIDGE_HOST :
case PCI_CLASS_BRIDGE_OTHER :
    cap = find_cap (num, slot, func, PCI_CAP_ID_AGP);
    if (!cap)
        break;
    *valid_agp = 1;
    return read_agp (num, slot, func, cap, order);
}

{
    if (!p)
        return -EINVAL;
    if (!strncmp (p, "off", 3))
        gart_fix_e820 = 0;
    else if (!strncmp (p, "on", 2))
        gart_fix_e820 = 1;
    return 0;
}

{
    int fix, num;
    u32 ctl;
    u32 aper_size = 0, aper_order = 0, last_aper_order = 0;
    u64 aper_base = 0, last_aper_base = 0;
    int aper_enabled = 0, last_aper_enabled = 0;
    if (!early_pci_allowed ())
        return;
    fix = 0;
    for (num = 24; num < 32; num++) {
        if (!early_is_k8_nb (read_pci_config (0, num, 3, 0x00)))
            continue;
        ctl = read_pci_config (0, num, 3, 0x90);
        aper_enabled = ctl & 1;
        aper_order = (ctl >> 1) & 7;
        aper_size = (32 * 1024 * 1024) << aper_order;
        aper_base = read_pci_config (0, num, 3, 0x94) & 0x7fff;
        aper_base <<= 25;
        if ((last_aper_order && aper_order != last_aper_order) || (last_aper_base && aper_base != last_aper_base) || (last_aper_enabled && aper_enabled != last_aper_enabled)) {
            fix = 1;
            break;
        }
        last_aper_order = aper_order;
        last_aper_base = aper_base;
        last_aper_enabled = aper_enabled;
    }
    if (!fix && !aper_enabled)
        return;
    if (!aper_base || !aper_size || aper_base + aper_size > 0x100000000UL)
        fix = 1;
    if (gart_fix_e820 && !fix && aper_enabled) {
        if (e820_any_mapped (aper_base, aper_base +aper_size, E820_RAM)) {
            printk (KERN_INFO "update e820 for GART\n");
            add_memory_region (aper_base, aper_size, E820_RESERVED);
            update_e820 ();
        }
        return;
    }
    for (num = 24; num < 32; num++) {
        if (!early_is_k8_nb (read_pci_config (0, num, 3, 0x00)))
            continue;
        ctl = read_pci_config (0, num, 3, 0x90);
        ctl &= ~1;
        write_pci_config (0, num, 3, 0x90, ctl);
    }
}

{
    if (!early_is_k8_nb (read_pci_config (0, num, 3, 0x00)))
        continue;
    ctl = read_pci_config (0, num, 3, 0x90);
    aper_enabled = ctl & 1;
    aper_order = (ctl >> 1) & 7;
    aper_size = (32 * 1024 * 1024) << aper_order;
    aper_base = read_pci_config (0, num, 3, 0x94) & 0x7fff;
    aper_base <<= 25;
    if ((last_aper_order && aper_order != last_aper_order) || (last_aper_base && aper_base != last_aper_base) || (last_aper_enabled && aper_enabled != last_aper_enabled)) {
        fix = 1;
        break;
    }
    last_aper_order = aper_order;
    last_aper_base = aper_base;
    last_aper_enabled = aper_enabled;
}

{
    fix = 1;
    break;
}

{
    if (e820_any_mapped (aper_base, aper_base +aper_size, E820_RAM)) {
        printk (KERN_INFO "update e820 for GART\n");
        add_memory_region (aper_base, aper_size, E820_RESERVED);
        update_e820 ();
    }
    return;
}

{
    printk (KERN_INFO "update e820 for GART\n");
    add_memory_region (aper_base, aper_size, E820_RESERVED);
    update_e820 ();
}

{
    if (!early_is_k8_nb (read_pci_config (0, num, 3, 0x00)))
        continue;
    ctl = read_pci_config (0, num, 3, 0x90);
    ctl &= ~1;
    write_pci_config (0, num, 3, 0x90, ctl);
}

{
    u32 aper_size, aper_alloc = 0, aper_order = 0, last_aper_order = 0;
    u64 aper_base, last_aper_base = 0;
    int fix, num, valid_agp = 0;
    int node;
    if (gart_iommu_aperture_disabled || !fix_aperture || !early_pci_allowed ())
        return;
    printk (KERN_INFO "Checking aperture...\n");
    fix = 0;
    node = 0;
    for (num = 24; num < 32; num++) {
        if (!early_is_k8_nb (read_pci_config (0, num, 3, 0x00)))
            continue;
        iommu_detected = 1;
        gart_iommu_aperture = 1;
        aper_order = (read_pci_config (0, num, 3, 0x90) >> 1) & 7;
        aper_size = (32 * 1024 * 1024) << aper_order;
        aper_base = read_pci_config (0, num, 3, 0x94) & 0x7fff;
        aper_base <<= 25;
        printk (KERN_INFO "Node %d: aperture @ %Lx size %u MB\n", node, aper_base, aper_size >> 20);
        node++;
        if (!aperture_valid (aper_base, aper_size)) {
            fix = 1;
            break;
        }
        if ((last_aper_order && aper_order != last_aper_order) || (last_aper_base && aper_base != last_aper_base)) {
            fix = 1;
            break;
        }
        last_aper_order = aper_order;
        last_aper_base = aper_base;
    }
    if (!fix && !fallback_aper_force) {
        if (last_aper_base) {
            unsigned long n = (32 * 1024 * 1024) << last_aper_order;
            insert_aperture_resource ((u32) last_aper_base, n);
        }
        return;
    }
    if (!fallback_aper_force)
        aper_alloc = search_agp_bridge (&aper_order, &valid_agp);
    if (aper_alloc) {
    }
    else if (swiotlb && !valid_agp) {
    }
    else if ((!no_iommu && end_pfn > MAX_DMA32_PFN) || force_iommu || valid_agp || fallback_aper_force) {
        printk (KERN_ERR "Your BIOS doesn't leave a aperture memory hole\n");
        printk (KERN_ERR "Please enable the IOMMU option in the BIOS setup\n");
        printk (KERN_ERR "This costs you %d MB of RAM\n", 32 << fallback_aper_order);
        aper_order = fallback_aper_order;
        aper_alloc = allocate_aperture ();
        if (!aper_alloc) {
            panic ("Not enough memory for aperture");
        }
    }
    else {
        return;
    }
    for (num = 24; num < 32; num++) {
        if (!early_is_k8_nb (read_pci_config (0, num, 3, 0x00)))
            continue;
        write_pci_config (0, num, 3, 0x90, aper_order << 1);
        write_pci_config (0, num, 3, 0x94, aper_alloc >> 25);
    }
}

{
    if (!early_is_k8_nb (read_pci_config (0, num, 3, 0x00)))
        continue;
    iommu_detected = 1;
    gart_iommu_aperture = 1;
    aper_order = (read_pci_config (0, num, 3, 0x90) >> 1) & 7;
    aper_size = (32 * 1024 * 1024) << aper_order;
    aper_base = read_pci_config (0, num, 3, 0x94) & 0x7fff;
    aper_base <<= 25;
    printk (KERN_INFO "Node %d: aperture @ %Lx size %u MB\n", node, aper_base, aper_size >> 20);
    node++;
    if (!aperture_valid (aper_base, aper_size)) {
        fix = 1;
        break;
    }
    if ((last_aper_order && aper_order != last_aper_order) || (last_aper_base && aper_base != last_aper_base)) {
        fix = 1;
        break;
    }
    last_aper_order = aper_order;
    last_aper_base = aper_base;
}

{
    fix = 1;
    break;
}

{
    fix = 1;
    break;
}

{
    if (last_aper_base) {
        unsigned long n = (32 * 1024 * 1024) << last_aper_order;
        insert_aperture_resource ((u32) last_aper_base, n);
    }
    return;
}

{
    unsigned long n = (32 * 1024 * 1024) << last_aper_order;
    insert_aperture_resource ((u32) last_aper_base, n);
}

{
    printk (KERN_ERR "Your BIOS doesn't leave a aperture memory hole\n");
    printk (KERN_ERR "Please enable the IOMMU option in the BIOS setup\n");
    printk (KERN_ERR "This costs you %d MB of RAM\n", 32 << fallback_aper_order);
    aper_order = fallback_aper_order;
    aper_alloc = allocate_aperture ();
    if (!aper_alloc) {
        panic ("Not enough memory for aperture");
    }
}

{
    panic ("Not enough memory for aperture");
}

{
    return;
}

{
    if (!early_is_k8_nb (read_pci_config (0, num, 3, 0x00)))
        continue;
    write_pci_config (0, num, 3, 0x90, aper_order << 1);
    write_pci_config (0, num, 3, 0x94, aper_alloc >> 25);
}

{
    if (read_pda (mmu_state) == TLBSTATE_OK)
        BUG ();
    cpu_clear (cpu, read_pda (active_mm) -> cpu_vm_mask);
    load_cr3 (swapper_pg_dir);
}

{
    int cpu;
    int sender;
    union smp_flush_state *f;
    cpu = smp_processor_id ();
    sender = ~regs->orig_ax - INVALIDATE_TLB_VECTOR_START;
    f = &per_cpu (flush_state, sender);
    if (!cpu_isset (cpu, f->flush_cpumask))
        goto out;
    if (f->flush_mm == read_pda (active_mm)) {
        if (read_pda (mmu_state) == TLBSTATE_OK) {
            if (f->flush_va == TLB_FLUSH_ALL)
                local_flush_tlb ();
            else
                __flush_tlb_one (f->flush_va);
        }
        else
            leave_mm (cpu);
    }
out :
    ack_APIC_irq ();
    cpu_clear (cpu, f -> flush_cpumask);
    add_pda (irq_tlb_count, 1);
}

{
    if (read_pda (mmu_state) == TLBSTATE_OK) {
        if (f->flush_va == TLB_FLUSH_ALL)
            local_flush_tlb ();
        else
            __flush_tlb_one (f->flush_va);
    }
    else
        leave_mm (cpu);
}

{
    if (f->flush_va == TLB_FLUSH_ALL)
        local_flush_tlb ();
    else
        __flush_tlb_one (f->flush_va);
}

{
    int sender;
    union smp_flush_state *f;
    cpumask_t cpumask = *cpumaskp;
    sender = smp_processor_id () % NUM_INVALIDATE_TLB_VECTORS;
    f = &per_cpu (flush_state, sender);
    spin_lock (& f -> tlbstate_lock);
    f->flush_mm = mm;
    f->flush_va = va;
    cpus_or (f -> flush_cpumask, cpumask, f -> flush_cpumask);
    send_IPI_mask (cpumask, INVALIDATE_TLB_VECTOR_START + sender);
    while (!cpus_empty (f->flush_cpumask))
        cpu_relax ();
    f->flush_mm = NULL;
    f->flush_va = 0;
    spin_unlock (& f -> tlbstate_lock);
}

{
    int i;
    for_each_possible_cpu (i)
    spin_lock_init (& per_cpu (flush_state, i).tlbstate_lock);
    return 0;
}

{
    struct mm_struct *mm = current->mm;
    cpumask_t cpu_mask;
    preempt_disable ();
    cpu_mask = mm->cpu_vm_mask;
    cpu_clear (smp_processor_id (), cpu_mask);
    local_flush_tlb ();
    if (!cpus_empty (cpu_mask))
        flush_tlb_others (cpu_mask, mm, TLB_FLUSH_ALL);
    preempt_enable ();
}

{
    cpumask_t cpu_mask;
    preempt_disable ();
    cpu_mask = mm->cpu_vm_mask;
    cpu_clear (smp_processor_id (), cpu_mask);
    if (current->active_mm == mm) {
        if (current->mm)
            local_flush_tlb ();
        else
            leave_mm (smp_processor_id ());
    }
    if (!cpus_empty (cpu_mask))
        flush_tlb_others (cpu_mask, mm, TLB_FLUSH_ALL);
    preempt_enable ();
}

{
    if (current->mm)
        local_flush_tlb ();
    else
        leave_mm (smp_processor_id ());
}

{
    struct mm_struct *mm = vma->vm_mm;
    cpumask_t cpu_mask;
    preempt_disable ();
    cpu_mask = mm->cpu_vm_mask;
    cpu_clear (smp_processor_id (), cpu_mask);
    if (current->active_mm == mm) {
        if (current->mm)
            __flush_tlb_one (va);
        else
            leave_mm (smp_processor_id ());
    }
    if (!cpus_empty (cpu_mask))
        flush_tlb_others (cpu_mask, mm, va);
    preempt_enable ();
}

{
    if (current->mm)
        __flush_tlb_one (va);
    else
        leave_mm (smp_processor_id ());
}

{
    unsigned long cpu = smp_processor_id ();
    __flush_tlb_all ();
    if (read_pda (mmu_state) == TLBSTATE_LAZY)
        leave_mm (cpu);
}

{
    on_each_cpu (do_flush_tlb_all, NULL, 1, 1);
}

{
    unsigned long flags;
    write_seqlock_irqsave (& vsyscall_gtod_data.lock, flags);
    vsyscall_gtod_data.sys_tz = sys_tz;
    write_sequnlock_irqrestore (& vsyscall_gtod_data.lock, flags);
}

{
    unsigned long flags;
    write_seqlock_irqsave (& vsyscall_gtod_data.lock, flags);
    vsyscall_gtod_data.clock.vread = clock->vread;
    vsyscall_gtod_data.clock.cycle_last = clock->cycle_last;
    vsyscall_gtod_data.clock.mask = clock->mask;
    vsyscall_gtod_data.clock.mult = clock->mult;
    vsyscall_gtod_data.clock.shift = clock->shift;
    vsyscall_gtod_data.wall_time_sec = wall_time->tv_sec;
    vsyscall_gtod_data.wall_time_nsec = wall_time->tv_nsec;
    vsyscall_gtod_data.wall_to_monotonic = wall_to_monotonic;
    write_sequnlock_irqrestore (& vsyscall_gtod_data.lock, flags);
}

{
    *tz = __vsyscall_gtod_data.sys_tz;
}

{
    int ret;
    asm volatile ("syscall"
        : "=a" (ret)
        : "0" (__NR_gettimeofday), "D" (tv), "S" (tz)
        : __syscall_clobber
    ) return ret;
}

{
    long secs;
    asm volatile ("syscall"
        : "=a" (secs)
        : "0" (__NR_time), "D" (t)
        : __syscall_clobber
    ) return secs;
}

{
    cycle_t now, base, mask, cycle_delta;
    unsigned seq;
    unsigned long mult, shift, nsec;
    cycle_t (*vread) (void);
    do {
        seq = read_seqbegin (&__vsyscall_gtod_data.lock);
        vread = __vsyscall_gtod_data.clock.vread;
        if (unlikely (!__vsyscall_gtod_data.sysctl_enabled || !vread)) {
            gettimeofday (tv, NULL);
            return;
        }
        now = vread ();
        base = __vsyscall_gtod_data.clock.cycle_last;
        mask = __vsyscall_gtod_data.clock.mask;
        mult = __vsyscall_gtod_data.clock.mult;
        shift = __vsyscall_gtod_data.clock.shift;
        tv->tv_sec = __vsyscall_gtod_data.wall_time_sec;
        nsec = __vsyscall_gtod_data.wall_time_nsec;
    }
    while (read_seqretry (&__vsyscall_gtod_data.lock, seq));
    cycle_delta = (now - base) & mask;
    nsec += (cycle_delta * mult) >> shift;
    while (nsec >= NSEC_PER_SEC) {
        tv->tv_sec += 1;
        nsec -= NSEC_PER_SEC;
    }
    tv->tv_usec = nsec / NSEC_PER_USEC;
}

{
    seq = read_seqbegin (&__vsyscall_gtod_data.lock);
    vread = __vsyscall_gtod_data.clock.vread;
    if (unlikely (!__vsyscall_gtod_data.sysctl_enabled || !vread)) {
        gettimeofday (tv, NULL);
        return;
    }
    now = vread ();
    base = __vsyscall_gtod_data.clock.cycle_last;
    mask = __vsyscall_gtod_data.clock.mask;
    mult = __vsyscall_gtod_data.clock.mult;
    shift = __vsyscall_gtod_data.clock.shift;
    tv->tv_sec = __vsyscall_gtod_data.wall_time_sec;
    nsec = __vsyscall_gtod_data.wall_time_nsec;
}

{
    gettimeofday (tv, NULL);
    return;
}

{
    tv->tv_sec += 1;
    nsec -= NSEC_PER_SEC;
}

{
    if (tv)
        do_vgettimeofday (tv);
    if (tz)
        do_get_tz (tz);
    return 0;
}

{
    struct timeval tv;
    time_t result;
    if (unlikely (!__vsyscall_gtod_data.sysctl_enabled))
        return time_syscall (t);
    vgettimeofday (& tv, NULL);
    result = tv.tv_sec;
    if (t)
        *t = result;
    return result;
}

{
    unsigned int p;
    unsigned long j = 0;
    if (tcache && tcache->blob[0] == (j = __jiffies)) {
        p = tcache->blob[1];
    }
    else if (__vgetcpu_mode == VGETCPU_RDTSCP) {
        native_read_tscp (& p);
    }
    else {
        asm ("lsl %1,%0"
            : "=r" (p)
            : "r" (__PER_CPU_SEG)
        )}
    if (tcache) {
        tcache->blob[0] = j;
        tcache->blob[1] = p;
    }
    if (cpu)
        *cpu = p & 0xfff;
    if (node)
        *node = p >> 12;
    return 0;
}

{
    p = tcache->blob[1];
}

{
    native_read_tscp (& p);
}

{
    asm ("lsl %1,%0"
        : "=r" (p)
        : "r" (__PER_CPU_SEG)
    )}

{
    tcache->blob[0] = j;
    tcache->blob[1] = p;
}

{
    return -ENOSYS;
}

{
    unsigned long *d;
    unsigned long node = 0;
    if (cpu_has (&cpu_data (cpu), X86_FEATURE_RDTSCP))
        write_rdtscp_aux ((node << 12) | cpu);
    d = (unsigned long *) (get_cpu_gdt_table (cpu) + GDT_ENTRY_PER_CPU);
    *d = 0x0f40000000000ULL;
    *d |= cpu;
    *d |= (node & 0xf) << 12;
    *d |= (node >> 4) << 48;
}

{
    vsyscall_set_cpu (raw_smp_processor_id ());
}

{
    long cpu = (long) arg;
    if (action == CPU_ONLINE || action == CPU_ONLINE_FROZEN)
        smp_call_function_single (cpu, cpu_vsyscall_init, NULL, 0, 1);
    return NOTIFY_DONE;
}

{
    extern char __vsyscall_0;
    unsigned long physaddr_page0 = __pa_symbol (&__vsyscall_0);
    __set_fixmap (VSYSCALL_FIRST_PAGE, physaddr_page0, PAGE_KERNEL_VSYSCALL);
}

{
    BUG_ON (((unsigned long) & vgettimeofday != VSYSCALL_ADDR (__NR_vgettimeofday)));
    BUG_ON ((unsigned long) & vtime != VSYSCALL_ADDR (__NR_vtime));
    BUG_ON ((VSYSCALL_ADDR (0) != __fix_to_virt (VSYSCALL_FIRST_PAGE)));
    BUG_ON ((unsigned long) & vgetcpu != VSYSCALL_ADDR (__NR_vgetcpu));
    on_each_cpu (cpu_vsyscall_init, NULL, 0, 1);
    hotcpu_notifier (cpu_vsyscall_notifier, 0);
    return 0;
}

{
    int sum = 0;
    while (len--)
        sum += *mp++;
    return sum & 0xFF;
}

{
    int apicid;
    char *bootup_cpu = "";
    if (!(m->mpc_cpuflag & CPU_ENABLED)) {
        disabled_cpus++;
        return;
    }
    apicid = m->mpc_apicid;
    if (m->mpc_cpuflag & CPU_BOOTPROCESSOR) {
        bootup_cpu = " (Bootup-CPU)";
        boot_cpu_physical_apicid = m->mpc_apicid;
    }
    printk (KERN_INFO "Processor #%d%s\n", m -> mpc_apicid, bootup_cpu);
    generic_processor_info (apicid, m -> mpc_apicver);
}

{
    disabled_cpus++;
    return;
}

{
    bootup_cpu = " (Bootup-CPU)";
    boot_cpu_physical_apicid = m->mpc_apicid;
}

{
    char str [7];
    memcpy (str, m -> mpc_bustype, 6);
    str[6] = 0;
    Dprintk ("Bus #%d is %s\n", m -> mpc_busid, str);
    if (strncmp (str, BUSTYPE_ISA, sizeof (BUSTYPE_ISA) - 1) == 0) {
        set_bit (m -> mpc_busid, mp_bus_not_pci);
    }
    else if (strncmp (str, BUSTYPE_PCI, sizeof (BUSTYPE_PCI) - 1) == 0) {
        clear_bit (m -> mpc_busid, mp_bus_not_pci);
        mp_bus_id_to_pci_bus[m->mpc_busid] = mp_current_pci_id;
        mp_current_pci_id++;
    }
    else
        printk (KERN_WARNING "Unknown bustype %s - ignoring\n", str);
}

{
    set_bit (m -> mpc_busid, mp_bus_not_pci);
}

{
    clear_bit (m -> mpc_busid, mp_bus_not_pci);
    mp_bus_id_to_pci_bus[m->mpc_busid] = mp_current_pci_id;
    mp_current_pci_id++;
}

{
    Dprintk ("Lint: type %d, pol %d, trig %d, bus %d," " IRQ %02x, APIC ID %x, APIC LINT %02x\n", m -> mpc_irqtype, m -> mpc_irqflag & 3, (m -> mpc_irqflag >> 2) & 3, m -> mpc_srcbusid, m -> mpc_srcbusirq, m -> mpc_destapic, m -> mpc_destapiclint);
}

{
    char str [16];
    char oem [10];
    int count = sizeof (*mpc);
    unsigned char *mpt = ((unsigned char *) mpc) + count;
    if (memcmp (mpc->mpc_signature, MPC_SIGNATURE, 4)) {
        printk (KERN_ERR "MPTABLE: bad signature [%c%c%c%c]!\n", mpc -> mpc_signature [0], mpc -> mpc_signature [1], mpc -> mpc_signature [2], mpc -> mpc_signature [3]);
        return 0;
    }
    if (mpf_checksum ((unsigned char *) mpc, mpc->mpc_length)) {
        printk (KERN_ERR "MPTABLE: checksum error!\n");
        return 0;
    }
    if (mpc->mpc_spec != 0x01 && mpc->mpc_spec != 0x04) {
        printk (KERN_ERR "MPTABLE: bad table version (%d)!!\n", mpc -> mpc_spec);
        return 0;
    }
    if (!mpc->mpc_lapic) {
        printk (KERN_ERR "MPTABLE: null local APIC address!\n");
        return 0;
    }
    memcpy (oem, mpc -> mpc_oem, 8);
    oem[8] = 0;
    printk (KERN_INFO "MPTABLE: OEM ID: %s ", oem);
    memcpy (str, mpc -> mpc_productid, 12);
    str[12] = 0;
    printk ("Product ID: %s ", str);
    printk (KERN_INFO "MPTABLE: Product ID: %s ", str);
    printk (KERN_INFO "MPTABLE: APIC at: 0x%X\n", mpc -> mpc_lapic);
    if (!acpi_lapic)
        mp_lapic_addr = mpc->mpc_lapic;
    if (early)
        return 1;
    while (count < mpc->mpc_length) {
        switch (*mpt) {
        case MP_PROCESSOR :
            {
                struct mpc_config_processor *m = (struct mpc_config_processor *) mpt;
                if (!acpi_lapic)
                    MP_processor_info (m);
                mpt += sizeof (*m);
                count += sizeof (*m);
                break;
            }
        case MP_BUS :
            {
                struct mpc_config_bus *m = (struct mpc_config_bus *) mpt;
                MP_bus_info (m);
                mpt += sizeof (*m);
                count += sizeof (*m);
                break;
            }
        case MP_IOAPIC :
            {
                mpt += sizeof (struct mpc_config_ioapic);
                count += sizeof (struct mpc_config_ioapic);
                break;
            }
        case MP_INTSRC :
            {
                mpt += sizeof (struct mpc_config_intsrc);
                count += sizeof (struct mpc_config_intsrc);
                break;
            }
        case MP_LINTSRC :
            {
                struct mpc_config_lintsrc *m = (struct mpc_config_lintsrc *) mpt;
                MP_lintsrc_info (m);
                mpt += sizeof (*m);
                count += sizeof (*m);
                break;
            }
        default :
            printk (KERN_ERR "Your mptable is wrong, contact your HW vendor!\n");
            printk (KERN_ERR "type %x\n", * mpt);
            print_hex_dump (KERN_ERR, "  ", DUMP_PREFIX_ADDRESS, 16, 1, mpc, mpc -> mpc_length, 1);
            count = mpc->mpc_length;
            break;
        }
    }
    setup_apic_routing ();
    if (!num_processors)
        printk (KERN_ERR "MPTABLE: no processors registered!\n");
    return num_processors;
}

{
    printk (KERN_ERR "MPTABLE: bad signature [%c%c%c%c]!\n", mpc -> mpc_signature [0], mpc -> mpc_signature [1], mpc -> mpc_signature [2], mpc -> mpc_signature [3]);
    return 0;
}

{
    printk (KERN_ERR "MPTABLE: checksum error!\n");
    return 0;
}

{
    printk (KERN_ERR "MPTABLE: bad table version (%d)!!\n", mpc -> mpc_spec);
    return 0;
}

