{
    struct intel_mp_floating *mpf = mpf_found;
    if (acpi_lapic && early)
        return;
    if (acpi_lapic && acpi_ioapic) {
        printk (KERN_INFO "Using ACPI (MADT) for SMP configuration " "information\n");
        return;
    }
    else if (acpi_lapic)
        printk (KERN_INFO "Using ACPI for processor (LAPIC) " "configuration information\n");
    printk (KERN_INFO "Intel MultiProcessor Specification v1.%d\n", mpf -> mpf_specification);
    if (mpf->mpf_feature1 != 0) {
        if (early) {
            mp_lapic_addr = APIC_DEFAULT_PHYS_BASE;
            return;
        }
        printk (KERN_INFO "Default MP configuration #%d\n", mpf -> mpf_feature1);
        construct_default_ISA_mptable (mpf -> mpf_feature1);
    }
    else if (mpf->mpf_physptr) {
        if (!smp_read_mpc (phys_to_virt (mpf->mpf_physptr), early)) {
            smp_found_config = 0;
            printk (KERN_ERR "BIOS bug, MP table errors detected!...\n");
            printk (KERN_ERR "... disabling SMP support. " "(tell your hw vendor)\n");
            return;
        }
        if (early)
            return;
    }
    else
        BUG ();
    if (!early)
        printk (KERN_INFO "Processors: %d\n", num_processors);
}

{
    printk (KERN_INFO "Using ACPI (MADT) for SMP configuration " "information\n");
    return;
}

{
    if (early) {
        mp_lapic_addr = APIC_DEFAULT_PHYS_BASE;
        return;
    }
    printk (KERN_INFO "Default MP configuration #%d\n", mpf -> mpf_feature1);
    construct_default_ISA_mptable (mpf -> mpf_feature1);
}

{
    mp_lapic_addr = APIC_DEFAULT_PHYS_BASE;
    return;
}

{
    if (!smp_read_mpc (phys_to_virt (mpf->mpf_physptr), early)) {
        smp_found_config = 0;
        printk (KERN_ERR "BIOS bug, MP table errors detected!...\n");
        printk (KERN_ERR "... disabling SMP support. " "(tell your hw vendor)\n");
        return;
    }
    if (early)
        return;
}

{
    smp_found_config = 0;
    printk (KERN_ERR "BIOS bug, MP table errors detected!...\n");
    printk (KERN_ERR "... disabling SMP support. " "(tell your hw vendor)\n");
    return;
}

{
    __get_smp_config (1);
}

{
    __get_smp_config (0);
}

{
    unsigned int *bp = phys_to_virt (base);
    struct intel_mp_floating *mpf;
    Dprintk ("Scan SMP from %p for %ld bytes.\n", bp, length);
    BUILD_BUG_ON (sizeof (* mpf) != 16);
    while (length > 0) {
        mpf = (struct intel_mp_floating *) bp;
        if ((*bp == SMP_MAGIC_IDENT) && (mpf->mpf_length == 1) && !mpf_checksum ((unsigned char *) bp, 16) && ((mpf->mpf_specification == 1) || (mpf->mpf_specification == 4))) {
            smp_found_config = 1;
            mpf_found = mpf;
            if (!reserve)
                return 1;
            reserve_bootmem_generic (virt_to_phys (mpf), PAGE_SIZE);
            if (mpf->mpf_physptr)
                reserve_bootmem_generic (mpf->mpf_physptr, PAGE_SIZE);
            return 1;
        }
        bp += 4;
        length -= 16;
    }
    return 0;
}

{
    mpf = (struct intel_mp_floating *) bp;
    if ((*bp == SMP_MAGIC_IDENT) && (mpf->mpf_length == 1) && !mpf_checksum ((unsigned char *) bp, 16) && ((mpf->mpf_specification == 1) || (mpf->mpf_specification == 4))) {
        smp_found_config = 1;
        mpf_found = mpf;
        if (!reserve)
            return 1;
        reserve_bootmem_generic (virt_to_phys (mpf), PAGE_SIZE);
        if (mpf->mpf_physptr)
            reserve_bootmem_generic (mpf->mpf_physptr, PAGE_SIZE);
        return 1;
    }
    bp += 4;
    length -= 16;
}

{
    smp_found_config = 1;
    mpf_found = mpf;
    if (!reserve)
        return 1;
    reserve_bootmem_generic (virt_to_phys (mpf), PAGE_SIZE);
    if (mpf->mpf_physptr)
        reserve_bootmem_generic (mpf->mpf_physptr, PAGE_SIZE);
    return 1;
}

{
    unsigned int address;
    if (smp_scan_config (0x0, 0x400, reserve) || smp_scan_config (639 * 0x400, 0x400, reserve) || smp_scan_config (0xF0000, 0x10000, reserve))
        return;
    address = get_bios_ebda ();
    if (address)
        smp_scan_config (address, 0x400, reserve);
}

{
    __find_smp_config (0);
}

{
    __find_smp_config (1);
}

{
    return do_sigaltstack (uss, uoss, regs->sp);
}

{
    unsigned int err = 0;
    current_thread_info ()->restart_block.fn = do_no_restart_syscall;
    COPY (di);
    COPY (si);
    COPY (bp);
    COPY (sp);
    COPY (bx);
    COPY (dx);
    COPY (cx);
    COPY (ip);
    COPY (r8);
    COPY (r9);
    COPY (r10);
    COPY (r11);
    COPY (r12);
    COPY (r13);
    COPY (r14);
    COPY (r15);
    {
        unsigned cs;
        err |= __get_user (cs, &sc->cs);
        regs->cs = cs | 3;
    }
    {
        unsigned int tmpflags;
        err |= __get_user (tmpflags, &sc->flags);
        regs->flags = (regs->flags & ~FIX_EFLAGS) | (tmpflags & FIX_EFLAGS);
        regs->orig_ax = -1;
    }
    {
        struct _fpstate __user *buf;
        err |= __get_user (buf, &sc->fpstate);
        if (buf) {
            if (!access_ok (VERIFY_READ, buf, sizeof (*buf)))
                goto badframe;
            err |= restore_i387 (buf);
        }
        else {
            struct task_struct *me = current;
            if (used_math ()) {
                clear_fpu (me);
                clear_used_math ();
            }
        }
    }
    err |= __get_user (*pax, &sc->ax);
    return err;
badframe :
    return 1;
}

{
    unsigned cs;
    err |= __get_user (cs, &sc->cs);
    regs->cs = cs | 3;
}

{
    unsigned int tmpflags;
    err |= __get_user (tmpflags, &sc->flags);
    regs->flags = (regs->flags & ~FIX_EFLAGS) | (tmpflags & FIX_EFLAGS);
    regs->orig_ax = -1;
}

{
    struct _fpstate __user *buf;
    err |= __get_user (buf, &sc->fpstate);
    if (buf) {
        if (!access_ok (VERIFY_READ, buf, sizeof (*buf)))
            goto badframe;
        err |= restore_i387 (buf);
    }
    else {
        struct task_struct *me = current;
        if (used_math ()) {
            clear_fpu (me);
            clear_used_math ();
        }
    }
}

{
    if (!access_ok (VERIFY_READ, buf, sizeof (*buf)))
        goto badframe;
    err |= restore_i387 (buf);
}

{
    struct task_struct *me = current;
    if (used_math ()) {
        clear_fpu (me);
        clear_used_math ();
    }
}

{
    clear_fpu (me);
    clear_used_math ();
}

{
    struct rt_sigframe __user *frame;
    sigset_t set;
    unsigned long ax;
    frame = (struct rt_sigframe __user *) (regs->sp - sizeof (long));
    if (!access_ok (VERIFY_READ, frame, sizeof (*frame)))
        goto badframe;
    if (__copy_from_user (&set, &frame->uc.uc_sigmask, sizeof (set)))
        goto badframe;
    sigdelsetmask (& set, ~ _BLOCKABLE);
    spin_lock_irq (& current -> sighand -> siglock);
    current->blocked = set;
    recalc_sigpending ();
    spin_unlock_irq (& current -> sighand -> siglock);
    if (restore_sigcontext (regs, &frame->uc.uc_mcontext, &ax))
        goto badframe;
    if (do_sigaltstack (&frame->uc.uc_stack, NULL, regs->sp) == -EFAULT)
        goto badframe;
    return ax;
badframe :
    signal_fault (regs, frame, "sigreturn");
    return 0;
}

{
    int err = 0;
    err |= __put_user (regs->cs, &sc->cs);
    err |= __put_user (0, &sc->gs);
    err |= __put_user (0, &sc->fs);
    err |= __put_user (regs->di, &sc->di);
    err |= __put_user (regs->si, &sc->si);
    err |= __put_user (regs->bp, &sc->bp);
    err |= __put_user (regs->sp, &sc->sp);
    err |= __put_user (regs->bx, &sc->bx);
    err |= __put_user (regs->dx, &sc->dx);
    err |= __put_user (regs->cx, &sc->cx);
    err |= __put_user (regs->ax, &sc->ax);
    err |= __put_user (regs->r8, &sc->r8);
    err |= __put_user (regs->r9, &sc->r9);
    err |= __put_user (regs->r10, &sc->r10);
    err |= __put_user (regs->r11, &sc->r11);
    err |= __put_user (regs->r12, &sc->r12);
    err |= __put_user (regs->r13, &sc->r13);
    err |= __put_user (regs->r14, &sc->r14);
    err |= __put_user (regs->r15, &sc->r15);
    err |= __put_user (me->thread.trap_no, &sc->trapno);
    err |= __put_user (me->thread.error_code, &sc->err);
    err |= __put_user (regs->ip, &sc->ip);
    err |= __put_user (regs->flags, &sc->flags);
    err |= __put_user (mask, &sc->oldmask);
    err |= __put_user (me->thread.cr2, &sc->cr2);
    return err;
}

{
    unsigned long sp;
    sp = regs->sp - 128;
    if (ka->sa.sa_flags & SA_ONSTACK) {
        if (sas_ss_flags (sp) == 0)
            sp = current->sas_ss_sp + current->sas_ss_size;
    }
    return (void __user *) round_down (sp -size, 16);
}

{
    if (sas_ss_flags (sp) == 0)
        sp = current->sas_ss_sp + current->sas_ss_size;
}

{
    struct rt_sigframe __user *frame;
    struct _fpstate __user *fp = NULL;
    int err = 0;
    struct task_struct *me = current;
    if (used_math ()) {
        fp = get_stack (ka, regs, sizeof (struct _fpstate));
        frame = (void __user *) round_down ((unsigned long) fp - sizeof (struct rt_sigframe), 16) - 8;
        if (!access_ok (VERIFY_WRITE, fp, sizeof (struct _fpstate)))
            goto give_sigsegv;
        if (save_i387 (fp) < 0)
            err |= -1;
    }
    else
        frame = get_stack (ka, regs, sizeof (struct rt_sigframe)) - 8;
    if (!access_ok (VERIFY_WRITE, frame, sizeof (*frame)))
        goto give_sigsegv;
    if (ka->sa.sa_flags & SA_SIGINFO) {
        err |= copy_siginfo_to_user (&frame->info, info);
        if (err)
            goto give_sigsegv;
    }
    err |= __put_user (0, &frame->uc.uc_flags);
    err |= __put_user (0, &frame->uc.uc_link);
    err |= __put_user (me->sas_ss_sp, &frame->uc.uc_stack.ss_sp);
    err |= __put_user (sas_ss_flags (regs->sp), &frame->uc.uc_stack.ss_flags);
    err |= __put_user (me->sas_ss_size, &frame->uc.uc_stack.ss_size);
    err |= setup_sigcontext (&frame->uc.uc_mcontext, regs, set->sig[0], me);
    err |= __put_user (fp, &frame->uc.uc_mcontext.fpstate);
    if (sizeof (*set) == 16) {
        __put_user (set -> sig [0], & frame -> uc.uc_sigmask.sig [0]);
        __put_user (set -> sig [1], & frame -> uc.uc_sigmask.sig [1]);
    }
    else
        err |= __copy_to_user (&frame->uc.uc_sigmask, set, sizeof (*set));
    if (ka->sa.sa_flags & SA_RESTORER) {
        err |= __put_user (ka->sa.sa_restorer, &frame->pretcode);
    }
    else {
        goto give_sigsegv;
    }
    if (err)
        goto give_sigsegv;
    regs->di = sig;
    regs->ax = 0;
    regs->si = (unsigned long) &frame->info;
    regs->dx = (unsigned long) &frame->uc;
    regs->ip = (unsigned long) ka->sa.sa_handler;
    regs->sp = (unsigned long) frame;
    regs->cs = __USER_CS;
    return 0;
give_sigsegv :
    force_sigsegv (sig, current);
    return -EFAULT;
}

{
    fp = get_stack (ka, regs, sizeof (struct _fpstate));
    frame = (void __user *) round_down ((unsigned long) fp - sizeof (struct rt_sigframe), 16) - 8;
    if (!access_ok (VERIFY_WRITE, fp, sizeof (struct _fpstate)))
        goto give_sigsegv;
    if (save_i387 (fp) < 0)
        err |= -1;
}

{
    err |= copy_siginfo_to_user (&frame->info, info);
    if (err)
        goto give_sigsegv;
}

{
    __put_user (set -> sig [0], & frame -> uc.uc_sigmask.sig [0]);
    __put_user (set -> sig [1], & frame -> uc.uc_sigmask.sig [1]);
}

{
    err |= __put_user (ka->sa.sa_restorer, &frame->pretcode);
}

{
    goto give_sigsegv;
}

{
    return regs->orig_ax;
}

{
    return regs->ax;
}

{
    int ret;
    if (current_syscall (regs) >= 0) {
        switch (current_syscall_ret (regs)) {
        case -ERESTART_RESTARTBLOCK :
        case -ERESTARTNOHAND :
            regs->ax = -EINTR;
            break;
        case -ERESTARTSYS :
            if (!(ka->sa.sa_flags & SA_RESTART)) {
                regs->ax = -EINTR;
                break;
            }
        case -ERESTARTNOINTR :
            regs->ax = regs->orig_ax;
            regs->ip -= 2;
            break;
        }
    }
    if (unlikely (regs->flags & X86_EFLAGS_TF) && likely (test_and_clear_thread_flag (TIF_FORCED_TF)))
        regs->flags &= ~X86_EFLAGS_TF;
    ret = setup_rt_frame (sig, ka, info, oldset, regs);
    if (ret == 0) {
        set_fs (USER_DS);
        regs->flags &= ~X86_EFLAGS_DF;
        regs->flags &= ~X86_EFLAGS_TF;
        if (test_thread_flag (TIF_SINGLESTEP))
            ptrace_notify (SIGTRAP);
        spin_lock_irq (& current -> sighand -> siglock);
        sigorsets (& current -> blocked, & current -> blocked, & ka -> sa.sa_mask);
        if (!(ka->sa.sa_flags & SA_NODEFER))
            sigaddset (&current->blocked, sig);
        recalc_sigpending ();
        spin_unlock_irq (& current -> sighand -> siglock);
    }
    return ret;
}

{
    switch (current_syscall_ret (regs)) {
    case -ERESTART_RESTARTBLOCK :
    case -ERESTARTNOHAND :
        regs->ax = -EINTR;
        break;
    case -ERESTARTSYS :
        if (!(ka->sa.sa_flags & SA_RESTART)) {
            regs->ax = -EINTR;
            break;
        }
    case -ERESTARTNOINTR :
        regs->ax = regs->orig_ax;
        regs->ip -= 2;
        break;
    }
}

{
case -ERESTART_RESTARTBLOCK :
case -ERESTARTNOHAND :
    regs->ax = -EINTR;
    break;
case -ERESTARTSYS :
    if (!(ka->sa.sa_flags & SA_RESTART)) {
        regs->ax = -EINTR;
        break;
    }
case -ERESTARTNOINTR :
    regs->ax = regs->orig_ax;
    regs->ip -= 2;
    break;
}

{
    regs->ax = -EINTR;
    break;
}

{
    set_fs (USER_DS);
    regs->flags &= ~X86_EFLAGS_DF;
    regs->flags &= ~X86_EFLAGS_TF;
    if (test_thread_flag (TIF_SINGLESTEP))
        ptrace_notify (SIGTRAP);
    spin_lock_irq (& current -> sighand -> siglock);
    sigorsets (& current -> blocked, & current -> blocked, & ka -> sa.sa_mask);
    if (!(ka->sa.sa_flags & SA_NODEFER))
        sigaddset (&current->blocked, sig);
    recalc_sigpending ();
    spin_unlock_irq (& current -> sighand -> siglock);
}

{
    struct k_sigaction ka;
    siginfo_t info;
    int signr;
    sigset_t *oldset;
    if (!user_mode (regs))
        return;
    if (current_thread_info ()->status & TS_RESTORE_SIGMASK)
        oldset = &current->saved_sigmask;
    else
        oldset = &current->blocked;
    signr = get_signal_to_deliver (&info, &ka, regs, NULL);
    if (signr > 0) {
        if (current->thread.debugreg7)
            set_debugreg (current->thread.debugreg7, 7);
        if (handle_signal (signr, &info, &ka, oldset, regs) == 0) {
            current_thread_info ()->status &= ~TS_RESTORE_SIGMASK;
        }
        return;
    }
    if (current_syscall (regs) >= 0) {
        switch (current_syscall_ret (regs)) {
        case -ERESTARTNOHAND :
        case -ERESTARTSYS :
        case -ERESTARTNOINTR :
            regs->ax = regs->orig_ax;
            regs->ip -= 2;
            break;
        case -ERESTART_RESTARTBLOCK :
            regs->ax = test_thread_flag (TIF_IA32) ? __NR_ia32_restart_syscall : __NR_restart_syscall;
            regs->ip -= 2;
            break;
        }
    }
    if (current_thread_info ()->status & TS_RESTORE_SIGMASK) {
        current_thread_info ()->status &= ~TS_RESTORE_SIGMASK;
        sigprocmask (SIG_SETMASK, & current -> saved_sigmask, NULL);
    }
}

{
    if (current->thread.debugreg7)
        set_debugreg (current->thread.debugreg7, 7);
    if (handle_signal (signr, &info, &ka, oldset, regs) == 0) {
        current_thread_info ()->status &= ~TS_RESTORE_SIGMASK;
    }
    return;
}

{
    current_thread_info ()->status &= ~TS_RESTORE_SIGMASK;
}

{
    switch (current_syscall_ret (regs)) {
    case -ERESTARTNOHAND :
    case -ERESTARTSYS :
    case -ERESTARTNOINTR :
        regs->ax = regs->orig_ax;
        regs->ip -= 2;
        break;
    case -ERESTART_RESTARTBLOCK :
        regs->ax = test_thread_flag (TIF_IA32) ? __NR_ia32_restart_syscall : __NR_restart_syscall;
        regs->ip -= 2;
        break;
    }
}

{
case -ERESTARTNOHAND :
case -ERESTARTSYS :
case -ERESTARTNOINTR :
    regs->ax = regs->orig_ax;
    regs->ip -= 2;
    break;
case -ERESTART_RESTARTBLOCK :
    regs->ax = test_thread_flag (TIF_IA32) ? __NR_ia32_restart_syscall : __NR_restart_syscall;
    regs->ip -= 2;
    break;
}

{
    current_thread_info ()->status &= ~TS_RESTORE_SIGMASK;
    sigprocmask (SIG_SETMASK, & current -> saved_sigmask, NULL);
}

{
    if (thread_info_flags & _TIF_SINGLESTEP) {
        regs->flags |= X86_EFLAGS_TF;
        clear_thread_flag (TIF_SINGLESTEP);
    }
    if (thread_info_flags & _TIF_SIGPENDING)
        do_signal (regs);
    if (thread_info_flags & _TIF_HRTICK_RESCHED)
        hrtick_resched ();
}

{
    regs->flags |= X86_EFLAGS_TF;
    clear_thread_flag (TIF_SINGLESTEP);
}

{
    struct task_struct *me = current;
    if (show_unhandled_signals && printk_ratelimit ()) {
        printk ("%s[%d] bad frame in %s frame:%p ip:%lx sp:%lx orax:%lx", me -> comm, me -> pid, where, frame, regs -> ip, regs -> sp, regs -> orig_ax);
        print_vma_addr (" in ", regs -> ip);
        printk ("\n");
    }
    force_sig (SIGSEGV, me);
}

{
    printk ("%s[%d] bad frame in %s frame:%p ip:%lx sp:%lx orax:%lx", me -> comm, me -> pid, where, frame, regs -> ip, regs -> sp, regs -> orig_ax);
    print_vma_addr (" in ", regs -> ip);
    printk ("\n");
}

{
    return unlikely (value != 0 && (value & SEGMENT_RPL_MASK) != USER_RPL);
}

{
    BUILD_BUG_ON (offsetof (struct pt_regs, r15) != 0);
    return &regs->r15 + (offset / sizeof (regs->r15));
}

{
    unsigned int seg;
    switch (offset) {
    case offsetof (struct user_regs_struct, fs) :
        if (task == current) {
            asm ("movl %%fs,%0"
                : "=r" (seg)
            ) return seg;
        }
        return task->thread.fsindex;
    case offsetof (struct user_regs_struct, gs) :
        if (task == current) {
            asm ("movl %%gs,%0"
                : "=r" (seg)
            ) return seg;
        }
        return task->thread.gsindex;
    case offsetof (struct user_regs_struct, ds) :
        if (task == current) {
            asm ("movl %%ds,%0"
                : "=r" (seg)
            ) return seg;
        }
        return task->thread.ds;
    case offsetof (struct user_regs_struct, es) :
        if (task == current) {
            asm ("movl %%es,%0"
                : "=r" (seg)
            ) return seg;
        }
        return task->thread.es;
    case offsetof (struct user_regs_struct, cs) :
    case offsetof (struct user_regs_struct, ss) :
        break;
    }
    return *pt_regs_access (task_pt_regs (task), offset);
}

{
case offsetof (struct user_regs_struct, fs) :
    if (task == current) {
        asm ("movl %%fs,%0"
            : "=r" (seg)
        ) return seg;
    }
    return task->thread.fsindex;
case offsetof (struct user_regs_struct, gs) :
    if (task == current) {
        asm ("movl %%gs,%0"
            : "=r" (seg)
        ) return seg;
    }
    return task->thread.gsindex;
case offsetof (struct user_regs_struct, ds) :
    if (task == current) {
        asm ("movl %%ds,%0"
            : "=r" (seg)
        ) return seg;
    }
    return task->thread.ds;
case offsetof (struct user_regs_struct, es) :
    if (task == current) {
        asm ("movl %%es,%0"
            : "=r" (seg)
        ) return seg;
    }
    return task->thread.es;
case offsetof (struct user_regs_struct, cs) :
case offsetof (struct user_regs_struct, ss) :
    break;
}

{
    asm ("movl %%fs,%0"
        : "=r" (seg)
    ) return seg;
}

{
    asm ("movl %%gs,%0"
        : "=r" (seg)
    ) return seg;
}

{
    asm ("movl %%ds,%0"
        : "=r" (seg)
    ) return seg;
}

{
    asm ("movl %%es,%0"
        : "=r" (seg)
    ) return seg;
}

{
    if (invalid_selector (value))
        return -EIO;
    switch (offset) {
    case offsetof (struct user_regs_struct, fs) :
        if ((value == FS_TLS_SEL && task->thread.fsindex == 0 && task->thread.fs != 0) || (value == 0 && task->thread.fsindex == FS_TLS_SEL && task->thread.fs == 0))
            break;
        task->thread.fsindex = value;
        if (task == current)
            loadsegment (fs, task->thread.fsindex);
        break;
    case offsetof (struct user_regs_struct, gs) :
        if ((value == GS_TLS_SEL && task->thread.gsindex == 0 && task->thread.gs != 0) || (value == 0 && task->thread.gsindex == GS_TLS_SEL && task->thread.gs == 0))
            break;
        task->thread.gsindex = value;
        if (task == current)
            load_gs_index (task->thread.gsindex);
        break;
    case offsetof (struct user_regs_struct, ds) :
        task->thread.ds = value;
        if (task == current)
            loadsegment (ds, task->thread.ds);
        break;
    case offsetof (struct user_regs_struct, es) :
        task->thread.es = value;
        if (task == current)
            loadsegment (es, task->thread.es);
        break;
    case offsetof (struct user_regs_struct, cs) :
        if (unlikely (value == 0))
            return -EIO;
        break;
    case offsetof (struct user_regs_struct, ss) :
        if (unlikely (value == 0))
            return -EIO;
        break;
    }
    return 0;
}

{
case offsetof (struct user_regs_struct, fs) :
    if ((value == FS_TLS_SEL && task->thread.fsindex == 0 && task->thread.fs != 0) || (value == 0 && task->thread.fsindex == FS_TLS_SEL && task->thread.fs == 0))
        break;
    task->thread.fsindex = value;
    if (task == current)
        loadsegment (fs, task->thread.fsindex);
    break;
case offsetof (struct user_regs_struct, gs) :
    if ((value == GS_TLS_SEL && task->thread.gsindex == 0 && task->thread.gs != 0) || (value == 0 && task->thread.gsindex == GS_TLS_SEL && task->thread.gs == 0))
        break;
    task->thread.gsindex = value;
    if (task == current)
        load_gs_index (task->thread.gsindex);
    break;
case offsetof (struct user_regs_struct, ds) :
    task->thread.ds = value;
    if (task == current)
        loadsegment (ds, task->thread.ds);
    break;
case offsetof (struct user_regs_struct, es) :
    task->thread.es = value;
    if (task == current)
        loadsegment (es, task->thread.es);
    break;
case offsetof (struct user_regs_struct, cs) :
    if (unlikely (value == 0))
        return -EIO;
    break;
case offsetof (struct user_regs_struct, ss) :
    if (unlikely (value == 0))
        return -EIO;
    break;
}

{
    return TASK_SIZE64 - 7;
}

{
    unsigned long retval = task_pt_regs (task)->flags;
    if (test_tsk_thread_flag (task, TIF_FORCED_TF))
        retval &= ~X86_EFLAGS_TF;
    return retval;
}

{
    struct pt_regs *regs = task_pt_regs (task);
    if (value & X86_EFLAGS_TF)
        clear_tsk_thread_flag (task, TIF_FORCED_TF);
    else if (test_tsk_thread_flag (task, TIF_FORCED_TF))
        value |= X86_EFLAGS_TF;
    regs->flags = (regs->flags & ~FLAG_MASK) | (value & FLAG_MASK);
    return 0;
}

{
    switch (offset) {
    case offsetof (struct user_regs_struct, cs) :
    case offsetof (struct user_regs_struct, ds) :
    case offsetof (struct user_regs_struct, es) :
    case offsetof (struct user_regs_struct, fs) :
    case offsetof (struct user_regs_struct, gs) :
    case offsetof (struct user_regs_struct, ss) :
        return set_segment_reg (child, offset, value);
    case offsetof (struct user_regs_struct, flags) :
        return set_flags (child, value);
    }
    *pt_regs_access (task_pt_regs (child), offset) = value;
    return 0;
}

{
case offsetof (struct user_regs_struct, cs) :
case offsetof (struct user_regs_struct, ds) :
case offsetof (struct user_regs_struct, es) :
case offsetof (struct user_regs_struct, fs) :
case offsetof (struct user_regs_struct, gs) :
case offsetof (struct user_regs_struct, ss) :
    return set_segment_reg (child, offset, value);
case offsetof (struct user_regs_struct, flags) :
    return set_flags (child, value);
}

{
    switch (offset) {
    case offsetof (struct user_regs_struct, cs) :
    case offsetof (struct user_regs_struct, ds) :
    case offsetof (struct user_regs_struct, es) :
    case offsetof (struct user_regs_struct, fs) :
    case offsetof (struct user_regs_struct, gs) :
    case offsetof (struct user_regs_struct, ss) :
        return get_segment_reg (task, offset);
    case offsetof (struct user_regs_struct, flags) :
        return get_flags (task);
    }
    return *pt_regs_access (task_pt_regs (task), offset);
}

{
case offsetof (struct user_regs_struct, cs) :
case offsetof (struct user_regs_struct, ds) :
case offsetof (struct user_regs_struct, es) :
case offsetof (struct user_regs_struct, fs) :
case offsetof (struct user_regs_struct, gs) :
case offsetof (struct user_regs_struct, ss) :
    return get_segment_reg (task, offset);
case offsetof (struct user_regs_struct, flags) :
    return get_flags (task);
}

{
    if (kbuf) {
        unsigned long *k = kbuf;
        while (count > 0) {
            *k++ = getreg (target, pos);
            count -= sizeof (*k);
            pos += sizeof (*k);
        }
    }
    else {
        unsigned long __user *u = ubuf;
        while (count > 0) {
            if (__put_user (getreg (target, pos), u++))
                return -EFAULT;
            count -= sizeof (*u);
            pos += sizeof (*u);
        }
    }
    return 0;
}

{
    unsigned long *k = kbuf;
    while (count > 0) {
        *k++ = getreg (target, pos);
        count -= sizeof (*k);
        pos += sizeof (*k);
    }
}

{
    *k++ = getreg (target, pos);
    count -= sizeof (*k);
    pos += sizeof (*k);
}

{
    unsigned long __user *u = ubuf;
    while (count > 0) {
        if (__put_user (getreg (target, pos), u++))
            return -EFAULT;
        count -= sizeof (*u);
        pos += sizeof (*u);
    }
}

{
    if (__put_user (getreg (target, pos), u++))
        return -EFAULT;
    count -= sizeof (*u);
    pos += sizeof (*u);
}

{
    int ret = 0;
    if (kbuf) {
        const unsigned long *k = kbuf;
        while (count > 0 && !ret) {
            ret = putreg (target, pos, *k++);
            count -= sizeof (*k);
            pos += sizeof (*k);
        }
    }
    else {
        const unsigned long __user *u = ubuf;
        while (count > 0 && !ret) {
            unsigned long word;
            ret = __get_user (word, u++);
            if (ret)
                break;
            ret = putreg (target, pos, word);
            count -= sizeof (*u);
            pos += sizeof (*u);
        }
    }
    return ret;
}

{
    const unsigned long *k = kbuf;
    while (count > 0 && !ret) {
        ret = putreg (target, pos, *k++);
        count -= sizeof (*k);
        pos += sizeof (*k);
    }
}

{
    ret = putreg (target, pos, *k++);
    count -= sizeof (*k);
    pos += sizeof (*k);
}

{
    const unsigned long __user *u = ubuf;
    while (count > 0 && !ret) {
        unsigned long word;
        ret = __get_user (word, u++);
        if (ret)
            break;
        ret = putreg (target, pos, word);
        count -= sizeof (*u);
        pos += sizeof (*u);
    }
}

{
    unsigned long word;
    ret = __get_user (word, u++);
    if (ret)
        break;
    ret = putreg (target, pos, word);
    count -= sizeof (*u);
    pos += sizeof (*u);
}

{
    switch (n) {
    case 0 :
        return child->thread.debugreg0;
    case 1 :
        return child->thread.debugreg1;
    case 2 :
        return child->thread.debugreg2;
    case 3 :
        return child->thread.debugreg3;
    case 6 :
        return child->thread.debugreg6;
    case 7 :
        return child->thread.debugreg7;
    }
    return 0;
}

{
case 0 :
    return child->thread.debugreg0;
case 1 :
    return child->thread.debugreg1;
case 2 :
    return child->thread.debugreg2;
case 3 :
    return child->thread.debugreg3;
case 6 :
    return child->thread.debugreg6;
case 7 :
    return child->thread.debugreg7;
}

{
    int i;
    if (unlikely (n == 4 || n == 5))
        return -EIO;
    if (n < 4 && unlikely (data >= debugreg_addr_limit (child)))
        return -EIO;
    switch (n) {
    case 0 :
        child->thread.debugreg0 = data;
        break;
    case 1 :
        child->thread.debugreg1 = data;
        break;
    case 2 :
        child->thread.debugreg2 = data;
        break;
    case 3 :
        child->thread.debugreg3 = data;
        break;
    case 6 :
        if ((data & ~0xffffffffUL) != 0)
            return -EIO;
        child->thread.debugreg6 = data;
        break;
    case 7 :
        data &= ~DR_CONTROL_RESERVED;
        for (i = 0; i < 4; i++)
            if ((DR7_MASK >> ((data >> (16 + 4 * i)) & 0xf)) & 1)
                return -EIO;
        child->thread.debugreg7 = data;
        if (data)
            set_tsk_thread_flag (child, TIF_DEBUG);
        else
            clear_tsk_thread_flag (child, TIF_DEBUG);
        break;
    }
    return 0;
}

{
case 0 :
    child->thread.debugreg0 = data;
    break;
case 1 :
    child->thread.debugreg1 = data;
    break;
case 2 :
    child->thread.debugreg2 = data;
    break;
case 3 :
    child->thread.debugreg3 = data;
    break;
case 6 :
    if ((data & ~0xffffffffUL) != 0)
        return -EIO;
    child->thread.debugreg6 = data;
    break;
case 7 :
    data &= ~DR_CONTROL_RESERVED;
    for (i = 0; i < 4; i++)
        if ((DR7_MASK >> ((data >> (16 + 4 * i)) & 0xf)) & 1)
            return -EIO;
    child->thread.debugreg7 = data;
    if (data)
        set_tsk_thread_flag (child, TIF_DEBUG);
    else
        clear_tsk_thread_flag (child, TIF_DEBUG);
    break;
}

{
    user_disable_single_step (child);
    if (child->thread.ds_area_msr) {
        child->thread.debugctlmsr &= ~ds_debugctl_mask ();
        if (!child->thread.debugctlmsr)
            clear_tsk_thread_flag (child, TIF_DEBUGCTLMSR);
        clear_tsk_thread_flag (child, TIF_BTS_TRACE_TS);
    }
}

{
    child->thread.debugctlmsr &= ~ds_debugctl_mask ();
    if (!child->thread.debugctlmsr)
        clear_tsk_thread_flag (child, TIF_DEBUGCTLMSR);
    clear_tsk_thread_flag (child, TIF_BTS_TRACE_TS);
}

{
    int ret;
    unsigned long __user *datap = (unsigned long __user *) data;
    switch (request) {
    case PTRACE_PEEKUSR :
        {
            unsigned long tmp;
            ret = -EIO;
            if ((addr & (sizeof (data) - 1)) || addr < 0 || addr >= sizeof (struct user))
                break;
            tmp = 0;
            if (addr < sizeof (struct user_regs_struct))
                tmp = getreg (child, addr);
            else if (addr >= offsetof (struct user, u_debugreg[0]) && addr <= offsetof (struct user, u_debugreg[7])) {
                addr -= offsetof (struct user, u_debugreg[0]);
                tmp = ptrace_get_debugreg (child, addr / sizeof (data));
            }
            ret = put_user (tmp, datap);
            break;
        }
    case PTRACE_POKEUSR :
        ret = -EIO;
        if ((addr & (sizeof (data) - 1)) || addr < 0 || addr >= sizeof (struct user))
            break;
        if (addr < sizeof (struct user_regs_struct))
            ret = putreg (child, addr, data);
        else if (addr >= offsetof (struct user, u_debugreg[0]) && addr <= offsetof (struct user, u_debugreg[7])) {
            addr -= offsetof (struct user, u_debugreg[0]);
            ret = ptrace_set_debugreg (child, addr / sizeof (data), data);
        }
        break;
    case PTRACE_GETREGS :
        return copy_regset_to_user (child, task_user_regset_view (current), REGSET_GENERAL, 0, sizeof (struct user_regs_struct), datap);
    case PTRACE_SETREGS :
        return copy_regset_from_user (child, task_user_regset_view (current), REGSET_GENERAL, 0, sizeof (struct user_regs_struct), datap);
    case PTRACE_GETFPREGS :
        return copy_regset_to_user (child, task_user_regset_view (current), REGSET_FP, 0, sizeof (struct user_i387_struct), datap);
    case PTRACE_SETFPREGS :
        return copy_regset_from_user (child, task_user_regset_view (current), REGSET_FP, 0, sizeof (struct user_i387_struct), datap);
    default :
        ret = ptrace_request (child, request, addr, data);
        break;
    }
    return ret;
}

{
case PTRACE_PEEKUSR :
    {
        unsigned long tmp;
        ret = -EIO;
        if ((addr & (sizeof (data) - 1)) || addr < 0 || addr >= sizeof (struct user))
            break;
        tmp = 0;
        if (addr < sizeof (struct user_regs_struct))
            tmp = getreg (child, addr);
        else if (addr >= offsetof (struct user, u_debugreg[0]) && addr <= offsetof (struct user, u_debugreg[7])) {
            addr -= offsetof (struct user, u_debugreg[0]);
            tmp = ptrace_get_debugreg (child, addr / sizeof (data));
        }
        ret = put_user (tmp, datap);
        break;
    }
case PTRACE_POKEUSR :
    ret = -EIO;
    if ((addr & (sizeof (data) - 1)) || addr < 0 || addr >= sizeof (struct user))
        break;
    if (addr < sizeof (struct user_regs_struct))
        ret = putreg (child, addr, data);
    else if (addr >= offsetof (struct user, u_debugreg[0]) && addr <= offsetof (struct user, u_debugreg[7])) {
        addr -= offsetof (struct user, u_debugreg[0]);
        ret = ptrace_set_debugreg (child, addr / sizeof (data), data);
    }
    break;
case PTRACE_GETREGS :
    return copy_regset_to_user (child, task_user_regset_view (current), REGSET_GENERAL, 0, sizeof (struct user_regs_struct), datap);
case PTRACE_SETREGS :
    return copy_regset_from_user (child, task_user_regset_view (current), REGSET_GENERAL, 0, sizeof (struct user_regs_struct), datap);
case PTRACE_GETFPREGS :
    return copy_regset_to_user (child, task_user_regset_view (current), REGSET_FP, 0, sizeof (struct user_i387_struct), datap);
case PTRACE_SETFPREGS :
    return copy_regset_from_user (child, task_user_regset_view (current), REGSET_FP, 0, sizeof (struct user_i387_struct), datap);
default :
    ret = ptrace_request (child, request, addr, data);
    break;
}

{
    unsigned long tmp;
    ret = -EIO;
    if ((addr & (sizeof (data) - 1)) || addr < 0 || addr >= sizeof (struct user))
        break;
    tmp = 0;
    if (addr < sizeof (struct user_regs_struct))
        tmp = getreg (child, addr);
    else if (addr >= offsetof (struct user, u_debugreg[0]) && addr <= offsetof (struct user, u_debugreg[7])) {
        addr -= offsetof (struct user, u_debugreg[0]);
        tmp = ptrace_get_debugreg (child, addr / sizeof (data));
    }
    ret = put_user (tmp, datap);
    break;
}

{
    addr -= offsetof (struct user, u_debugreg[0]);
    tmp = ptrace_get_debugreg (child, addr / sizeof (data));
}

{
    addr -= offsetof (struct user, u_debugreg[0]);
    ret = ptrace_set_debugreg (child, addr / sizeof (data), data);
}

{
    ptrace_notify (SIGTRAP | ((current -> ptrace & PT_TRACESYSGOOD) ? 0x80 : 0));
    if (current->exit_code) {
        send_sig (current -> exit_code, current, 1);
        current->exit_code = 0;
    }
}

{
    send_sig (current -> exit_code, current, 1);
    current->exit_code = 0;
}

{
    secure_computing (regs -> orig_ax);
    if (test_thread_flag (TIF_SYSCALL_TRACE) && (current->ptrace & PT_PTRACED))
        syscall_trace (regs);
    if (unlikely (current->audit_context)) {
        if (test_thread_flag (TIF_IA32)) {
            audit_syscall_entry (AUDIT_ARCH_I386, regs -> orig_ax, regs -> bx, regs -> cx, regs -> dx, regs -> si);
        }
        else {
            audit_syscall_entry (AUDIT_ARCH_X86_64, regs -> orig_ax, regs -> di, regs -> si, regs -> dx, regs -> r10);
        }
    }
}

{
    if (test_thread_flag (TIF_IA32)) {
        audit_syscall_entry (AUDIT_ARCH_I386, regs -> orig_ax, regs -> bx, regs -> cx, regs -> dx, regs -> si);
    }
    else {
        audit_syscall_entry (AUDIT_ARCH_X86_64, regs -> orig_ax, regs -> di, regs -> si, regs -> dx, regs -> r10);
    }
}

{
    audit_syscall_entry (AUDIT_ARCH_I386, regs -> orig_ax, regs -> bx, regs -> cx, regs -> dx, regs -> si);
}

{
    audit_syscall_entry (AUDIT_ARCH_X86_64, regs -> orig_ax, regs -> di, regs -> si, regs -> dx, regs -> r10);
}

{
    if (unlikely (current->audit_context))
        audit_syscall_exit (AUDITSC_RESULT (regs->ax), regs->ax);
    if ((test_thread_flag (TIF_SYSCALL_TRACE) || test_thread_flag (TIF_SINGLESTEP)) && (current->ptrace & PT_PTRACED))
        syscall_trace (regs);
}

{
    if (!str)
        return -EINVAL;
    if (!strncmp (str, "on", 2)) {
        __supported_pte_mask |= _PAGE_NX;
        do_not_nx = 0;
    }
    else if (!strncmp (str, "off", 3)) {
        do_not_nx = 1;
        __supported_pte_mask &= ~_PAGE_NX;
    }
    return 0;
}

{
    __supported_pte_mask |= _PAGE_NX;
    do_not_nx = 0;
}

{
    do_not_nx = 1;
    __supported_pte_mask &= ~_PAGE_NX;
}

{
    if (!strcmp (str, "on"))
        force_personality32 &= ~READ_IMPLIES_EXEC;
    else if (!strcmp (str, "off"))
        force_personality32 |= READ_IMPLIES_EXEC;
    return 1;
}

{
    struct x8664_pda *pda = cpu_pda (cpu);
    asm volatile ("movl %0,%%fs ; movl %0,%%gs"
        :
        : "r" (0)
    ) mb ();
    wrmsrl (MSR_GS_BASE, pda);
    mb ();
    pda->cpunumber = cpu;
    pda->irqcount = -1;
    pda->kernelstack = (unsigned long) stack_thread_info () - PDA_STACKOFFSET + THREAD_SIZE;
    pda->active_mm = &init_mm;
    pda->mmu_state = 0;
    if (cpu == 0) {
        pda->pcurrent = &init_task;
        pda->irqstackptr = boot_cpu_stack;
    }
    else {
        pda->irqstackptr = (char *) __get_free_pages (GFP_ATOMIC, IRQSTACK_ORDER);
        if (!pda->irqstackptr)
            panic ("cannot allocate irqstack for cpu %d", cpu);
    }
    pda->irqstackptr += IRQSTACKSIZE - 64;
}

{
    pda->pcurrent = &init_task;
    pda->irqstackptr = boot_cpu_stack;
}

{
    pda->irqstackptr = (char *) __get_free_pages (GFP_ATOMIC, IRQSTACK_ORDER);
    if (!pda->irqstackptr)
        panic ("cannot allocate irqstack for cpu %d", cpu);
}

{
    wrmsrl (MSR_STAR, ((u64) __USER32_CS) << 48 | ((u64) __KERNEL_CS) << 32);
    wrmsrl (MSR_LSTAR, system_call);
    wrmsrl (MSR_CSTAR, ignore_sysret);
    wrmsrl (MSR_SYSCALL_MASK, X86_EFLAGS_TF | X86_EFLAGS_DF | X86_EFLAGS_IF | X86_EFLAGS_IOPL);
}

{
    unsigned long efer;
    rdmsrl (MSR_EFER, efer);
    if (!(efer & EFER_NX) || do_not_nx) {
        __supported_pte_mask &= ~_PAGE_NX;
    }
}

{
    __supported_pte_mask &= ~_PAGE_NX;
}

{
    int cpu = stack_smp_processor_id ();
    struct tss_struct *t = &per_cpu (init_tss, cpu);
    struct orig_ist *orig_ist = &per_cpu (orig_ist, cpu);
    unsigned long v;
    char *estacks = NULL;
    struct task_struct *me;
    int i;
    if (cpu != 0) {
        pda_init (cpu);
    }
    else
        estacks = boot_exception_stacks;
    me = current;
    if (cpu_test_and_set (cpu, cpu_initialized))
        panic ("CPU#%d already initialized!\n", cpu);
    printk ("Initializing CPU#%d\n", cpu);
    clear_in_cr4 (X86_CR4_VME | X86_CR4_PVI | X86_CR4_TSD | X86_CR4_DE);
    if (cpu)
        memcpy (get_cpu_gdt_table (cpu), cpu_gdt_table, GDT_SIZE);
    cpu_gdt_descr[cpu].size = GDT_SIZE;
    load_gdt ((const struct desc_ptr *) & cpu_gdt_descr [cpu]);
    load_idt ((const struct desc_ptr *) & idt_descr);
    memset (me -> thread.tls_array, 0, GDT_ENTRY_TLS_ENTRIES * 8);
    syscall_init ();
    wrmsrl (MSR_FS_BASE, 0);
    wrmsrl (MSR_KERNEL_GS_BASE, 0);
    barrier ();
    check_efer ();
    for (v = 0; v < N_EXCEPTION_STACKS; v++) {
        static const unsigned int order [N_EXCEPTION_STACKS] = {
            [0 ... N_EXCEPTION_STACKS - 1] = EXCEPTION_STACK_ORDER,
            [DEBUG_STACK - 1] = DEBUG_STACK_ORDER
        };
        if (cpu) {
            estacks = (char *) __get_free_pages (GFP_ATOMIC, order[v]);
            if (!estacks)
                panic ("Cannot allocate exception stack %ld %d\n", v, cpu);
        }
        estacks += PAGE_SIZE << order[v];
        orig_ist->ist[v] = t->x86_tss.ist[v] = (unsigned long) estacks;
    }
    t->x86_tss.io_bitmap_base = offsetof (struct tss_struct, io_bitmap);
    for (i = 0; i <= IO_BITMAP_LONGS; i++)
        t->io_bitmap[i] = ~0UL;
    atomic_inc (& init_mm.mm_count);
    me->active_mm = &init_mm;
    if (me->mm)
        BUG ();
    enter_lazy_tlb (& init_mm, me);
    set_tss_desc (cpu, t);
    load_TR_desc ();
    load_LDT (& init_mm.context);
    set_debugreg (0UL, 0);
    set_debugreg (0UL, 1);
    set_debugreg (0UL, 2);
    set_debugreg (0UL, 3);
    set_debugreg (0UL, 6);
    set_debugreg (0UL, 7);
    fpu_init ();
    raw_local_save_flags (kernel_eflags);
    if (is_uv_system ())
        uv_cpu_init ();
}

{
    pda_init (cpu);
}

{
    static const unsigned int order [N_EXCEPTION_STACKS] = {
        [0 ... N_EXCEPTION_STACKS - 1] = EXCEPTION_STACK_ORDER,
        [DEBUG_STACK - 1] = DEBUG_STACK_ORDER
    };
    if (cpu) {
        estacks = (char *) __get_free_pages (GFP_ATOMIC, order[v]);
        if (!estacks)
            panic ("Cannot allocate exception stack %ld %d\n", v, cpu);
    }
    estacks += PAGE_SIZE << order[v];
    orig_ist->ist[v] = t->x86_tss.ist[v] = (unsigned long) estacks;
}

{
    estacks = (char *) __get_free_pages (GFP_ATOMIC, order[v]);
    if (!estacks)
        panic ("Cannot allocate exception stack %ld %d\n", v, cpu);
}

{
    memcpy (trampoline_base, trampoline_data, trampoline_end - trampoline_data);
    return virt_to_phys (trampoline_base);
}

{
    if (hwdev && bus + size > *hwdev->dma_mask) {
        if (*hwdev->dma_mask >= DMA_32BIT_MASK)
            printk (KERN_ERR "nommu_%s: overflow %Lx+%zu of device mask %Lx\n", name, (long long) bus, size, (long long) *hwdev->dma_mask);
        return 0;
    }
    return 1;
}

{
    if (*hwdev->dma_mask >= DMA_32BIT_MASK)
        printk (KERN_ERR "nommu_%s: overflow %Lx+%zu of device mask %Lx\n", name, (long long) bus, size, (long long) *hwdev->dma_mask);
    return 0;
}

{
    dma_addr_t bus = paddr;
    WARN_ON (size == 0);
    if (!check_addr ("map_single", hwdev, bus, size))
        return bad_dma_address;
    flush_write_buffers ();
    return bus;
}

{
    struct scatterlist *s;
    int i;
    WARN_ON (nents == 0 || sg [0].length == 0);

    for_each_sg (sg, s, nents, i) {
        BUG_ON (! sg_page (s));
        s->dma_address = sg_phys (s);
        if (!check_addr ("map_sg", hwdev, s->dma_address, s->length))
            return 0;
        s->dma_length = s->length;
    }

    flush_write_buffers ();
    return nents;
}

{
    BUG_ON (! sg_page (s));
    s->dma_address = sg_phys (s);
    if (!check_addr ("map_sg", hwdev, s->dma_address, s->length))
        return 0;
    s->dma_length = s->length;
}

{
    return (dma_addr == bad_dma_address);
}

{
    if (dma_ops)
        return;
    force_iommu = 0;
    dma_ops = &nommu_dma_ops;
}

{
    if (!dev->dma_mask || !dma_supported (dev, mask))
        return -EIO;
    *dev->dma_mask = mask;
    return 0;
}

{
    iommu_merge = 1;
    if (!p)
        return -EINVAL;
    while (*p) {
        if (!strncmp (p, "off", 3))
            no_iommu = 1;
        if (!strncmp (p, "force", 5))
            force_iommu = 1;
        if (!strncmp (p, "noforce", 7)) {
            iommu_merge = 0;
            force_iommu = 0;
        }
        if (!strncmp (p, "biomerge", 8)) {
            iommu_bio_merge = 4096;
            iommu_merge = 1;
            force_iommu = 1;
        }
        if (!strncmp (p, "panic", 5))
            panic_on_overflow = 1;
        if (!strncmp (p, "nopanic", 7))
            panic_on_overflow = 0;
        if (!strncmp (p, "merge", 5)) {
            iommu_merge = 1;
            force_iommu = 1;
        }
        if (!strncmp (p, "nomerge", 7))
            iommu_merge = 0;
        if (!strncmp (p, "forcesac", 8))
            iommu_sac_force = 1;
        if (!strncmp (p, "allowdac", 8))
            forbid_dac = 0;
        if (!strncmp (p, "nodac", 5))
            forbid_dac = -1;
        if (!strncmp (p, "usedac", 6)) {
            forbid_dac = -1;
            return 1;
        }
        p += strcspn (p, ",");
        if (*p == ',')
            ++p;
    }
    return 0;
}

{
    if (!strncmp (p, "off", 3))
        no_iommu = 1;
    if (!strncmp (p, "force", 5))
        force_iommu = 1;
    if (!strncmp (p, "noforce", 7)) {
        iommu_merge = 0;
        force_iommu = 0;
    }
    if (!strncmp (p, "biomerge", 8)) {
        iommu_bio_merge = 4096;
        iommu_merge = 1;
        force_iommu = 1;
    }
    if (!strncmp (p, "panic", 5))
        panic_on_overflow = 1;
    if (!strncmp (p, "nopanic", 7))
        panic_on_overflow = 0;
    if (!strncmp (p, "merge", 5)) {
        iommu_merge = 1;
        force_iommu = 1;
    }
    if (!strncmp (p, "nomerge", 7))
        iommu_merge = 0;
    if (!strncmp (p, "forcesac", 8))
        iommu_sac_force = 1;
    if (!strncmp (p, "allowdac", 8))
        forbid_dac = 0;
    if (!strncmp (p, "nodac", 5))
        forbid_dac = -1;
    if (!strncmp (p, "usedac", 6)) {
        forbid_dac = -1;
        return 1;
    }
    p += strcspn (p, ",");
    if (*p == ',')
        ++p;
}

{
    iommu_merge = 0;
    force_iommu = 0;
}

{
    iommu_bio_merge = 4096;
    iommu_merge = 1;
    force_iommu = 1;
}

{
    iommu_merge = 1;
    force_iommu = 1;
}

{
    forbid_dac = -1;
    return 1;
}

{
    if (dma_ops->dma_supported)
        return dma_ops->dma_supported (dev, mask);
    if (mask < DMA_24BIT_MASK)
        return 0;
    if (iommu_sac_force && (mask >= DMA_40BIT_MASK)) {
        printk (KERN_INFO "%s: Force SAC with mask %Lx\n", dev -> bus_id, mask);
        return 0;
    }
    return 1;
}

{
    printk (KERN_INFO "%s: Force SAC with mask %Lx\n", dev -> bus_id, mask);
    return 0;
}

{
    int node;
    node = dev_to_node (dev);
    return alloc_pages_node (node, gfp, order);
}

{
    void *memory = NULL;
    struct page *page;
    unsigned long dma_mask = 0;
    dma_addr_t bus;
    int noretry = 0;
    gfp &= ~(__GFP_DMA | __GFP_HIGHMEM | __GFP_DMA32);
    if (dma_alloc_from_coherent_mem (dev, size, dma_handle, &memory))
        return memory;
    if (!dev) {
        dev = &fallback_dev;
        gfp |= GFP_DMA;
    }
    dma_mask = dev->coherent_dma_mask;
    if (dma_mask == 0)
        dma_mask = (gfp & GFP_DMA) ? DMA_24BIT_MASK : DMA_32BIT_MASK;
    if (dev->dma_mask == NULL)
        return NULL;
    if (gfp & __GFP_DMA)
        noretry = 1;
again :
    page = dma_alloc_pages (dev, noretry ? gfp | __GFP_NORETRY : gfp, get_order (size));
    if (page == NULL)
        return NULL;
    {
        int high, mmu;
        bus = page_to_phys (page);
        memory = page_address (page);
        high = (bus + size) >= dma_mask;
        mmu = high;
        if (force_iommu && !(gfp & GFP_DMA))
            mmu = 1;
        else if (high) {
            free_pages ((unsigned long) memory, get_order (size));
            if (dma_mask < DMA_32BIT_MASK && !(gfp & GFP_DMA)) {
                gfp = (gfp & ~GFP_DMA32) | GFP_DMA;
                goto again;
            }
            gfp &= ~(GFP_DMA32 | GFP_DMA);
            if (dma_ops->alloc_coherent)
                return dma_ops->alloc_coherent (dev, size, dma_handle, gfp);
            return NULL;
        }
        memset (memory, 0, size);
        if (!mmu) {
            *dma_handle = bus;
            return memory;
        }
    }
    if (dma_ops->alloc_coherent) {
        free_pages ((unsigned long) memory, get_order (size));
        gfp &= ~(GFP_DMA | GFP_DMA32);
        return dma_ops->alloc_coherent (dev, size, dma_handle, gfp);
    }
    if (dma_ops->map_simple) {
        *dma_handle = dma_ops->map_simple (dev, virt_to_phys (memory), size, PCI_DMA_BIDIRECTIONAL);
        if (*dma_handle != bad_dma_address)
            return memory;
    }
    if (panic_on_overflow)
        panic ("dma_alloc_coherent: IOMMU overflow by %lu bytes\n", (unsigned long) size);
    free_pages ((unsigned long) memory, get_order (size));
    return NULL;
}

{
    dev = &fallback_dev;
    gfp |= GFP_DMA;
}

{
    int high, mmu;
    bus = page_to_phys (page);
    memory = page_address (page);
    high = (bus + size) >= dma_mask;
    mmu = high;
    if (force_iommu && !(gfp & GFP_DMA))
        mmu = 1;
    else if (high) {
        free_pages ((unsigned long) memory, get_order (size));
        if (dma_mask < DMA_32BIT_MASK && !(gfp & GFP_DMA)) {
            gfp = (gfp & ~GFP_DMA32) | GFP_DMA;
            goto again;
        }
        gfp &= ~(GFP_DMA32 | GFP_DMA);
        if (dma_ops->alloc_coherent)
            return dma_ops->alloc_coherent (dev, size, dma_handle, gfp);
        return NULL;
    }
    memset (memory, 0, size);
    if (!mmu) {
        *dma_handle = bus;
        return memory;
    }
}

{
    free_pages ((unsigned long) memory, get_order (size));
    if (dma_mask < DMA_32BIT_MASK && !(gfp & GFP_DMA)) {
        gfp = (gfp & ~GFP_DMA32) | GFP_DMA;
        goto again;
    }
    gfp &= ~(GFP_DMA32 | GFP_DMA);
    if (dma_ops->alloc_coherent)
        return dma_ops->alloc_coherent (dev, size, dma_handle, gfp);
    return NULL;
}

{
    gfp = (gfp & ~GFP_DMA32) | GFP_DMA;
    goto again;
}

{
    *dma_handle = bus;
    return memory;
}

{
    free_pages ((unsigned long) memory, get_order (size));
    gfp &= ~(GFP_DMA | GFP_DMA32);
    return dma_ops->alloc_coherent (dev, size, dma_handle, gfp);
}

{
    *dma_handle = dma_ops->map_simple (dev, virt_to_phys (memory), size, PCI_DMA_BIDIRECTIONAL);
    if (*dma_handle != bad_dma_address)
        return memory;
}

{
    int order = get_order (size);
    WARN_ON (irqs_disabled ());
    if (dma_release_coherent (dev, order, vaddr))
        return;
    if (dma_ops->unmap_single)
        dma_ops->unmap_single (dev, bus, size, 0);
    free_pages ((unsigned long) vaddr, order);
}

{
    intel_iommu_init ();
    no_iommu_init ();
    return 0;
}

{
    gart_iommu_shutdown ();
}

{
    u32 max_level;
    u32 regs [4];
    const struct cpuid_bit *cb;
    static const struct cpuid_bit cpuid_bits [] = {{X86_FEATURE_IDA, CR_EAX, 1, 0x00000006}, {0, 0, 0, 0}};
    for (cb = cpuid_bits; cb->feature; cb++) {
        max_level = cpuid_eax (cb->level & 0xffff0000);
        if (max_level < cb->level || max_level > (cb->level | 0xffff))
            continue;
        cpuid (cb -> level, & regs [CR_EAX], & regs [CR_EBX], & regs [CR_ECX], & regs [CR_EDX]);
        if (regs[cb->reg] & (1 << cb->bit))
            set_cpu_cap (c, cb->feature);
    }
}

{
    max_level = cpuid_eax (cb->level & 0xffff0000);
    if (max_level < cb->level || max_level > (cb->level | 0xffff))
        continue;
    cpuid (cb -> level, & regs [CR_EAX], & regs [CR_EBX], & regs [CR_ECX], & regs [CR_EDX]);
    if (regs[cb->reg] & (1 << cb->bit))
        set_cpu_cap (c, cb->feature);
}

{
    unsigned dummy;
    unsigned line_size, lines_per_tag, assoc, size_in_kb;
    union l1_cache l1i, l1d;
    union l2_cache l2;
    union l3_cache l3;
    union l1_cache *l1 = &l1d;
    eax->full = 0;
    ebx->full = 0;
    ecx->full = 0;
    cpuid (0x80000005, & dummy, & dummy, & l1d.val, & l1i.val);
    cpuid (0x80000006, & dummy, & dummy, & l2.val, & l3.val);
    switch (leaf) {
    case 1 :
        l1 = &l1i;
    case 0 :
        if (!l1->val)
            return;
        assoc = l1->assoc;
        line_size = l1->line_size;
        lines_per_tag = l1->lines_per_tag;
        size_in_kb = l1->size_in_kb;
        break;
    case 2 :
        if (!l2.val)
            return;
        assoc = l2.assoc;
        line_size = l2.line_size;
        lines_per_tag = l2.lines_per_tag;
        size_in_kb = current_cpu_data.x86_cache_size;
        break;
    case 3 :
        if (!l3.val)
            return;
        assoc = l3.assoc;
        line_size = l3.line_size;
        lines_per_tag = l3.lines_per_tag;
        size_in_kb = l3.size_encoded * 512;
        break;
    default :
        return;
    }
    eax->split.is_self_initializing = 1;
    eax->split.type = types[leaf];
    eax->split.level = levels[leaf];
    if (leaf == 3)
        eax->split.num_threads_sharing = current_cpu_data.x86_max_cores - 1;
    else
        eax->split.num_threads_sharing = 0;
    eax->split.num_cores_on_die = current_cpu_data.x86_max_cores - 1;
    if (assoc == 0xf)
        eax->split.is_fully_associative = 1;
    ebx->split.coherency_line_size = line_size - 1;
    ebx->split.ways_of_associativity = assocs[assoc] - 1;
    ebx->split.physical_line_partition = lines_per_tag - 1;
    ecx->split.number_of_sets = (size_in_kb * 1024) / line_size / (ebx->split.ways_of_associativity + 1) - 1;
}

{
case 1 :
    l1 = &l1i;
case 0 :
    if (!l1->val)
        return;
    assoc = l1->assoc;
    line_size = l1->line_size;
    lines_per_tag = l1->lines_per_tag;
    size_in_kb = l1->size_in_kb;
    break;
case 2 :
    if (!l2.val)
        return;
    assoc = l2.assoc;
    line_size = l2.line_size;
    lines_per_tag = l2.lines_per_tag;
    size_in_kb = current_cpu_data.x86_cache_size;
    break;
case 3 :
    if (!l3.val)
        return;
    assoc = l3.assoc;
    line_size = l3.line_size;
    lines_per_tag = l3.lines_per_tag;
    size_in_kb = l3.size_encoded * 512;
    break;
default :
    return;
}

{
    union _cpuid4_leaf_eax eax;
    union _cpuid4_leaf_ebx ebx;
    union _cpuid4_leaf_ecx ecx;
    unsigned edx;
    if (boot_cpu_data.x86_vendor == X86_VENDOR_AMD)
        amd_cpuid4 (index, &eax, &ebx, &ecx);
    else
        cpuid_count (4, index, &eax.full, &ebx.full, &ecx.full, &edx);
    if (eax.split.type == CACHE_TYPE_NULL)
        return -EIO;
    this_leaf->eax = eax;
    this_leaf->ebx = ebx;
    this_leaf->ecx = ecx;
    this_leaf->size = (ecx.split.number_of_sets + 1) * (ebx.split.coherency_line_size + 1) * (ebx.split.physical_line_partition + 1) * (ebx.split.ways_of_associativity + 1);
    return 0;
}

{
    unsigned int eax, ebx, ecx, edx;
    union _cpuid4_leaf_eax cache_eax;
    int i = -1;
    do {
        ++i;
        cpuid_count (4, i, & eax, & ebx, & ecx, & edx);
        cache_eax.full = eax;
    }
    while (cache_eax.split.type != CACHE_TYPE_NULL);
    return i;
}

{
    ++i;
    cpuid_count (4, i, & eax, & ebx, & ecx, & edx);
    cache_eax.full = eax;
}

{
    unsigned int trace = 0, l1i = 0, l1d = 0, l2 = 0, l3 = 0;
    unsigned int new_l1d = 0, new_l1i = 0;
    unsigned int new_l2 = 0, new_l3 = 0, i;
    unsigned int l2_id = 0, l3_id = 0, num_threads_sharing, index_msb;
    if (c->cpuid_level > 3) {
        static int is_initialized;
        if (is_initialized == 0) {
            num_cache_leaves = find_num_cache_leaves ();
            is_initialized++;
        }
        for (i = 0; i < num_cache_leaves; i++) {
            struct _cpuid4_info this_leaf;
            int retval;
            retval = cpuid4_cache_lookup (i, &this_leaf);
            if (retval >= 0) {
                switch (this_leaf.eax.split.level) {
                case 1 :
                    if (this_leaf.eax.split.type == CACHE_TYPE_DATA)
                        new_l1d = this_leaf.size / 1024;
                    else if (this_leaf.eax.split.type == CACHE_TYPE_INST)
                        new_l1i = this_leaf.size / 1024;
                    break;
                case 2 :
                    new_l2 = this_leaf.size / 1024;
                    num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
                    index_msb = get_count_order (num_threads_sharing);
                    l2_id = c->apicid >> index_msb;
                    break;
                case 3 :
                    new_l3 = this_leaf.size / 1024;
                    num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
                    index_msb = get_count_order (num_threads_sharing);
                    l3_id = c->apicid >> index_msb;
                    break;
                default :
                    break;
                }
            }
        }
    }
    if ((num_cache_leaves == 0 || c->x86 == 15) && c->cpuid_level > 1) {
        int j, n;
        unsigned int regs [4];
        unsigned char *dp = (unsigned char *) regs;
        int only_trace = 0;
        if (num_cache_leaves != 0 && c->x86 == 15)
            only_trace = 1;
        n = cpuid_eax (2) & 0xFF;
        for (i = 0; i < n; i++) {
            cpuid (2, & regs [0], & regs [1], & regs [2], & regs [3]);
            for (j = 0; j < 3; j++) {
                if (regs[j] & (1 << 31))
                    regs[j] = 0;
            }
            for (j = 1; j < 16; j++) {
                unsigned char des = dp[j];
                unsigned char k = 0;
                while (cache_table[k].descriptor != 0) {
                    if (cache_table[k].descriptor == des) {
                        if (only_trace && cache_table[k].cache_type != LVL_TRACE)
                            break;
                        switch (cache_table[k].cache_type) {
                        case LVL_1_INST :
                            l1i += cache_table[k].size;
                            break;
                        case LVL_1_DATA :
                            l1d += cache_table[k].size;
                            break;
                        case LVL_2 :
                            l2 += cache_table[k].size;
                            break;
                        case LVL_3 :
                            l3 += cache_table[k].size;
                            break;
                        case LVL_TRACE :
                            trace += cache_table[k].size;
                            break;
                        }
                        break;
                    }
                    k++;
                }
            }
        }
    }
    if (new_l1d)
        l1d = new_l1d;
    if (new_l1i)
        l1i = new_l1i;
    if (new_l2) {
        l2 = new_l2;
    }
    if (new_l3) {
        l3 = new_l3;
    }
    if (trace)
        printk (KERN_INFO "CPU: Trace cache: %dK uops", trace);
    else if (l1i)
        printk (KERN_INFO "CPU: L1 I cache: %dK", l1i);
    if (l1d)
        printk (", L1 D cache: %dK\n", l1d);
    else
        printk ("\n");
    if (l2)
        printk (KERN_INFO "CPU: L2 cache: %dK\n", l2);
    if (l3)
        printk (KERN_INFO "CPU: L3 cache: %dK\n", l3);
    c->x86_cache_size = l3 ? l3 : (l2 ? l2 : (l1i + l1d));
    return l2;
}

{
    static int is_initialized;
    if (is_initialized == 0) {
        num_cache_leaves = find_num_cache_leaves ();
        is_initialized++;
    }
    for (i = 0; i < num_cache_leaves; i++) {
        struct _cpuid4_info this_leaf;
        int retval;
        retval = cpuid4_cache_lookup (i, &this_leaf);
        if (retval >= 0) {
            switch (this_leaf.eax.split.level) {
            case 1 :
                if (this_leaf.eax.split.type == CACHE_TYPE_DATA)
                    new_l1d = this_leaf.size / 1024;
                else if (this_leaf.eax.split.type == CACHE_TYPE_INST)
                    new_l1i = this_leaf.size / 1024;
                break;
            case 2 :
                new_l2 = this_leaf.size / 1024;
                num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
                index_msb = get_count_order (num_threads_sharing);
                l2_id = c->apicid >> index_msb;
                break;
            case 3 :
                new_l3 = this_leaf.size / 1024;
                num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
                index_msb = get_count_order (num_threads_sharing);
                l3_id = c->apicid >> index_msb;
                break;
            default :
                break;
            }
        }
    }
}

{
    num_cache_leaves = find_num_cache_leaves ();
    is_initialized++;
}

{
    struct _cpuid4_info this_leaf;
    int retval;
    retval = cpuid4_cache_lookup (i, &this_leaf);
    if (retval >= 0) {
        switch (this_leaf.eax.split.level) {
        case 1 :
            if (this_leaf.eax.split.type == CACHE_TYPE_DATA)
                new_l1d = this_leaf.size / 1024;
            else if (this_leaf.eax.split.type == CACHE_TYPE_INST)
                new_l1i = this_leaf.size / 1024;
            break;
        case 2 :
            new_l2 = this_leaf.size / 1024;
            num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
            index_msb = get_count_order (num_threads_sharing);
            l2_id = c->apicid >> index_msb;
            break;
        case 3 :
            new_l3 = this_leaf.size / 1024;
            num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
            index_msb = get_count_order (num_threads_sharing);
            l3_id = c->apicid >> index_msb;
            break;
        default :
            break;
        }
    }
}

{
    switch (this_leaf.eax.split.level) {
    case 1 :
        if (this_leaf.eax.split.type == CACHE_TYPE_DATA)
            new_l1d = this_leaf.size / 1024;
        else if (this_leaf.eax.split.type == CACHE_TYPE_INST)
            new_l1i = this_leaf.size / 1024;
        break;
    case 2 :
        new_l2 = this_leaf.size / 1024;
        num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
        index_msb = get_count_order (num_threads_sharing);
        l2_id = c->apicid >> index_msb;
        break;
    case 3 :
        new_l3 = this_leaf.size / 1024;
        num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
        index_msb = get_count_order (num_threads_sharing);
        l3_id = c->apicid >> index_msb;
        break;
    default :
        break;
    }
}

{
case 1 :
    if (this_leaf.eax.split.type == CACHE_TYPE_DATA)
        new_l1d = this_leaf.size / 1024;
    else if (this_leaf.eax.split.type == CACHE_TYPE_INST)
        new_l1i = this_leaf.size / 1024;
    break;
case 2 :
    new_l2 = this_leaf.size / 1024;
    num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
    index_msb = get_count_order (num_threads_sharing);
    l2_id = c->apicid >> index_msb;
    break;
case 3 :
    new_l3 = this_leaf.size / 1024;
    num_threads_sharing = 1 + this_leaf.eax.split.num_threads_sharing;
    index_msb = get_count_order (num_threads_sharing);
    l3_id = c->apicid >> index_msb;
    break;
default :
    break;
}

{
    int j, n;
    unsigned int regs [4];
    unsigned char *dp = (unsigned char *) regs;
    int only_trace = 0;
    if (num_cache_leaves != 0 && c->x86 == 15)
        only_trace = 1;
    n = cpuid_eax (2) & 0xFF;
    for (i = 0; i < n; i++) {
        cpuid (2, & regs [0], & regs [1], & regs [2], & regs [3]);
        for (j = 0; j < 3; j++) {
            if (regs[j] & (1 << 31))
                regs[j] = 0;
        }
        for (j = 1; j < 16; j++) {
            unsigned char des = dp[j];
            unsigned char k = 0;
            while (cache_table[k].descriptor != 0) {
                if (cache_table[k].descriptor == des) {
                    if (only_trace && cache_table[k].cache_type != LVL_TRACE)
                        break;
                    switch (cache_table[k].cache_type) {
                    case LVL_1_INST :
                        l1i += cache_table[k].size;
                        break;
                    case LVL_1_DATA :
                        l1d += cache_table[k].size;
                        break;
                    case LVL_2 :
                        l2 += cache_table[k].size;
                        break;
                    case LVL_3 :
                        l3 += cache_table[k].size;
                        break;
                    case LVL_TRACE :
                        trace += cache_table[k].size;
                        break;
                    }
                    break;
                }
                k++;
            }
        }
    }
}

{
    cpuid (2, & regs [0], & regs [1], & regs [2], & regs [3]);
    for (j = 0; j < 3; j++) {
        if (regs[j] & (1 << 31))
            regs[j] = 0;
    }
    for (j = 1; j < 16; j++) {
        unsigned char des = dp[j];
        unsigned char k = 0;
        while (cache_table[k].descriptor != 0) {
            if (cache_table[k].descriptor == des) {
                if (only_trace && cache_table[k].cache_type != LVL_TRACE)
                    break;
                switch (cache_table[k].cache_type) {
                case LVL_1_INST :
                    l1i += cache_table[k].size;
                    break;
                case LVL_1_DATA :
                    l1d += cache_table[k].size;
                    break;
                case LVL_2 :
                    l2 += cache_table[k].size;
                    break;
                case LVL_3 :
                    l3 += cache_table[k].size;
                    break;
                case LVL_TRACE :
                    trace += cache_table[k].size;
                    break;
                }
                break;
            }
            k++;
        }
    }
}

{
    unsigned char des = dp[j];
    unsigned char k = 0;
    while (cache_table[k].descriptor != 0) {
        if (cache_table[k].descriptor == des) {
            if (only_trace && cache_table[k].cache_type != LVL_TRACE)
                break;
            switch (cache_table[k].cache_type) {
            case LVL_1_INST :
                l1i += cache_table[k].size;
                break;
            case LVL_1_DATA :
                l1d += cache_table[k].size;
                break;
            case LVL_2 :
                l2 += cache_table[k].size;
                break;
            case LVL_3 :
                l3 += cache_table[k].size;
                break;
            case LVL_TRACE :
                trace += cache_table[k].size;
                break;
            }
            break;
        }
        k++;
    }
}

{
    if (cache_table[k].descriptor == des) {
        if (only_trace && cache_table[k].cache_type != LVL_TRACE)
            break;
        switch (cache_table[k].cache_type) {
        case LVL_1_INST :
            l1i += cache_table[k].size;
            break;
        case LVL_1_DATA :
            l1d += cache_table[k].size;
            break;
        case LVL_2 :
            l2 += cache_table[k].size;
            break;
        case LVL_3 :
            l3 += cache_table[k].size;
            break;
        case LVL_TRACE :
            trace += cache_table[k].size;
            break;
        }
        break;
    }
    k++;
}

{
    if (only_trace && cache_table[k].cache_type != LVL_TRACE)
        break;
    switch (cache_table[k].cache_type) {
    case LVL_1_INST :
        l1i += cache_table[k].size;
        break;
    case LVL_1_DATA :
        l1d += cache_table[k].size;
        break;
    case LVL_2 :
        l2 += cache_table[k].size;
        break;
    case LVL_3 :
        l3 += cache_table[k].size;
        break;
    case LVL_TRACE :
        trace += cache_table[k].size;
        break;
    }
    break;
}

{
case LVL_1_INST :
    l1i += cache_table[k].size;
    break;
case LVL_1_DATA :
    l1d += cache_table[k].size;
    break;
case LVL_2 :
    l2 += cache_table[k].size;
    break;
case LVL_3 :
    l3 += cache_table[k].size;
    break;
case LVL_TRACE :
    trace += cache_table[k].size;
    break;
}

{
    l2 = new_l2;
}

{
    l3 = new_l3;
}

{
    int i;
    for (i = 0; i < num_cache_leaves; i++)
        cache_remove_shared_cpu_map (cpu, i);
    kfree (per_cpu (cpuid4_info, cpu));
    per_cpu (cpuid4_info, cpu) = NULL;
}

{
    struct _cpuid4_info *this_leaf;
    unsigned long j;
    int retval;
    cpumask_t oldmask;
    if (num_cache_leaves == 0)
        return -ENOENT;
    per_cpu (cpuid4_info, cpu) = kzalloc (sizeof (struct _cpuid4_info) * num_cache_leaves, GFP_KERNEL);
    if (per_cpu (cpuid4_info, cpu) == NULL)
        return -ENOMEM;
    oldmask = current->cpus_allowed;
    retval = set_cpus_allowed_ptr (current, &cpumask_of_cpu (cpu));
    if (retval)
        goto out;
    for (j = 0; j < num_cache_leaves; j++) {
        this_leaf = CPUID4_INFO_IDX (cpu, j);
        retval = cpuid4_cache_lookup (j, this_leaf);
        if (unlikely (retval < 0)) {
            int i;
            for (i = 0; i < j; i++)
                cache_remove_shared_cpu_map (cpu, i);
            break;
        }
        cache_shared_cpu_map_setup (cpu, j);
    }
    set_cpus_allowed_ptr (current, & oldmask);
out :
    if (retval) {
        kfree (per_cpu (cpuid4_info, cpu));
        per_cpu (cpuid4_info, cpu) = NULL;
    }
    return retval;
}

{
    this_leaf = CPUID4_INFO_IDX (cpu, j);
    retval = cpuid4_cache_lookup (j, this_leaf);
    if (unlikely (retval < 0)) {
        int i;
        for (i = 0; i < j; i++)
            cache_remove_shared_cpu_map (cpu, i);
        break;
    }
    cache_shared_cpu_map_setup (cpu, j);
}

{
    int i;
    for (i = 0; i < j; i++)
        cache_remove_shared_cpu_map (cpu, i);
    break;
}

{
    kfree (per_cpu (cpuid4_info, cpu));
    per_cpu (cpuid4_info, cpu) = NULL;
}

{
    struct cpuinfo_x86 *cpu = &cpu_data (cpuid);
    if (cpu->x86_vendor != X86_VENDOR_INTEL || !cpu_has (cpu, X86_FEATURE_EST))
        return 0;
    return 1;
}

{
    struct acpi_processor_performance *perf;
    int i;
    perf = data->acpi_data;
    for (i = 0; i < perf->state_count; i++) {
        if (value == perf->states[i].status)
            return data->freq_table[i].frequency;
    }
    return 0;
}

{
    if (value == perf->states[i].status)
        return data->freq_table[i].frequency;
}

{
    int i;
    struct acpi_processor_performance *perf;
    msr &= INTEL_MSR_RANGE;
    perf = data->acpi_data;
    for (i = 0; data->freq_table[i].frequency != CPUFREQ_TABLE_END; i++) {
        if (msr == perf->states[data->freq_table[i].index].status)
            return data->freq_table[i].frequency;
    }
    return data->freq_table[0].frequency;
}

{
    if (msr == perf->states[data->freq_table[i].index].status)
        return data->freq_table[i].frequency;
}

{
    switch (data->cpu_feature) {
    case SYSTEM_INTEL_MSR_CAPABLE :
        return extract_msr (val, data);
    case SYSTEM_IO_CAPABLE :
        return extract_io (val, data);
    default :
        return 0;
    }
}

{
case SYSTEM_INTEL_MSR_CAPABLE :
    return extract_msr (val, data);
case SYSTEM_IO_CAPABLE :
    return extract_io (val, data);
default :
    return 0;
}

{
    u32 h;
    switch (cmd->type) {
    case SYSTEM_INTEL_MSR_CAPABLE :
        rdmsr (cmd->addr.msr.reg, cmd->val, h);
        break;
    case SYSTEM_IO_CAPABLE :
        acpi_os_read_port ((acpi_io_address) cmd->addr.io.port, &cmd->val, (u32) cmd->addr.io.bit_width);
        break;
    default :
        break;
    }
}

{
case SYSTEM_INTEL_MSR_CAPABLE :
    rdmsr (cmd->addr.msr.reg, cmd->val, h);
    break;
case SYSTEM_IO_CAPABLE :
    acpi_os_read_port ((acpi_io_address) cmd->addr.io.port, &cmd->val, (u32) cmd->addr.io.bit_width);
    break;
default :
    break;
}

{
    u32 lo, hi;
    switch (cmd->type) {
    case SYSTEM_INTEL_MSR_CAPABLE :
        rdmsr (cmd->addr.msr.reg, lo, hi);
        lo = (lo & ~INTEL_MSR_RANGE) | (cmd->val & INTEL_MSR_RANGE);
        wrmsr (cmd -> addr.msr.reg, lo, hi);
        break;
    case SYSTEM_IO_CAPABLE :
        acpi_os_write_port ((acpi_io_address) cmd->addr.io.port, cmd->val, (u32) cmd->addr.io.bit_width);
        break;
    default :
        break;
    }
}

{
case SYSTEM_INTEL_MSR_CAPABLE :
    rdmsr (cmd->addr.msr.reg, lo, hi);
    lo = (lo & ~INTEL_MSR_RANGE) | (cmd->val & INTEL_MSR_RANGE);
    wrmsr (cmd -> addr.msr.reg, lo, hi);
    break;
case SYSTEM_IO_CAPABLE :
    acpi_os_write_port ((acpi_io_address) cmd->addr.io.port, cmd->val, (u32) cmd->addr.io.bit_width);
    break;
default :
    break;
}

{
    cpumask_t saved_mask = current->cpus_allowed;
    cmd->val = 0;
    set_cpus_allowed_ptr (current, & cmd -> mask);
    do_drv_read (cmd);
    set_cpus_allowed_ptr (current, & saved_mask);
}

{
    cpumask_t saved_mask = current->cpus_allowed;
    unsigned int i;
    for_each_cpu_mask (i, cmd -> mask) {
        set_cpus_allowed_ptr (current, &cpumask_of_cpu (i)) do_drv_write (cmd)
    } set_cpus_allowed_ptr (current, &saved_mask)
    return;
}

{
    struct acpi_processor_performance *perf;
    struct drv_cmd cmd;
    if (unlikely (cpus_empty (*mask)))
        return 0;
    switch (per_cpu (drv_data, first_cpu (*mask))->cpu_feature) {
    case SYSTEM_INTEL_MSR_CAPABLE :
        cmd.type = SYSTEM_INTEL_MSR_CAPABLE;
        cmd.addr.msr.reg = MSR_IA32_PERF_STATUS;
        break;
    case SYSTEM_IO_CAPABLE :
        cmd.type = SYSTEM_IO_CAPABLE;
        perf = per_cpu (drv_data, first_cpu (*mask))->acpi_data;
        cmd.addr.io.port = perf->control_register.address;
        cmd.addr.io.bit_width = perf->control_register.bit_width;
        break;
    default :
        return 0;
    }
    cmd.mask = *mask;
    drv_read (& cmd);
    dprintk ("get_cur_val = %u\n", cmd.val);
    return cmd.val;
}

{
case SYSTEM_INTEL_MSR_CAPABLE :
    cmd.type = SYSTEM_INTEL_MSR_CAPABLE;
    cmd.addr.msr.reg = MSR_IA32_PERF_STATUS;
    break;
case SYSTEM_IO_CAPABLE :
    cmd.type = SYSTEM_IO_CAPABLE;
    perf = per_cpu (drv_data, first_cpu (*mask))->acpi_data;
    cmd.addr.io.port = perf->control_register.address;
    cmd.addr.io.bit_width = perf->control_register.bit_width;
    break;
default :
    return 0;
}

{
    union {
        struct {
            u32 lo;
            u32 hi;
        } split;
        u64 whole;
    } aperf_cur, mperf_cur;
    cpumask_t saved_mask;
    unsigned int perf_percent;
    unsigned int retval;
    saved_mask = current->cpus_allowed;
    set_cpus_allowed_ptr (current, & cpumask_of_cpu (cpu));
    if (get_cpu () != cpu) {
        put_cpu ();
        return 0;
    }
    rdmsr (MSR_IA32_APERF, aperf_cur.split.lo, aperf_cur.split.hi);
    rdmsr (MSR_IA32_MPERF, mperf_cur.split.lo, mperf_cur.split.hi);
    wrmsr (MSR_IA32_APERF, 0, 0);
    wrmsr (MSR_IA32_MPERF, 0, 0);
    if (unlikely (((unsigned long) (-1) / 100) < aperf_cur.whole)) {
        int shift_count = 7;
        aperf_cur.whole >>= shift_count;
        mperf_cur.whole >>= shift_count;
    }
    if (aperf_cur.whole && mperf_cur.whole)
        perf_percent = (aperf_cur.whole * 100) / mperf_cur.whole;
    else
        perf_percent = 0;
    retval = per_cpu (drv_data, cpu)->max_freq * perf_percent / 100;
    put_cpu ();
    set_cpus_allowed_ptr (current, & saved_mask);
    dprintk ("cpu %d: performance percent %d\n", cpu, perf_percent);
    return retval;
}

{
    put_cpu ();
    return 0;
}

{
    int shift_count = 7;
    aperf_cur.whole >>= shift_count;
    mperf_cur.whole >>= shift_count;
}

{
    struct acpi_cpufreq_data *data = per_cpu (drv_data, cpu);
    unsigned int freq;
    unsigned int cached_freq;
    dprintk ("get_cur_freq_on_cpu (%d)\n", cpu);
    if (unlikely (data == NULL || data->acpi_data == NULL || data->freq_table == NULL)) {
        return 0;
    }
    cached_freq = data->freq_table[data->acpi_data->state].frequency;
    freq = extract_freq (get_cur_val (&cpumask_of_cpu (cpu)), data);
    if (freq != cached_freq) {
        data->resume = 1;
    }
    dprintk ("cur freq = %u\n", freq);
    return freq;
}

{
    return 0;
}

{
    data->resume = 1;
}

{
    unsigned int cur_freq;
    unsigned int i;
    for (i = 0; i < 100; i++) {
        cur_freq = extract_freq (get_cur_val (mask), data);
        if (cur_freq == freq)
            return 1;
        udelay (10);
    }
    return 0;
}

{
    cur_freq = extract_freq (get_cur_val (mask), data);
    if (cur_freq == freq)
        return 1;
    udelay (10);
}

{
    struct acpi_cpufreq_data *data = per_cpu (drv_data, policy->cpu);
    struct acpi_processor_performance *perf;
    struct cpufreq_freqs freqs;
    cpumask_t online_policy_cpus;
    struct drv_cmd cmd;
    unsigned int next_state = 0;
    unsigned int next_perf_state = 0;
    unsigned int i;
    int result = 0;
    dprintk ("acpi_cpufreq_target %d (%d)\n", target_freq, policy -> cpu);
    if (unlikely (data == NULL || data->acpi_data == NULL || data->freq_table == NULL)) {
        return -ENODEV;
    }
    perf = data->acpi_data;
    result = cpufreq_frequency_table_target (policy, data->freq_table, target_freq, relation, &next_state);
    if (unlikely (result))
        return -ENODEV;
    online_policy_cpus = policy->cpus;
    next_perf_state = data->freq_table[next_state].index;
    if (perf->state == next_perf_state) {
        if (unlikely (data->resume)) {
            dprintk ("Called after resume, resetting to P%d\n", next_perf_state);
            data->resume = 0;
        }
        else {
            dprintk ("Already at target state (P%d)\n", next_perf_state);
            return 0;
        }
    }
    switch (data->cpu_feature) {
    case SYSTEM_INTEL_MSR_CAPABLE :
        cmd.type = SYSTEM_INTEL_MSR_CAPABLE;
        cmd.addr.msr.reg = MSR_IA32_PERF_CTL;
        cmd.val = (u32) perf->states[next_perf_state].control;
        break;
    case SYSTEM_IO_CAPABLE :
        cmd.type = SYSTEM_IO_CAPABLE;
        cmd.addr.io.port = perf->control_register.address;
        cmd.addr.io.bit_width = perf->control_register.bit_width;
        cmd.val = (u32) perf->states[next_perf_state].control;
        break;
    default :
        return -ENODEV;
    }
    cpus_clear (cmd.mask);
    if (policy->shared_type != CPUFREQ_SHARED_TYPE_ANY)
        cmd.mask = online_policy_cpus;
    else
        cpu_set (policy->cpu, cmd.mask);
    freqs.old = perf->states[perf->state].core_frequency * 1000;
    freqs.new = data->freq_table[next_state].frequency;
    for_each_cpu_mask (i, cmd.mask) {freqs
        .cpu = i cpufreq_notify_transition (&freqs, CPUFREQ_PRECHANGE)
    } drv_write (&cmd)
    if (acpi_pstate_strict) {
        if (!check_freqs (&cmd.mask, freqs.new, data)) {
            dprintk ("acpi_cpufreq_target failed (%d)\n", policy -> cpu);
            return -EAGAIN;
        }
    }
    for_each_cpu_mask (i, cmd.mask) {freqs
        .cpu = i cpufreq_notify_transition (&freqs, CPUFREQ_POSTCHANGE)
    } perf->state
    = next_perf_state;
    return result;
}

{
    return -ENODEV;
}

{
    if (unlikely (data->resume)) {
        dprintk ("Called after resume, resetting to P%d\n", next_perf_state);
        data->resume = 0;
    }
    else {
        dprintk ("Already at target state (P%d)\n", next_perf_state);
        return 0;
    }
}

{
    dprintk ("Called after resume, resetting to P%d\n", next_perf_state);
    data->resume = 0;
}

{
    dprintk ("Already at target state (P%d)\n", next_perf_state);
    return 0;
}

{
case SYSTEM_INTEL_MSR_CAPABLE :
    cmd.type = SYSTEM_INTEL_MSR_CAPABLE;
    cmd.addr.msr.reg = MSR_IA32_PERF_CTL;
    cmd.val = (u32) perf->states[next_perf_state].control;
    break;
case SYSTEM_IO_CAPABLE :
    cmd.type = SYSTEM_IO_CAPABLE;
    cmd.addr.io.port = perf->control_register.address;
    cmd.addr.io.bit_width = perf->control_register.bit_width;
    cmd.val = (u32) perf->states[next_perf_state].control;
    break;
default :
    return -ENODEV;
}

{
    if (!check_freqs (&cmd.mask, freqs.new, data)) {
        dprintk ("acpi_cpufreq_target failed (%d)\n", policy -> cpu);
        return -EAGAIN;
    }
}

{
    dprintk ("acpi_cpufreq_target failed (%d)\n", policy -> cpu);
    return -EAGAIN;
}

{
    struct acpi_cpufreq_data *data = per_cpu (drv_data, policy->cpu);
    dprintk ("acpi_cpufreq_verify\n");
    return cpufreq_frequency_table_verify (policy, data->freq_table);
}

{
    struct acpi_processor_performance *perf = data->acpi_data;
    if (cpu_khz) {
        unsigned int i;
        unsigned long freq;
        unsigned long freqn = perf->states[0].core_frequency * 1000;
        for (i = 0; i < (perf->state_count - 1); i++) {
            freq = freqn;
            freqn = perf->states[i + 1].core_frequency * 1000;
            if ((2 * cpu_khz) > (freqn + freq)) {
                perf->state = i;
                return freq;
            }
        }
        perf->state = perf->state_count - 1;
        return freqn;
    }
    else {
        perf->state = 0;
        return perf->states[0].core_frequency * 1000;
    }
}

{
    unsigned int i;
    unsigned long freq;
    unsigned long freqn = perf->states[0].core_frequency * 1000;
    for (i = 0; i < (perf->state_count - 1); i++) {
        freq = freqn;
        freqn = perf->states[i + 1].core_frequency * 1000;
        if ((2 * cpu_khz) > (freqn + freq)) {
            perf->state = i;
            return freq;
        }
    }
    perf->state = perf->state_count - 1;
    return freqn;
}

{
    freq = freqn;
    freqn = perf->states[i + 1].core_frequency * 1000;
    if ((2 * cpu_khz) > (freqn + freq)) {
        perf->state = i;
        return freq;
    }
}

{
    perf->state = i;
    return freq;
}

{
    perf->state = 0;
    return perf->states[0].core_frequency * 1000;
}

{
    dprintk ("acpi_cpufreq_early_init\n");
    acpi_perf_data = alloc_percpu (struct acpi_processor_performance);
    if (!acpi_perf_data) {
        dprintk ("Memory allocation error for acpi_perf_data.\n");
        return -ENOMEM;
    }
    acpi_processor_preregister_performance (acpi_perf_data);
    return 0;
}

{
    dprintk ("Memory allocation error for acpi_perf_data.\n");
    return -ENOMEM;
}

{
    unsigned int i;
    unsigned int valid_states = 0;
    unsigned int cpu = policy->cpu;
    struct acpi_cpufreq_data *data;
    unsigned int result = 0;
    struct cpuinfo_x86 *c = &cpu_data (policy->cpu);
    struct acpi_processor_performance *perf;
    dprintk ("acpi_cpufreq_cpu_init\n");
    data = kzalloc (sizeof (struct acpi_cpufreq_data), GFP_KERNEL);
    if (!data)
        return -ENOMEM;
    data->acpi_data = percpu_ptr (acpi_perf_data, cpu);
    per_cpu (drv_data, cpu) = data;
    if (cpu_has (c, X86_FEATURE_CONSTANT_TSC))
        acpi_cpufreq_driver.flags |= CPUFREQ_CONST_LOOPS;
    result = acpi_processor_register_performance (data->acpi_data, cpu);
    if (result)
        goto err_free;
    perf = data->acpi_data;
    policy->shared_type = perf->shared_type;
    if (policy->shared_type == CPUFREQ_SHARED_TYPE_ALL || policy->shared_type == CPUFREQ_SHARED_TYPE_ANY) {
        policy->cpus = perf->shared_cpu_map;
    }
    policy->related_cpus = perf->shared_cpu_map;
    if (perf->state_count <= 1) {
        dprintk ("No P-States\n");
        result = -ENODEV;
        goto err_unreg;
    }
    if (perf->control_register.space_id != perf->status_register.space_id) {
        result = -ENODEV;
        goto err_unreg;
    }
    switch (perf->control_register.space_id) {
    case ACPI_ADR_SPACE_SYSTEM_IO :
        dprintk ("SYSTEM IO addr space\n");
        data->cpu_feature = SYSTEM_IO_CAPABLE;
        break;
    case ACPI_ADR_SPACE_FIXED_HARDWARE :
        dprintk ("HARDWARE addr space\n");
        if (!check_est_cpu (cpu)) {
            result = -ENODEV;
            goto err_unreg;
        }
        data->cpu_feature = SYSTEM_INTEL_MSR_CAPABLE;
        break;
    default :
        dprintk ("Unknown addr space %d\n", (u32) (perf->control_register.space_id));
        result = -ENODEV;
        goto err_unreg;
    }
    data->freq_table = kmalloc (sizeof (struct cpufreq_frequency_table) * (perf->state_count + 1), GFP_KERNEL);
    if (!data->freq_table) {
        result = -ENOMEM;
        goto err_unreg;
    }
    policy->cpuinfo.transition_latency = 0;
    for (i = 0; i < perf->state_count; i++) {
        if ((perf->states[i].transition_latency * 1000) > policy->cpuinfo.transition_latency)
            policy->cpuinfo.transition_latency = perf->states[i].transition_latency * 1000;
    }
    data->max_freq = perf->states[0].core_frequency * 1000;
    for (i = 0; i < perf->state_count; i++) {
        if (i > 0 && perf->states[i].core_frequency >= data->freq_table[valid_states - 1].frequency / 1000)
            continue;
        data->freq_table[valid_states].index = i;
        data->freq_table[valid_states].frequency = perf->states[i].core_frequency * 1000;
        valid_states++;
    }
    data->freq_table[valid_states].frequency = CPUFREQ_TABLE_END;
    perf->state = 0;
    result = cpufreq_frequency_table_cpuinfo (policy, data->freq_table);
    if (result)
        goto err_freqfree;
    switch (perf->control_register.space_id) {
    case ACPI_ADR_SPACE_SYSTEM_IO :
        policy->cur = acpi_cpufreq_guess_freq (data, policy->cpu);
        break;
    case ACPI_ADR_SPACE_FIXED_HARDWARE :
        acpi_cpufreq_driver.get = get_cur_freq_on_cpu;
        policy->cur = get_cur_freq_on_cpu (cpu);
        break;
    default :
        break;
    }
    acpi_processor_notify_smm (THIS_MODULE);
    if (c->x86_vendor == X86_VENDOR_INTEL && c->cpuid_level >= 6) {
        unsigned int ecx;
        ecx = cpuid_ecx (6);
        if (ecx & CPUID_6_ECX_APERFMPERF_CAPABILITY)
            acpi_cpufreq_driver.getavg = get_measured_perf;
    }
    dprintk ("CPU%u - ACPI performance management activated.\n", cpu);
    for (i = 0; i < perf->state_count; i++)
        dprintk ("     %cP%d: %d MHz, %d mW, %d uS\n", (i == perf->state ? '*' : ' '), i, (u32) perf->states[i].core_frequency, (u32) perf->states[i].power, (u32) perf->states[i].transition_latency);
    cpufreq_frequency_table_get_attr (data -> freq_table, policy -> cpu);
    data->resume = 1;
    return result;
err_freqfree :
    kfree (data->freq_table);
err_unreg :
    acpi_processor_unregister_performance (perf, cpu);
err_free :
    kfree (data);
    per_cpu (drv_data, cpu) = NULL;
    return result;
}

{
    policy->cpus = perf->shared_cpu_map;
}

{
    dprintk ("No P-States\n");
    result = -ENODEV;
    goto err_unreg;
}

{
    result = -ENODEV;
    goto err_unreg;
}

{
case ACPI_ADR_SPACE_SYSTEM_IO :
    dprintk ("SYSTEM IO addr space\n");
    data->cpu_feature = SYSTEM_IO_CAPABLE;
    break;
case ACPI_ADR_SPACE_FIXED_HARDWARE :
    dprintk ("HARDWARE addr space\n");
    if (!check_est_cpu (cpu)) {
        result = -ENODEV;
        goto err_unreg;
    }
    data->cpu_feature = SYSTEM_INTEL_MSR_CAPABLE;
    break;
default :
    dprintk ("Unknown addr space %d\n", (u32) (perf->control_register.space_id));
    result = -ENODEV;
    goto err_unreg;
}

{
    result = -ENODEV;
    goto err_unreg;
}

{
    result = -ENOMEM;
    goto err_unreg;
}

{
    if ((perf->states[i].transition_latency * 1000) > policy->cpuinfo.transition_latency)
        policy->cpuinfo.transition_latency = perf->states[i].transition_latency * 1000;
}

{
    if (i > 0 && perf->states[i].core_frequency >= data->freq_table[valid_states - 1].frequency / 1000)
        continue;
    data->freq_table[valid_states].index = i;
    data->freq_table[valid_states].frequency = perf->states[i].core_frequency * 1000;
    valid_states++;
}

{
case ACPI_ADR_SPACE_SYSTEM_IO :
    policy->cur = acpi_cpufreq_guess_freq (data, policy->cpu);
    break;
case ACPI_ADR_SPACE_FIXED_HARDWARE :
    acpi_cpufreq_driver.get = get_cur_freq_on_cpu;
    policy->cur = get_cur_freq_on_cpu (cpu);
    break;
default :
    break;
}

{
    unsigned int ecx;
    ecx = cpuid_ecx (6);
    if (ecx & CPUID_6_ECX_APERFMPERF_CAPABILITY)
        acpi_cpufreq_driver.getavg = get_measured_perf;
}

{
    struct acpi_cpufreq_data *data = per_cpu (drv_data, policy->cpu);
    dprintk ("acpi_cpufreq_cpu_exit\n");
    if (data) {
        cpufreq_frequency_table_put_attr (policy -> cpu);
        per_cpu (drv_data, policy->cpu) = NULL;
        acpi_processor_unregister_performance (data -> acpi_data, policy -> cpu);
        kfree (data);
    }
    return 0;
}

{
    cpufreq_frequency_table_put_attr (policy -> cpu);
    per_cpu (drv_data, policy->cpu) = NULL;
    acpi_processor_unregister_performance (data -> acpi_data, policy -> cpu);
    kfree (data);
}

{
    struct acpi_cpufreq_data *data = per_cpu (drv_data, policy->cpu);
    dprintk ("acpi_cpufreq_resume\n");
    data->resume = 1;
    return 0;
}

{
    int ret;
    dprintk ("acpi_cpufreq_init\n");
    ret = acpi_cpufreq_early_init ();
    if (ret)
        return ret;
    return cpufreq_register_driver (&acpi_cpufreq_driver);
}

{
    dprintk ("acpi_cpufreq_exit\n");
    cpufreq_unregister_driver (& acpi_cpufreq_driver);
    free_percpu (acpi_perf_data);
    return;
}

{
    return 800 + (fid * 100);
}

{
    return 1000 * find_freq_from_fid (fid);
}

{
    return data[pstate].frequency;
}

{
    if (fid < HI_FID_TABLE_BOTTOM)
        return 8 + (2 * fid);
    else
        return fid;
}

{
    u32 lo, hi;
    if (cpu_family == CPU_HW_PSTATE)
        return 0;
    rdmsr (MSR_FIDVID_STATUS, lo, hi);
    return lo & MSR_S_LO_CHANGE_PENDING ? 1 : 0;
}

{
    u32 lo, hi;
    u32 i = 0;
    if (cpu_family == CPU_HW_PSTATE) {
        rdmsr (MSR_PSTATE_STATUS, lo, hi);
        i = lo & HW_PSTATE_MASK;
        data->currpstate = i;
        return 0;
    }
    do {
        if (i++ > 10000) {
            dprintk ("detected change pending stuck\n");
            return 1;
        }
        rdmsr (MSR_FIDVID_STATUS, lo, hi);
    }
    while (lo & MSR_S_LO_CHANGE_PENDING);
    data->currvid = hi & MSR_S_HI_CURRENT_VID;
    data->currfid = lo & MSR_S_LO_CURRENT_FID;
    return 0;
}

{
    rdmsr (MSR_PSTATE_STATUS, lo, hi);
    i = lo & HW_PSTATE_MASK;
    data->currpstate = i;
    return 0;
}

{
    if (i++ > 10000) {
        dprintk ("detected change pending stuck\n");
        return 1;
    }
    rdmsr (MSR_FIDVID_STATUS, lo, hi);
}

{
    dprintk ("detected change pending stuck\n");
    return 1;
}

{
    udelay ((1 << data -> irt) * 10);
    return;
}

{
    udelay (data -> vstable * VST_UNITS_20US);
    return;
}

{
    u32 lo, hi;
    u8 fid, vid;
    rdmsr (MSR_FIDVID_STATUS, lo, hi);
    vid = hi & MSR_S_HI_CURRENT_VID;
    fid = lo & MSR_S_LO_CURRENT_FID;
    lo = fid | (vid << MSR_C_LO_VID_SHIFT);
    hi = MSR_C_HI_STP_GNT_BENIGN;
    dprintk ("cpu%d, init lo 0x%x, hi 0x%x\n", smp_processor_id (), lo, hi);
    wrmsr (MSR_FIDVID_CTL, lo, hi);
}

{
    u32 lo;
    u32 savevid = data->currvid;
    u32 i = 0;
    if ((fid & INVALID_FID_MASK) || (data->currvid & INVALID_VID_MASK)) {
        printk (KERN_ERR PFX "internal error - overflow on fid write\n");
        return 1;
    }
    lo = fid | (data->currvid << MSR_C_LO_VID_SHIFT) | MSR_C_LO_INIT_FID_VID;
    dprintk ("writing fid 0x%x, lo 0x%x, hi 0x%x\n", fid, lo, data -> plllock * PLL_LOCK_CONVERSION);
    do {
        wrmsr (MSR_FIDVID_CTL, lo, data -> plllock * PLL_LOCK_CONVERSION);
        if (i++ > 100) {
            printk (KERN_ERR PFX "Hardware error - pending bit very stuck - no further pstate changes possible\n");
            return 1;
        }
    }
    while (query_current_values_with_pending_wait (data));
    count_off_irt (data);
    if (savevid != data->currvid) {
        printk (KERN_ERR PFX "vid change on fid trans, old 0x%x, new 0x%x\n", savevid, data -> currvid);
        return 1;
    }
    if (fid != data->currfid) {
        printk (KERN_ERR PFX "fid trans failed, fid 0x%x, curr 0x%x\n", fid, data -> currfid);
        return 1;
    }
    return 0;
}

{
    printk (KERN_ERR PFX "internal error - overflow on fid write\n");
    return 1;
}

{
    wrmsr (MSR_FIDVID_CTL, lo, data -> plllock * PLL_LOCK_CONVERSION);
    if (i++ > 100) {
        printk (KERN_ERR PFX "Hardware error - pending bit very stuck - no further pstate changes possible\n");
        return 1;
    }
}

{
    printk (KERN_ERR PFX "Hardware error - pending bit very stuck - no further pstate changes possible\n");
    return 1;
}

{
    printk (KERN_ERR PFX "vid change on fid trans, old 0x%x, new 0x%x\n", savevid, data -> currvid);
    return 1;
}

{
    printk (KERN_ERR PFX "fid trans failed, fid 0x%x, curr 0x%x\n", fid, data -> currfid);
    return 1;
}

{
    u32 lo;
    u32 savefid = data->currfid;
    int i = 0;
    if ((data->currfid & INVALID_FID_MASK) || (vid & INVALID_VID_MASK)) {
        printk (KERN_ERR PFX "internal error - overflow on vid write\n");
        return 1;
    }
    lo = data->currfid | (vid << MSR_C_LO_VID_SHIFT) | MSR_C_LO_INIT_FID_VID;
    dprintk ("writing vid 0x%x, lo 0x%x, hi 0x%x\n", vid, lo, STOP_GRANT_5NS);
    do {
        wrmsr (MSR_FIDVID_CTL, lo, STOP_GRANT_5NS);
        if (i++ > 100) {
            printk (KERN_ERR PFX "internal error - pending bit very stuck - no further pstate changes possible\n");
            return 1;
        }
    }
    while (query_current_values_with_pending_wait (data));
    if (savefid != data->currfid) {
        printk (KERN_ERR PFX "fid changed on vid trans, old 0x%x new 0x%x\n", savefid, data -> currfid);
        return 1;
    }
    if (vid != data->currvid) {
        printk (KERN_ERR PFX "vid trans failed, vid 0x%x, curr 0x%x\n", vid, data -> currvid);
        return 1;
    }
    return 0;
}

{
    printk (KERN_ERR PFX "internal error - overflow on vid write\n");
    return 1;
}

{
    wrmsr (MSR_FIDVID_CTL, lo, STOP_GRANT_5NS);
    if (i++ > 100) {
        printk (KERN_ERR PFX "internal error - pending bit very stuck - no further pstate changes possible\n");
        return 1;
    }
}

{
    printk (KERN_ERR PFX "internal error - pending bit very stuck - no further pstate changes possible\n");
    return 1;
}

{
    printk (KERN_ERR PFX "fid changed on vid trans, old 0x%x new 0x%x\n", savefid, data -> currfid);
    return 1;
}

{
    printk (KERN_ERR PFX "vid trans failed, vid 0x%x, curr 0x%x\n", vid, data -> currvid);
    return 1;
}

{
    if ((data->currvid - reqvid) > step)
        reqvid = data->currvid - step;
    if (write_new_vid (data, reqvid))
        return 1;
    count_off_vst (data);
    return 0;
}

{
    wrmsr (MSR_PSTATE_CTRL, pstate, 0);
    data->currpstate = pstate;
    return 0;
}

{
    if (core_voltage_pre_transition (data, reqvid))
        return 1;
    if (core_frequency_transition (data, reqfid))
        return 1;
    if (core_voltage_post_transition (data, reqvid))
        return 1;
    if (query_current_values_with_pending_wait (data))
        return 1;
    if ((reqfid != data->currfid) || (reqvid != data->currvid)) {
        printk (KERN_ERR PFX "failed (cpu%d): req 0x%x 0x%x, curr 0x%x 0x%x\n", smp_processor_id (), reqfid, reqvid, data -> currfid, data -> currvid);
        return 1;
    }
    dprintk ("transitioned (cpu%d): new fid 0x%x, vid 0x%x\n", smp_processor_id (), data -> currfid, data -> currvid);
    return 0;
}

{
    printk (KERN_ERR PFX "failed (cpu%d): req 0x%x 0x%x, curr 0x%x 0x%x\n", smp_processor_id (), reqfid, reqvid, data -> currfid, data -> currvid);
    return 1;
}

{
    u32 rvosteps = data->rvo;
    u32 savefid = data->currfid;
    u32 maxvid, lo;
    dprintk ("ph1 (cpu%d): start, currfid 0x%x, currvid 0x%x, reqvid 0x%x, rvo 0x%x\n", smp_processor_id (), data -> currfid, data -> currvid, reqvid, data -> rvo);
    rdmsr (MSR_FIDVID_STATUS, lo, maxvid);
    maxvid = 0x1f & (maxvid >> 16);
    dprintk ("ph1 maxvid=0x%x\n", maxvid);
    if (reqvid < maxvid)
        reqvid = maxvid;
    while (data->currvid > reqvid) {
        dprintk ("ph1: curr 0x%x, req vid 0x%x\n", data -> currvid, reqvid);
        if (decrease_vid_code_by_step (data, reqvid, data->vidmvs))
            return 1;
    }
    while ((rvosteps > 0) && ((data->rvo + data->currvid) > reqvid)) {
        if (data->currvid == maxvid) {
            rvosteps = 0;
        }
        else {
            dprintk ("ph1: changing vid for rvo, req 0x%x\n", data -> currvid - 1);
            if (decrease_vid_code_by_step (data, data->currvid - 1, 1))
                return 1;
            rvosteps--;
        }
    }
    if (query_current_values_with_pending_wait (data))
        return 1;
    if (savefid != data->currfid) {
        printk (KERN_ERR PFX "ph1 err, currfid changed 0x%x\n", data -> currfid);
        return 1;
    }
    dprintk ("ph1 complete, currfid 0x%x, currvid 0x%x\n", data -> currfid, data -> currvid);
    return 0;
}

{
    dprintk ("ph1: curr 0x%x, req vid 0x%x\n", data -> currvid, reqvid);
    if (decrease_vid_code_by_step (data, reqvid, data->vidmvs))
        return 1;
}

{
    if (data->currvid == maxvid) {
        rvosteps = 0;
    }
    else {
        dprintk ("ph1: changing vid for rvo, req 0x%x\n", data -> currvid - 1);
        if (decrease_vid_code_by_step (data, data->currvid - 1, 1))
            return 1;
        rvosteps--;
    }
}

{
    rvosteps = 0;
}

{
    dprintk ("ph1: changing vid for rvo, req 0x%x\n", data -> currvid - 1);
    if (decrease_vid_code_by_step (data, data->currvid - 1, 1))
        return 1;
    rvosteps--;
}

{
    printk (KERN_ERR PFX "ph1 err, currfid changed 0x%x\n", data -> currfid);
    return 1;
}

{
    u32 vcoreqfid, vcocurrfid, vcofiddiff, fid_interval, savevid = data->currvid;
    if ((reqfid < HI_FID_TABLE_BOTTOM) && (data->currfid < HI_FID_TABLE_BOTTOM)) {
        printk (KERN_ERR PFX "ph2: illegal lo-lo transition 0x%x 0x%x\n", reqfid, data -> currfid);
        return 1;
    }
    if (data->currfid == reqfid) {
        printk (KERN_ERR PFX "ph2 null fid transition 0x%x\n", data -> currfid);
        return 0;
    }
    dprintk ("ph2 (cpu%d): starting, currfid 0x%x, currvid 0x%x, reqfid 0x%x\n", smp_processor_id (), data -> currfid, data -> currvid, reqfid);
    vcoreqfid = convert_fid_to_vco_fid (reqfid);
    vcocurrfid = convert_fid_to_vco_fid (data->currfid);
    vcofiddiff = vcocurrfid > vcoreqfid ? vcocurrfid - vcoreqfid : vcoreqfid - vcocurrfid;
    while (vcofiddiff > 2) {
        (data->currfid & 1) ? (fid_interval = 1) : (fid_interval = 2);
        if (reqfid > data->currfid) {
            if (data->currfid > LO_FID_TABLE_TOP) {
                if (write_new_fid (data, data->currfid + fid_interval)) {
                    return 1;
                }
            }
            else {
                if (write_new_fid (data, 2 + convert_fid_to_vco_fid (data->currfid))) {
                    return 1;
                }
            }
        }
        else {
            if (write_new_fid (data, data->currfid - fid_interval))
                return 1;
        }
        vcocurrfid = convert_fid_to_vco_fid (data->currfid);
        vcofiddiff = vcocurrfid > vcoreqfid ? vcocurrfid - vcoreqfid : vcoreqfid - vcocurrfid;
    }
    if (write_new_fid (data, reqfid))
        return 1;
    if (query_current_values_with_pending_wait (data))
        return 1;
    if (data->currfid != reqfid) {
        printk (KERN_ERR PFX "ph2: mismatch, failed fid transition, curr 0x%x, req 0x%x\n", data -> currfid, reqfid);
        return 1;
    }
    if (savevid != data->currvid) {
        printk (KERN_ERR PFX "ph2: vid changed, save 0x%x, curr 0x%x\n", savevid, data -> currvid);
        return 1;
    }
    dprintk ("ph2 complete, currfid 0x%x, currvid 0x%x\n", data -> currfid, data -> currvid);
    return 0;
}

{
    printk (KERN_ERR PFX "ph2: illegal lo-lo transition 0x%x 0x%x\n", reqfid, data -> currfid);
    return 1;
}

{
    printk (KERN_ERR PFX "ph2 null fid transition 0x%x\n", data -> currfid);
    return 0;
}

{
    (data->currfid & 1) ? (fid_interval = 1) : (fid_interval = 2);
    if (reqfid > data->currfid) {
        if (data->currfid > LO_FID_TABLE_TOP) {
            if (write_new_fid (data, data->currfid + fid_interval)) {
                return 1;
            }
        }
        else {
            if (write_new_fid (data, 2 + convert_fid_to_vco_fid (data->currfid))) {
                return 1;
            }
        }
    }
    else {
        if (write_new_fid (data, data->currfid - fid_interval))
            return 1;
    }
    vcocurrfid = convert_fid_to_vco_fid (data->currfid);
    vcofiddiff = vcocurrfid > vcoreqfid ? vcocurrfid - vcoreqfid : vcoreqfid - vcocurrfid;
}

{
    if (data->currfid > LO_FID_TABLE_TOP) {
        if (write_new_fid (data, data->currfid + fid_interval)) {
            return 1;
        }
    }
    else {
        if (write_new_fid (data, 2 + convert_fid_to_vco_fid (data->currfid))) {
            return 1;
        }
    }
}

{
    if (write_new_fid (data, data->currfid + fid_interval)) {
        return 1;
    }
}

{
    return 1;
}

{
    if (write_new_fid (data, 2 + convert_fid_to_vco_fid (data->currfid))) {
        return 1;
    }
}

{
    return 1;
}

{
    if (write_new_fid (data, data->currfid - fid_interval))
        return 1;
}

{
    printk (KERN_ERR PFX "ph2: mismatch, failed fid transition, curr 0x%x, req 0x%x\n", data -> currfid, reqfid);
    return 1;
}

{
    printk (KERN_ERR PFX "ph2: vid changed, save 0x%x, curr 0x%x\n", savevid, data -> currvid);
    return 1;
}

{
    u32 savefid = data->currfid;
    u32 savereqvid = reqvid;
    dprintk ("ph3 (cpu%d): starting, currfid 0x%x, currvid 0x%x\n", smp_processor_id (), data -> currfid, data -> currvid);
    if (reqvid != data->currvid) {
        if (write_new_vid (data, reqvid))
            return 1;
        if (savefid != data->currfid) {
            printk (KERN_ERR PFX "ph3: bad fid change, save 0x%x, curr 0x%x\n", savefid, data -> currfid);
            return 1;
        }
        if (data->currvid != reqvid) {
            printk (KERN_ERR PFX "ph3: failed vid transition\n, req 0x%x, curr 0x%x", reqvid, data -> currvid);
            return 1;
        }
    }
    if (query_current_values_with_pending_wait (data))
        return 1;
    if (savereqvid != data->currvid) {
        dprintk ("ph3 failed, currvid 0x%x\n", data -> currvid);
        return 1;
    }
    if (savefid != data->currfid) {
        dprintk ("ph3 failed, currfid changed 0x%x\n", data -> currfid);
        return 1;
    }
    dprintk ("ph3 complete, currfid 0x%x, currvid 0x%x\n", data -> currfid, data -> currvid);
    return 0;
}

{
    if (write_new_vid (data, reqvid))
        return 1;
    if (savefid != data->currfid) {
        printk (KERN_ERR PFX "ph3: bad fid change, save 0x%x, curr 0x%x\n", savefid, data -> currfid);
        return 1;
    }
    if (data->currvid != reqvid) {
        printk (KERN_ERR PFX "ph3: failed vid transition\n, req 0x%x, curr 0x%x", reqvid, data -> currvid);
        return 1;
    }
}

{
    printk (KERN_ERR PFX "ph3: bad fid change, save 0x%x, curr 0x%x\n", savefid, data -> currfid);
    return 1;
}

{
    printk (KERN_ERR PFX "ph3: failed vid transition\n, req 0x%x, curr 0x%x", reqvid, data -> currvid);
    return 1;
}

{
    dprintk ("ph3 failed, currvid 0x%x\n", data -> currvid);
    return 1;
}

{
    dprintk ("ph3 failed, currfid changed 0x%x\n", data -> currfid);
    return 1;
}

{
    cpumask_t oldmask;
    u32 eax, ebx, ecx, edx;
    unsigned int rc = 0;
    oldmask = current->cpus_allowed;
    set_cpus_allowed_ptr (current, & cpumask_of_cpu (cpu));
    if (smp_processor_id () != cpu) {
        printk (KERN_ERR PFX "limiting to cpu %u failed\n", cpu);
        goto out;
    }
    if (current_cpu_data.x86_vendor != X86_VENDOR_AMD)
        goto out;
    eax = cpuid_eax (CPUID_PROCESSOR_SIGNATURE);
    if (((eax & CPUID_XFAM) != CPUID_XFAM_K8) && ((eax & CPUID_XFAM) < CPUID_XFAM_10H))
        goto out;
    if ((eax & CPUID_XFAM) == CPUID_XFAM_K8) {
        if (((eax & CPUID_USE_XFAM_XMOD) != CPUID_USE_XFAM_XMOD) || ((eax & CPUID_XMOD) > CPUID_XMOD_REV_MASK)) {
            printk (KERN_INFO PFX "Processor cpuid %x not supported\n", eax);
            goto out;
        }
        eax = cpuid_eax (CPUID_GET_MAX_CAPABILITIES);
        if (eax < CPUID_FREQ_VOLT_CAPABILITIES) {
            printk (KERN_INFO PFX "No frequency change capabilities detected\n");
            goto out;
        }
        cpuid (CPUID_FREQ_VOLT_CAPABILITIES, & eax, & ebx, & ecx, & edx);
        if ((edx & P_STATE_TRANSITION_CAPABLE) != P_STATE_TRANSITION_CAPABLE) {
            printk (KERN_INFO PFX "Power state transitions not supported\n");
            goto out;
        }
    }
    else {
        cpuid (CPUID_FREQ_VOLT_CAPABILITIES, & eax, & ebx, & ecx, & edx);
        if ((edx & USE_HW_PSTATE) == USE_HW_PSTATE)
            cpu_family = CPU_HW_PSTATE;
        else
            goto out;
    }
    rc = 1;
out :
    set_cpus_allowed_ptr (current, &oldmask);
    return rc;
}

{
    printk (KERN_ERR PFX "limiting to cpu %u failed\n", cpu);
    goto out;
}

{
    if (((eax & CPUID_USE_XFAM_XMOD) != CPUID_USE_XFAM_XMOD) || ((eax & CPUID_XMOD) > CPUID_XMOD_REV_MASK)) {
        printk (KERN_INFO PFX "Processor cpuid %x not supported\n", eax);
        goto out;
    }
    eax = cpuid_eax (CPUID_GET_MAX_CAPABILITIES);
    if (eax < CPUID_FREQ_VOLT_CAPABILITIES) {
        printk (KERN_INFO PFX "No frequency change capabilities detected\n");
        goto out;
    }
    cpuid (CPUID_FREQ_VOLT_CAPABILITIES, & eax, & ebx, & ecx, & edx);
    if ((edx & P_STATE_TRANSITION_CAPABLE) != P_STATE_TRANSITION_CAPABLE) {
        printk (KERN_INFO PFX "Power state transitions not supported\n");
        goto out;
    }
}

{
    printk (KERN_INFO PFX "Processor cpuid %x not supported\n", eax);
    goto out;
}

{
    printk (KERN_INFO PFX "No frequency change capabilities detected\n");
    goto out;
}

{
    printk (KERN_INFO PFX "Power state transitions not supported\n");
    goto out;
}

{
    cpuid (CPUID_FREQ_VOLT_CAPABILITIES, & eax, & ebx, & ecx, & edx);
    if ((edx & USE_HW_PSTATE) == USE_HW_PSTATE)
        cpu_family = CPU_HW_PSTATE;
    else
        goto out;
}

{
    unsigned int j;
    u8 lastfid = 0xff;
    for (j = 0; j < data->numps; j++) {
        if (pst[j].vid > LEAST_VID) {
            printk (KERN_ERR PFX "vid %d invalid : 0x%x\n", j, pst [j].vid);
            return -EINVAL;
        }
        if (pst[j].vid < data->rvo) {
            printk (KERN_ERR BFX "0 vid exceeded with pstate %d\n", j);
            return -ENODEV;
        }
        if (pst[j].vid < maxvid + data->rvo) {
            printk (KERN_ERR BFX "maxvid exceeded with pstate %d\n", j);
            return -ENODEV;
        }
        if (pst[j].fid > MAX_FID) {
            printk (KERN_ERR BFX "maxfid exceeded with pstate %d\n", j);
            return -ENODEV;
        }
        if (j && (pst[j].fid < HI_FID_TABLE_BOTTOM)) {
            printk (KERN_ERR BFX "two low fids - %d : 0x%x\n", j, pst [j].fid);
            return -EINVAL;
        }
        if (pst[j].fid < lastfid)
            lastfid = pst[j].fid;
    }
    if (lastfid & 1) {
        printk (KERN_ERR BFX "lastfid invalid\n");
        return -EINVAL;
    }
    if (lastfid > LO_FID_TABLE_TOP)
        printk (KERN_INFO BFX "first fid not from lo freq table\n");
    return 0;
}

{
    if (pst[j].vid > LEAST_VID) {
        printk (KERN_ERR PFX "vid %d invalid : 0x%x\n", j, pst [j].vid);
        return -EINVAL;
    }
    if (pst[j].vid < data->rvo) {
        printk (KERN_ERR BFX "0 vid exceeded with pstate %d\n", j);
        return -ENODEV;
    }
    if (pst[j].vid < maxvid + data->rvo) {
        printk (KERN_ERR BFX "maxvid exceeded with pstate %d\n", j);
        return -ENODEV;
    }
    if (pst[j].fid > MAX_FID) {
        printk (KERN_ERR BFX "maxfid exceeded with pstate %d\n", j);
        return -ENODEV;
    }
    if (j && (pst[j].fid < HI_FID_TABLE_BOTTOM)) {
        printk (KERN_ERR BFX "two low fids - %d : 0x%x\n", j, pst [j].fid);
        return -EINVAL;
    }
    if (pst[j].fid < lastfid)
        lastfid = pst[j].fid;
}

{
    printk (KERN_ERR PFX "vid %d invalid : 0x%x\n", j, pst [j].vid);
    return -EINVAL;
}

{
    printk (KERN_ERR BFX "0 vid exceeded with pstate %d\n", j);
    return -ENODEV;
}

{
    printk (KERN_ERR BFX "maxvid exceeded with pstate %d\n", j);
    return -ENODEV;
}

{
    printk (KERN_ERR BFX "maxfid exceeded with pstate %d\n", j);
    return -ENODEV;
}

{
    printk (KERN_ERR BFX "two low fids - %d : 0x%x\n", j, pst [j].fid);
    return -EINVAL;
}

{
    printk (KERN_ERR BFX "lastfid invalid\n");
    return -EINVAL;
}

{
    int j;
    for (j = 0; j < data->numps; j++) {
        if (data->powernow_table[j].frequency != CPUFREQ_ENTRY_INVALID) {
            if (cpu_family == CPU_HW_PSTATE) {
                printk (KERN_INFO PFX "   %d : pstate %d (%d MHz)\n", j, data -> powernow_table [j].index, data -> powernow_table [j].frequency / 1000);
            }
            else {
                printk (KERN_INFO PFX "   %d : fid 0x%x (%d MHz), vid 0x%x\n", j, data -> powernow_table [j].index & 0xff, data -> powernow_table [j].frequency / 1000, data -> powernow_table [j].index >> 8);
            }
        }
    }
    if (data->batps)
        printk (KERN_INFO PFX "Only %d pstates on battery\n", data->batps);
}

{
    if (data->powernow_table[j].frequency != CPUFREQ_ENTRY_INVALID) {
        if (cpu_family == CPU_HW_PSTATE) {
            printk (KERN_INFO PFX "   %d : pstate %d (%d MHz)\n", j, data -> powernow_table [j].index, data -> powernow_table [j].frequency / 1000);
        }
        else {
            printk (KERN_INFO PFX "   %d : fid 0x%x (%d MHz), vid 0x%x\n", j, data -> powernow_table [j].index & 0xff, data -> powernow_table [j].frequency / 1000, data -> powernow_table [j].index >> 8);
        }
    }
}

{
    if (cpu_family == CPU_HW_PSTATE) {
        printk (KERN_INFO PFX "   %d : pstate %d (%d MHz)\n", j, data -> powernow_table [j].index, data -> powernow_table [j].frequency / 1000);
    }
    else {
        printk (KERN_INFO PFX "   %d : fid 0x%x (%d MHz), vid 0x%x\n", j, data -> powernow_table [j].index & 0xff, data -> powernow_table [j].frequency / 1000, data -> powernow_table [j].index >> 8);
    }
}

{
    printk (KERN_INFO PFX "   %d : pstate %d (%d MHz)\n", j, data -> powernow_table [j].index, data -> powernow_table [j].frequency / 1000);
}

{
    printk (KERN_INFO PFX "   %d : fid 0x%x (%d MHz), vid 0x%x\n", j, data -> powernow_table [j].index & 0xff, data -> powernow_table [j].frequency / 1000, data -> powernow_table [j].index >> 8);
}

{
    struct cpufreq_frequency_table *powernow_table;
    unsigned int j;
    if (data->batps) {
        printk (KERN_WARNING PFX "Only %d pstates usable (use ACPI driver for full range\n", data -> batps);
        data->numps = data->batps;
    }
    for (j = 1; j < data->numps; j++) {
        if (pst[j - 1].fid >= pst[j].fid) {
            printk (KERN_ERR PFX "PST out of sequence\n");
            return -EINVAL;
        }
    }
    if (data->numps < 2) {
        printk (KERN_ERR PFX "no p states to transition\n");
        return -ENODEV;
    }
    if (check_pst_table (data, pst, maxvid))
        return -EINVAL;
    powernow_table = kmalloc ((sizeof (struct cpufreq_frequency_table) * (data->numps + 1)), GFP_KERNEL);
    if (!powernow_table) {
        printk (KERN_ERR PFX "powernow_table memory alloc failure\n");
        return -ENOMEM;
    }
    for (j = 0; j < data->numps; j++) {
        powernow_table[j].index = pst[j].fid;
        powernow_table[j].index |= (pst[j].vid << 8);
        powernow_table[j].frequency = find_khz_freq_from_fid (pst[j].fid);
    }
    powernow_table[data->numps].frequency = CPUFREQ_TABLE_END;
    powernow_table[data->numps].index = 0;
    if (query_current_values_with_pending_wait (data)) {
        kfree (powernow_table);
        return -EIO;
    }
    dprintk ("cfid 0x%x, cvid 0x%x\n", data -> currfid, data -> currvid);
    data->powernow_table = powernow_table;
    if (first_cpu (per_cpu (cpu_core_map, data->cpu)) == data->cpu)
        print_basics (data);
    for (j = 0; j < data->numps; j++)
        if ((pst[j].fid == data->currfid) && (pst[j].vid == data->currvid))
            return 0;
    dprintk ("currfid/vid do not match PST, ignoring\n");
    return 0;
}

{
    printk (KERN_WARNING PFX "Only %d pstates usable (use ACPI driver for full range\n", data -> batps);
    data->numps = data->batps;
}

{
    if (pst[j - 1].fid >= pst[j].fid) {
        printk (KERN_ERR PFX "PST out of sequence\n");
        return -EINVAL;
    }
}

{
    printk (KERN_ERR PFX "PST out of sequence\n");
    return -EINVAL;
}

{
    printk (KERN_ERR PFX "no p states to transition\n");
    return -ENODEV;
}

{
    printk (KERN_ERR PFX "powernow_table memory alloc failure\n");
    return -ENOMEM;
}

{
    powernow_table[j].index = pst[j].fid;
    powernow_table[j].index |= (pst[j].vid << 8);
    powernow_table[j].frequency = find_khz_freq_from_fid (pst[j].fid);
}

{
    kfree (powernow_table);
    return -EIO;
}

{
    struct psb_s *psb;
    unsigned int i;
    u32 mvs;
    u8 maxvid;
    u32 cpst = 0;
    u32 thiscpuid;
    for (i = 0xc0000; i < 0xffff0; i += 0x10) {
        psb = phys_to_virt (i);
        if (memcmp (psb, PSB_ID_STRING, PSB_ID_STRING_LEN) != 0)
            continue;
        dprintk ("found PSB header at 0x%p\n", psb);
        dprintk ("table vers: 0x%x\n", psb -> tableversion);
        if (psb->tableversion != PSB_VERSION_1_4) {
            printk (KERN_ERR BFX "PSB table is not v1.4\n");
            return -ENODEV;
        }
        dprintk ("flags: 0x%x\n", psb -> flags1);
        if (psb->flags1) {
            printk (KERN_ERR BFX "unknown flags\n");
            return -ENODEV;
        }
        data->vstable = psb->vstable;
        dprintk ("voltage stabilization time: %d(*20us)\n", data -> vstable);
        dprintk ("flags2: 0x%x\n", psb -> flags2);
        data->rvo = psb->flags2 & 3;
        data->irt = ((psb->flags2) >> 2) & 3;
        mvs = ((psb->flags2) >> 4) & 3;
        data->vidmvs = 1 << mvs;
        data->batps = ((psb->flags2) >> 6) & 3;
        dprintk ("ramp voltage offset: %d\n", data -> rvo);
        dprintk ("isochronous relief time: %d\n", data -> irt);
        dprintk ("maximum voltage step: %d - 0x%x\n", mvs, data -> vidmvs);
        dprintk ("numpst: 0x%x\n", psb -> num_tables);
        cpst = psb->num_tables;
        if ((psb->cpuid == 0x00000fc0) || (psb->cpuid == 0x00000fe0)) {
            thiscpuid = cpuid_eax (CPUID_PROCESSOR_SIGNATURE);
            if ((thiscpuid == 0x00000fc0) || (thiscpuid == 0x00000fe0)) {
                cpst = 1;
            }
        }
        if (cpst != 1) {
            printk (KERN_ERR BFX "numpst must be 1\n");
            return -ENODEV;
        }
        data->plllock = psb->plllocktime;
        dprintk ("plllocktime: 0x%x (units 1us)\n", psb -> plllocktime);
        dprintk ("maxfid: 0x%x\n", psb -> maxfid);
        dprintk ("maxvid: 0x%x\n", psb -> maxvid);
        maxvid = psb->maxvid;
        data->numps = psb->numps;
        dprintk ("numpstates: 0x%x\n", data -> numps);
        return fill_powernow_table (data, (struct pst_s *) (psb + 1), maxvid);
    }
    printk (KERN_ERR PFX "BIOS error - no PSB or ACPI _PSS objects\n");
    return -ENODEV;
}

{
    psb = phys_to_virt (i);
    if (memcmp (psb, PSB_ID_STRING, PSB_ID_STRING_LEN) != 0)
        continue;
    dprintk ("found PSB header at 0x%p\n", psb);
    dprintk ("table vers: 0x%x\n", psb -> tableversion);
    if (psb->tableversion != PSB_VERSION_1_4) {
        printk (KERN_ERR BFX "PSB table is not v1.4\n");
        return -ENODEV;
    }
    dprintk ("flags: 0x%x\n", psb -> flags1);
    if (psb->flags1) {
        printk (KERN_ERR BFX "unknown flags\n");
        return -ENODEV;
    }
    data->vstable = psb->vstable;
    dprintk ("voltage stabilization time: %d(*20us)\n", data -> vstable);
    dprintk ("flags2: 0x%x\n", psb -> flags2);
    data->rvo = psb->flags2 & 3;
    data->irt = ((psb->flags2) >> 2) & 3;
    mvs = ((psb->flags2) >> 4) & 3;
    data->vidmvs = 1 << mvs;
    data->batps = ((psb->flags2) >> 6) & 3;
    dprintk ("ramp voltage offset: %d\n", data -> rvo);
    dprintk ("isochronous relief time: %d\n", data -> irt);
    dprintk ("maximum voltage step: %d - 0x%x\n", mvs, data -> vidmvs);
    dprintk ("numpst: 0x%x\n", psb -> num_tables);
    cpst = psb->num_tables;
    if ((psb->cpuid == 0x00000fc0) || (psb->cpuid == 0x00000fe0)) {
        thiscpuid = cpuid_eax (CPUID_PROCESSOR_SIGNATURE);
        if ((thiscpuid == 0x00000fc0) || (thiscpuid == 0x00000fe0)) {
            cpst = 1;
        }
    }
    if (cpst != 1) {
        printk (KERN_ERR BFX "numpst must be 1\n");
        return -ENODEV;
    }
    data->plllock = psb->plllocktime;
    dprintk ("plllocktime: 0x%x (units 1us)\n", psb -> plllocktime);
    dprintk ("maxfid: 0x%x\n", psb -> maxfid);
    dprintk ("maxvid: 0x%x\n", psb -> maxvid);
    maxvid = psb->maxvid;
    data->numps = psb->numps;
    dprintk ("numpstates: 0x%x\n", data -> numps);
    return fill_powernow_table (data, (struct pst_s *) (psb + 1), maxvid);
}

{
    printk (KERN_ERR BFX "PSB table is not v1.4\n");
    return -ENODEV;
}

{
    printk (KERN_ERR BFX "unknown flags\n");
    return -ENODEV;
}

{
    thiscpuid = cpuid_eax (CPUID_PROCESSOR_SIGNATURE);
    if ((thiscpuid == 0x00000fc0) || (thiscpuid == 0x00000fe0)) {
        cpst = 1;
    }
}

{
    cpst = 1;
}

{
    printk (KERN_ERR BFX "numpst must be 1\n");
    return -ENODEV;
}

{
    return -ENODEV;
}

{
    return;
}

{
    return;
}

{
    u32 fid = 0;
    u32 vid = 0;
    int res, i;
    struct cpufreq_freqs freqs;
    dprintk ("cpu %d transition to index %u\n", smp_processor_id (), index);
    fid = data->powernow_table[index].index & 0xFF;
    vid = (data->powernow_table[index].index & 0xFF00) >> 8;
    dprintk ("table matched fid 0x%x, giving vid 0x%x\n", fid, vid);
    if (query_current_values_with_pending_wait (data))
        return 1;
    if ((data->currvid == vid) && (data->currfid == fid)) {
        dprintk ("target matches current values (fid 0x%x, vid 0x%x)\n", fid, vid);
        return 0;
    }
    if ((fid < HI_FID_TABLE_BOTTOM) && (data->currfid < HI_FID_TABLE_BOTTOM)) {
        printk (KERN_ERR PFX "ignoring illegal change in lo freq table-%x to 0x%x\n", data -> currfid, fid);
        return 1;
    }
    dprintk ("cpu %d, changing to fid 0x%x, vid 0x%x\n", smp_processor_id (), fid, vid);
    freqs.old = find_khz_freq_from_fid (data->currfid);
    freqs.new = find_khz_freq_from_fid (fid);
    for_each_cpu_mask (i, * (data -> available_cores)) {freqs
        .cpu = i cpufreq_notify_transition (&freqs, CPUFREQ_PRECHANGE)
    } res
    = transition_fid_vid (data, fid, vid);
    freqs.new = find_khz_freq_from_fid (data->currfid);
    for_each_cpu_mask (i, * (data -> available_cores)) {freqs
        .cpu = i cpufreq_notify_transition (&freqs, CPUFREQ_POSTCHANGE)
    }
    return res;
}

{
    dprintk ("target matches current values (fid 0x%x, vid 0x%x)\n", fid, vid);
    return 0;
}

{
    printk (KERN_ERR PFX "ignoring illegal change in lo freq table-%x to 0x%x\n", data -> currfid, fid);
    return 1;
}

{
    u32 pstate = 0;
    int res, i;
    struct cpufreq_freqs freqs;
    dprintk ("cpu %d transition to index %u\n", smp_processor_id (), index);
    pstate = index & HW_PSTATE_MASK;
    if (pstate > data->max_hw_pstate)
        return 0;
    freqs.old = find_khz_freq_from_pstate (data->powernow_table, data->currpstate);
    freqs.new = find_khz_freq_from_pstate (data->powernow_table, pstate);
    for_each_cpu_mask (i, * (data -> available_cores)) {freqs
        .cpu = i cpufreq_notify_transition (&freqs, CPUFREQ_PRECHANGE)
    } res
    = transition_pstate (data, pstate);
    freqs.new = find_khz_freq_from_pstate (data->powernow_table, pstate);
    for_each_cpu_mask (i, * (data -> available_cores)) {freqs
        .cpu = i cpufreq_notify_transition (&freqs, CPUFREQ_POSTCHANGE)
    }
    return res;
}

{
    cpumask_t oldmask;
    struct powernow_k8_data *data = per_cpu (powernow_data, pol->cpu);
    u32 checkfid;
    u32 checkvid;
    unsigned int newstate;
    int ret = -EIO;
    if (!data)
        return -EINVAL;
    checkfid = data->currfid;
    checkvid = data->currvid;
    oldmask = current->cpus_allowed;
    set_cpus_allowed_ptr (current, & cpumask_of_cpu (pol -> cpu));
    if (smp_processor_id () != pol->cpu) {
        printk (KERN_ERR PFX "limiting to cpu %u failed\n", pol -> cpu);
        goto err_out;
    }
    if (pending_bit_stuck ()) {
        printk (KERN_ERR PFX "failing targ, change pending bit set\n");
        goto err_out;
    }
    dprintk ("targ: cpu %d, %d kHz, min %d, max %d, relation %d\n", pol -> cpu, targfreq, pol -> min, pol -> max, relation);
    if (query_current_values_with_pending_wait (data))
        goto err_out;
    if (cpu_family != CPU_HW_PSTATE) {
        dprintk ("targ: curr fid 0x%x, vid 0x%x\n", data -> currfid, data -> currvid);
        if ((checkvid != data->currvid) || (checkfid != data->currfid)) {
            printk (KERN_INFO PFX "error - out of sync, fix 0x%x 0x%x, vid 0x%x 0x%x\n", checkfid, data -> currfid, checkvid, data -> currvid);
        }
    }
    if (cpufreq_frequency_table_target (pol, data->powernow_table, targfreq, relation, &newstate))
        goto err_out;
    mutex_lock (& fidvid_mutex);
    powernow_k8_acpi_pst_values (data, newstate);
    if (cpu_family == CPU_HW_PSTATE)
        ret = transition_frequency_pstate (data, newstate);
    else
        ret = transition_frequency_fidvid (data, newstate);
    if (ret) {
        printk (KERN_ERR PFX "transition frequency failed\n");
        ret = 1;
        mutex_unlock (& fidvid_mutex);
        goto err_out;
    }
    mutex_unlock (& fidvid_mutex);
    if (cpu_family == CPU_HW_PSTATE)
        pol->cur = find_khz_freq_from_pstate (data->powernow_table, newstate);
    else
        pol->cur = find_khz_freq_from_fid (data->currfid);
    ret = 0;
err_out :
    set_cpus_allowed_ptr (current, &oldmask);
    return ret;
}

{
    printk (KERN_ERR PFX "limiting to cpu %u failed\n", pol -> cpu);
    goto err_out;
}

{
    printk (KERN_ERR PFX "failing targ, change pending bit set\n");
    goto err_out;
}

{
    dprintk ("targ: curr fid 0x%x, vid 0x%x\n", data -> currfid, data -> currvid);
    if ((checkvid != data->currvid) || (checkfid != data->currfid)) {
        printk (KERN_INFO PFX "error - out of sync, fix 0x%x 0x%x, vid 0x%x 0x%x\n", checkfid, data -> currfid, checkvid, data -> currvid);
    }
}

{
    printk (KERN_INFO PFX "error - out of sync, fix 0x%x 0x%x, vid 0x%x 0x%x\n", checkfid, data -> currfid, checkvid, data -> currvid);
}

{
    printk (KERN_ERR PFX "transition frequency failed\n");
    ret = 1;
    mutex_unlock (& fidvid_mutex);
    goto err_out;
}

{
    struct powernow_k8_data *data = per_cpu (powernow_data, pol->cpu);
    if (!data)
        return -EINVAL;
    return cpufreq_frequency_table_verify (pol, data->powernow_table);
}

{
    struct powernow_k8_data *data;
    cpumask_t oldmask;
    int rc;
    if (!cpu_online (pol->cpu))
        return -ENODEV;
    if (!check_supported_cpu (pol->cpu))
        return -ENODEV;
    data = kzalloc (sizeof (struct powernow_k8_data), GFP_KERNEL);
    if (!data) {
        printk (KERN_ERR PFX "unable to alloc powernow_k8_data");
        return -ENOMEM;
    }
    data->cpu = pol->cpu;
    if (powernow_k8_cpu_init_acpi (data)) {
        if (num_online_cpus () != 1) {
            printk (KERN_ERR PFX "ACPI Processor support is required " "for SMP systems but is absent. Please load the " "ACPI Processor module before starting this " "driver.\n");
            kfree (data);
            return -ENODEV;
        }
        if (pol->cpu != 0) {
            printk (KERN_ERR PFX "No ACPI _PSS objects for CPU other than " "CPU0. Complain to your BIOS vendor.\n");
            kfree (data);
            return -ENODEV;
        }
        rc = find_psb_table (data);
        if (rc) {
            kfree (data);
            return -ENODEV;
        }
    }
    oldmask = current->cpus_allowed;
    set_cpus_allowed_ptr (current, & cpumask_of_cpu (pol -> cpu));
    if (smp_processor_id () != pol->cpu) {
        printk (KERN_ERR PFX "limiting to cpu %u failed\n", pol -> cpu);
        goto err_out;
    }
    if (pending_bit_stuck ()) {
        printk (KERN_ERR PFX "failing init, change pending bit set\n");
        goto err_out;
    }
    if (query_current_values_with_pending_wait (data))
        goto err_out;
    if (cpu_family == CPU_OPTERON)
        fidvid_msr_init ();
    set_cpus_allowed_ptr (current, & oldmask);
    if (cpu_family == CPU_HW_PSTATE)
        pol->cpus = cpumask_of_cpu (pol->cpu);
    else
        pol->cpus = per_cpu (cpu_core_map, pol->cpu);
    data->available_cores = &(pol->cpus);
    pol->cpuinfo.transition_latency = (((data->rvo + 8) * data->vstable * VST_UNITS_20US) + (3 * (1 << data->irt) * 10)) * 1000;
    if (cpu_family == CPU_HW_PSTATE)
        pol->cur = find_khz_freq_from_pstate (data->powernow_table, data->currpstate);
    else
        pol->cur = find_khz_freq_from_fid (data->currfid);
    dprintk ("policy current frequency %d kHz\n", pol -> cur);
    if (cpufreq_frequency_table_cpuinfo (pol, data->powernow_table)) {
        printk (KERN_ERR PFX "invalid powernow_table\n");
        powernow_k8_cpu_exit_acpi (data);
        kfree (data -> powernow_table);
        kfree (data);
        return -EINVAL;
    }
    cpufreq_frequency_table_get_attr (data -> powernow_table, pol -> cpu);
    if (cpu_family == CPU_HW_PSTATE)
        dprintk ("cpu_init done, current pstate 0x%x\n", data->currpstate);
    else
        dprintk ("cpu_init done, current fid 0x%x, vid 0x%x\n", data->currfid, data->currvid);
    per_cpu (powernow_data, pol->cpu) = data;
    return 0;
err_out :
    set_cpus_allowed_ptr (current, &oldmask);
    powernow_k8_cpu_exit_acpi (data);
    kfree (data);
    return -ENODEV;
}

{
    printk (KERN_ERR PFX "unable to alloc powernow_k8_data");
    return -ENOMEM;
}

{
    if (num_online_cpus () != 1) {
        printk (KERN_ERR PFX "ACPI Processor support is required " "for SMP systems but is absent. Please load the " "ACPI Processor module before starting this " "driver.\n");
        kfree (data);
        return -ENODEV;
    }
    if (pol->cpu != 0) {
        printk (KERN_ERR PFX "No ACPI _PSS objects for CPU other than " "CPU0. Complain to your BIOS vendor.\n");
        kfree (data);
        return -ENODEV;
    }
    rc = find_psb_table (data);
    if (rc) {
        kfree (data);
        return -ENODEV;
    }
}

{
    printk (KERN_ERR PFX "ACPI Processor support is required " "for SMP systems but is absent. Please load the " "ACPI Processor module before starting this " "driver.\n");
    kfree (data);
    return -ENODEV;
}

{
    printk (KERN_ERR PFX "No ACPI _PSS objects for CPU other than " "CPU0. Complain to your BIOS vendor.\n");
    kfree (data);
    return -ENODEV;
}

{
    kfree (data);
    return -ENODEV;
}

{
    printk (KERN_ERR PFX "limiting to cpu %u failed\n", pol -> cpu);
    goto err_out;
}

{
    printk (KERN_ERR PFX "failing init, change pending bit set\n");
    goto err_out;
}

{
    printk (KERN_ERR PFX "invalid powernow_table\n");
    powernow_k8_cpu_exit_acpi (data);
    kfree (data -> powernow_table);
    kfree (data);
    return -EINVAL;
}

{
    struct powernow_k8_data *data = per_cpu (powernow_data, pol->cpu);
    if (!data)
        return -EINVAL;
    powernow_k8_cpu_exit_acpi (data);
    cpufreq_frequency_table_put_attr (pol -> cpu);
    kfree (data -> powernow_table);
    kfree (data);
    return 0;
}

{
    struct powernow_k8_data *data;
    cpumask_t oldmask = current->cpus_allowed;
    unsigned int khz = 0;
    unsigned int first;
    first = first_cpu (per_cpu (cpu_core_map, cpu));
    data = per_cpu (powernow_data, first);
    if (!data)
        return -EINVAL;
    set_cpus_allowed_ptr (current, & cpumask_of_cpu (cpu));
    if (smp_processor_id () != cpu) {
        printk (KERN_ERR PFX "limiting to CPU %d failed in powernowk8_get\n", cpu);
        set_cpus_allowed_ptr (current, & oldmask);
        return 0;
    }
    if (query_current_values_with_pending_wait (data))
        goto out;
    if (cpu_family == CPU_HW_PSTATE)
        khz = find_khz_freq_from_pstate (data->powernow_table, data->currpstate);
    else
        khz = find_khz_freq_from_fid (data->currfid);
out :
    set_cpus_allowed_ptr (current, &oldmask);
    return khz;
}

{
    printk (KERN_ERR PFX "limiting to CPU %d failed in powernowk8_get\n", cpu);
    set_cpus_allowed_ptr (current, & oldmask);
    return 0;
}

{
    unsigned int i, supported_cpus = 0;

    for_each_online_cpu (i) {
        if (check_supported_cpu (i))
            supported_cpus++;
    }

    if (supported_cpus == num_online_cpus ()) {
        printk (KERN_INFO PFX "Found %d %s " "processors (%d cpu cores) (" VERSION ")\n", num_online_nodes (), boot_cpu_data.x86_model_id, supported_cpus);
        return cpufreq_register_driver (&cpufreq_amd64_driver);
    }
    return -ENODEV;
}

{
    if (check_supported_cpu (i))
        supported_cpus++;
}

{
    printk (KERN_INFO PFX "Found %d %s " "processors (%d cpu cores) (" VERSION ")\n", num_online_nodes (), boot_cpu_data.x86_model_id, supported_cpus);
    return cpufreq_register_driver (&cpufreq_amd64_driver);
}

{
    dprintk ("exit\n");
    cpufreq_unregister_driver (& cpufreq_amd64_driver);
}

{
    switch (boot_cpu_data.x86_vendor) {
    case X86_VENDOR_AMD :
        return (msr - MSR_K7_PERFCTR0);
    case X86_VENDOR_INTEL :
        if (cpu_has (&boot_cpu_data, X86_FEATURE_ARCH_PERFMON))
            return (msr - MSR_ARCH_PERFMON_PERFCTR0);
        switch (boot_cpu_data.x86) {
        case 6 :
            return (msr - MSR_P6_PERFCTR0);
        case 15 :
            return (msr - MSR_P4_BPU_PERFCTR0);
        }
    }
    return 0;
}

{
case X86_VENDOR_AMD :
    return (msr - MSR_K7_PERFCTR0);
case X86_VENDOR_INTEL :
    if (cpu_has (&boot_cpu_data, X86_FEATURE_ARCH_PERFMON))
        return (msr - MSR_ARCH_PERFMON_PERFCTR0);
    switch (boot_cpu_data.x86) {
    case 6 :
        return (msr - MSR_P6_PERFCTR0);
    case 15 :
        return (msr - MSR_P4_BPU_PERFCTR0);
    }
}

{
case 6 :
    return (msr - MSR_P6_PERFCTR0);
case 15 :
    return (msr - MSR_P4_BPU_PERFCTR0);
}

{
    switch (boot_cpu_data.x86_vendor) {
    case X86_VENDOR_AMD :
        return (msr - MSR_K7_EVNTSEL0);
    case X86_VENDOR_INTEL :
        if (cpu_has (&boot_cpu_data, X86_FEATURE_ARCH_PERFMON))
            return (msr - MSR_ARCH_PERFMON_EVENTSEL0);
        switch (boot_cpu_data.x86) {
        case 6 :
            return (msr - MSR_P6_EVNTSEL0);
        case 15 :
            return (msr - MSR_P4_BSU_ESCR0);
        }
    }
    return 0;
}

{
case X86_VENDOR_AMD :
    return (msr - MSR_K7_EVNTSEL0);
case X86_VENDOR_INTEL :
    if (cpu_has (&boot_cpu_data, X86_FEATURE_ARCH_PERFMON))
        return (msr - MSR_ARCH_PERFMON_EVENTSEL0);
    switch (boot_cpu_data.x86) {
    case 6 :
        return (msr - MSR_P6_EVNTSEL0);
    case 15 :
        return (msr - MSR_P4_BSU_ESCR0);
    }
}

{
case 6 :
    return (msr - MSR_P6_EVNTSEL0);
case 15 :
    return (msr - MSR_P4_BSU_ESCR0);
}

{
    BUG_ON (counter > NMI_MAX_COUNTER_BITS);
    return (!test_bit (counter, perfctr_nmi_owner));
}

{
    unsigned int counter;
    counter = nmi_perfctr_msr_to_bit (msr);
    BUG_ON (counter > NMI_MAX_COUNTER_BITS);
    return (!test_bit (counter, perfctr_nmi_owner));
}

{
    unsigned int counter;
    counter = nmi_perfctr_msr_to_bit (msr);
    if (counter > NMI_MAX_COUNTER_BITS)
        return 1;
    if (!test_and_set_bit (counter, perfctr_nmi_owner))
        return 1;
    return 0;
}

{
    unsigned int counter;
    counter = nmi_perfctr_msr_to_bit (msr);
    if (counter > NMI_MAX_COUNTER_BITS)
        return;
    clear_bit (counter, perfctr_nmi_owner);
}

{
    unsigned int counter;
    counter = nmi_evntsel_msr_to_bit (msr);
    if (counter > NMI_MAX_COUNTER_BITS)
        return 1;
    if (!test_and_set_bit (counter, evntsel_nmi_owner))
        return 1;
    return 0;
}

{
    unsigned int counter;
    counter = nmi_evntsel_msr_to_bit (msr);
    if (counter > NMI_MAX_COUNTER_BITS)
        return;
    clear_bit (counter, evntsel_nmi_owner);
}

{
    BUG_ON (nmi_watchdog != NMI_LOCAL_APIC);
    if (atomic_read (&nmi_active) <= 0)
        return;
    on_each_cpu (stop_apic_nmi_watchdog, NULL, 0, 1);
    wd_ops->unreserve ();
    BUG_ON (atomic_read (& nmi_active) != 0);
}

{
    BUG_ON (nmi_watchdog != NMI_LOCAL_APIC);
    if (atomic_read (&nmi_active) != 0)
        return;
    if (!wd_ops)
        return;
    if (!wd_ops->reserve ()) {
        printk (KERN_ERR "NMI watchdog: cannot reserve perfctrs\n");
        return;
    }
    on_each_cpu (setup_apic_nmi_watchdog, NULL, 0, 1);
    touch_nmi_watchdog ();
}

{
    printk (KERN_ERR "NMI watchdog: cannot reserve perfctrs\n");
    return;
}

{
    u64 counter_val;
    unsigned int retval = hz;
    counter_val = (u64) cpu_khz * 1000;
    do_div (counter_val, retval);
    if (counter_val > 0x7fffffffULL) {
        u64 count = (u64) cpu_khz * 1000;
        do_div (count, 0x7fffffffUL);
        retval = count + 1;
    }
    return retval;
}

{
    u64 count = (u64) cpu_khz * 1000;
    do_div (count, 0x7fffffffUL);
    retval = count + 1;
}

{
    u64 count = (u64) cpu_khz * 1000;
    do_div (count, nmi_hz);
    if (descr)
        Dprintk ("setting %s to -0x%08Lx\n", descr, count);
    wrmsrl (perfctr_msr, 0 - count);
}

{
    u64 count = (u64) cpu_khz * 1000;
    do_div (count, nmi_hz);
    if (descr)
        Dprintk ("setting %s to -0x%08Lx\n", descr, count);
    wrmsr (perfctr_msr, (u32) (- count), 0);
}

{
    unsigned int perfctr_msr, evntsel_msr;
    unsigned int evntsel;
    struct nmi_watchdog_ctlblk *wd = &__get_cpu_var (nmi_watchdog_ctlblk);
    perfctr_msr = wd_ops->perfctr;
    evntsel_msr = wd_ops->evntsel;
    wrmsrl (perfctr_msr, 0UL);
    evntsel = K7_EVNTSEL_INT | K7_EVNTSEL_OS | K7_EVNTSEL_USR | K7_NMI_EVENT;
    wrmsr (evntsel_msr, evntsel, 0);
    write_watchdog_counter (perfctr_msr, "K7_PERFCTR0", nmi_hz);
    apic_write (APIC_LVTPC, APIC_DM_NMI);
    evntsel |= K7_EVNTSEL_ENABLE;
    wrmsr (evntsel_msr, evntsel, 0);
    wd->perfctr_msr = perfctr_msr;
    wd->evntsel_msr = evntsel_msr;
    wd->cccr_msr = 0;
    return 1;
}

{
    struct nmi_watchdog_ctlblk *wd = &__get_cpu_var (nmi_watchdog_ctlblk);
    wrmsr (wd -> evntsel_msr, 0, 0);
}

{
    if (!reserve_perfctr_nmi (wd_ops->perfctr))
        return 0;
    if (!reserve_evntsel_nmi (wd_ops->evntsel)) {
        release_perfctr_nmi (wd_ops -> perfctr);
        return 0;
    }
    return 1;
}

{
    release_perfctr_nmi (wd_ops -> perfctr);
    return 0;
}

{
    release_evntsel_nmi (wd_ops -> evntsel);
    release_perfctr_nmi (wd_ops -> perfctr);
}

{
    write_watchdog_counter (wd -> perfctr_msr, NULL, nmi_hz);
}

{
    unsigned int perfctr_msr, evntsel_msr;
    unsigned int evntsel;
    struct nmi_watchdog_ctlblk *wd = &__get_cpu_var (nmi_watchdog_ctlblk);
    perfctr_msr = wd_ops->perfctr;
    evntsel_msr = wd_ops->evntsel;
    if (wrmsr_safe (perfctr_msr, 0, 0) < 0)
        return 0;
    evntsel = P6_EVNTSEL_INT | P6_EVNTSEL_OS | P6_EVNTSEL_USR | P6_NMI_EVENT;
    wrmsr (evntsel_msr, evntsel, 0);
    nmi_hz = adjust_for_32bit_ctr (nmi_hz);
    write_watchdog_counter32 (perfctr_msr, "P6_PERFCTR0", nmi_hz);
    apic_write (APIC_LVTPC, APIC_DM_NMI);
    evntsel |= P6_EVNTSEL0_ENABLE;
    wrmsr (evntsel_msr, evntsel, 0);
    wd->perfctr_msr = perfctr_msr;
    wd->evntsel_msr = evntsel_msr;
    wd->cccr_msr = 0;
    return 1;
}

{
    apic_write (APIC_LVTPC, APIC_DM_NMI);
    write_watchdog_counter32 (wd -> perfctr_msr, NULL, nmi_hz);
}

{
    unsigned int perfctr_msr, evntsel_msr, cccr_msr;
    unsigned int evntsel, cccr_val;
    unsigned int misc_enable, dummy;
    unsigned int ht_num;
    struct nmi_watchdog_ctlblk *wd = &__get_cpu_var (nmi_watchdog_ctlblk);
    rdmsr (MSR_IA32_MISC_ENABLE, misc_enable, dummy);
    if (!(misc_enable & MSR_P4_MISC_ENABLE_PERF_AVAIL))
        return 0;
    ht_num = 0;
    if (!ht_num) {
        perfctr_msr = MSR_P4_IQ_PERFCTR0;
        evntsel_msr = MSR_P4_CRU_ESCR0;
        cccr_msr = MSR_P4_IQ_CCCR0;
        cccr_val = P4_CCCR_OVF_PMI0 | P4_CCCR_ESCR_SELECT (4);
    }
    else {
        perfctr_msr = MSR_P4_IQ_PERFCTR1;
        evntsel_msr = MSR_P4_CRU_ESCR0;
        cccr_msr = MSR_P4_IQ_CCCR1;
        cccr_val = P4_CCCR_OVF_PMI1 | P4_CCCR_ESCR_SELECT (4);
    }
    evntsel = P4_ESCR_EVENT_SELECT (0x3F) | P4_ESCR_OS | P4_ESCR_USR;
    cccr_val |= P4_CCCR_THRESHOLD (15) | P4_CCCR_COMPLEMENT | P4_CCCR_COMPARE | P4_CCCR_REQUIRED;
    wrmsr (evntsel_msr, evntsel, 0);
    wrmsr (cccr_msr, cccr_val, 0);
    write_watchdog_counter (perfctr_msr, "P4_IQ_COUNTER0", nmi_hz);
    apic_write (APIC_LVTPC, APIC_DM_NMI);
    cccr_val |= P4_CCCR_ENABLE;
    wrmsr (cccr_msr, cccr_val, 0);
    wd->perfctr_msr = perfctr_msr;
    wd->evntsel_msr = evntsel_msr;
    wd->cccr_msr = cccr_msr;
    return 1;
}

{
    perfctr_msr = MSR_P4_IQ_PERFCTR0;
    evntsel_msr = MSR_P4_CRU_ESCR0;
    cccr_msr = MSR_P4_IQ_CCCR0;
    cccr_val = P4_CCCR_OVF_PMI0 | P4_CCCR_ESCR_SELECT (4);
}

{
    perfctr_msr = MSR_P4_IQ_PERFCTR1;
    evntsel_msr = MSR_P4_CRU_ESCR0;
    cccr_msr = MSR_P4_IQ_CCCR1;
    cccr_val = P4_CCCR_OVF_PMI1 | P4_CCCR_ESCR_SELECT (4);
}

{
    struct nmi_watchdog_ctlblk *wd = &__get_cpu_var (nmi_watchdog_ctlblk);
    wrmsr (wd -> cccr_msr, 0, 0);
    wrmsr (wd -> evntsel_msr, 0, 0);
}

{
    if (!reserve_perfctr_nmi (MSR_P4_IQ_PERFCTR0))
        return 0;
    if (!reserve_evntsel_nmi (MSR_P4_CRU_ESCR0))
        goto fail2;
    return 1;
fail2 :
    release_perfctr_nmi (MSR_P4_IQ_PERFCTR0);
    return 0;
}

{
    release_evntsel_nmi (MSR_P4_CRU_ESCR0);
    release_perfctr_nmi (MSR_P4_IQ_PERFCTR0);
}

{
    unsigned dummy;
    rdmsrl (wd -> cccr_msr, dummy);
    dummy &= ~P4_CCCR_OVF;
    wrmsrl (wd -> cccr_msr, dummy);
    apic_write (APIC_LVTPC, APIC_DM_NMI);
    write_watchdog_counter (wd -> perfctr_msr, NULL, nmi_hz);
}

{
    unsigned int ebx;
    union cpuid10_eax eax;
    unsigned int unused;
    unsigned int perfctr_msr, evntsel_msr;
    unsigned int evntsel;
    struct nmi_watchdog_ctlblk *wd = &__get_cpu_var (nmi_watchdog_ctlblk);
    cpuid (10, & (eax.full), & ebx, & unused, & unused);
    if ((eax.split.mask_length < (ARCH_PERFMON_UNHALTED_CORE_CYCLES_INDEX + 1)) || (ebx & ARCH_PERFMON_UNHALTED_CORE_CYCLES_PRESENT))
        return 0;
    perfctr_msr = wd_ops->perfctr;
    evntsel_msr = wd_ops->evntsel;
    wrmsrl (perfctr_msr, 0UL);
    evntsel = ARCH_PERFMON_EVENTSEL_INT | ARCH_PERFMON_EVENTSEL_OS | ARCH_PERFMON_EVENTSEL_USR | ARCH_PERFMON_NMI_EVENT_SEL | ARCH_PERFMON_NMI_EVENT_UMASK;
    wrmsr (evntsel_msr, evntsel, 0);
    nmi_hz = adjust_for_32bit_ctr (nmi_hz);
    write_watchdog_counter32 (perfctr_msr, "INTEL_ARCH_PERFCTR0", nmi_hz);
    apic_write (APIC_LVTPC, APIC_DM_NMI);
    evntsel |= ARCH_PERFMON_EVENTSEL0_ENABLE;
    wrmsr (evntsel_msr, evntsel, 0);
    wd->perfctr_msr = perfctr_msr;
    wd->evntsel_msr = evntsel_msr;
    wd->cccr_msr = 0;
    intel_arch_wd_ops.checkbit = 1ULL << (eax.split.bit_width - 1);
    return 1;
}

{
    switch (boot_cpu_data.x86_vendor) {
    case X86_VENDOR_AMD :
        if (boot_cpu_data.x86 != 6 && boot_cpu_data.x86 != 15 && boot_cpu_data.x86 != 16)
            return;
        wd_ops = &k7_wd_ops;
        break;
    case X86_VENDOR_INTEL :
        if (boot_cpu_data.x86 == 6 && boot_cpu_data.x86_model == 14) {
            intel_arch_wd_ops.perfctr = MSR_ARCH_PERFMON_PERFCTR0;
            intel_arch_wd_ops.evntsel = MSR_ARCH_PERFMON_EVENTSEL0;
        }
        if (cpu_has (&boot_cpu_data, X86_FEATURE_ARCH_PERFMON)) {
            wd_ops = &intel_arch_wd_ops;
            break;
        }
        switch (boot_cpu_data.x86) {
        case 6 :
            if (boot_cpu_data.x86_model > 0xd)
                return;
            wd_ops = &p6_wd_ops;
            break;
        case 15 :
            wd_ops = &p4_wd_ops;
            break;
        default :
            return;
        }
        break;
    }
}

{
case X86_VENDOR_AMD :
    if (boot_cpu_data.x86 != 6 && boot_cpu_data.x86 != 15 && boot_cpu_data.x86 != 16)
        return;
    wd_ops = &k7_wd_ops;
    break;
case X86_VENDOR_INTEL :
    if (boot_cpu_data.x86 == 6 && boot_cpu_data.x86_model == 14) {
        intel_arch_wd_ops.perfctr = MSR_ARCH_PERFMON_PERFCTR0;
        intel_arch_wd_ops.evntsel = MSR_ARCH_PERFMON_EVENTSEL0;
    }
    if (cpu_has (&boot_cpu_data, X86_FEATURE_ARCH_PERFMON)) {
        wd_ops = &intel_arch_wd_ops;
        break;
    }
    switch (boot_cpu_data.x86) {
    case 6 :
        if (boot_cpu_data.x86_model > 0xd)
            return;
        wd_ops = &p6_wd_ops;
        break;
    case 15 :
        wd_ops = &p4_wd_ops;
        break;
    default :
        return;
    }
    break;
}

{
    intel_arch_wd_ops.perfctr = MSR_ARCH_PERFMON_PERFCTR0;
    intel_arch_wd_ops.evntsel = MSR_ARCH_PERFMON_EVENTSEL0;
}

{
    wd_ops = &intel_arch_wd_ops;
    break;
}

{
case 6 :
    if (boot_cpu_data.x86_model > 0xd)
        return;
    wd_ops = &p6_wd_ops;
    break;
case 15 :
    wd_ops = &p4_wd_ops;
    break;
default :
    return;
}

{
    if (!wd_ops) {
        probe_nmi_watchdog ();
        if (!wd_ops) {
            printk (KERN_INFO "NMI watchdog: CPU not supported\n");
            return -1;
        }
        if (!wd_ops->reserve ()) {
            printk (KERN_ERR "NMI watchdog: cannot reserve perfctrs\n");
            return -1;
        }
    }
    if (!(wd_ops->setup (nmi_hz))) {
        printk (KERN_ERR "Cannot setup NMI watchdog on CPU %d\n", raw_smp_processor_id ());
        return -1;
    }
    return 0;
}

{
    probe_nmi_watchdog ();
    if (!wd_ops) {
        printk (KERN_INFO "NMI watchdog: CPU not supported\n");
        return -1;
    }
    if (!wd_ops->reserve ()) {
        printk (KERN_ERR "NMI watchdog: cannot reserve perfctrs\n");
        return -1;
    }
}

{
    printk (KERN_INFO "NMI watchdog: CPU not supported\n");
    return -1;
}

{
    printk (KERN_ERR "NMI watchdog: cannot reserve perfctrs\n");
    return -1;
}

{
    printk (KERN_ERR "Cannot setup NMI watchdog on CPU %d\n", raw_smp_processor_id ());
    return -1;
}

{
    if (wd_ops)
        wd_ops->stop ();
}

{
    struct nmi_watchdog_ctlblk *wd = &__get_cpu_var (nmi_watchdog_ctlblk);
    if (wd->perfctr_msr == MSR_P6_PERFCTR0 || wd->perfctr_msr == MSR_ARCH_PERFMON_PERFCTR1)
        hz = adjust_for_32bit_ctr (hz);
    return hz;
}

{
    struct nmi_watchdog_ctlblk *wd = &__get_cpu_var (nmi_watchdog_ctlblk);
    u64 ctr;
    rdmsrl (wd -> perfctr_msr, ctr);
    if (ctr & wd_ops->checkbit) {
        return 0;
    }
    wd_ops->rearm (wd, nmi_hz);
    return 1;
}

{
    return 0;
}

{
    return wd_ops != NULL;
}

{
    unsigned int cr0;
    local_irq_save (ctxt -> flags);
    if (use_intel () || is_cpu (CYRIX)) {
        if (cpu_has_pge) {
            ctxt->cr4val = read_cr4 ();
            write_cr4 (ctxt -> cr4val & ~ X86_CR4_PGE);
        }
        cr0 = read_cr0 () | X86_CR0_CD;
        wbinvd ();
        write_cr0 (cr0);
        wbinvd ();
        if (use_intel ())
            rdmsr (MTRRdefType_MSR, ctxt->deftype_lo, ctxt->deftype_hi);
        else
            ctxt->ccr3 = getCx86 (CX86_CCR3);
    }
}

{
    if (cpu_has_pge) {
        ctxt->cr4val = read_cr4 ();
        write_cr4 (ctxt -> cr4val & ~ X86_CR4_PGE);
    }
    cr0 = read_cr0 () | X86_CR0_CD;
    wbinvd ();
    write_cr0 (cr0);
    wbinvd ();
    if (use_intel ())
        rdmsr (MTRRdefType_MSR, ctxt->deftype_lo, ctxt->deftype_hi);
    else
        ctxt->ccr3 = getCx86 (CX86_CCR3);
}

{
    ctxt->cr4val = read_cr4 ();
    write_cr4 (ctxt -> cr4val & ~ X86_CR4_PGE);
}

{
    if (use_intel ())
        mtrr_wrmsr (MTRRdefType_MSR, ctxt->deftype_lo & 0xf300UL, ctxt->deftype_hi);
    else if (is_cpu (CYRIX))
        setCx86 (CX86_CCR3, (ctxt->ccr3 & 0x0f) | 0x10);
}

{
    if (use_intel () || is_cpu (CYRIX)) {
        wbinvd ();
        if (use_intel ())
            mtrr_wrmsr (MTRRdefType_MSR, ctxt->deftype_lo, ctxt->deftype_hi);
        else
            setCx86 (CX86_CCR3, ctxt->ccr3);
        write_cr0 (read_cr0 () & 0xbfffffff);
        if (cpu_has_pge)
            write_cr4 (ctxt->cr4val);
    }
    local_irq_restore (ctxt -> flags);
}

{
    wbinvd ();
    if (use_intel ())
        mtrr_wrmsr (MTRRdefType_MSR, ctxt->deftype_lo, ctxt->deftype_hi);
    else
        setCx86 (CX86_CCR3, ctxt->ccr3);
    write_cr0 (read_cr0 () & 0xbfffffff);
    if (cpu_has_pge)
        write_cr4 (ctxt->cr4val);
}

{
    return (x <= 6) ? mtrr_strings[x] : "?";
}

{
    int i;
    u64 base, mask;
    u8 prev_match, curr_match;
    if (!mtrr_state_set)
        return 0xFF;
    if (!mtrr_state.enabled)
        return 0xFF;
    end--;
    if (mtrr_state.have_fixed && (start < 0x100000)) {
        int idx;
        if (start < 0x80000) {
            idx = 0;
            idx += (start >> 16);
            return mtrr_state.fixed_ranges[idx];
        }
        else if (start < 0xC0000) {
            idx = 1 * 8;
            idx += ((start - 0x80000) >> 14);
            return mtrr_state.fixed_ranges[idx];
        }
        else if (start < 0x1000000) {
            idx = 3 * 8;
            idx += ((start - 0xC0000) >> 12);
            return mtrr_state.fixed_ranges[idx];
        }
    }
    if (!(mtrr_state.enabled & 2)) {
        return mtrr_state.def_type;
    }
    prev_match = 0xFF;
    for (i = 0; i < num_var_ranges; ++i) {
        unsigned short start_state, end_state;
        if (!(mtrr_state.var_ranges[i].mask_lo & (1 << 11)))
            continue;
        base = (((u64) mtrr_state.var_ranges[i].base_hi) << 32) + (mtrr_state.var_ranges[i].base_lo & PAGE_MASK);
        mask = (((u64) mtrr_state.var_ranges[i].mask_hi) << 32) + (mtrr_state.var_ranges[i].mask_lo & PAGE_MASK);
        start_state = ((start & mask) == (base & mask));
        end_state = ((end & mask) == (base & mask));
        if (start_state != end_state)
            return 0xFE;
        if ((start & mask) != (base & mask)) {
            continue;
        }
        curr_match = mtrr_state.var_ranges[i].base_lo & 0xff;
        if (prev_match == 0xFF) {
            prev_match = curr_match;
            continue;
        }
        if (prev_match == MTRR_TYPE_UNCACHABLE || curr_match == MTRR_TYPE_UNCACHABLE) {
            return MTRR_TYPE_UNCACHABLE;
        }
        if ((prev_match == MTRR_TYPE_WRBACK && curr_match == MTRR_TYPE_WRTHROUGH) || (prev_match == MTRR_TYPE_WRTHROUGH && curr_match == MTRR_TYPE_WRBACK)) {
            prev_match = MTRR_TYPE_WRTHROUGH;
            curr_match = MTRR_TYPE_WRTHROUGH;
        }
        if (prev_match != curr_match) {
            return MTRR_TYPE_UNCACHABLE;
        }
    }
    if (tom2) {
        if (start >= (1ULL << 32) && (end < tom2))
            return MTRR_TYPE_WRBACK;
    }
    if (prev_match != 0xFF)
        return prev_match;
    return mtrr_state.def_type;
}

{
    int idx;
    if (start < 0x80000) {
        idx = 0;
        idx += (start >> 16);
        return mtrr_state.fixed_ranges[idx];
    }
    else if (start < 0xC0000) {
        idx = 1 * 8;
        idx += ((start - 0x80000) >> 14);
        return mtrr_state.fixed_ranges[idx];
    }
    else if (start < 0x1000000) {
        idx = 3 * 8;
        idx += ((start - 0xC0000) >> 12);
        return mtrr_state.fixed_ranges[idx];
    }
}

{
    idx = 0;
    idx += (start >> 16);
    return mtrr_state.fixed_ranges[idx];
}

{
    idx = 1 * 8;
    idx += ((start - 0x80000) >> 14);
    return mtrr_state.fixed_ranges[idx];
}

{
    idx = 3 * 8;
    idx += ((start - 0xC0000) >> 12);
    return mtrr_state.fixed_ranges[idx];
}

{
    return mtrr_state.def_type;
}

{
    unsigned short start_state, end_state;
    if (!(mtrr_state.var_ranges[i].mask_lo & (1 << 11)))
        continue;
    base = (((u64) mtrr_state.var_ranges[i].base_hi) << 32) + (mtrr_state.var_ranges[i].base_lo & PAGE_MASK);
    mask = (((u64) mtrr_state.var_ranges[i].mask_hi) << 32) + (mtrr_state.var_ranges[i].mask_lo & PAGE_MASK);
    start_state = ((start & mask) == (base & mask));
    end_state = ((end & mask) == (base & mask));
    if (start_state != end_state)
        return 0xFE;
    if ((start & mask) != (base & mask)) {
        continue;
    }
    curr_match = mtrr_state.var_ranges[i].base_lo & 0xff;
    if (prev_match == 0xFF) {
        prev_match = curr_match;
        continue;
    }
    if (prev_match == MTRR_TYPE_UNCACHABLE || curr_match == MTRR_TYPE_UNCACHABLE) {
        return MTRR_TYPE_UNCACHABLE;
    }
    if ((prev_match == MTRR_TYPE_WRBACK && curr_match == MTRR_TYPE_WRTHROUGH) || (prev_match == MTRR_TYPE_WRTHROUGH && curr_match == MTRR_TYPE_WRBACK)) {
        prev_match = MTRR_TYPE_WRTHROUGH;
        curr_match = MTRR_TYPE_WRTHROUGH;
    }
    if (prev_match != curr_match) {
        return MTRR_TYPE_UNCACHABLE;
    }
}

{
    continue;
}

{
    prev_match = curr_match;
    continue;
}

{
    return MTRR_TYPE_UNCACHABLE;
}

{
    prev_match = MTRR_TYPE_WRTHROUGH;
    curr_match = MTRR_TYPE_WRTHROUGH;
}

{
    return MTRR_TYPE_UNCACHABLE;
}

{
    if (start >= (1ULL << 32) && (end < tom2))
        return MTRR_TYPE_WRBACK;
}

{
    rdmsr (MTRRphysBase_MSR (index), vr -> base_lo, vr -> base_hi);
    rdmsr (MTRRphysMask_MSR (index), vr -> mask_lo, vr -> mask_hi);
}

{
    unsigned int *p = (unsigned int *) frs;
    int i;
    rdmsr (MTRRfix64K_00000_MSR, p [0], p [1]);
    for (i = 0; i < 2; i++)
        rdmsr (MTRRfix16K_80000_MSR +i, p[2 + i * 2], p[3 + i * 2]);
    for (i = 0; i < 8; i++)
        rdmsr (MTRRfix4K_C0000_MSR +i, p[6 + i * 2], p[7 + i * 2]);
}

{
    if (cpu_has_mtrr)
        get_fixed_ranges (mtrr_state.fixed_ranges);
}

{
    unsigned i;
    for (i = 0; i < 8; ++i, ++types, base += step)
        printk (KERN_INFO "MTRR %05X-%05X %s\n", base, base +step - 1, mtrr_attrib_to_str (*types));
}

{
    unsigned int i;
    struct mtrr_var_range *vrs;
    unsigned lo, dummy;
    unsigned long flags;
    vrs = mtrr_state.var_ranges;
    rdmsr (MTRRcap_MSR, lo, dummy);
    mtrr_state.have_fixed = (lo >> 8) & 1;
    for (i = 0; i < num_var_ranges; i++)
        get_mtrr_var_range (i, &vrs[i]);
    if (mtrr_state.have_fixed)
        get_fixed_ranges (mtrr_state.fixed_ranges);
    rdmsr (MTRRdefType_MSR, lo, dummy);
    mtrr_state.def_type = (lo & 0xff);
    mtrr_state.enabled = (lo & 0xc00) >> 10;
    if (amd_special_default_mtrr ()) {
        unsigned lo, hi;
        rdmsr (MSR_K8_TOP_MEM2, lo, hi);
        tom2 = hi;
        tom2 <<= 32;
        tom2 |= lo;
        tom2 &= 0xffffff8000000ULL;
    }
    if (mtrr_show) {
        int high_width;
        printk (KERN_INFO "MTRR default type: %s\n", mtrr_attrib_to_str (mtrr_state.def_type));
        if (mtrr_state.have_fixed) {
            printk (KERN_INFO "MTRR fixed ranges %sabled:\n", mtrr_state.enabled & 1 ? "en" : "dis");
            print_fixed (0x00000, 0x10000, mtrr_state.fixed_ranges + 0);
            for (i = 0; i < 2; ++i)
                print_fixed (0x80000 + i * 0x20000, 0x04000, mtrr_state.fixed_ranges + (i + 1) * 8);
            for (i = 0; i < 8; ++i)
                print_fixed (0xC0000 + i * 0x08000, 0x01000, mtrr_state.fixed_ranges + (i + 3) * 8);
        }
        printk (KERN_INFO "MTRR variable ranges %sabled:\n", mtrr_state.enabled & 2 ? "en" : "dis");
        high_width = ((size_or_mask ? ffs (size_or_mask) - 1 : 32) - (32 - PAGE_SHIFT) + 3) / 4;
        for (i = 0; i < num_var_ranges; ++i) {
            if (mtrr_state.var_ranges[i].mask_lo & (1 << 11))
                printk (KERN_INFO "MTRR %u base %0*X%05X000 mask %0*X%05X000 %s\n", i, high_width, mtrr_state.var_ranges[i].base_hi, mtrr_state.var_ranges[i].base_lo >> 12, high_width, mtrr_state.var_ranges[i].mask_hi, mtrr_state.var_ranges[i].mask_lo >> 12, mtrr_attrib_to_str (mtrr_state.var_ranges[i].base_lo & 0xff));
            else
                printk (KERN_INFO "MTRR %u disabled\n", i);
        }
        if (tom2) {
            printk (KERN_INFO "TOM2: %016llx aka %lldM\n", tom2, tom2 >> 20);
        }
    }
    mtrr_state_set = 1;
    local_irq_save (flags);
    prepare_set ();
    pat_init ();
    post_set ();
    local_irq_restore (flags);
}

{
    unsigned lo, hi;
    rdmsr (MSR_K8_TOP_MEM2, lo, hi);
    tom2 = hi;
    tom2 <<= 32;
    tom2 |= lo;
    tom2 &= 0xffffff8000000ULL;
}

{
    int high_width;
    printk (KERN_INFO "MTRR default type: %s\n", mtrr_attrib_to_str (mtrr_state.def_type));
    if (mtrr_state.have_fixed) {
        printk (KERN_INFO "MTRR fixed ranges %sabled:\n", mtrr_state.enabled & 1 ? "en" : "dis");
        print_fixed (0x00000, 0x10000, mtrr_state.fixed_ranges + 0);
        for (i = 0; i < 2; ++i)
            print_fixed (0x80000 + i * 0x20000, 0x04000, mtrr_state.fixed_ranges + (i + 1) * 8);
        for (i = 0; i < 8; ++i)
            print_fixed (0xC0000 + i * 0x08000, 0x01000, mtrr_state.fixed_ranges + (i + 3) * 8);
    }
    printk (KERN_INFO "MTRR variable ranges %sabled:\n", mtrr_state.enabled & 2 ? "en" : "dis");
    high_width = ((size_or_mask ? ffs (size_or_mask) - 1 : 32) - (32 - PAGE_SHIFT) + 3) / 4;
    for (i = 0; i < num_var_ranges; ++i) {
        if (mtrr_state.var_ranges[i].mask_lo & (1 << 11))
            printk (KERN_INFO "MTRR %u base %0*X%05X000 mask %0*X%05X000 %s\n", i, high_width, mtrr_state.var_ranges[i].base_hi, mtrr_state.var_ranges[i].base_lo >> 12, high_width, mtrr_state.var_ranges[i].mask_hi, mtrr_state.var_ranges[i].mask_lo >> 12, mtrr_attrib_to_str (mtrr_state.var_ranges[i].base_lo & 0xff));
        else
            printk (KERN_INFO "MTRR %u disabled\n", i);
    }
    if (tom2) {
        printk (KERN_INFO "TOM2: %016llx aka %lldM\n", tom2, tom2 >> 20);
    }
}

{
    printk (KERN_INFO "MTRR fixed ranges %sabled:\n", mtrr_state.enabled & 1 ? "en" : "dis");
    print_fixed (0x00000, 0x10000, mtrr_state.fixed_ranges + 0);
    for (i = 0; i < 2; ++i)
        print_fixed (0x80000 + i * 0x20000, 0x04000, mtrr_state.fixed_ranges + (i + 1) * 8);
    for (i = 0; i < 8; ++i)
        print_fixed (0xC0000 + i * 0x08000, 0x01000, mtrr_state.fixed_ranges + (i + 3) * 8);
}

{
    if (mtrr_state.var_ranges[i].mask_lo & (1 << 11))
        printk (KERN_INFO "MTRR %u base %0*X%05X000 mask %0*X%05X000 %s\n", i, high_width, mtrr_state.var_ranges[i].base_hi, mtrr_state.var_ranges[i].base_lo >> 12, high_width, mtrr_state.var_ranges[i].mask_hi, mtrr_state.var_ranges[i].mask_lo >> 12, mtrr_attrib_to_str (mtrr_state.var_ranges[i].base_lo & 0xff));
    else
        printk (KERN_INFO "MTRR %u disabled\n", i);
}

{
    printk (KERN_INFO "TOM2: %016llx aka %lldM\n", tom2, tom2 >> 20);
}

{
    unsigned long mask = smp_changes_mask;
    if (!mask)
        return;
    if (mask & MTRR_CHANGE_MASK_FIXED)
        printk (KERN_WARNING "mtrr: your CPUs had inconsistent fixed MTRR settings\n");
    if (mask & MTRR_CHANGE_MASK_VARIABLE)
        printk (KERN_WARNING "mtrr: your CPUs had inconsistent variable MTRR settings\n");
    if (mask & MTRR_CHANGE_MASK_DEFTYPE)
        printk (KERN_WARNING "mtrr: your CPUs had inconsistent MTRRdefType settings\n");
    printk (KERN_INFO "mtrr: probably your BIOS does not setup all CPUs.\n");
    printk (KERN_INFO "mtrr: corrected configuration.\n");
}

{
    if (wrmsr_safe (msr, a, b) < 0)
        printk (KERN_ERR "MTRR: CPU %u: Writing MSR %x to %x:%x failed\n", smp_processor_id (), msr, a, b);
}

{
    unsigned lo, hi;
    rdmsr (MSR_K8_SYSCFG, lo, hi);
    mtrr_wrmsr (MSR_K8_SYSCFG, lo | K8_MTRRFIXRANGE_DRAM_ENABLE | K8_MTRRFIXRANGE_DRAM_MODIFY, hi);
}

{
    unsigned lo, hi;
    rdmsr (msr, lo, hi);
    if (lo != msrwords[0] || hi != msrwords[1]) {
        if (boot_cpu_data.x86_vendor == X86_VENDOR_AMD && boot_cpu_data.x86 == 15 && ((msrwords[0] | msrwords[1]) & K8_MTRR_RDMEM_WRMEM_MASK))
            k8_enable_fixed_iorrs ();
        mtrr_wrmsr (msr, msrwords [0], msrwords [1]);
        *changed = true;
    }
}

{
    if (boot_cpu_data.x86_vendor == X86_VENDOR_AMD && boot_cpu_data.x86 == 15 && ((msrwords[0] | msrwords[1]) & K8_MTRR_RDMEM_WRMEM_MASK))
        k8_enable_fixed_iorrs ();
    mtrr_wrmsr (msr, msrwords [0], msrwords [1]);
    *changed = true;
}

{
    int i, max;
    mtrr_type ltype;
    unsigned long lbase, lsize;
    max = num_var_ranges;
    if (replace_reg >= 0 && replace_reg < max)
        return replace_reg;
    for (i = 0; i < max; ++i) {
        mtrr_if->get (i, &lbase, &lsize, &ltype);
        if (lsize == 0)
            return i;
    }
    return -ENOSPC;
}

{
    mtrr_if->get (i, &lbase, &lsize, &ltype);
    if (lsize == 0)
        return i;
}

{
    unsigned int mask_lo, mask_hi, base_lo, base_hi;
    rdmsr (MTRRphysMask_MSR (reg), mask_lo, mask_hi);
    if ((mask_lo & 0x800) == 0) {
        *base = 0;
        *size = 0;
        *type = 0;
        return;
    }
    rdmsr (MTRRphysBase_MSR (reg), base_lo, base_hi);
    mask_lo = size_or_mask | mask_hi << (32 - PAGE_SHIFT) | mask_lo >> PAGE_SHIFT;
    *size = -mask_lo;
    *base = base_hi << (32 - PAGE_SHIFT) | base_lo >> PAGE_SHIFT;
    *type = base_lo & 0xff;
}

{
    *base = 0;
    *size = 0;
    *type = 0;
    return;
}

{
    unsigned long long *saved = (unsigned long long *) frs;
    bool changed = false;
    int block = -1, range;
    while (fixed_range_blocks[++block].ranges)
        for (range = 0; range < fixed_range_blocks[block].ranges; range++)
            set_fixed_range (fixed_range_blocks[block].base_msr + range, &changed, (unsigned int *) saved++);
    return changed;
}

{
    unsigned int lo, hi;
    bool changed = false;
    rdmsr (MTRRphysBase_MSR (index), lo, hi);
    if ((vr->base_lo & 0xfffff0ffUL) != (lo & 0xfffff0ffUL) || (vr->base_hi & (size_and_mask >> (32 - PAGE_SHIFT))) != (hi & (size_and_mask >> (32 - PAGE_SHIFT)))) {
        mtrr_wrmsr (MTRRphysBase_MSR (index), vr -> base_lo, vr -> base_hi);
        changed = true;
    }
    rdmsr (MTRRphysMask_MSR (index), lo, hi);
    if ((vr->mask_lo & 0xfffff800UL) != (lo & 0xfffff800UL) || (vr->mask_hi & (size_and_mask >> (32 - PAGE_SHIFT))) != (hi & (size_and_mask >> (32 - PAGE_SHIFT)))) {
        mtrr_wrmsr (MTRRphysMask_MSR (index), vr -> mask_lo, vr -> mask_hi);
        changed = true;
    }
    return changed;
}

{
    mtrr_wrmsr (MTRRphysBase_MSR (index), vr -> base_lo, vr -> base_hi);
    changed = true;
}

{
    mtrr_wrmsr (MTRRphysMask_MSR (index), vr -> mask_lo, vr -> mask_hi);
    changed = true;
}

{
    unsigned int i;
    unsigned long change_mask = 0;
    for (i = 0; i < num_var_ranges; i++)
        if (set_mtrr_var_ranges (i, &mtrr_state.var_ranges[i]))
            change_mask |= MTRR_CHANGE_MASK_VARIABLE;
    if (mtrr_state.have_fixed && set_fixed_ranges (mtrr_state.fixed_ranges))
        change_mask |= MTRR_CHANGE_MASK_FIXED;
    if ((deftype_lo & 0xff) != mtrr_state.def_type || ((deftype_lo & 0xc00) >> 10) != mtrr_state.enabled) {
        deftype_lo = (deftype_lo & ~0xcff) | mtrr_state.def_type | (mtrr_state.enabled << 10);
        change_mask |= MTRR_CHANGE_MASK_DEFTYPE;
    }
    return change_mask;
}

{
    deftype_lo = (deftype_lo & ~0xcff) | mtrr_state.def_type | (mtrr_state.enabled << 10);
    change_mask |= MTRR_CHANGE_MASK_DEFTYPE;
}

{
    unsigned long cr0;
    spin_lock (& set_atomicity_lock);
    cr0 = read_cr0 () | X86_CR0_CD;
    write_cr0 (cr0);
    wbinvd ();
    if (cpu_has_pge) {
        cr4 = read_cr4 ();
        write_cr4 (cr4 & ~ X86_CR4_PGE);
    }
    __flush_tlb ();
    rdmsr (MTRRdefType_MSR, deftype_lo, deftype_hi);
    mtrr_wrmsr (MTRRdefType_MSR, deftype_lo & ~ 0xcff, deftype_hi);
}

{
    cr4 = read_cr4 ();
    write_cr4 (cr4 & ~ X86_CR4_PGE);
}

{
    __flush_tlb ();
    mtrr_wrmsr (MTRRdefType_MSR, deftype_lo, deftype_hi);
    write_cr0 (read_cr0 () & 0xbfffffff);
    if (cpu_has_pge)
        write_cr4 (cr4);
    spin_unlock (& set_atomicity_lock);
}

{
    unsigned long mask, count;
    unsigned long flags;
    local_irq_save (flags);
    prepare_set ();
    mask = set_mtrr_state ();
    pat_init ();
    post_set ();
    local_irq_restore (flags);
    for (count = 0; count < sizeof mask * 8; ++count) {
        if (mask & 0x01)
            set_bit (count, &smp_changes_mask);
        mask >>= 1;
    }
}

{
    if (mask & 0x01)
        set_bit (count, &smp_changes_mask);
    mask >>= 1;
}

{
    unsigned long flags;
    struct mtrr_var_range *vr;
    vr = &mtrr_state.var_ranges[reg];
    local_irq_save (flags);
    prepare_set ();
    if (size == 0) {
        mtrr_wrmsr (MTRRphysMask_MSR (reg), 0, 0);
        memset (vr, 0, sizeof (struct mtrr_var_range));
    }
    else {
        vr->base_lo = base << PAGE_SHIFT | type;
        vr->base_hi = (base & size_and_mask) >> (32 - PAGE_SHIFT);
        vr->mask_lo = -size << PAGE_SHIFT | 0x800;
        vr->mask_hi = (-size & size_and_mask) >> (32 - PAGE_SHIFT);
        mtrr_wrmsr (MTRRphysBase_MSR (reg), vr -> base_lo, vr -> base_hi);
        mtrr_wrmsr (MTRRphysMask_MSR (reg), vr -> mask_lo, vr -> mask_hi);
    }
    post_set ();
    local_irq_restore (flags);
}

{
    mtrr_wrmsr (MTRRphysMask_MSR (reg), 0, 0);
    memset (vr, 0, sizeof (struct mtrr_var_range));
}

{
    vr->base_lo = base << PAGE_SHIFT | type;
    vr->base_hi = (base & size_and_mask) >> (32 - PAGE_SHIFT);
    vr->mask_lo = -size << PAGE_SHIFT | 0x800;
    vr->mask_hi = (-size & size_and_mask) >> (32 - PAGE_SHIFT);
    mtrr_wrmsr (MTRRphysBase_MSR (reg), vr -> base_lo, vr -> base_hi);
    mtrr_wrmsr (MTRRphysMask_MSR (reg), vr -> mask_lo, vr -> mask_hi);
}

{
    unsigned long lbase, last;
    if (is_cpu (INTEL) && boot_cpu_data.x86 == 6 && boot_cpu_data.x86_model == 1 && boot_cpu_data.x86_mask <= 7) {
        if (base & ((1 << (22 - PAGE_SHIFT)) - 1)) {
            printk (KERN_WARNING "mtrr: base(0x%lx000) is not 4 MiB aligned\n", base);
            return -EINVAL;
        }
        if (!(base + size < 0x70000 || base > 0x7003F) && (type == MTRR_TYPE_WRCOMB || type == MTRR_TYPE_WRBACK)) {
            printk (KERN_WARNING "mtrr: writable mtrr between 0x70000000 and 0x7003FFFF may hang the CPU.\n");
            return -EINVAL;
        }
    }
    last = base + size - 1;
    for (lbase = base; !(lbase & 1) && (last & 1); lbase = lbase >> 1, last = last >> 1)
        ;
    if (lbase != last) {
        printk (KERN_WARNING "mtrr: base(0x%lx000) is not aligned on a size(0x%lx000) boundary\n", base, size);
        return -EINVAL;
    }
    return 0;
}

{
    if (base & ((1 << (22 - PAGE_SHIFT)) - 1)) {
        printk (KERN_WARNING "mtrr: base(0x%lx000) is not 4 MiB aligned\n", base);
        return -EINVAL;
    }
    if (!(base + size < 0x70000 || base > 0x7003F) && (type == MTRR_TYPE_WRCOMB || type == MTRR_TYPE_WRBACK)) {
        printk (KERN_WARNING "mtrr: writable mtrr between 0x70000000 and 0x7003FFFF may hang the CPU.\n");
        return -EINVAL;
    }
}

{
    printk (KERN_WARNING "mtrr: base(0x%lx000) is not 4 MiB aligned\n", base);
    return -EINVAL;
}

{
    printk (KERN_WARNING "mtrr: writable mtrr between 0x70000000 and 0x7003FFFF may hang the CPU.\n");
    return -EINVAL;
}

{
    printk (KERN_WARNING "mtrr: base(0x%lx000) is not aligned on a size(0x%lx000) boundary\n", base, size);
    return -EINVAL;
}

{
    unsigned long config, dummy;
    rdmsr (MTRRcap_MSR, config, dummy);
    return (config & (1 << 10));
}

{
    return 1;
}

{
    if (ops->vendor && ops->vendor < X86_VENDOR_NUM)
        mtrr_ops[ops->vendor] = ops;
}

{
    struct pci_dev *dev;
    u8 rev;
    if ((dev = pci_get_class (PCI_CLASS_BRIDGE_HOST << 8, NULL)) != NULL) {
        if (dev->vendor == PCI_VENDOR_ID_SERVERWORKS && dev->device == PCI_DEVICE_ID_SERVERWORKS_LE) {
            pci_read_config_byte (dev, PCI_CLASS_REVISION, & rev);
            if (rev <= 5) {
                printk (KERN_INFO "mtrr: Serverworks LE rev < 6 detected. Write-combining disabled.\n");
                pci_dev_put (dev);
                return 0;
            }
        }
        if (dev->vendor == PCI_VENDOR_ID_INTEL && dev->device == PCI_DEVICE_ID_INTEL_82451NX) {
            printk (KERN_INFO "mtrr: Intel 450NX MMC detected. Write-combining disabled.\n");
            pci_dev_put (dev);
            return 0;
        }
        pci_dev_put (dev);
    }
    return (mtrr_if->have_wrcomb ? mtrr_if->have_wrcomb () : 0);
}

{
    if (dev->vendor == PCI_VENDOR_ID_SERVERWORKS && dev->device == PCI_DEVICE_ID_SERVERWORKS_LE) {
        pci_read_config_byte (dev, PCI_CLASS_REVISION, & rev);
        if (rev <= 5) {
            printk (KERN_INFO "mtrr: Serverworks LE rev < 6 detected. Write-combining disabled.\n");
            pci_dev_put (dev);
            return 0;
        }
    }
    if (dev->vendor == PCI_VENDOR_ID_INTEL && dev->device == PCI_DEVICE_ID_INTEL_82451NX) {
        printk (KERN_INFO "mtrr: Intel 450NX MMC detected. Write-combining disabled.\n");
        pci_dev_put (dev);
        return 0;
    }
    pci_dev_put (dev);
}

{
    pci_read_config_byte (dev, PCI_CLASS_REVISION, & rev);
    if (rev <= 5) {
        printk (KERN_INFO "mtrr: Serverworks LE rev < 6 detected. Write-combining disabled.\n");
        pci_dev_put (dev);
        return 0;
    }
}

{
    printk (KERN_INFO "mtrr: Serverworks LE rev < 6 detected. Write-combining disabled.\n");
    pci_dev_put (dev);
    return 0;
}

{
    printk (KERN_INFO "mtrr: Intel 450NX MMC detected. Write-combining disabled.\n");
    pci_dev_put (dev);
    return 0;
}

{
    unsigned long config = 0, dummy;
    if (use_intel ()) {
        rdmsr (MTRRcap_MSR, config, dummy);
    }
    else if (is_cpu (AMD))
        config = 2;
    else if (is_cpu (CYRIX) || is_cpu (CENTAUR))
        config = 8;
    num_var_ranges = config & 0xff;
}

{
    rdmsr (MTRRcap_MSR, config, dummy);
}

{
    int i, max;
    max = num_var_ranges;
    for (i = 0; i < max; i++)
        mtrr_usage_table[i] = 1;
}

{
    return type1 == MTRR_TYPE_UNCACHABLE || type2 == MTRR_TYPE_UNCACHABLE || (type1 == MTRR_TYPE_WRTHROUGH && type2 == MTRR_TYPE_WRBACK) || (type1 == MTRR_TYPE_WRBACK && type2 == MTRR_TYPE_WRTHROUGH);
}

{
    struct set_mtrr_data data;
    unsigned long flags;
    data.smp_reg = reg;
    data.smp_base = base;
    data.smp_size = size;
    data.smp_type = type;
    atomic_set (& data.count, num_booting_cpus () - 1);
    smp_wmb ();
    atomic_set (& data.gate, 0);
    if (smp_call_function (ipi_handler, &data, 1, 0) != 0)
        panic ("mtrr: timed out waiting for other CPUs\n");
    local_irq_save (flags);
    while (atomic_read (&data.count))
        cpu_relax ();
    atomic_set (& data.count, num_booting_cpus () - 1);
    smp_wmb ();
    atomic_set (& data.gate, 1);
    if (reg != ~0U)
        mtrr_if->set (reg, base, size, type);
    while (atomic_read (&data.count))
        cpu_relax ();
    atomic_set (& data.count, num_booting_cpus () - 1);
    smp_wmb ();
    atomic_set (& data.gate, 0);
    while (atomic_read (&data.count))
        cpu_relax ();
    local_irq_restore (flags);
}

{
    int i, replace, error;
    mtrr_type ltype;
    unsigned long lbase, lsize;
    if (!mtrr_if)
        return -ENXIO;
    if ((error = mtrr_if->validate_add_page (base, size, type)))
        return error;
    if (type >= MTRR_NUM_TYPES) {
        printk (KERN_WARNING "mtrr: type: %u invalid\n", type);
        return -EINVAL;
    }
    if ((type == MTRR_TYPE_WRCOMB) && !have_wrcomb ()) {
        printk (KERN_WARNING "mtrr: your processor doesn't support write-combining\n");
        return -ENOSYS;
    }
    if (!size) {
        printk (KERN_WARNING "mtrr: zero sized request\n");
        return -EINVAL;
    }
    if (base & size_or_mask || size & size_or_mask) {
        printk (KERN_WARNING "mtrr: base or size exceeds the MTRR width\n");
        return -EINVAL;
    }
    error = -EINVAL;
    replace = -1;
    get_online_cpus ();
    mutex_lock (& mtrr_mutex);
    for (i = 0; i < num_var_ranges; ++i) {
        mtrr_if->get (i, &lbase, &lsize, &ltype);
        if (!lsize || base > lbase + lsize - 1 || base + size - 1 < lbase)
            continue;
        if (base < lbase || base + size - 1 > lbase + lsize - 1) {
            if (base <= lbase && base + size - 1 >= lbase + lsize - 1) {
                if (type == ltype) {
                    replace = replace == -1 ? i : -2;
                    continue;
                }
                else if (types_compatible (type, ltype))
                    continue;
            }
            printk (KERN_WARNING "mtrr: 0x%lx000,0x%lx000 overlaps existing" " 0x%lx000,0x%lx000\n", base, size, lbase, lsize);
            goto out;
        }
        if (ltype != type) {
            if (types_compatible (type, ltype))
                continue;
            printk (KERN_WARNING "mtrr: type mismatch for %lx000,%lx000 old: %s new: %s\n", base, size, mtrr_attrib_to_str (ltype), mtrr_attrib_to_str (type));
            goto out;
        }
        if (increment)
            ++mtrr_usage_table[i];
        error = i;
        goto out;
    }
    i = mtrr_if->get_free_region (base, size, replace);
    if (i >= 0) {
        set_mtrr (i, base, size, type);
        if (likely (replace < 0)) {
            mtrr_usage_table[i] = 1;
        }
        else {
            mtrr_usage_table[i] = mtrr_usage_table[replace];
            if (increment)
                mtrr_usage_table[i]++;
            if (unlikely (replace != i)) {
                set_mtrr (replace, 0, 0, 0);
                mtrr_usage_table[replace] = 0;
            }
        }
    }
    else
        printk (KERN_INFO "mtrr: no more MTRRs available\n");
    error = i;
out :
    mutex_unlock (&mtrr_mutex);
    put_online_cpus ();
    return error;
}

{
    printk (KERN_WARNING "mtrr: type: %u invalid\n", type);
    return -EINVAL;
}

{
    printk (KERN_WARNING "mtrr: your processor doesn't support write-combining\n");
    return -ENOSYS;
}

{
    printk (KERN_WARNING "mtrr: zero sized request\n");
    return -EINVAL;
}

{
    printk (KERN_WARNING "mtrr: base or size exceeds the MTRR width\n");
    return -EINVAL;
}

{
    mtrr_if->get (i, &lbase, &lsize, &ltype);
    if (!lsize || base > lbase + lsize - 1 || base + size - 1 < lbase)
        continue;
    if (base < lbase || base + size - 1 > lbase + lsize - 1) {
        if (base <= lbase && base + size - 1 >= lbase + lsize - 1) {
            if (type == ltype) {
                replace = replace == -1 ? i : -2;
                continue;
            }
            else if (types_compatible (type, ltype))
                continue;
        }
        printk (KERN_WARNING "mtrr: 0x%lx000,0x%lx000 overlaps existing" " 0x%lx000,0x%lx000\n", base, size, lbase, lsize);
        goto out;
    }
    if (ltype != type) {
        if (types_compatible (type, ltype))
            continue;
        printk (KERN_WARNING "mtrr: type mismatch for %lx000,%lx000 old: %s new: %s\n", base, size, mtrr_attrib_to_str (ltype), mtrr_attrib_to_str (type));
        goto out;
    }
    if (increment)
        ++mtrr_usage_table[i];
    error = i;
    goto out;
}

{
    if (base <= lbase && base + size - 1 >= lbase + lsize - 1) {
        if (type == ltype) {
            replace = replace == -1 ? i : -2;
            continue;
        }
        else if (types_compatible (type, ltype))
            continue;
    }
    printk (KERN_WARNING "mtrr: 0x%lx000,0x%lx000 overlaps existing" " 0x%lx000,0x%lx000\n", base, size, lbase, lsize);
    goto out;
}

{
    if (type == ltype) {
        replace = replace == -1 ? i : -2;
        continue;
    }
    else if (types_compatible (type, ltype))
        continue;
}

{
    replace = replace == -1 ? i : -2;
    continue;
}

{
    if (types_compatible (type, ltype))
        continue;
    printk (KERN_WARNING "mtrr: type mismatch for %lx000,%lx000 old: %s new: %s\n", base, size, mtrr_attrib_to_str (ltype), mtrr_attrib_to_str (type));
    goto out;
}

{
    set_mtrr (i, base, size, type);
    if (likely (replace < 0)) {
        mtrr_usage_table[i] = 1;
    }
    else {
        mtrr_usage_table[i] = mtrr_usage_table[replace];
        if (increment)
            mtrr_usage_table[i]++;
        if (unlikely (replace != i)) {
            set_mtrr (replace, 0, 0, 0);
            mtrr_usage_table[replace] = 0;
        }
    }
}

{
    mtrr_usage_table[i] = 1;
}

{
    mtrr_usage_table[i] = mtrr_usage_table[replace];
    if (increment)
        mtrr_usage_table[i]++;
    if (unlikely (replace != i)) {
        set_mtrr (replace, 0, 0, 0);
        mtrr_usage_table[replace] = 0;
    }
}

{
    set_mtrr (replace, 0, 0, 0);
    mtrr_usage_table[replace] = 0;
}

{
    if ((base & (PAGE_SIZE - 1)) || (size & (PAGE_SIZE - 1))) {
        printk (KERN_WARNING "mtrr: size and base must be multiples of 4 kiB\n");
        printk (KERN_DEBUG "mtrr: size: 0x%lx  base: 0x%lx\n", size, base);
        dump_stack ();
        return -1;
    }
    return 0;
}

{
    printk (KERN_WARNING "mtrr: size and base must be multiples of 4 kiB\n");
    printk (KERN_DEBUG "mtrr: size: 0x%lx  base: 0x%lx\n", size, base);
    dump_stack ();
    return -1;
}

{
    if (mtrr_check (base, size))
        return -EINVAL;
    return mtrr_add_page (base >> PAGE_SHIFT, size >> PAGE_SHIFT, type, increment);
}

{
    int i, max;
    mtrr_type ltype;
    unsigned long lbase, lsize;
    int error = -EINVAL;
    if (!mtrr_if)
        return -ENXIO;
    max = num_var_ranges;
    get_online_cpus ();
    mutex_lock (& mtrr_mutex);
    if (reg < 0) {
        for (i = 0; i < max; ++i) {
            mtrr_if->get (i, &lbase, &lsize, &ltype);
            if (lbase == base && lsize == size) {
                reg = i;
                break;
            }
        }
        if (reg < 0) {
            printk (KERN_DEBUG "mtrr: no MTRR for %lx000,%lx000 found\n", base, size);
            goto out;
        }
    }
    if (reg >= max) {
        printk (KERN_WARNING "mtrr: register: %d too big\n", reg);
        goto out;
    }
    mtrr_if->get (reg, &lbase, &lsize, &ltype);
    if (lsize < 1) {
        printk (KERN_WARNING "mtrr: MTRR %d not used\n", reg);
        goto out;
    }
    if (mtrr_usage_table[reg] < 1) {
        printk (KERN_WARNING "mtrr: reg: %d has count=0\n", reg);
        goto out;
    }
    if (--mtrr_usage_table[reg] < 1)
        set_mtrr (reg, 0, 0, 0);
    error = reg;
out :
    mutex_unlock (&mtrr_mutex);
    put_online_cpus ();
    return error;
}

{
    for (i = 0; i < max; ++i) {
        mtrr_if->get (i, &lbase, &lsize, &ltype);
        if (lbase == base && lsize == size) {
            reg = i;
            break;
        }
    }
    if (reg < 0) {
        printk (KERN_DEBUG "mtrr: no MTRR for %lx000,%lx000 found\n", base, size);
        goto out;
    }
}

{
    mtrr_if->get (i, &lbase, &lsize, &ltype);
    if (lbase == base && lsize == size) {
        reg = i;
        break;
    }
}

{
    reg = i;
    break;
}

{
    printk (KERN_DEBUG "mtrr: no MTRR for %lx000,%lx000 found\n", base, size);
    goto out;
}

{
    printk (KERN_WARNING "mtrr: register: %d too big\n", reg);
    goto out;
}

{
    printk (KERN_WARNING "mtrr: MTRR %d not used\n", reg);
    goto out;
}

{
    printk (KERN_WARNING "mtrr: reg: %d has count=0\n", reg);
    goto out;
}

{
    if (mtrr_check (base, size))
        return -EINVAL;
    return mtrr_del_page (reg, base >> PAGE_SHIFT, size >> PAGE_SHIFT);
}

{
    amd_init_mtrr ();
    cyrix_init_mtrr ();
    centaur_init_mtrr ();
}

{
    int i;
    for (i = 0; i < num_var_ranges; i++) {
        mtrr_if->get (i, &mtrr_state[i].lbase, &mtrr_state[i].lsize, &mtrr_state[i].ltype);
    }
    return 0;
}

{
    mtrr_if->get (i, &mtrr_state[i].lbase, &mtrr_state[i].lsize, &mtrr_state[i].ltype);
}

{
    int i;
    for (i = 0; i < num_var_ranges; i++) {
        if (mtrr_state[i].lsize)
            set_mtrr (i, mtrr_state[i].lbase, mtrr_state[i].lsize, mtrr_state[i].ltype);
    }
    return 0;
}

{
    if (mtrr_state[i].lsize)
        set_mtrr (i, mtrr_state[i].lbase, mtrr_state[i].lsize, mtrr_state[i].ltype);
}

{
    disable_mtrr_trim = 1;
    return 0;
}

{
    u32 l, h;
    if (boot_cpu_data.x86_vendor != X86_VENDOR_AMD)
        return 0;
    if (boot_cpu_data.x86 < 0xf || boot_cpu_data.x86 > 0x11)
        return 0;
    if (rdmsr_safe (MSR_K8_SYSCFG, &l, &h) < 0)
        return 0;
    if ((l & (Tom2Enabled | Tom2ForceMemTypeWB)) == (Tom2Enabled | Tom2ForceMemTypeWB))
        return 1;
    return 0;
}

{
    unsigned long i, base, size, highest_pfn = 0, def, dummy;
    mtrr_type type;
    u64 trim_start, trim_size;
    if (!is_cpu (INTEL) || disable_mtrr_trim)
        return 0;
    rdmsr (MTRRdefType_MSR, def, dummy);
    def &= 0xff;
    if (def != MTRR_TYPE_UNCACHABLE)
        return 0;
    if (amd_special_default_mtrr ())
        return 0;
    for (i = 0; i < num_var_ranges; i++) {
        mtrr_if->get (i, &base, &size, &type);
        if (type != MTRR_TYPE_WRBACK)
            continue;
        if (highest_pfn < base + size)
            highest_pfn = base + size;
    }
    if (!highest_pfn) {
        if (!kvm_para_available ()) {
            printk (KERN_WARNING "WARNING: strange, CPU MTRRs all blank?\n");
            WARN_ON (1);
        }
        return 0;
    }
    if (highest_pfn < end_pfn) {
        printk (KERN_WARNING "WARNING: BIOS bug: CPU MTRRs don't cover" " all of memory, losing %luMB of RAM.\n", (end_pfn - highest_pfn) >> (20 - PAGE_SHIFT));
        WARN_ON (1);
        printk (KERN_INFO "update e820 for mtrr\n");
        trim_start = highest_pfn;
        trim_start <<= PAGE_SHIFT;
        trim_size = end_pfn;
        trim_size <<= PAGE_SHIFT;
        trim_size -= trim_start;
        update_memory_range (trim_start, trim_size, E820_RAM, E820_RESERVED);
        update_e820 ();
        return 1;
    }
    return 0;
}

{
    mtrr_if->get (i, &base, &size, &type);
    if (type != MTRR_TYPE_WRBACK)
        continue;
    if (highest_pfn < base + size)
        highest_pfn = base + size;
}

{
    if (!kvm_para_available ()) {
        printk (KERN_WARNING "WARNING: strange, CPU MTRRs all blank?\n");
        WARN_ON (1);
    }
    return 0;
}

{
    printk (KERN_WARNING "WARNING: strange, CPU MTRRs all blank?\n");
    WARN_ON (1);
}

{
    printk (KERN_WARNING "WARNING: BIOS bug: CPU MTRRs don't cover" " all of memory, losing %luMB of RAM.\n", (end_pfn - highest_pfn) >> (20 - PAGE_SHIFT));
    WARN_ON (1);
    printk (KERN_INFO "update e820 for mtrr\n");
    trim_start = highest_pfn;
    trim_start <<= PAGE_SHIFT;
    trim_size = end_pfn;
    trim_size <<= PAGE_SHIFT;
    trim_size -= trim_start;
    update_memory_range (trim_start, trim_size, E820_RAM, E820_RESERVED);
    update_e820 ();
    return 1;
}

{
    init_ifs ();
    if (cpu_has_mtrr) {
        mtrr_if = &generic_mtrr_ops;
        size_or_mask = 0xff000000;
        size_and_mask = 0x00f00000;
        if (cpuid_eax (0x80000000) >= 0x80000008) {
            u32 phys_addr;
            phys_addr = cpuid_eax (0x80000008) & 0xff;
            if (boot_cpu_data.x86_vendor == X86_VENDOR_INTEL && boot_cpu_data.x86 == 0xF && boot_cpu_data.x86_model == 0x3 && (boot_cpu_data.x86_mask == 0x3 || boot_cpu_data.x86_mask == 0x4))
                phys_addr = 36;
            size_or_mask = ~((1ULL << (phys_addr - PAGE_SHIFT)) - 1);
            size_and_mask = ~size_or_mask & 0xfffff00000ULL;
        }
        else if (boot_cpu_data.x86_vendor == X86_VENDOR_CENTAUR && boot_cpu_data.x86 == 6) {
            size_or_mask = 0xfff00000;
            size_and_mask = 0;
        }
    }
    else {
        switch (boot_cpu_data.x86_vendor) {
        case X86_VENDOR_AMD :
            if (cpu_has_k6_mtrr) {
                mtrr_if = mtrr_ops[X86_VENDOR_AMD];
                size_or_mask = 0xfff00000;
                size_and_mask = 0;
            }
            break;
        case X86_VENDOR_CENTAUR :
            if (cpu_has_centaur_mcr) {
                mtrr_if = mtrr_ops[X86_VENDOR_CENTAUR];
                size_or_mask = 0xfff00000;
                size_and_mask = 0;
            }
            break;
        case X86_VENDOR_CYRIX :
            if (cpu_has_cyrix_arr) {
                mtrr_if = mtrr_ops[X86_VENDOR_CYRIX];
                size_or_mask = 0xfff00000;
                size_and_mask = 0;
            }
            break;
        default :
            break;
        }
    }
    if (mtrr_if) {
        set_num_var_ranges ();
        init_table ();
        if (use_intel ())
            get_mtrr_state ();
    }
}

{
    mtrr_if = &generic_mtrr_ops;
    size_or_mask = 0xff000000;
    size_and_mask = 0x00f00000;
    if (cpuid_eax (0x80000000) >= 0x80000008) {
        u32 phys_addr;
        phys_addr = cpuid_eax (0x80000008) & 0xff;
        if (boot_cpu_data.x86_vendor == X86_VENDOR_INTEL && boot_cpu_data.x86 == 0xF && boot_cpu_data.x86_model == 0x3 && (boot_cpu_data.x86_mask == 0x3 || boot_cpu_data.x86_mask == 0x4))
            phys_addr = 36;
        size_or_mask = ~((1ULL << (phys_addr - PAGE_SHIFT)) - 1);
        size_and_mask = ~size_or_mask & 0xfffff00000ULL;
    }
    else if (boot_cpu_data.x86_vendor == X86_VENDOR_CENTAUR && boot_cpu_data.x86 == 6) {
        size_or_mask = 0xfff00000;
        size_and_mask = 0;
    }
}

{
    u32 phys_addr;
    phys_addr = cpuid_eax (0x80000008) & 0xff;
    if (boot_cpu_data.x86_vendor == X86_VENDOR_INTEL && boot_cpu_data.x86 == 0xF && boot_cpu_data.x86_model == 0x3 && (boot_cpu_data.x86_mask == 0x3 || boot_cpu_data.x86_mask == 0x4))
        phys_addr = 36;
    size_or_mask = ~((1ULL << (phys_addr - PAGE_SHIFT)) - 1);
    size_and_mask = ~size_or_mask & 0xfffff00000ULL;
}

{
    size_or_mask = 0xfff00000;
    size_and_mask = 0;
}

{
    switch (boot_cpu_data.x86_vendor) {
    case X86_VENDOR_AMD :
        if (cpu_has_k6_mtrr) {
            mtrr_if = mtrr_ops[X86_VENDOR_AMD];
            size_or_mask = 0xfff00000;
            size_and_mask = 0;
        }
        break;
    case X86_VENDOR_CENTAUR :
        if (cpu_has_centaur_mcr) {
            mtrr_if = mtrr_ops[X86_VENDOR_CENTAUR];
            size_or_mask = 0xfff00000;
            size_and_mask = 0;
        }
        break;
    case X86_VENDOR_CYRIX :
        if (cpu_has_cyrix_arr) {
            mtrr_if = mtrr_ops[X86_VENDOR_CYRIX];
            size_or_mask = 0xfff00000;
            size_and_mask = 0;
        }
        break;
    default :
        break;
    }
}

{
case X86_VENDOR_AMD :
    if (cpu_has_k6_mtrr) {
        mtrr_if = mtrr_ops[X86_VENDOR_AMD];
        size_or_mask = 0xfff00000;
        size_and_mask = 0;
    }
    break;
case X86_VENDOR_CENTAUR :
    if (cpu_has_centaur_mcr) {
        mtrr_if = mtrr_ops[X86_VENDOR_CENTAUR];
        size_or_mask = 0xfff00000;
        size_and_mask = 0;
    }
    break;
case X86_VENDOR_CYRIX :
    if (cpu_has_cyrix_arr) {
        mtrr_if = mtrr_ops[X86_VENDOR_CYRIX];
        size_or_mask = 0xfff00000;
        size_and_mask = 0;
    }
    break;
default :
    break;
}

{
    mtrr_if = mtrr_ops[X86_VENDOR_AMD];
    size_or_mask = 0xfff00000;
    size_and_mask = 0;
}

{
    mtrr_if = mtrr_ops[X86_VENDOR_CENTAUR];
    size_or_mask = 0xfff00000;
    size_and_mask = 0;
}

{
    mtrr_if = mtrr_ops[X86_VENDOR_CYRIX];
    size_or_mask = 0xfff00000;
    size_and_mask = 0;
}

{
    set_num_var_ranges ();
    init_table ();
    if (use_intel ())
        get_mtrr_state ();
}

{
    unsigned long flags;
    if (!mtrr_if || !use_intel ())
        return;
    local_irq_save (flags);
    mtrr_if->set_all ();
    local_irq_restore (flags);
}

{
    smp_call_function_single (0, mtrr_save_fixed_ranges, NULL, 1, 1);
}

{
    if (!mtrr_if)
        return 0;
    if (use_intel ())
        mtrr_state_warn ();
    else {
        sysdev_driver_register (& cpu_sysdev_class, & mtrr_sysdev_driver);
    }
    return 0;
}

{
    sysdev_driver_register (& cpu_sysdev_class, & mtrr_sysdev_driver);
}

{
    unsigned next, entry;
    atomic_inc (& mce_events);
    mce->finished = 0;
    wmb ();
    for (;;) {
        entry = rcu_dereference (mcelog.next);
        for (;;) {
            if (entry >= MCE_LOG_LEN) {
                set_bit (MCE_OVERFLOW, (unsigned long *) & mcelog.flags);
                return;
            }
            if (mcelog.entry[entry].finished) {
                entry++;
                continue;
            }
            break;
        }
        smp_rmb ();
        next = entry + 1;
        if (cmpxchg (&mcelog.next, entry, next) == entry)
            break;
    }
    memcpy (mcelog.entry + entry, mce, sizeof (struct mce));
    wmb ();
    mcelog.entry[entry].finished = 1;
    wmb ();
    set_bit (0, & notify_user);
}

{
    entry = rcu_dereference (mcelog.next);
    for (;;) {
        if (entry >= MCE_LOG_LEN) {
            set_bit (MCE_OVERFLOW, (unsigned long *) & mcelog.flags);
            return;
        }
        if (mcelog.entry[entry].finished) {
            entry++;
            continue;
        }
        break;
    }
    smp_rmb ();
    next = entry + 1;
    if (cmpxchg (&mcelog.next, entry, next) == entry)
        break;
}

{
    if (entry >= MCE_LOG_LEN) {
        set_bit (MCE_OVERFLOW, (unsigned long *) & mcelog.flags);
        return;
    }
    if (mcelog.entry[entry].finished) {
        entry++;
        continue;
    }
    break;
}

{
    set_bit (MCE_OVERFLOW, (unsigned long *) & mcelog.flags);
    return;
}

{
    entry++;
    continue;
}

{
    printk (KERN_EMERG "\n" KERN_EMERG "HARDWARE ERROR\n" KERN_EMERG "CPU %d: Machine Check Exception: %16Lx Bank %d: %016Lx\n", m -> cpu, m -> mcgstatus, m -> bank, m -> status);
    if (m->ip) {
        printk (KERN_EMERG "RIP%s %02x:<%016Lx> ", ! (m -> mcgstatus & MCG_STATUS_EIPV) ? " !INEXACT!" : "", m -> cs, m -> ip);
        if (m->cs == __KERNEL_CS)
            print_symbol ("{%s}", m->ip);
        printk ("\n");
    }
    printk (KERN_EMERG "TSC %Lx ", m -> tsc);
    if (m->addr)
        printk ("ADDR %Lx ", m->addr);
    if (m->misc)
        printk ("MISC %Lx ", m->misc);
    printk ("\n");
    printk (KERN_EMERG "This is not a software problem!\n");
    printk (KERN_EMERG "Run through mcelog --ascii to decode " "and contact your hardware vendor\n");
}

{
    printk (KERN_EMERG "RIP%s %02x:<%016Lx> ", ! (m -> mcgstatus & MCG_STATUS_EIPV) ? " !INEXACT!" : "", m -> cs, m -> ip);
    if (m->cs == __KERNEL_CS)
        print_symbol ("{%s}", m->ip);
    printk ("\n");
}

{
    int i;
    oops_begin ();
    for (i = 0; i < MCE_LOG_LEN; i++) {
        unsigned long tsc = mcelog.entry[i].tsc;
        if (time_before (tsc, start))
            continue;
        print_mce (& mcelog.entry [i]);
        if (backup && mcelog.entry[i].tsc == backup->tsc)
            backup = NULL;
    }
    if (backup)
        print_mce (backup);
    panic (msg);
}

{
    unsigned long tsc = mcelog.entry[i].tsc;
    if (time_before (tsc, start))
        continue;
    print_mce (& mcelog.entry [i]);
    if (backup && mcelog.entry[i].tsc == backup->tsc)
        backup = NULL;
}

{
    return cpu_has (c, X86_FEATURE_MCE) && cpu_has (c, X86_FEATURE_MCA);
}

{
    if (regs && (m->mcgstatus & MCG_STATUS_RIPV)) {
        m->ip = regs->ip;
        m->cs = regs->cs;
    }
    else {
        m->ip = 0;
        m->cs = 0;
    }
    if (rip_msr) {
        m->mcgstatus |= MCG_STATUS_EIPV;
        rdmsrl (rip_msr, m -> ip);
        m->cs = 0;
    }
}

{
    m->ip = regs->ip;
    m->cs = regs->cs;
}

{
    m->mcgstatus |= MCG_STATUS_EIPV;
    rdmsrl (rip_msr, m -> ip);
    m->cs = 0;
}

{
    struct mce m, panicm;
    u64 mcestart = 0;
    int i;
    int panicm_found = 0;
    int no_way_out = 0;
    int kill_it = 0;
    atomic_inc (& mce_entry);
    if ((regs && notify_die (DIE_NMI, "machine check", regs, error_code, 18, SIGKILL) == NOTIFY_STOP) || !banks)
        goto out2;
    memset (& m, 0, sizeof (struct mce));
    m.cpu = smp_processor_id ();
    rdmsrl (MSR_IA32_MCG_STATUS, m.mcgstatus);
    if (!(m.mcgstatus & MCG_STATUS_RIPV))
        no_way_out = 1;
    rdtscll (mcestart);
    barrier ();
    for (i = 0; i < banks; i++) {
        if (!bank[i])
            continue;
        m.misc = 0;
        m.addr = 0;
        m.bank = i;
        m.tsc = 0;
        rdmsrl (MSR_IA32_MC0_STATUS + i * 4, m.status);
        if ((m.status & MCI_STATUS_VAL) == 0)
            continue;
        if (m.status & MCI_STATUS_EN) {
            no_way_out |= !!(m.status & MCI_STATUS_PCC);
            if (m.status & MCI_STATUS_UC) {
                if (tolerant < 1 || m.status & MCI_STATUS_OVER)
                    no_way_out = 1;
                kill_it = 1;
            }
        }
        if (m.status & MCI_STATUS_MISCV)
            rdmsrl (MSR_IA32_MC0_MISC +i * 4, m.misc);
        if (m.status & MCI_STATUS_ADDRV)
            rdmsrl (MSR_IA32_MC0_ADDR +i * 4, m.addr);
        mce_get_rip (& m, regs);
        if (error_code >= 0)
            rdtscll (m.tsc);
        if (error_code != -2)
            mce_log (&m);
        if ((m.status & MCI_STATUS_UC) && (m.status & MCI_STATUS_EN)) {
            panicm = m;
            panicm_found = 1;
        }
        add_taint (TAINT_MACHINE_CHECK);
    }
    if (!regs)
        goto out;
    if (!panicm_found)
        panicm = m;
    if (no_way_out && tolerant < 3)
        mce_panic ("Machine check", &panicm, mcestart);
    if (kill_it && tolerant < 3) {
        int user_space = 0;
        if (m.mcgstatus & MCG_STATUS_EIPV)
            user_space = panicm.ip && (panicm.cs & 3);
        if (user_space) {
            do_exit (SIGBUS);
        }
        else if (panic_on_oops || tolerant < 2) {
            mce_panic ("Uncorrected machine check", & panicm, mcestart);
        }
    }
    set_thread_flag (TIF_MCE_NOTIFY);
out :
    for (i = 0; i < banks; i++)
        wrmsrl (MSR_IA32_MC0_STATUS +4 * i, 0);
    wrmsrl (MSR_IA32_MCG_STATUS, 0);
out2 :
    atomic_dec (&mce_entry);
}

{
    if (!bank[i])
        continue;
    m.misc = 0;
    m.addr = 0;
    m.bank = i;
    m.tsc = 0;
    rdmsrl (MSR_IA32_MC0_STATUS + i * 4, m.status);
    if ((m.status & MCI_STATUS_VAL) == 0)
        continue;
    if (m.status & MCI_STATUS_EN) {
        no_way_out |= !!(m.status & MCI_STATUS_PCC);
        if (m.status & MCI_STATUS_UC) {
            if (tolerant < 1 || m.status & MCI_STATUS_OVER)
                no_way_out = 1;
            kill_it = 1;
        }
    }
    if (m.status & MCI_STATUS_MISCV)
        rdmsrl (MSR_IA32_MC0_MISC +i * 4, m.misc);
    if (m.status & MCI_STATUS_ADDRV)
        rdmsrl (MSR_IA32_MC0_ADDR +i * 4, m.addr);
    mce_get_rip (& m, regs);
    if (error_code >= 0)
        rdtscll (m.tsc);
    if (error_code != -2)
        mce_log (&m);
    if ((m.status & MCI_STATUS_UC) && (m.status & MCI_STATUS_EN)) {
        panicm = m;
        panicm_found = 1;
    }
    add_taint (TAINT_MACHINE_CHECK);
}

{
    no_way_out |= !!(m.status & MCI_STATUS_PCC);
    if (m.status & MCI_STATUS_UC) {
        if (tolerant < 1 || m.status & MCI_STATUS_OVER)
            no_way_out = 1;
        kill_it = 1;
    }
}

{
    if (tolerant < 1 || m.status & MCI_STATUS_OVER)
        no_way_out = 1;
    kill_it = 1;
}

{
    panicm = m;
    panicm_found = 1;
}

{
    int user_space = 0;
    if (m.mcgstatus & MCG_STATUS_EIPV)
        user_space = panicm.ip && (panicm.cs & 3);
    if (user_space) {
        do_exit (SIGBUS);
    }
    else if (panic_on_oops || tolerant < 2) {
        mce_panic ("Uncorrected machine check", & panicm, mcestart);
    }
}

{
    do_exit (SIGBUS);
}

{
    mce_panic ("Uncorrected machine check", & panicm, mcestart);
}

{
    if (mce_available (&current_cpu_data))
        do_machine_check (NULL, 0);
}

{
    on_each_cpu (mcheck_check_cpu, NULL, 1, 1);
    if (mce_notify_user ()) {
        next_interval = max (next_interval / 2, HZ / 100);
    }
    else {
        next_interval = min (next_interval *2, (int) round_jiffies_relative (check_interval *HZ));
    }
    schedule_delayed_work (& mcheck_work, next_interval);
}

{
    next_interval = max (next_interval / 2, HZ / 100);
}

{
    next_interval = min (next_interval *2, (int) round_jiffies_relative (check_interval *HZ));
}

{
    clear_thread_flag (TIF_MCE_NOTIFY);
    if (test_and_clear_bit (0, &notify_user)) {
        static unsigned long last_print;
        unsigned long now = jiffies;
        wake_up_interruptible (& mce_wait);
        if (trigger[0])
            call_usermodehelper (trigger, trigger_argv, NULL, UMH_NO_WAIT);
        if (time_after_eq (now, last_print +(check_interval * HZ))) {
            last_print = now;
            printk (KERN_INFO "Machine check events logged\n");
        }
        return 1;
    }
    return 0;
}

{
    static unsigned long last_print;
    unsigned long now = jiffies;
    wake_up_interruptible (& mce_wait);
    if (trigger[0])
        call_usermodehelper (trigger, trigger_argv, NULL, UMH_NO_WAIT);
    if (time_after_eq (now, last_print +(check_interval * HZ))) {
        last_print = now;
        printk (KERN_INFO "Machine check events logged\n");
    }
    return 1;
}

{
    last_print = now;
    printk (KERN_INFO "Machine check events logged\n");
}

{
    if (action == IDLE_END && test_thread_flag (TIF_MCE_NOTIFY))
        mce_notify_user ();
    return NOTIFY_OK;
}

{
    next_interval = check_interval * HZ;
    if (next_interval)
        schedule_delayed_work (&mcheck_work, round_jiffies_relative (next_interval));
    idle_notifier_register (& mce_idle_notifier);
    return 0;
}

{
    u64 cap;
    int i;
    rdmsrl (MSR_IA32_MCG_CAP, cap);
    banks = cap & 0xff;
    if (banks > NR_BANKS) {
        printk (KERN_INFO "MCE: warning: using only %d banks\n", banks);
        banks = NR_BANKS;
    }
    if ((cap & (1 << 9)) && ((cap >> 16) & 0xff) >= 9)
        rip_msr = MSR_IA32_MCG_EIP;
    do_machine_check (NULL, mce_bootlog ? - 1 : - 2);
    set_in_cr4 (X86_CR4_MCE);
    if (cap & MCG_CTL_P)
        wrmsr (MSR_IA32_MCG_CTL, 0xffffffff, 0xffffffff);
    for (i = 0; i < banks; i++) {
        wrmsrl (MSR_IA32_MC0_CTL + 4 * i, bank [i]);
        wrmsrl (MSR_IA32_MC0_STATUS + 4 * i, 0);
    }
}

{
    printk (KERN_INFO "MCE: warning: using only %d banks\n", banks);
    banks = NR_BANKS;
}

{
    wrmsrl (MSR_IA32_MC0_CTL + 4 * i, bank [i]);
    wrmsrl (MSR_IA32_MC0_STATUS + 4 * i, 0);
}

{
    if (c->x86_vendor == X86_VENDOR_AMD) {
        if (c->x86 == 15)
            clear_bit (10, &bank[4]);
        if (c->x86 <= 17 && mce_bootlog < 0)
            mce_bootlog = 0;
    }
}

{
    if (c->x86 == 15)
        clear_bit (10, &bank[4]);
    if (c->x86 <= 17 && mce_bootlog < 0)
        mce_bootlog = 0;
}

{
    switch (c->x86_vendor) {
    case X86_VENDOR_INTEL :
        mce_intel_feature_init (c);
        break;
    case X86_VENDOR_AMD :
        mce_amd_feature_init (c);
        break;
    default :
        break;
    }
}

{
case X86_VENDOR_INTEL :
    mce_intel_feature_init (c);
    break;
case X86_VENDOR_AMD :
    mce_amd_feature_init (c);
    break;
default :
    break;
}

{
    static cpumask_t mce_cpus = CPU_MASK_NONE;
    mce_cpu_quirks (c);
    if (mce_dont_init || cpu_test_and_set (smp_processor_id (), mce_cpus) || !mce_available (c))
        return;
    mce_init (NULL);
    mce_cpu_features (c);
}

{
    spin_lock (& mce_state_lock);
    if (open_exclu || (open_count && (file->f_flags & O_EXCL))) {
        spin_unlock (& mce_state_lock);
        return -EBUSY;
    }
    if (file->f_flags & O_EXCL)
        open_exclu = 1;
    open_count++;
    spin_unlock (& mce_state_lock);
    return nonseekable_open (inode, file);
}

{
    spin_unlock (& mce_state_lock);
    return -EBUSY;
}

{
    spin_lock (& mce_state_lock);
    open_count--;
    open_exclu = 0;
    spin_unlock (& mce_state_lock);
    return 0;
}

{
    unsigned long *cpu_tsc = (unsigned long *) data;
    rdtscll (cpu_tsc [smp_processor_id ()]);
}

{
    unsigned long *cpu_tsc;
    static DEFINE_MUTEX (mce_read_mutex);
    unsigned next;
    char __user *buf = ubuf;
    int i, err;
    cpu_tsc = kmalloc (NR_CPUS * sizeof (long), GFP_KERNEL);
    if (!cpu_tsc)
        return -ENOMEM;
    mutex_lock (& mce_read_mutex);
    next = rcu_dereference (mcelog.next);
    if (*off != 0 || usize < MCE_LOG_LEN * sizeof (struct mce)) {
        mutex_unlock (& mce_read_mutex);
        kfree (cpu_tsc);
        return -EINVAL;
    }
    err = 0;
    for (i = 0; i < next; i++) {
        unsigned long start = jiffies;
        while (!mcelog.entry[i].finished) {
            if (time_after_eq (jiffies, start +2)) {
                memset (mcelog.entry + i, 0, sizeof (struct mce));
                goto timeout;
            }
            cpu_relax ();
        }
        smp_rmb ();
        err |= copy_to_user (buf, mcelog.entry + i, sizeof (struct mce));
        buf += sizeof (struct mce);
    timeout :
        ;
    }
    memset (mcelog.entry, 0, next * sizeof (struct mce));
    mcelog.next = 0;
    synchronize_sched ();
    on_each_cpu (collect_tscs, cpu_tsc, 1, 1);
    for (i = next; i < MCE_LOG_LEN; i++) {
        if (mcelog.entry[i].finished && mcelog.entry[i].tsc < cpu_tsc[mcelog.entry[i].cpu]) {
            err |= copy_to_user (buf, mcelog.entry + i, sizeof (struct mce));
            smp_rmb ();
            buf += sizeof (struct mce);
            memset (& mcelog.entry [i], 0, sizeof (struct mce));
        }
    }
    mutex_unlock (& mce_read_mutex);
    kfree (cpu_tsc);
    return err ? -EFAULT : buf - ubuf;
}

{
    mutex_unlock (& mce_read_mutex);
    kfree (cpu_tsc);
    return -EINVAL;
}

{
    unsigned long start = jiffies;
    while (!mcelog.entry[i].finished) {
        if (time_after_eq (jiffies, start +2)) {
            memset (mcelog.entry + i, 0, sizeof (struct mce));
            goto timeout;
        }
        cpu_relax ();
    }
    smp_rmb ();
    err |= copy_to_user (buf, mcelog.entry + i, sizeof (struct mce));
    buf += sizeof (struct mce);
timeout :
    ;
}

{
    if (time_after_eq (jiffies, start +2)) {
        memset (mcelog.entry + i, 0, sizeof (struct mce));
        goto timeout;
    }
    cpu_relax ();
}

{
    memset (mcelog.entry + i, 0, sizeof (struct mce));
    goto timeout;
}

{
    if (mcelog.entry[i].finished && mcelog.entry[i].tsc < cpu_tsc[mcelog.entry[i].cpu]) {
        err |= copy_to_user (buf, mcelog.entry + i, sizeof (struct mce));
        smp_rmb ();
        buf += sizeof (struct mce);
        memset (& mcelog.entry [i], 0, sizeof (struct mce));
    }
}

{
    err |= copy_to_user (buf, mcelog.entry + i, sizeof (struct mce));
    smp_rmb ();
    buf += sizeof (struct mce);
    memset (& mcelog.entry [i], 0, sizeof (struct mce));
}

{
    poll_wait (file, & mce_wait, wait);
    if (rcu_dereference (mcelog.next))
        return POLLIN | POLLRDNORM;
    return 0;
}

{
    int __user *p = (int __user *) arg;
    if (!capable (CAP_SYS_ADMIN))
        return -EPERM;
    switch (cmd) {
    case MCE_GET_RECORD_LEN :
        return put_user (sizeof (struct mce), p);
    case MCE_GET_LOG_LEN :
        return put_user (MCE_LOG_LEN, p);
    case MCE_GETCLEAR_FLAGS :
        {
            unsigned flags;
            do {
                flags = mcelog.flags;
            }
            while (cmpxchg (&mcelog.flags, flags, 0) != flags);
            return put_user (flags, p);
        }
    default :
        return -ENOTTY;
    }
}

{
case MCE_GET_RECORD_LEN :
    return put_user (sizeof (struct mce), p);
case MCE_GET_LOG_LEN :
    return put_user (MCE_LOG_LEN, p);
case MCE_GETCLEAR_FLAGS :
    {
        unsigned flags;
        do {
            flags = mcelog.flags;
        }
        while (cmpxchg (&mcelog.flags, flags, 0) != flags);
        return put_user (flags, p);
    }
default :
    return -ENOTTY;
}

{
    unsigned flags;
    do {
        flags = mcelog.flags;
    }
    while (cmpxchg (&mcelog.flags, flags, 0) != flags);
    return put_user (flags, p);
}

{
    flags = mcelog.flags;
}

{
    old_cr4 = read_cr4 ();
    clear_in_cr4 (X86_CR4_MCE);
}

{
    if (old_cr4 & X86_CR4_MCE)
        set_in_cr4 (X86_CR4_MCE);
}

{
    mce_dont_init = 1;
    return 1;
}

{
    if (!strcmp (str, "off"))
        mce_dont_init = 1;
    else if (!strcmp (str, "bootlog") || !strcmp (str, "nobootlog"))
        mce_bootlog = str[0] == 'b';
    else if (isdigit (str[0]))
        get_option (&str, &tolerant);
    else
        printk ("mce= argument %s ignored. Please use /sys", str);
    return 1;
}

{
    mce_init (NULL);
    return 0;
}

{
    if (next_interval)
        cancel_delayed_work (&mcheck_work);
    on_each_cpu (mce_init, NULL, 1, 1);
    next_interval = check_interval * HZ;
    if (next_interval)
        schedule_delayed_work (&mcheck_work, round_jiffies_relative (next_interval));
}

{
    strcpy (buf, trigger);
    strcat (buf, "\n");
    return strlen (trigger) + 1;
}

{
    char *p;
    int len;
    strncpy (trigger, buf, sizeof (trigger));
    trigger[sizeof (trigger) - 1] = 0;
    len = strlen (trigger);
    p = strchr (trigger, '\n');
    if (*p)
        *p = 0;
    return len;
}

{
    int err;
    int i;
    if (!mce_available (&boot_cpu_data))
        return -EIO;
    memset (& per_cpu (device_mce, cpu).kobj, 0, sizeof (struct kobject));
    per_cpu (device_mce, cpu).id = cpu;
    per_cpu (device_mce, cpu).cls = &mce_sysclass;
    err = sysdev_register (&per_cpu (device_mce, cpu));
    if (err)
        return err;
    for (i = 0; mce_attributes[i]; i++) {
        err = sysdev_create_file (&per_cpu (device_mce, cpu), mce_attributes[i]);
        if (err)
            goto error;
    }
    cpu_set (cpu, mce_device_initialized);
    return 0;
error :
    while (i--) {
        sysdev_remove_file (& per_cpu (device_mce, cpu), mce_attributes [i]);
    }
    sysdev_unregister (& per_cpu (device_mce, cpu));
    return err;
}

{
    err = sysdev_create_file (&per_cpu (device_mce, cpu), mce_attributes[i]);
    if (err)
        goto error;
}

{
    sysdev_remove_file (& per_cpu (device_mce, cpu), mce_attributes [i]);
}

{
    int i;
    if (!cpu_isset (cpu, mce_device_initialized))
        return;
    for (i = 0; mce_attributes[i]; i++)
        sysdev_remove_file (&per_cpu (device_mce, cpu), mce_attributes[i]);
    sysdev_unregister (& per_cpu (device_mce, cpu));
    cpu_clear (cpu, mce_device_initialized);
}

{
    unsigned int cpu = (unsigned long) hcpu;
    switch (action) {
    case CPU_ONLINE :
    case CPU_ONLINE_FROZEN :
        mce_create_device (cpu);
        break;
    case CPU_DEAD :
    case CPU_DEAD_FROZEN :
        mce_remove_device (cpu);
        break;
    }
    return NOTIFY_OK;
}

{
case CPU_ONLINE :
case CPU_ONLINE_FROZEN :
    mce_create_device (cpu);
    break;
case CPU_DEAD :
case CPU_DEAD_FROZEN :
    mce_remove_device (cpu);
    break;
}

{
    int err;
    int i = 0;
    if (!mce_available (&boot_cpu_data))
        return -EIO;
    err = sysdev_class_register (&mce_sysclass);
    if (err)
        return err;

    for_each_online_cpu (i) {
        err = mce_create_device (i);
        if (err)
            return err;
    }

    register_hotcpu_notifier (& mce_cpu_notifier);
    misc_register (& mce_log_device);
    return err;
}

{
    err = mce_create_device (i);
    if (err)
        return err;
}

{
    u32 mci_misc_hi, mci_misc_lo;
    rdmsr (b -> address, mci_misc_lo, mci_misc_hi);
    if (b->threshold_limit < (mci_misc_hi & THRESHOLD_MAX))
        reset = 1;
    if (reset) {
        mci_misc_hi = (mci_misc_hi & ~(MASK_ERR_COUNT_HI | MASK_OVERFLOW_HI)) | (THRESHOLD_MAX - b->threshold_limit);
    }
    else if (old_limit) {
        int new_count = (mci_misc_hi & THRESHOLD_MAX) + (old_limit - b->threshold_limit);
        mci_misc_hi = (mci_misc_hi & ~MASK_ERR_COUNT_HI) | (new_count & THRESHOLD_MAX);
    }
    b->interrupt_enable ? (mci_misc_hi = (mci_misc_hi & ~MASK_INT_TYPE_HI) | INT_TYPE_APIC) : (mci_misc_hi &= ~MASK_INT_TYPE_HI);
    mci_misc_hi |= MASK_COUNT_EN_HI;
    wrmsr (b -> address, mci_misc_lo, mci_misc_hi);
}

{
    mci_misc_hi = (mci_misc_hi & ~(MASK_ERR_COUNT_HI | MASK_OVERFLOW_HI)) | (THRESHOLD_MAX - b->threshold_limit);
}

{
    int new_count = (mci_misc_hi & THRESHOLD_MAX) + (old_limit - b->threshold_limit);
    mci_misc_hi = (mci_misc_hi & ~MASK_ERR_COUNT_HI) | (new_count & THRESHOLD_MAX);
}

{
    unsigned int bank, block;
    unsigned int cpu = smp_processor_id ();
    u8 lvt_off;
    u32 low = 0, high = 0, address = 0;
    for (bank = 0; bank < NR_BANKS; ++bank) {
        for (block = 0; block < NR_BLOCKS; ++block) {
            if (block == 0)
                address = MSR_IA32_MC0_MISC + bank * 4;
            else if (block == 1) {
                address = (low & MASK_BLKPTR_LO) >> 21;
                if (!address)
                    break;
                address += MCG_XBLK_ADDR;
            }
            else
                ++address;
            if (rdmsr_safe (address, &low, &high))
                break;
            if (!(high & MASK_VALID_HI)) {
                if (block)
                    continue;
                else
                    break;
            }
            if (!(high & MASK_CNTP_HI) || (high & MASK_LOCKED_HI))
                continue;
            if (!block)
                per_cpu (bank_map, cpu) |= (1 << bank);
            lvt_off = setup_APIC_eilvt_mce (THRESHOLD_APIC_VECTOR, APIC_EILVT_MSG_FIX, 0);
            high &= ~MASK_LVTOFF_HI;
            high |= lvt_off << 20;
            wrmsr (address, low, high);
            threshold_defaults.address = address;
            threshold_restart_bank (& threshold_defaults, 0, 0);
        }
    }
}

{
    for (block = 0; block < NR_BLOCKS; ++block) {
        if (block == 0)
            address = MSR_IA32_MC0_MISC + bank * 4;
        else if (block == 1) {
            address = (low & MASK_BLKPTR_LO) >> 21;
            if (!address)
                break;
            address += MCG_XBLK_ADDR;
        }
        else
            ++address;
        if (rdmsr_safe (address, &low, &high))
            break;
        if (!(high & MASK_VALID_HI)) {
            if (block)
                continue;
            else
                break;
        }
        if (!(high & MASK_CNTP_HI) || (high & MASK_LOCKED_HI))
            continue;
        if (!block)
            per_cpu (bank_map, cpu) |= (1 << bank);
        lvt_off = setup_APIC_eilvt_mce (THRESHOLD_APIC_VECTOR, APIC_EILVT_MSG_FIX, 0);
        high &= ~MASK_LVTOFF_HI;
        high |= lvt_off << 20;
        wrmsr (address, low, high);
        threshold_defaults.address = address;
        threshold_restart_bank (& threshold_defaults, 0, 0);
    }
}

{
    if (block == 0)
        address = MSR_IA32_MC0_MISC + bank * 4;
    else if (block == 1) {
        address = (low & MASK_BLKPTR_LO) >> 21;
        if (!address)
            break;
        address += MCG_XBLK_ADDR;
    }
    else
        ++address;
    if (rdmsr_safe (address, &low, &high))
        break;
    if (!(high & MASK_VALID_HI)) {
        if (block)
            continue;
        else
            break;
    }
    if (!(high & MASK_CNTP_HI) || (high & MASK_LOCKED_HI))
        continue;
    if (!block)
        per_cpu (bank_map, cpu) |= (1 << bank);
    lvt_off = setup_APIC_eilvt_mce (THRESHOLD_APIC_VECTOR, APIC_EILVT_MSG_FIX, 0);
    high &= ~MASK_LVTOFF_HI;
    high |= lvt_off << 20;
    wrmsr (address, low, high);
    threshold_defaults.address = address;
    threshold_restart_bank (& threshold_defaults, 0, 0);
}

{
    address = (low & MASK_BLKPTR_LO) >> 21;
    if (!address)
        break;
    address += MCG_XBLK_ADDR;
}

{
    if (block)
        continue;
    else
        break;
}

{
    unsigned int bank, block;
    struct mce m;
    u32 low = 0, high = 0, address = 0;
    ack_APIC_irq ();
    exit_idle ();
    irq_enter ();
    memset (& m, 0, sizeof (m));
    rdtscll (m.tsc);
    m.cpu = smp_processor_id ();
    for (bank = 0; bank < NR_BANKS; ++bank) {
        if (!(per_cpu (bank_map, m.cpu) & (1 << bank)))
            continue;
        for (block = 0; block < NR_BLOCKS; ++block) {
            if (block == 0)
                address = MSR_IA32_MC0_MISC + bank * 4;
            else if (block == 1) {
                address = (low & MASK_BLKPTR_LO) >> 21;
                if (!address)
                    break;
                address += MCG_XBLK_ADDR;
            }
            else
                ++address;
            if (rdmsr_safe (address, &low, &high))
                break;
            if (!(high & MASK_VALID_HI)) {
                if (block)
                    continue;
                else
                    break;
            }
            if (!(high & MASK_CNTP_HI) || (high & MASK_LOCKED_HI))
                continue;
            do_machine_check (NULL, 0);
            if (high & MASK_OVERFLOW_HI) {
                rdmsrl (address, m.misc);
                rdmsrl (MSR_IA32_MC0_STATUS + bank * 4, m.status);
                m.bank = K8_MCE_THRESHOLD_BASE + bank * NR_BLOCKS + block;
                mce_log (& m);
                goto out;
            }
        }
    }
out :
    add_pda (irq_threshold_count, 1);
    irq_exit ();
}

{
    if (!(per_cpu (bank_map, m.cpu) & (1 << bank)))
        continue;
    for (block = 0; block < NR_BLOCKS; ++block) {
        if (block == 0)
            address = MSR_IA32_MC0_MISC + bank * 4;
        else if (block == 1) {
            address = (low & MASK_BLKPTR_LO) >> 21;
            if (!address)
                break;
            address += MCG_XBLK_ADDR;
        }
        else
            ++address;
        if (rdmsr_safe (address, &low, &high))
            break;
        if (!(high & MASK_VALID_HI)) {
            if (block)
                continue;
            else
                break;
        }
        if (!(high & MASK_CNTP_HI) || (high & MASK_LOCKED_HI))
            continue;
        do_machine_check (NULL, 0);
        if (high & MASK_OVERFLOW_HI) {
            rdmsrl (address, m.misc);
            rdmsrl (MSR_IA32_MC0_STATUS + bank * 4, m.status);
            m.bank = K8_MCE_THRESHOLD_BASE + bank * NR_BLOCKS + block;
            mce_log (& m);
            goto out;
        }
    }
}

{
    if (block == 0)
        address = MSR_IA32_MC0_MISC + bank * 4;
    else if (block == 1) {
        address = (low & MASK_BLKPTR_LO) >> 21;
        if (!address)
            break;
        address += MCG_XBLK_ADDR;
    }
    else
        ++address;
    if (rdmsr_safe (address, &low, &high))
        break;
    if (!(high & MASK_VALID_HI)) {
        if (block)
            continue;
        else
            break;
    }
    if (!(high & MASK_CNTP_HI) || (high & MASK_LOCKED_HI))
        continue;
    do_machine_check (NULL, 0);
    if (high & MASK_OVERFLOW_HI) {
        rdmsrl (address, m.misc);
        rdmsrl (MSR_IA32_MC0_STATUS + bank * 4, m.status);
        m.bank = K8_MCE_THRESHOLD_BASE + bank * NR_BLOCKS + block;
        mce_log (& m);
        goto out;
    }
}

{
    address = (low & MASK_BLKPTR_LO) >> 21;
    if (!address)
        break;
    address += MCG_XBLK_ADDR;
}

{
    if (block)
        continue;
    else
        break;
}

{
    rdmsrl (address, m.misc);
    rdmsrl (MSR_IA32_MC0_STATUS + bank * 4, m.status);
    m.bank = K8_MCE_THRESHOLD_BASE + bank * NR_BLOCKS + block;
    mce_log (& m);
    goto out;
}

{
    *oldmask = current->cpus_allowed;
    cpus_clear (*newmask);
    cpu_set (cpu, * newmask);
    set_cpus_allowed_ptr (current, newmask);
}

{
    set_cpus_allowed_ptr (current, oldmask);
}

{
    char *end;
    cpumask_t oldmask, newmask;
    unsigned long new = simple_strtoul (buf, &end, 0);
    if (end == buf)
        return -EINVAL;
    b->interrupt_enable = !!new;
    affinity_set (b -> cpu, & oldmask, & newmask);
    threshold_restart_bank (b, 0, 0);
    affinity_restore (& oldmask);
    return end - buf;
}

{
    char *end;
    cpumask_t oldmask, newmask;
    u16 old;
    unsigned long new = simple_strtoul (buf, &end, 0);
    if (end == buf)
        return -EINVAL;
    if (new > THRESHOLD_MAX)
        new = THRESHOLD_MAX;
    if (new < 1)
        new = 1;
    old = b->threshold_limit;
    b->threshold_limit = new;
    affinity_set (b -> cpu, & oldmask, & newmask);
    threshold_restart_bank (b, 0, old);
    affinity_restore (& oldmask);
    return end - buf;
}

{
    u32 high, low;
    cpumask_t oldmask, newmask;
    affinity_set (b -> cpu, & oldmask, & newmask);
    rdmsr (b -> address, low, high);
    affinity_restore (& oldmask);
    return sprintf (buf, "%x\n", (high & 0xFFF) - (THRESHOLD_MAX - b->threshold_limit));
}

{
    cpumask_t oldmask, newmask;
    affinity_set (b -> cpu, & oldmask, & newmask);
    threshold_restart_bank (b, 1, 0);
    affinity_restore (& oldmask);
    return 1;
}

{
    struct threshold_block *b = to_block (kobj);
    struct threshold_attr *a = to_attr (attr);
    ssize_t ret;
    ret = a->show ? a->show (b, buf) : -EIO;
    return ret;
}

{
    struct threshold_block *b = to_block (kobj);
    struct threshold_attr *a = to_attr (attr);
    ssize_t ret;
    ret = a->store ? a->store (b, buf, count) : -EIO;
    return ret;
}

{
    int err;
    u32 low, high;
    struct threshold_block *b = NULL;
    if ((bank >= NR_BANKS) || (block >= NR_BLOCKS))
        return 0;
    if (rdmsr_safe (address, &low, &high))
        return 0;
    if (!(high & MASK_VALID_HI)) {
        if (block)
            goto recurse;
        else
            return 0;
    }
    if (!(high & MASK_CNTP_HI) || (high & MASK_LOCKED_HI))
        goto recurse;
    b = kzalloc (sizeof (struct threshold_block), GFP_KERNEL);
    if (!b)
        return -ENOMEM;
    b->block = block;
    b->bank = bank;
    b->cpu = cpu;
    b->address = address;
    b->interrupt_enable = 0;
    b->threshold_limit = THRESHOLD_MAX;
    INIT_LIST_HEAD (& b -> miscj);
    if (per_cpu (threshold_banks, cpu)[bank]->blocks)
        list_add (&b->miscj, &per_cpu (threshold_banks, cpu)[bank]->blocks->miscj);
    else
        per_cpu (threshold_banks, cpu)[bank]->blocks = b;
    err = kobject_init_and_add (&b->kobj, &threshold_ktype, per_cpu (threshold_banks, cpu)[bank]->kobj, "misc%i", block);
    if (err)
        goto out_free;
recurse :
    if (!block) {
        address = (low & MASK_BLKPTR_LO) >> 21;
        if (!address)
            return 0;
        address += MCG_XBLK_ADDR;
    }
    else
        ++address;
    err = allocate_threshold_blocks (cpu, bank, ++block, address);
    if (err)
        goto out_free;
    if (b)
        kobject_uevent (&b->kobj, KOBJ_ADD);
    return err;
out_free :
    if (b) {
        kobject_put (& b -> kobj);
        kfree (b);
    }
    return err;
}

{
    if (block)
        goto recurse;
    else
        return 0;
}

{
    address = (low & MASK_BLKPTR_LO) >> 21;
    if (!address)
        return 0;
    address += MCG_XBLK_ADDR;
}

{
    kobject_put (& b -> kobj);
    kfree (b);
}

{
    int i, err = 0;
    struct threshold_bank *b = NULL;
    cpumask_t oldmask, newmask;
    char name [32];
    sprintf (name, "threshold_bank%i", bank);
    b = kzalloc (sizeof (struct threshold_bank), GFP_KERNEL);
    if (!b) {
        err = -ENOMEM;
        goto out;
    }
    b->kobj = kobject_create_and_add (name, &per_cpu (device_mce, cpu).kobj);
    if (!b->kobj)
        goto out_free;
    b->cpus = CPU_MASK_ALL;
    per_cpu (threshold_banks, cpu)[bank] = b;
    affinity_set (cpu, & oldmask, & newmask);
    err = allocate_threshold_blocks (cpu, bank, 0, MSR_IA32_MC0_MISC +bank * 4);
    affinity_restore (& oldmask);
    if (err)
        goto out_free;
    for_each_cpu_mask (i, b -> cpus)
    {
        if (i == cpu)
            continue;
        err = sysfs_create_link (&per_cpu (device_mce, i).kobj, b->kobj, name);
        if (err)
            goto out;
        per_cpu (threshold_banks, i)[bank] = b;
    }
    goto out;
out_free :
    per_cpu (threshold_banks, cpu)[bank] = NULL;
    kfree (b);
out :
    return err;
}

{
    err = -ENOMEM;
    goto out;
}

{
    if (i == cpu)
        continue;
    err = sysfs_create_link (&per_cpu (device_mce, i).kobj, b->kobj, name);
    if (err)
        goto out;
    per_cpu (threshold_banks, i)[bank] = b;
}

{
    unsigned int bank;
    int err = 0;
    for (bank = 0; bank < NR_BANKS; ++bank) {
        if (!(per_cpu (bank_map, cpu) & (1 << bank)))
            continue;
        err = threshold_create_bank (cpu, bank);
        if (err)
            goto out;
    }
out :
    return err;
}

{
    if (!(per_cpu (bank_map, cpu) & (1 << bank)))
        continue;
    err = threshold_create_bank (cpu, bank);
    if (err)
        goto out;
}

{
    struct threshold_block *pos = NULL;
    struct threshold_block *tmp = NULL;
    struct threshold_bank *head = per_cpu (threshold_banks, cpu)[bank];
    if (!head)
        return;
    list_for_each_entry_safe (pos, tmp, &head->blocks->miscj, miscj) {
        kobject_put (& pos -> kobj);
        list_del (& pos -> miscj);
        kfree (pos);
    }
    kfree (per_cpu (threshold_banks, cpu) [bank] -> blocks);
    per_cpu (threshold_banks, cpu)[bank]->blocks = NULL;
}

{
    kobject_put (& pos -> kobj);
    list_del (& pos -> miscj);
    kfree (pos);
}

{
    int i = 0;
    struct threshold_bank *b;
    char name [32];
    b = per_cpu (threshold_banks, cpu)[bank];
    if (!b)
        return;
    if (!b->blocks)
        goto free_out;
    sprintf (name, "threshold_bank%i", bank);
    for_each_cpu_mask (i, b -> cpus)
    {
        if (i == cpu)
            continue;
        sysfs_remove_link (& per_cpu (device_mce, i).kobj, name);
        per_cpu (threshold_banks, i)[bank] = NULL;
    }
    deallocate_threshold_block (cpu, bank);
free_out :
    kobject_put (b->kobj);
    kfree (b);
    per_cpu (threshold_banks, cpu)[bank] = NULL;
}

{
    if (i == cpu)
        continue;
    sysfs_remove_link (& per_cpu (device_mce, i).kobj, name);
    per_cpu (threshold_banks, i)[bank] = NULL;
}

{
    unsigned int bank;
    for (bank = 0; bank < NR_BANKS; ++bank) {
        if (!(per_cpu (bank_map, cpu) & (1 << bank)))
            continue;
        threshold_remove_bank (cpu, bank);
    }
}

{
    if (!(per_cpu (bank_map, cpu) & (1 << bank)))
        continue;
    threshold_remove_bank (cpu, bank);
}

{
    unsigned int cpu = (unsigned long) hcpu;
    if (cpu >= NR_CPUS)
        goto out;
    switch (action) {
    case CPU_ONLINE :
    case CPU_ONLINE_FROZEN :
        threshold_create_device (cpu);
        break;
    case CPU_DEAD :
    case CPU_DEAD_FROZEN :
        threshold_remove_device (cpu);
        break;
    default :
        break;
    }
out :
    return NOTIFY_OK;
}

{
case CPU_ONLINE :
case CPU_ONLINE_FROZEN :
    threshold_create_device (cpu);
    break;
case CPU_DEAD :
case CPU_DEAD_FROZEN :
    threshold_remove_device (cpu);
    break;
default :
    break;
}

{
    unsigned lcpu = 0;

    for_each_online_cpu (lcpu) {
        int err = threshold_create_device (lcpu);
        if (err)
            return err;
    }

    register_hotcpu_notifier (& threshold_cpu_notifier);
    return 0;
}

{
    int err = threshold_create_device (lcpu);
    if (err)
        return err;
}

{
    __u64 msr_val;
    ack_APIC_irq ();
    exit_idle ();
    irq_enter ();
    rdmsrl (MSR_IA32_THERM_STATUS, msr_val);
    if (therm_throt_process (msr_val &1))
        mce_log_therm_throt_event (smp_processor_id (), msr_val);
    add_pda (irq_thermal_count, 1);
    irq_exit ();
}

{
    u32 l, h;
    int tm2 = 0;
    unsigned int cpu = smp_processor_id ();
    if (!cpu_has (c, X86_FEATURE_ACPI))
        return;
    if (!cpu_has (c, X86_FEATURE_ACC))
        return;
    rdmsr (MSR_IA32_MISC_ENABLE, l, h);
    h = apic_read (APIC_LVTTHMR);
    if ((l & (1 << 3)) && (h & APIC_DM_SMI)) {
        printk (KERN_DEBUG "CPU%d: Thermal monitoring handled by SMI\n", cpu);
        return;
    }
    if (cpu_has (c, X86_FEATURE_TM2) && (l & (1 << 13)))
        tm2 = 1;
    if (h & APIC_VECTOR_MASK) {
        printk (KERN_DEBUG "CPU%d: Thermal LVT vector (%#x) already " "installed\n", cpu, (h & APIC_VECTOR_MASK));
        return;
    }
    h = THERMAL_APIC_VECTOR;
    h |= (APIC_DM_FIXED | APIC_LVT_MASKED);
    apic_write (APIC_LVTTHMR, h);
    rdmsr (MSR_IA32_THERM_INTERRUPT, l, h);
    wrmsr (MSR_IA32_THERM_INTERRUPT, l | 0x03, h);
    rdmsr (MSR_IA32_MISC_ENABLE, l, h);
    wrmsr (MSR_IA32_MISC_ENABLE, l | (1 << 3), h);
    l = apic_read (APIC_LVTTHMR);
    apic_write (APIC_LVTTHMR, l & ~ APIC_LVT_MASKED);
    printk (KERN_INFO "CPU%d: Thermal monitoring enabled (%s)\n", cpu, tm2 ? "TM2" : "TM1");
    atomic_set (& therm_throt_en, 1);
    return;
}

{
    printk (KERN_DEBUG "CPU%d: Thermal monitoring handled by SMI\n", cpu);
    return;
}

{
    printk (KERN_DEBUG "CPU%d: Thermal LVT vector (%#x) already " "installed\n", cpu, (h & APIC_VECTOR_MASK));
    return;
}

{
    intel_init_thermal (c);
}

{
    unsigned int cpu = smp_processor_id ();
    __u64 tmp_jiffs = get_jiffies_64 ();
    if (curr)
        __get_cpu_var (thermal_throttle_count)++;
    if (time_before64 (tmp_jiffs, __get_cpu_var (next_check)))
        return 0;
    __get_cpu_var (next_check) = tmp_jiffs + CHECK_INTERVAL;
    if (curr) {
        printk (KERN_CRIT "CPU%d: Temperature above threshold, " "cpu clock throttled (total events = %lu)\n", cpu, __get_cpu_var (thermal_throttle_count));
        add_taint (TAINT_MACHINE_CHECK);
    }
    else {
        printk (KERN_CRIT "CPU%d: Temperature/speed normal\n", cpu);
    }
    return 1;
}

{
    printk (KERN_CRIT "CPU%d: Temperature above threshold, " "cpu clock throttled (total events = %lu)\n", cpu, __get_cpu_var (thermal_throttle_count));
    add_taint (TAINT_MACHINE_CHECK);
}

{
    printk (KERN_CRIT "CPU%d: Temperature/speed normal\n", cpu);
}

{
    seq_printf (m, "fpu\t\t: yes\n" "fpu_exception\t: yes\n" "cpuid level\t: %d\n" "wp\t\t: yes\n", c -> cpuid_level);
}

{
    struct cpuinfo_x86 *c = v;
    unsigned int cpu = 0;
    int i;
    seq_printf (m, "processor\t: %u\n" "vendor_id\t: %s\n" "cpu family\t: %d\n" "model\t\t: %u\n" "model name\t: %s\n", cpu, c -> x86_vendor_id [0] ? c -> x86_vendor_id : "unknown", c -> x86, c -> x86_model, c -> x86_model_id [0] ? c -> x86_model_id : "unknown");
    if (c->x86_mask || c->cpuid_level >= 0)
        seq_printf (m, "stepping\t: %d\n", c->x86_mask);
    else
        seq_printf (m, "stepping\t: unknown\n");
    if (cpu_has (c, X86_FEATURE_TSC)) {
        unsigned int freq = cpufreq_quick_get (cpu);
        if (!freq)
            freq = cpu_khz;
        seq_printf (m, "cpu MHz\t\t: %u.%03u\n", freq / 1000, (freq % 1000));
    }
    if (c->x86_cache_size >= 0)
        seq_printf (m, "cache size\t: %d KB\n", c->x86_cache_size);
    show_cpuinfo_core (m, c, cpu);
    show_cpuinfo_misc (m, c);
    seq_printf (m, "flags\t\t:");
    for (i = 0; i < 32 * NCAPINTS; i++)
        if (cpu_has (c, i) && x86_cap_flags[i] != NULL)
            seq_printf (m, " %s", x86_cap_flags[i]);
    seq_printf (m, "\nbogomips\t: %lu.%02lu\n", c -> loops_per_jiffy / (500000 / HZ), (c -> loops_per_jiffy / (5000 / HZ)) % 100);
    seq_printf (m, "clflush size\t: %u\n", c -> x86_clflush_size);
    seq_printf (m, "power management:");
    for (i = 0; i < 32; i++) {
        if (c->x86_power & (1 << i)) {
            if (i < ARRAY_SIZE (x86_power_flags) && x86_power_flags[i])
                seq_printf (m, "%s%s", x86_power_flags[i][0] ? " " : "", x86_power_flags[i]);
            else
                seq_printf (m, " [%d]", i);
        }
    }
    seq_printf (m, "\n\n");
    return 0;
}

{
    unsigned int freq = cpufreq_quick_get (cpu);
    if (!freq)
        freq = cpu_khz;
    seq_printf (m, "cpu MHz\t\t: %u.%03u\n", freq / 1000, (freq % 1000));
}

{
    if (c->x86_power & (1 << i)) {
        if (i < ARRAY_SIZE (x86_power_flags) && x86_power_flags[i])
            seq_printf (m, "%s%s", x86_power_flags[i][0] ? " " : "", x86_power_flags[i]);
        else
            seq_printf (m, " [%d]", i);
    }
}

{
    if (i < ARRAY_SIZE (x86_power_flags) && x86_power_flags[i])
        seq_printf (m, "%s%s", x86_power_flags[i][0] ? " " : "", x86_power_flags[i]);
    else
        seq_printf (m, " [%d]", i);
}

{
    if (*pos == 0)
        *pos = first_cpu (cpu_online_map);
    if ((*pos) < NR_CPUS && cpu_online (*pos))
        return &cpu_data (*pos);
    return NULL;
}

{
    *pos = next_cpu (*pos, cpu_online_map);
    return c_start (m, pos);
}

{
    return register_cpu (&per_cpu (cpu_devices, num).cpu, num);
}

{
    int i;
    for_each_present_cpu (i)
    arch_register_cpu (i);
    return 0;
}

{
    unsigned long pc = instruction_pointer (regs);
    if (!user_mode (regs) && in_lock_functions (pc)) {
        unsigned long *sp = (unsigned long *) regs->sp;
        if (sp[0] >> 22)
            return sp[0];
        if (sp[1] >> 22)
            return sp[1];
    }
    return pc;
}

{
    unsigned long *sp = (unsigned long *) regs->sp;
    if (sp[0] >> 22)
        return sp[0];
    if (sp[1] >> 22)
        return sp[1];
}

{
    add_pda (irq0_irqs, 1);
    global_clock_event->event_handler (global_clock_event);
    return IRQ_HANDLED;
}

{
    int tsc_start, tsc_now;
    int i, no_ctr_free;
    unsigned long evntsel3 = 0, pmc3 = 0, pmc_now = 0;
    unsigned long flags;
    for (i = 0; i < 4; i++)
        if (avail_to_resrv_perfctr_nmi_bit (i))
            break;
    no_ctr_free = (i == 4);
    if (no_ctr_free) {
        i = 3;
        rdmsrl (MSR_K7_EVNTSEL3, evntsel3);
        wrmsrl (MSR_K7_EVNTSEL3, 0);
        rdmsrl (MSR_K7_PERFCTR3, pmc3);
    }
    else {
        reserve_perfctr_nmi (MSR_K7_PERFCTR0 + i);
        reserve_evntsel_nmi (MSR_K7_EVNTSEL0 + i);
    }
    local_irq_save (flags);
    wrmsrl (MSR_K7_PERFCTR0 + i, 0);
    wrmsrl (MSR_K7_EVNTSEL0 + i, 1 << 22 | 3 << 16 | 0x76);
    rdtscl (tsc_start);
    do {
        rdmsrl (MSR_K7_PERFCTR0 + i, pmc_now);
        tsc_now = get_cycles ();
    }
    while ((tsc_now - tsc_start) < TICK_COUNT);
    local_irq_restore (flags);
    if (no_ctr_free) {
        wrmsrl (MSR_K7_EVNTSEL3, 0);
        wrmsrl (MSR_K7_PERFCTR3, pmc3);
        wrmsrl (MSR_K7_EVNTSEL3, evntsel3);
    }
    else {
        release_perfctr_nmi (MSR_K7_PERFCTR0 + i);
        release_evntsel_nmi (MSR_K7_EVNTSEL0 + i);
    }
    return pmc_now * tsc_khz / (tsc_now - tsc_start);
}

{
    i = 3;
    rdmsrl (MSR_K7_EVNTSEL3, evntsel3);
    wrmsrl (MSR_K7_EVNTSEL3, 0);
    rdmsrl (MSR_K7_PERFCTR3, pmc3);
}

{
    reserve_perfctr_nmi (MSR_K7_PERFCTR0 + i);
    reserve_evntsel_nmi (MSR_K7_EVNTSEL0 + i);
}

{
    rdmsrl (MSR_K7_PERFCTR0 + i, pmc_now);
    tsc_now = get_cycles ();
}

{
    wrmsrl (MSR_K7_EVNTSEL3, 0);
    wrmsrl (MSR_K7_PERFCTR3, pmc3);
    wrmsrl (MSR_K7_EVNTSEL3, evntsel3);
}

{
    release_perfctr_nmi (MSR_K7_PERFCTR0 + i);
    release_evntsel_nmi (MSR_K7_EVNTSEL0 + i);
}

{
    if (!hpet_enable ())
        setup_pit_timer ();
    setup_irq (0, & irq0);
}

{
    tsc_calibrate ();
    cpu_khz = tsc_khz;
    if (cpu_has (&boot_cpu_data, X86_FEATURE_CONSTANT_TSC) && (boot_cpu_data.x86_vendor == X86_VENDOR_AMD))
        cpu_khz = calculate_cpu_khz ();
    if (unsynchronized_tsc ())
        mark_tsc_unstable ("TSCs unsynchronized");
    if (cpu_has (&boot_cpu_data, X86_FEATURE_RDTSCP))
        vgetcpu_mode = VGETCPU_RDTSCP;
    else
        vgetcpu_mode = VGETCPU_LSL;
    printk (KERN_INFO "time.c: Detected %d.%03d MHz processor.\n", cpu_khz / 1000, cpu_khz % 1000);
    init_tsc_clocksource ();
    late_time_init = choose_time_init ();
}

{
    do {
        dev = pci_get_device (PCI_ANY_ID, PCI_ANY_ID, dev);
        if (!dev)
            break;
    }
    while (!pci_match_id (&k8_nb_ids[0], dev));
    return dev;
}

{
    dev = pci_get_device (PCI_ANY_ID, PCI_ANY_ID, dev);
    if (!dev)
        break;
}

{
    int i;
    struct pci_dev *dev;
    if (num_k8_northbridges)
        return 0;
    dev = NULL;
    while ((dev = next_k8_northbridge (dev)) != NULL)
        num_k8_northbridges++;
    k8_northbridges = kmalloc ((num_k8_northbridges + 1) * sizeof (void *), GFP_KERNEL);
    if (!k8_northbridges)
        return -ENOMEM;
    if (!num_k8_northbridges) {
        k8_northbridges[0] = NULL;
        return 0;
    }
    flush_words = kmalloc (num_k8_northbridges * sizeof (u32), GFP_KERNEL);
    if (!flush_words) {
        kfree (k8_northbridges);
        return -ENOMEM;
    }
    dev = NULL;
    i = 0;
    while ((dev = next_k8_northbridge (dev)) != NULL) {
        k8_northbridges[i] = dev;
        pci_read_config_dword (dev, 0x9c, & flush_words [i ++]);
    }
    k8_northbridges[i] = NULL;
    return 0;
}

{
    k8_northbridges[0] = NULL;
    return 0;
}

{
    kfree (k8_northbridges);
    return -ENOMEM;
}

{
    k8_northbridges[i] = dev;
    pci_read_config_dword (dev, 0x9c, & flush_words [i ++]);
}

{
    struct pci_device_id *id;
    u32 vendor = device & 0xffff;
    device >>= 16;
    for (id = k8_nb_ids; id->vendor; id++)
        if (vendor == id->vendor && device == id->device)
            return 1;
    return 0;
}

{
    int flushed, i;
    unsigned long flags;
    static DEFINE_SPINLOCK (gart_lock);
    spin_lock_irqsave (& gart_lock, flags);
    flushed = 0;
    for (i = 0; i < num_k8_northbridges; i++) {
        pci_write_config_dword (k8_northbridges [i], 0x9c, flush_words [i] | 1);
        flushed++;
    }
    for (i = 0; i < num_k8_northbridges; i++) {
        u32 w;
        for (;;) {
            pci_read_config_dword (k8_northbridges [i], 0x9c, & w);
            if (!(w & 1))
                break;
            cpu_relax ();
        }
    }
    spin_unlock_irqrestore (& gart_lock, flags);
    if (!flushed)
        printk ("nothing to flush?\n");
}

{
    pci_write_config_dword (k8_northbridges [i], 0x9c, flush_words [i] | 1);
    flushed++;
}

{
    u32 w;
    for (;;) {
        pci_read_config_dword (k8_northbridges [i], 0x9c, & w);
        if (!(w & 1))
            break;
        cpu_relax ();
    }
}

{
    pci_read_config_dword (k8_northbridges [i], 0x9c, & w);
    if (!(w & 1))
        break;
    cpu_relax ();
}

{
    *dst = *src;
    if (src->thread.xstate) {
        dst->thread.xstate = kmem_cache_alloc (task_xstate_cachep, GFP_KERNEL);
        if (!dst->thread.xstate)
            return -ENOMEM;
        WARN_ON ((unsigned long) dst -> thread.xstate & 15);
        memcpy (dst -> thread.xstate, src -> thread.xstate, xstate_size);
    }
    return 0;
}

{
    dst->thread.xstate = kmem_cache_alloc (task_xstate_cachep, GFP_KERNEL);
    if (!dst->thread.xstate)
        return -ENOMEM;
    WARN_ON ((unsigned long) dst -> thread.xstate & 15);
    memcpy (dst -> thread.xstate, src -> thread.xstate, xstate_size);
}

{
    if (tsk->thread.xstate) {
        kmem_cache_free (task_xstate_cachep, tsk -> thread.xstate);
        tsk->thread.xstate = NULL;
    }
}

{
    kmem_cache_free (task_xstate_cachep, tsk -> thread.xstate);
    tsk->thread.xstate = NULL;
}

{
    free_thread_xstate (ti -> task);
    free_pages ((unsigned long) ti, get_order (THREAD_SIZE));
}

{
    task_xstate_cachep = kmem_cache_create ("task_xstate", xstate_size, __alignof__ (union thread_xstate), SLAB_PANIC, NULL);
}

{
    smp_mb ();
    smp_call_function (do_nothing, NULL, 0, 1);
}

{
    if (!need_resched ()) {
        __monitor ((void *) & current_thread_info () -> flags, 0, 0);
        smp_mb ();
        if (!need_resched ())
            __mwait (ax, cx);
    }
}

{
    __monitor ((void *) & current_thread_info () -> flags, 0, 0);
    smp_mb ();
    if (!need_resched ())
        __mwait (ax, cx);
}

{
    if (!need_resched ()) {
        __monitor ((void *) & current_thread_info () -> flags, 0, 0);
        smp_mb ();
        if (!need_resched ())
            __sti_mwait (0, 0);
        else
            local_irq_enable ();
    }
    else
        local_irq_enable ();
}

{
    __monitor ((void *) & current_thread_info () -> flags, 0, 0);
    smp_mb ();
    if (!need_resched ())
        __sti_mwait (0, 0);
    else
        local_irq_enable ();
}

{
    local_irq_enable ();
    cpu_relax ();
}

{
    if (force_mwait)
        return 1;
    if (c->x86_vendor == X86_VENDOR_AMD) {
        switch (c->x86) {
        case 0x10 :
        case 0x11 :
            return 0;
        }
    }
    return 1;
}

{
    switch (c->x86) {
    case 0x10 :
    case 0x11 :
        return 0;
    }
}

{
case 0x10 :
case 0x11 :
    return 0;
}

{
    static int selected;
    if (selected)
        return;
    if (cpu_has (c, X86_FEATURE_MWAIT) && mwait_usable (c)) {
        if (!pm_idle) {
            printk (KERN_INFO "using mwait in idle threads.\n");
            pm_idle = mwait_idle;
        }
    }
    selected = 1;
}

{
    if (!pm_idle) {
        printk (KERN_INFO "using mwait in idle threads.\n");
        pm_idle = mwait_idle;
    }
}

{
    printk (KERN_INFO "using mwait in idle threads.\n");
    pm_idle = mwait_idle;
}

{
    if (!strcmp (str, "poll")) {
        printk ("using polling idle threads.\n");
        pm_idle = poll_idle;
    }
    else if (!strcmp (str, "mwait"))
        force_mwait = 1;
    else
        return -1;
    boot_option_idle_override = 1;
    return 0;
}

{
    printk ("using polling idle threads.\n");
    pm_idle = poll_idle;
}

{
    int error = 0;
    return error;
}

{
    struct __arch_jmp_op {
        char op;
        s32 raddr;
    } __attribute__ ((packed)) *jop;
    jop = (struct __arch_jmp_op *) from;
    jop->raddr = (s32) ((long) (to) -((long) (from) +5));
    jop->op = RELATIVEJUMP_INSTRUCTION;
}

{
    return 0;
}

{
    kprobe_opcode_t opcode;
    kprobe_opcode_t *orig_opcodes = opcodes;
retry :
    if (opcodes - orig_opcodes > MAX_INSN_SIZE - 1)
        return 0;
    opcode = *(opcodes++);
    if (opcode == 0x0f) {
        if (opcodes - orig_opcodes > MAX_INSN_SIZE - 1)
            return 0;
        return test_bit (*opcodes, (unsigned long *) twobyte_is_boostable);
    }
    switch (opcode & 0xf0) {
    case 0x60 :
        if (0x63 < opcode && opcode < 0x67)
            goto retry;
        return (opcode != 0x62 && opcode != 0x67);
    case 0x70 :
        return 0;
    case 0xc0 :
        return (0xc1 < opcode && opcode < 0xcc) || opcode == 0xcf;
    case 0xd0 :
        return (opcode == 0xd4 || opcode == 0xd5 || opcode == 0xd7);
    case 0xe0 :
        return ((opcode & 0x04) || opcode == 0xea);
    case 0xf0 :
        if ((opcode & 0x0c) == 0 && opcode != 0xf1)
            goto retry;
        return (opcode == 0xf5 || (0xf7 < opcode && opcode < 0xfe));
    default :
        if (opcode == 0x26 || opcode == 0x36 || opcode == 0x3e)
            goto retry;
        return (opcode != 0x2e && opcode != 0x9a);
    }
}

{
    if (opcodes - orig_opcodes > MAX_INSN_SIZE - 1)
        return 0;
    return test_bit (*opcodes, (unsigned long *) twobyte_is_boostable);
}

{
case 0x60 :
    if (0x63 < opcode && opcode < 0x67)
        goto retry;
    return (opcode != 0x62 && opcode != 0x67);
case 0x70 :
    return 0;
case 0xc0 :
    return (0xc1 < opcode && opcode < 0xcc) || opcode == 0xcf;
case 0xd0 :
    return (opcode == 0xd4 || opcode == 0xd5 || opcode == 0xd7);
case 0xe0 :
    return ((opcode & 0x04) || opcode == 0xea);
case 0xf0 :
    if ((opcode & 0x0c) == 0 && opcode != 0xf1)
        goto retry;
    return (opcode == 0xf5 || (0xf7 < opcode && opcode < 0xfe));
default :
    if (opcode == 0x26 || opcode == 0x36 || opcode == 0x3e)
        goto retry;
    return (opcode != 0x2e && opcode != 0x9a);
}

{
    switch (*insn) {
    case 0xfa :
    case 0xfb :
    case 0xcf :
    case 0x9d :
        return 1;
    }
    if (is_REX_prefix (insn))
        return is_IF_modifier (++insn);
    return 0;
}

{
case 0xfa :
case 0xfb :
case 0xcf :
case 0x9d :
    return 1;
}

{
    memcpy (p -> ainsn.insn, p -> addr, MAX_INSN_SIZE * sizeof (kprobe_opcode_t));
    fix_riprel (p);
    if (can_boost (p->addr))
        p->ainsn.boostable = 0;
    else
        p->ainsn.boostable = -1;
    p->opcode = *p->addr;
}

{
    p->ainsn.insn = get_insn_slot ();
    if (!p->ainsn.insn)
        return -ENOMEM;
    arch_copy_kprobe (p);
    return 0;
}

{
    text_poke (p -> addr, ((unsigned char []) {BREAKPOINT_INSTRUCTION}), 1);
}

{
    text_poke (p -> addr, & p -> opcode, 1);
}

{
    mutex_lock (& kprobe_mutex);
    free_insn_slot (p -> ainsn.insn, (p -> ainsn.boostable == 1));
    mutex_unlock (& kprobe_mutex);
}

{
    kcb->prev_kprobe.kp = kprobe_running ();
    kcb->prev_kprobe.status = kcb->kprobe_status;
    kcb->prev_kprobe.old_flags = kcb->kprobe_old_flags;
    kcb->prev_kprobe.saved_flags = kcb->kprobe_saved_flags;
}

{
    __get_cpu_var (current_kprobe) = kcb->prev_kprobe.kp;
    kcb->kprobe_status = kcb->prev_kprobe.status;
    kcb->kprobe_old_flags = kcb->prev_kprobe.old_flags;
    kcb->kprobe_saved_flags = kcb->prev_kprobe.saved_flags;
}

{
    __get_cpu_var (current_kprobe) = p;
    kcb->kprobe_saved_flags = kcb->kprobe_old_flags = (regs->flags & (X86_EFLAGS_TF | X86_EFLAGS_IF));
    if (is_IF_modifier (p->ainsn.insn))
        kcb->kprobe_saved_flags &= ~X86_EFLAGS_IF;
}

{
    if (test_thread_flag (TIF_DEBUGCTLMSR))
        update_debugctlmsr (0);
}

{
    if (test_thread_flag (TIF_DEBUGCTLMSR))
        update_debugctlmsr (current->thread.debugctlmsr);
}

{
    clear_btf ();
    regs->flags |= X86_EFLAGS_TF;
    regs->flags &= ~X86_EFLAGS_IF;
    if (p->opcode == BREAKPOINT_INSTRUCTION)
        regs->ip = (unsigned long) p->addr;
    else
        regs->ip = (unsigned long) p->ainsn.insn;
}

{
    unsigned long *sara = stack_addr (regs);
    ri->ret_addr = (kprobe_opcode_t *) *sara;
    *sara = (unsigned long) &kretprobe_trampoline;
}

{
    prepare_singlestep (p, regs);
    kcb->kprobe_status = KPROBE_HIT_SS;
}

{
    switch (kcb->kprobe_status) {
    case KPROBE_HIT_SSDONE :
    case KPROBE_HIT_ACTIVE :
        save_previous_kprobe (kcb);
        set_current_kprobe (p, regs, kcb);
        kprobes_inc_nmissed_count (p);
        prepare_singlestep (p, regs);
        kcb->kprobe_status = KPROBE_REENTER;
        break;
    case KPROBE_HIT_SS :
        if (p == kprobe_running ()) {
            regs->flags &= ~X86_EFLAGS_TF;
            regs->flags |= kcb->kprobe_saved_flags;
            return 0;
        }
        else {
        }
    default :
        WARN_ON (1);
        return 0;
    }
    return 1;
}

{
case KPROBE_HIT_SSDONE :
case KPROBE_HIT_ACTIVE :
    save_previous_kprobe (kcb);
    set_current_kprobe (p, regs, kcb);
    kprobes_inc_nmissed_count (p);
    prepare_singlestep (p, regs);
    kcb->kprobe_status = KPROBE_REENTER;
    break;
case KPROBE_HIT_SS :
    if (p == kprobe_running ()) {
        regs->flags &= ~X86_EFLAGS_TF;
        regs->flags |= kcb->kprobe_saved_flags;
        return 0;
    }
    else {
    }
default :
    WARN_ON (1);
    return 0;
}

{
    regs->flags &= ~X86_EFLAGS_TF;
    regs->flags |= kcb->kprobe_saved_flags;
    return 0;
}

{
    kprobe_opcode_t *addr;
    struct kprobe *p;
    struct kprobe_ctlblk *kcb;
    addr = (kprobe_opcode_t *) (regs->ip - sizeof (kprobe_opcode_t));
    if (*addr != BREAKPOINT_INSTRUCTION) {
        regs->ip = (unsigned long) addr;
        return 1;
    }
    preempt_disable ();
    kcb = get_kprobe_ctlblk ();
    p = get_kprobe (addr);
    if (p) {
        if (kprobe_running ()) {
            if (reenter_kprobe (p, regs, kcb))
                return 1;
        }
        else {
            set_current_kprobe (p, regs, kcb);
            kcb->kprobe_status = KPROBE_HIT_ACTIVE;
            if (!p->pre_handler || !p->pre_handler (p, regs))
                setup_singlestep (p, regs, kcb);
            return 1;
        }
    }
    else if (kprobe_running ()) {
        p = __get_cpu_var (current_kprobe);
        if (p->break_handler && p->break_handler (p, regs)) {
            setup_singlestep (p, regs, kcb);
            return 1;
        }
    }
    preempt_enable_no_resched ();
    return 0;
}

{
    regs->ip = (unsigned long) addr;
    return 1;
}

{
    if (kprobe_running ()) {
        if (reenter_kprobe (p, regs, kcb))
            return 1;
    }
    else {
        set_current_kprobe (p, regs, kcb);
        kcb->kprobe_status = KPROBE_HIT_ACTIVE;
        if (!p->pre_handler || !p->pre_handler (p, regs))
            setup_singlestep (p, regs, kcb);
        return 1;
    }
}

{
    if (reenter_kprobe (p, regs, kcb))
        return 1;
}

{
    set_current_kprobe (p, regs, kcb);
    kcb->kprobe_status = KPROBE_HIT_ACTIVE;
    if (!p->pre_handler || !p->pre_handler (p, regs))
        setup_singlestep (p, regs, kcb);
    return 1;
}

{
    p = __get_cpu_var (current_kprobe);
    if (p->break_handler && p->break_handler (p, regs)) {
        setup_singlestep (p, regs, kcb);
        return 1;
    }
}

{
    setup_singlestep (p, regs, kcb);
    return 1;
}

{
    struct kretprobe_instance *ri = NULL;
    struct hlist_head *head, empty_rp;
    struct hlist_node *node, *tmp;
    unsigned long flags, orig_ret_address = 0;
    unsigned long trampoline_address = (unsigned long) &kretprobe_trampoline;
    INIT_HLIST_HEAD (& empty_rp);
    spin_lock_irqsave (& kretprobe_lock, flags);
    head = kretprobe_inst_table_head (current);
    regs->cs = __KERNEL_CS | get_kernel_rpl ();
    regs->ip = trampoline_address;
    regs->orig_ax = ~0UL;
    hlist_for_each_entry_safe (ri, node, tmp, head, hlist) {
        if (ri->task != current)
            continue;
        if (ri->rp && ri->rp->handler) {
            __get_cpu_var (current_kprobe) = &ri->rp->kp;
            get_kprobe_ctlblk ()->kprobe_status = KPROBE_HIT_ACTIVE;
            ri->rp->handler (ri, regs);
            __get_cpu_var (current_kprobe) = NULL;
        }
        orig_ret_address = (unsigned long) ri->ret_addr;
        recycle_rp_inst (ri, & empty_rp);
        if (orig_ret_address != trampoline_address)
            break;
    }
    kretprobe_assert (ri, orig_ret_address, trampoline_address);
    spin_unlock_irqrestore (& kretprobe_lock, flags);
    hlist_for_each_entry_safe (ri, node, tmp, &empty_rp, hlist) {
        hlist_del (& ri -> hlist);
        kfree (ri);
    }
    return (void *) orig_ret_address;
}

{
    if (ri->task != current)
        continue;
    if (ri->rp && ri->rp->handler) {
        __get_cpu_var (current_kprobe) = &ri->rp->kp;
        get_kprobe_ctlblk ()->kprobe_status = KPROBE_HIT_ACTIVE;
        ri->rp->handler (ri, regs);
        __get_cpu_var (current_kprobe) = NULL;
    }
    orig_ret_address = (unsigned long) ri->ret_addr;
    recycle_rp_inst (ri, & empty_rp);
    if (orig_ret_address != trampoline_address)
        break;
}

{
    __get_cpu_var (current_kprobe) = &ri->rp->kp;
    get_kprobe_ctlblk ()->kprobe_status = KPROBE_HIT_ACTIVE;
    ri->rp->handler (ri, regs);
    __get_cpu_var (current_kprobe) = NULL;
}

{
    hlist_del (& ri -> hlist);
    kfree (ri);
}

{
    unsigned long *tos = stack_addr (regs);
    unsigned long copy_ip = (unsigned long) p->ainsn.insn;
    unsigned long orig_ip = (unsigned long) p->addr;
    kprobe_opcode_t *insn = p->ainsn.insn;
    if (is_REX_prefix (insn))
        insn++;
    regs->flags &= ~X86_EFLAGS_TF;
    switch (*insn) {
    case 0x9c :
        *tos &= ~(X86_EFLAGS_TF | X86_EFLAGS_IF);
        *tos |= kcb->kprobe_old_flags;
        break;
    case 0xc2 :
    case 0xc3 :
    case 0xca :
    case 0xcb :
    case 0xcf :
    case 0xea :
        p->ainsn.boostable = 1;
        goto no_change;
    case 0xe8 :
        *tos = orig_ip + (*tos - copy_ip);
        break;
    case 0xff :
        if ((insn[1] & 0x30) == 0x10) {
            *tos = orig_ip + (*tos - copy_ip);
            goto no_change;
        }
        else if (((insn[1] & 0x31) == 0x20) || ((insn[1] & 0x31) == 0x21)) {
            p->ainsn.boostable = 1;
            goto no_change;
        }
    default :
        break;
    }
    if (p->ainsn.boostable == 0) {
        if ((regs->ip > copy_ip) && (regs->ip - copy_ip) + 5 < MAX_INSN_SIZE) {
            set_jmp_op ((void *) regs -> ip, (void *) orig_ip + (regs -> ip - copy_ip));
            p->ainsn.boostable = 1;
        }
        else {
            p->ainsn.boostable = -1;
        }
    }
    regs->ip += orig_ip - copy_ip;
no_change :
    restore_btf ();
}

{
case 0x9c :
    *tos &= ~(X86_EFLAGS_TF | X86_EFLAGS_IF);
    *tos |= kcb->kprobe_old_flags;
    break;
case 0xc2 :
case 0xc3 :
case 0xca :
case 0xcb :
case 0xcf :
case 0xea :
    p->ainsn.boostable = 1;
    goto no_change;
case 0xe8 :
    *tos = orig_ip + (*tos - copy_ip);
    break;
case 0xff :
    if ((insn[1] & 0x30) == 0x10) {
        *tos = orig_ip + (*tos - copy_ip);
        goto no_change;
    }
    else if (((insn[1] & 0x31) == 0x20) || ((insn[1] & 0x31) == 0x21)) {
        p->ainsn.boostable = 1;
        goto no_change;
    }
default :
    break;
}

{
    *tos = orig_ip + (*tos - copy_ip);
    goto no_change;
}

{
    p->ainsn.boostable = 1;
    goto no_change;
}

{
    if ((regs->ip > copy_ip) && (regs->ip - copy_ip) + 5 < MAX_INSN_SIZE) {
        set_jmp_op ((void *) regs -> ip, (void *) orig_ip + (regs -> ip - copy_ip));
        p->ainsn.boostable = 1;
    }
    else {
        p->ainsn.boostable = -1;
    }
}

{
    set_jmp_op ((void *) regs -> ip, (void *) orig_ip + (regs -> ip - copy_ip));
    p->ainsn.boostable = 1;
}

{
    p->ainsn.boostable = -1;
}

{
    struct kprobe *cur = kprobe_running ();
    struct kprobe_ctlblk *kcb = get_kprobe_ctlblk ();
    if (!cur)
        return 0;
    resume_execution (cur, regs, kcb);
    regs->flags |= kcb->kprobe_saved_flags;
    trace_hardirqs_fixup_flags (regs -> flags);
    if ((kcb->kprobe_status != KPROBE_REENTER) && cur->post_handler) {
        kcb->kprobe_status = KPROBE_HIT_SSDONE;
        cur->post_handler (cur, regs, 0);
    }
    if (kcb->kprobe_status == KPROBE_REENTER) {
        restore_previous_kprobe (kcb);
        goto out;
    }
    reset_current_kprobe ();
out :
    preempt_enable_no_resched ();
    if (regs->flags & X86_EFLAGS_TF)
        return 0;
    return 1;
}

{
    kcb->kprobe_status = KPROBE_HIT_SSDONE;
    cur->post_handler (cur, regs, 0);
}

{
    restore_previous_kprobe (kcb);
    goto out;
}

{
    struct kprobe *cur = kprobe_running ();
    struct kprobe_ctlblk *kcb = get_kprobe_ctlblk ();
    switch (kcb->kprobe_status) {
    case KPROBE_HIT_SS :
    case KPROBE_REENTER :
        regs->ip = (unsigned long) cur->addr;
        regs->flags |= kcb->kprobe_old_flags;
        if (kcb->kprobe_status == KPROBE_REENTER)
            restore_previous_kprobe (kcb);
        else
            reset_current_kprobe ();
        preempt_enable_no_resched ();
        break;
    case KPROBE_HIT_ACTIVE :
    case KPROBE_HIT_SSDONE :
        kprobes_inc_nmissed_count (cur);
        if (cur->fault_handler && cur->fault_handler (cur, regs, trapnr))
            return 1;
        if (fixup_exception (regs))
            return 1;
        break;
    default :
        break;
    }
    return 0;
}

{
case KPROBE_HIT_SS :
case KPROBE_REENTER :
    regs->ip = (unsigned long) cur->addr;
    regs->flags |= kcb->kprobe_old_flags;
    if (kcb->kprobe_status == KPROBE_REENTER)
        restore_previous_kprobe (kcb);
    else
        reset_current_kprobe ();
    preempt_enable_no_resched ();
    break;
case KPROBE_HIT_ACTIVE :
case KPROBE_HIT_SSDONE :
    kprobes_inc_nmissed_count (cur);
    if (cur->fault_handler && cur->fault_handler (cur, regs, trapnr))
        return 1;
    if (fixup_exception (regs))
        return 1;
    break;
default :
    break;
}

{
    struct die_args *args = data;
    int ret = NOTIFY_DONE;
    if (args->regs && user_mode_vm (args->regs))
        return ret;
    switch (val) {
    case DIE_INT3 :
        if (kprobe_handler (args->regs))
            ret = NOTIFY_STOP;
        break;
    case DIE_DEBUG :
        if (post_kprobe_handler (args->regs))
            ret = NOTIFY_STOP;
        break;
    case DIE_GPF :
        if (!preemptible () && kprobe_running () && kprobe_fault_handler (args->regs, args->trapnr))
            ret = NOTIFY_STOP;
        break;
    default :
        break;
    }
    return ret;
}

{
case DIE_INT3 :
    if (kprobe_handler (args->regs))
        ret = NOTIFY_STOP;
    break;
case DIE_DEBUG :
    if (post_kprobe_handler (args->regs))
        ret = NOTIFY_STOP;
    break;
case DIE_GPF :
    if (!preemptible () && kprobe_running () && kprobe_fault_handler (args->regs, args->trapnr))
        ret = NOTIFY_STOP;
    break;
default :
    break;
}

{
    struct jprobe *jp = container_of (p, struct jprobe, kp);
    unsigned long addr;
    struct kprobe_ctlblk *kcb = get_kprobe_ctlblk ();
    kcb->jprobe_saved_regs = *regs;
    kcb->jprobe_saved_sp = stack_addr (regs);
    addr = (unsigned long) (kcb->jprobe_saved_sp);
    memcpy (kcb -> jprobes_stack, (kprobe_opcode_t *) addr, MIN_STACK_SIZE (addr));
    regs->flags &= ~X86_EFLAGS_IF;
    trace_hardirqs_off ();
    regs->ip = (unsigned long) (jp->entry);
    return 1;
}

{
    struct kprobe_ctlblk *kcb = get_kprobe_ctlblk ();
    asm volatile ("       xchgl   %%ebx,%%esp	\n"
        "       int3			\n"
        "       .globl jprobe_return_end\n"
        "       jprobe_return_end:	\n"
        "       nop			\n"
        :
        : "b" (kcb->jprobe_saved_sp)
        : "memory"
    )}

{
    struct kprobe_ctlblk *kcb = get_kprobe_ctlblk ();
    u8 *addr = (u8 *) (regs->ip - 1);
    struct jprobe *jp = container_of (p, struct jprobe, kp);
    if ((addr > (u8 *) jprobe_return) && (addr < (u8 *) jprobe_return_end)) {
        if (stack_addr (regs) != kcb->jprobe_saved_sp) {
            struct pt_regs *saved_regs = &kcb->jprobe_saved_regs;
            printk (KERN_ERR "current sp %p does not match saved sp %p\n", stack_addr (regs), kcb -> jprobe_saved_sp);
            printk (KERN_ERR "Saved registers for jprobe %p\n", jp);
            show_registers (saved_regs);
            printk (KERN_ERR "Current registers\n");
            show_registers (regs);
            BUG ();
        }
        *regs = kcb->jprobe_saved_regs;
        memcpy ((kprobe_opcode_t *) (kcb -> jprobe_saved_sp), kcb -> jprobes_stack, MIN_STACK_SIZE (kcb -> jprobe_saved_sp));
        preempt_enable_no_resched ();
        return 1;
    }
    return 0;
}

{
    if (stack_addr (regs) != kcb->jprobe_saved_sp) {
        struct pt_regs *saved_regs = &kcb->jprobe_saved_regs;
        printk (KERN_ERR "current sp %p does not match saved sp %p\n", stack_addr (regs), kcb -> jprobe_saved_sp);
        printk (KERN_ERR "Saved registers for jprobe %p\n", jp);
        show_registers (saved_regs);
        printk (KERN_ERR "Current registers\n");
