{
    struct thread_struct *t = &current->thread;
    int idx;
    for (idx = 0; idx < GDT_ENTRY_TLS_ENTRIES; idx++)
        if (desc_empty (&t->tls_array[idx]))
            return idx + GDT_ENTRY_TLS_MIN;
    return -ESRCH;
}

{
    struct thread_struct *t = &p->thread;
    struct desc_struct *desc = &t->tls_array[idx - GDT_ENTRY_TLS_MIN];
    int cpu;
    cpu = get_cpu ();
    while (n-- > 0) {
        if (LDT_empty (info))
            desc->a = desc->b = 0;
        else
            fill_ldt (desc, info);
        ++info;
        ++desc;
    }
    if (t == &current->thread)
        load_TLS (t, cpu);
    put_cpu ();
}

{
    if (LDT_empty (info))
        desc->a = desc->b = 0;
    else
        fill_ldt (desc, info);
    ++info;
    ++desc;
}

{
    struct user_desc info;
    if (copy_from_user (&info, u_info, sizeof (info)))
        return -EFAULT;
    if (idx == -1)
        idx = info.entry_number;
    if (idx == -1 && can_allocate) {
        idx = get_free_idx ();
        if (idx < 0)
            return idx;
        if (put_user (idx, &u_info->entry_number))
            return -EFAULT;
    }
    if (idx < GDT_ENTRY_TLS_MIN || idx > GDT_ENTRY_TLS_MAX)
        return -EINVAL;
    set_tls_desc (p, idx, & info, 1);
    return 0;
}

{
    idx = get_free_idx ();
    if (idx < 0)
        return idx;
    if (put_user (idx, &u_info->entry_number))
        return -EFAULT;
}

{
    int ret = do_set_thread_area (current, -1, u_info, 1);
    asmlinkage_protect (1, ret, u_info);
    return ret;
}

{
    memset (info, 0, sizeof (* info));
    info->entry_number = idx;
    info->base_addr = get_desc_base (desc);
    info->limit = get_desc_limit (desc);
    info->seg_32bit = desc->d;
    info->contents = desc->type >> 2;
    info->read_exec_only = !(desc->type & 2);
    info->limit_in_pages = desc->g;
    info->seg_not_present = !desc->p;
    info->useable = desc->avl;
}

{
    struct user_desc info;
    if (idx == -1 && get_user (idx, &u_info->entry_number))
        return -EFAULT;
    if (idx < GDT_ENTRY_TLS_MIN || idx > GDT_ENTRY_TLS_MAX)
        return -EINVAL;
    fill_user_desc (& info, idx, & p -> thread.tls_array [idx - GDT_ENTRY_TLS_MIN]);
    if (copy_to_user (u_info, &info, sizeof (info)))
        return -EFAULT;
    return 0;
}

{
    int ret = do_get_thread_area (current, -1, u_info);
    asmlinkage_protect (1, ret, u_info);
    return ret;
}

{
    struct thread_struct *t = &target->thread;
    int n = GDT_ENTRY_TLS_ENTRIES;
    while (n > 0 && desc_empty (&t->tls_array[n - 1]))
        --n;
    return n;
}

{
    const struct desc_struct *tls;
    if (pos > GDT_ENTRY_TLS_ENTRIES * sizeof (struct user_desc) || (pos % sizeof (struct user_desc)) != 0 || (count % sizeof (struct user_desc)) != 0)
        return -EINVAL;
    pos /= sizeof (struct user_desc);
    count /= sizeof (struct user_desc);
    tls = &target->thread.tls_array[pos];
    if (kbuf) {
        struct user_desc *info = kbuf;
        while (count-- > 0)
            fill_user_desc (info++, GDT_ENTRY_TLS_MIN +pos++, tls++);
    }
    else {
        struct user_desc __user *u_info = ubuf;
        while (count-- > 0) {
            struct user_desc info;
            fill_user_desc (& info, GDT_ENTRY_TLS_MIN + pos ++, tls ++);
            if (__copy_to_user (u_info++, &info, sizeof (info)))
                return -EFAULT;
        }
    }
    return 0;
}

{
    struct user_desc *info = kbuf;
    while (count-- > 0)
        fill_user_desc (info++, GDT_ENTRY_TLS_MIN +pos++, tls++);
}

{
    struct user_desc __user *u_info = ubuf;
    while (count-- > 0) {
        struct user_desc info;
        fill_user_desc (& info, GDT_ENTRY_TLS_MIN + pos ++, tls ++);
        if (__copy_to_user (u_info++, &info, sizeof (info)))
            return -EFAULT;
    }
}

{
    struct user_desc info;
    fill_user_desc (& info, GDT_ENTRY_TLS_MIN + pos ++, tls ++);
    if (__copy_to_user (u_info++, &info, sizeof (info)))
        return -EFAULT;
}

{
    struct user_desc infobuf [GDT_ENTRY_TLS_ENTRIES];
    const struct user_desc *info;
    if (pos > GDT_ENTRY_TLS_ENTRIES * sizeof (struct user_desc) || (pos % sizeof (struct user_desc)) != 0 || (count % sizeof (struct user_desc)) != 0)
        return -EINVAL;
    if (kbuf)
        info = kbuf;
    else if (__copy_from_user (infobuf, ubuf, count))
        return -EFAULT;
    else
        info = infobuf;
    set_tls_desc (target, GDT_ENTRY_TLS_MIN + (pos / sizeof (struct user_desc)), info, count / sizeof (struct user_desc));
    return 0;
}

{
    unsigned long flags;
    int i;
    flags = claim_dma_lock ();
    dma_outb (DMA1_RESET_REG, 0);
    dma_outb (DMA2_RESET_REG, 0);
    for (i = 0; i < 8; i++) {
        set_dma_addr (i, 0x000000);
        set_dma_count (i, 1);
    }
    enable_dma (4);
    release_dma_lock (flags);
    return 0;
}

