{
    int error = sysdev_class_register (&i8237_sysdev_class);
    if (!error)
        error = sysdev_register (&device_i8237A);
    return error;
}

{
    unsigned long offset, flags;
    unsigned long boundary_size;
    unsigned long base_index;
    base_index = ALIGN (iommu_bus_base &dma_get_seg_boundary (dev), PAGE_SIZE) >> PAGE_SHIFT;
    boundary_size = ALIGN (dma_get_seg_boundary (dev) +1, PAGE_SIZE) >> PAGE_SHIFT;
    spin_lock_irqsave (& iommu_bitmap_lock, flags);
    offset = iommu_area_alloc (iommu_gart_bitmap, iommu_pages, next_bit, size, base_index, boundary_size, 0);
    if (offset == -1) {
        need_flush = 1;
        offset = iommu_area_alloc (iommu_gart_bitmap, iommu_pages, 0, size, base_index, boundary_size, 0);
    }
    if (offset != -1) {
        set_bit_string (iommu_gart_bitmap, offset, size);
        next_bit = offset + size;
        if (next_bit >= iommu_pages) {
            next_bit = 0;
            need_flush = 1;
        }
    }
    if (iommu_fullflush)
        need_flush = 1;
    spin_unlock_irqrestore (& iommu_bitmap_lock, flags);
    return offset;
}

{
    need_flush = 1;
    offset = iommu_area_alloc (iommu_gart_bitmap, iommu_pages, 0, size, base_index, boundary_size, 0);
}

{
    set_bit_string (iommu_gart_bitmap, offset, size);
    next_bit = offset + size;
    if (next_bit >= iommu_pages) {
        next_bit = 0;
        need_flush = 1;
    }
}

{
    next_bit = 0;
    need_flush = 1;
}

{
    unsigned long flags;
    spin_lock_irqsave (& iommu_bitmap_lock, flags);
    iommu_area_free (iommu_gart_bitmap, offset, size);
    spin_unlock_irqrestore (& iommu_bitmap_lock, flags);
}

{
    unsigned long flags;
    spin_lock_irqsave (& iommu_bitmap_lock, flags);
    if (need_flush) {
        k8_flush_garts ();
        need_flush = 0;
    }
    spin_unlock_irqrestore (& iommu_bitmap_lock, flags);
}

{
    k8_flush_garts ();
    need_flush = 0;
}

{
    printk (KERN_ERR "PCI-DMA: Out of IOMMU space for %lu bytes at device %s\n", size, dev -> bus_id);
    if (size > PAGE_SIZE * EMERGENCY_PAGES) {
        if (dir == PCI_DMA_FROMDEVICE || dir == PCI_DMA_BIDIRECTIONAL)
            panic ("PCI-DMA: Memory would be corrupted\n");
        if (dir == PCI_DMA_TODEVICE || dir == PCI_DMA_BIDIRECTIONAL)
            panic (KERN_ERR "PCI-DMA: Random memory would be DMAed\n");
    }
}

{
    if (dir == PCI_DMA_FROMDEVICE || dir == PCI_DMA_BIDIRECTIONAL)
        panic ("PCI-DMA: Memory would be corrupted\n");
    if (dir == PCI_DMA_TODEVICE || dir == PCI_DMA_BIDIRECTIONAL)
        panic (KERN_ERR "PCI-DMA: Random memory would be DMAed\n");
}

{
    u64 mask = *dev->dma_mask;
    int high = addr + size > mask;
    int mmu = high;
    if (force_iommu)
        mmu = 1;
    return mmu;
}

{
    u64 mask = *dev->dma_mask;
    int high = addr + size > mask;
    int mmu = high;
    return mmu;
}

{
    unsigned long npages = to_pages (phys_mem, size);
    unsigned long iommu_page = alloc_iommu (dev, npages);
    int i;
    if (iommu_page == -1) {
        if (!nonforced_iommu (dev, phys_mem, size))
            return phys_mem;
        if (panic_on_overflow)
            panic ("dma_map_area overflow %lu bytes\n", size);
        iommu_full (dev, size, dir);
        return bad_dma_address;
    }
    for (i = 0; i < npages; i++) {
        iommu_gatt_base[iommu_page + i] = GPTE_ENCODE (phys_mem);
        SET_LEAK (iommu_page + i);
        phys_mem += PAGE_SIZE;
    }
    return iommu_bus_base + iommu_page * PAGE_SIZE + (phys_mem & ~PAGE_MASK);
}

{
    if (!nonforced_iommu (dev, phys_mem, size))
        return phys_mem;
    if (panic_on_overflow)
        panic ("dma_map_area overflow %lu bytes\n", size);
    iommu_full (dev, size, dir);
    return bad_dma_address;
}

{
    iommu_gatt_base[iommu_page + i] = GPTE_ENCODE (phys_mem);
    SET_LEAK (iommu_page + i);
    phys_mem += PAGE_SIZE;
}

{
    dma_addr_t map = dma_map_area (dev, paddr, size, dir);
    flush_gart ();
    return map;
}

{
    unsigned long bus;
    if (!dev)
        dev = &fallback_dev;
    if (!need_iommu (dev, paddr, size))
        return paddr;
    bus = gart_map_simple (dev, paddr, size, dir);
    return bus;
}

{
    unsigned long iommu_page;
    int npages;
    int i;
    if (dma_addr < iommu_bus_base + EMERGENCY_PAGES * PAGE_SIZE || dma_addr >= iommu_bus_base + iommu_size)
        return;
    iommu_page = (dma_addr - iommu_bus_base) >> PAGE_SHIFT;
    npages = to_pages (dma_addr, size);
    for (i = 0; i < npages; i++) {
        iommu_gatt_base[iommu_page + i] = gart_unmapped_entry;
        CLEAR_LEAK (iommu_page + i);
    }
    free_iommu (iommu_page, npages);
}

{
    iommu_gatt_base[iommu_page + i] = gart_unmapped_entry;
    CLEAR_LEAK (iommu_page + i);
}

{
    struct scatterlist *s;
    int i;

    for_each_sg (sg, s, nents, i) {
        if (!s->dma_length || !s->length)
            break;
        gart_unmap_single (dev, s -> dma_address, s -> dma_length, dir);
    }

}

{
    if (!s->dma_length || !s->length)
        break;
    gart_unmap_single (dev, s -> dma_address, s -> dma_length, dir);
}

{
    struct scatterlist *s;
    int i;

    for_each_sg (sg, s, nents, i) {
        unsigned long addr = sg_phys (s);
        if (nonforced_iommu (dev, addr, s->length)) {
            addr = dma_map_area (dev, addr, s->length, dir);
            if (addr == bad_dma_address) {
                if (i > 0)
                    gart_unmap_sg (dev, sg, i, dir);
                nents = 0;
                sg[0].dma_length = 0;
                break;
            }
        }
        s->dma_address = addr;
        s->dma_length = s->length;
    }

    flush_gart ();
    return nents;
}

{
    unsigned long addr = sg_phys (s);
    if (nonforced_iommu (dev, addr, s->length)) {
        addr = dma_map_area (dev, addr, s->length, dir);
        if (addr == bad_dma_address) {
            if (i > 0)
                gart_unmap_sg (dev, sg, i, dir);
            nents = 0;
            sg[0].dma_length = 0;
            break;
        }
    }
    s->dma_address = addr;
    s->dma_length = s->length;
}

{
    addr = dma_map_area (dev, addr, s->length, dir);
    if (addr == bad_dma_address) {
        if (i > 0)
            gart_unmap_sg (dev, sg, i, dir);
        nents = 0;
        sg[0].dma_length = 0;
        break;
    }
}

{
    if (i > 0)
        gart_unmap_sg (dev, sg, i, dir);
    nents = 0;
    sg[0].dma_length = 0;
    break;
}

{
    unsigned long iommu_start = alloc_iommu (dev, pages);
    unsigned long iommu_page = iommu_start;
    struct scatterlist *s;
    int i;
    if (iommu_start == -1)
        return -1;

    for_each_sg (start, s, nelems, i) {
        unsigned long pages, addr;
        unsigned long phys_addr = s->dma_address;
        BUG_ON (s != start && s -> offset);
        if (s == start) {
            sout->dma_address = iommu_bus_base;
            sout->dma_address += iommu_page * PAGE_SIZE + s->offset;
            sout->dma_length = s->length;
        }
        else {
            sout->dma_length += s->length;
        }
        addr = phys_addr;
        pages = to_pages (s->offset, s->length);
        while (pages--) {
            iommu_gatt_base[iommu_page] = GPTE_ENCODE (addr);
            SET_LEAK (iommu_page);
            addr += PAGE_SIZE;
            iommu_page++;
        }
    }

    BUG_ON (iommu_page - iommu_start != pages);
    return 0;
}

{
    unsigned long pages, addr;
    unsigned long phys_addr = s->dma_address;
    BUG_ON (s != start && s -> offset);
    if (s == start) {
        sout->dma_address = iommu_bus_base;
        sout->dma_address += iommu_page * PAGE_SIZE + s->offset;
        sout->dma_length = s->length;
    }
    else {
        sout->dma_length += s->length;
    }
    addr = phys_addr;
    pages = to_pages (s->offset, s->length);
    while (pages--) {
        iommu_gatt_base[iommu_page] = GPTE_ENCODE (addr);
        SET_LEAK (iommu_page);
        addr += PAGE_SIZE;
        iommu_page++;
    }
}

{
    sout->dma_address = iommu_bus_base;
    sout->dma_address += iommu_page * PAGE_SIZE + s->offset;
    sout->dma_length = s->length;
}

{
    sout->dma_length += s->length;
}

{
    iommu_gatt_base[iommu_page] = GPTE_ENCODE (addr);
    SET_LEAK (iommu_page);
    addr += PAGE_SIZE;
    iommu_page++;
}

{
    if (!need) {
        BUG_ON (nelems != 1);
        sout->dma_address = start->dma_address;
        sout->dma_length = start->length;
        return 0;
    }
    return __dma_map_cont (dev, start, nelems, sout, pages);
}

{
    BUG_ON (nelems != 1);
    sout->dma_address = start->dma_address;
    sout->dma_length = start->length;
    return 0;
}

{
    struct scatterlist *s, *ps, *start_sg, *sgmap;
    int need = 0, nextneed, i, out, start;
    unsigned long pages = 0;
    unsigned int seg_size;
    unsigned int max_seg_size;
    if (nents == 0)
        return 0;
    if (!dev)
        dev = &fallback_dev;
    out = 0;
    start = 0;
    start_sg = sgmap = sg;
    seg_size = 0;
    max_seg_size = dma_get_max_seg_size (dev);
    ps = NULL;

    for_each_sg (sg, s, nents, i) {
        dma_addr_t addr = sg_phys (s);
        s->dma_address = addr;
        BUG_ON (s -> length == 0);
        nextneed = need_iommu (dev, addr, s->length);
        if (i > start) {
            if (!iommu_merge || !nextneed || !need || s->offset || (s->length + seg_size > max_seg_size) || (ps->offset + ps->length) % PAGE_SIZE) {
                if (dma_map_cont (dev, start_sg, i -start, sgmap, pages, need) < 0)
                    goto error;
                out++;
                seg_size = 0;
                sgmap = sg_next (sgmap);
                pages = 0;
                start = i;
                start_sg = s;
            }
        }
        seg_size += s->length;
        need = nextneed;
        pages += to_pages (s->offset, s->length);
        ps = s;
    }

    if (dma_map_cont (dev, start_sg, i -start, sgmap, pages, need) < 0)
        goto error;
    out++;
    flush_gart ();
    if (out < nents) {
        sgmap = sg_next (sgmap);
        sgmap->dma_length = 0;
    }
    return out;
error :
    flush_gart ();
    gart_unmap_sg (dev, sg, out, dir);
    if (force_iommu || iommu_merge) {
        out = dma_map_sg_nonforce (dev, sg, nents, dir);
        if (out > 0)
            return out;
    }
    if (panic_on_overflow)
        panic ("dma_map_sg: overflow on %lu pages\n", pages);
    iommu_full (dev, pages << PAGE_SHIFT, dir);
    for_each_sg (sg, s, nents, i)
    s->dma_address = bad_dma_address;
    return 0;
}

{
    dma_addr_t addr = sg_phys (s);
    s->dma_address = addr;
    BUG_ON (s -> length == 0);
    nextneed = need_iommu (dev, addr, s->length);
    if (i > start) {
        if (!iommu_merge || !nextneed || !need || s->offset || (s->length + seg_size > max_seg_size) || (ps->offset + ps->length) % PAGE_SIZE) {
            if (dma_map_cont (dev, start_sg, i -start, sgmap, pages, need) < 0)
                goto error;
            out++;
            seg_size = 0;
            sgmap = sg_next (sgmap);
            pages = 0;
            start = i;
            start_sg = s;
        }
    }
    seg_size += s->length;
    need = nextneed;
    pages += to_pages (s->offset, s->length);
    ps = s;
}

{
    if (!iommu_merge || !nextneed || !need || s->offset || (s->length + seg_size > max_seg_size) || (ps->offset + ps->length) % PAGE_SIZE) {
        if (dma_map_cont (dev, start_sg, i -start, sgmap, pages, need) < 0)
            goto error;
        out++;
        seg_size = 0;
        sgmap = sg_next (sgmap);
        pages = 0;
        start = i;
        start_sg = s;
    }
}

{
    if (dma_map_cont (dev, start_sg, i -start, sgmap, pages, need) < 0)
        goto error;
    out++;
    seg_size = 0;
    sgmap = sg_next (sgmap);
    pages = 0;
    start = i;
    start_sg = s;
}

{
    sgmap = sg_next (sgmap);
    sgmap->dma_length = 0;
}

{
    out = dma_map_sg_nonforce (dev, sg, nents, dir);
    if (out > 0)
        return out;
}

{
    unsigned long a;
    if (!iommu_size) {
        iommu_size = aper_size;
        if (!no_agp)
            iommu_size /= 2;
    }
    a = aper + iommu_size;
    iommu_size -= round_up (a, PMD_PAGE_SIZE) - a;
    if (iommu_size < 64 * 1024 * 1024) {
        printk (KERN_WARNING "PCI-DMA: Warning: Small IOMMU %luMB." " Consider increasing the AGP aperture in BIOS\n", iommu_size >> 20);
    }
    return iommu_size;
}

{
    iommu_size = aper_size;
    if (!no_agp)
        iommu_size /= 2;
}

{
    printk (KERN_WARNING "PCI-DMA: Warning: Small IOMMU %luMB." " Consider increasing the AGP aperture in BIOS\n", iommu_size >> 20);
}

{
    unsigned aper_size = 0, aper_base_32, aper_order;
    u64 aper_base;
    pci_read_config_dword (dev, 0x94, & aper_base_32);
    pci_read_config_dword (dev, 0x90, & aper_order);
    aper_order = (aper_order >> 1) & 7;
    aper_base = aper_base_32 & 0x7fff;
    aper_base <<= 25;
    aper_size = (32 * 1024 * 1024) << aper_order;
    if (aper_base + aper_size > 0x100000000UL || !aper_size)
        aper_base = 0;
    *size = aper_size;
    return aper_base;
}

{
    return 0;
}

{
    return -EINVAL;
}

{
    unsigned aper_size, gatt_size, new_aper_size;
    unsigned aper_base, new_aper_base;
    struct pci_dev *dev;
    void *gatt;
    int i, error;
    printk (KERN_INFO "PCI-DMA: Disabling AGP.\n");
    aper_size = aper_base = info->aper_size = 0;
    dev = NULL;
    for (i = 0; i < num_k8_northbridges; i++) {
        dev = k8_northbridges[i];
        new_aper_base = read_aperture (dev, &new_aper_size);
        if (!new_aper_base)
            goto nommu;
        if (!aper_base) {
            aper_size = new_aper_size;
            aper_base = new_aper_base;
        }
        if (aper_size != new_aper_size || aper_base != new_aper_base)
            goto nommu;
    }
    if (!aper_base)
        goto nommu;
    info->aper_base = aper_base;
    info->aper_size = aper_size >> 20;
    gatt_size = (aper_size >> PAGE_SHIFT) * sizeof (u32);
    gatt = (void *) __get_free_pages (GFP_KERNEL, get_order (gatt_size));
    if (!gatt)
        panic ("Cannot allocate GATT table");
    if (set_memory_uc ((unsigned long) gatt, gatt_size >> PAGE_SHIFT))
        panic ("Could not set GART PTEs to uncacheable pages");
    memset (gatt, 0, gatt_size);
    agp_gatt_table = gatt;
    for (i = 0; i < num_k8_northbridges; i++) {
        u32 gatt_reg;
        u32 ctl;
        dev = k8_northbridges[i];
        gatt_reg = __pa (gatt) >> 12;
        gatt_reg <<= 4;
        pci_write_config_dword (dev, 0x98, gatt_reg);
        pci_read_config_dword (dev, 0x90, & ctl);
        ctl |= 1;
        ctl &= ~((1 << 4) | (1 << 5));
        pci_write_config_dword (dev, 0x90, ctl);
    }
    error = sysdev_class_register (&gart_sysdev_class);
    if (!error)
        error = sysdev_register (&device_gart);
    if (error)
        panic ("Could not register gart_sysdev -- would corrupt data on next suspend");
    flush_gart ();
    printk (KERN_INFO "PCI-DMA: aperture base @ %x size %u KB\n", aper_base, aper_size >> 10);
    return 0;
nommu :
    printk (KERN_WARNING "PCI-DMA: More than 4GB of RAM and no IOMMU\n" KERN_WARNING "falling back to iommu=soft.\n");
    return -1;
}

{
    dev = k8_northbridges[i];
    new_aper_base = read_aperture (dev, &new_aper_size);
    if (!new_aper_base)
        goto nommu;
    if (!aper_base) {
        aper_size = new_aper_size;
        aper_base = new_aper_base;
    }
    if (aper_size != new_aper_size || aper_base != new_aper_base)
        goto nommu;
}

{
    aper_size = new_aper_size;
    aper_base = new_aper_base;
}

{
    u32 gatt_reg;
    u32 ctl;
    dev = k8_northbridges[i];
    gatt_reg = __pa (gatt) >> 12;
    gatt_reg <<= 4;
    pci_write_config_dword (dev, 0x98, gatt_reg);
    pci_read_config_dword (dev, 0x90, & ctl);
    ctl |= 1;
    ctl &= ~((1 << 4) | (1 << 5));
    pci_write_config_dword (dev, 0x90, ctl);
}

{
    struct pci_dev *dev;
    int i;
    if (no_agp && (dma_ops != &gart_dma_ops))
        return;
    for (i = 0; i < num_k8_northbridges; i++) {
        u32 ctl;
        dev = k8_northbridges[i];
        pci_read_config_dword (dev, 0x90, & ctl);
        ctl &= ~1;
        pci_write_config_dword (dev, 0x90, ctl);
    }
}

{
    u32 ctl;
    dev = k8_northbridges[i];
    pci_read_config_dword (dev, 0x90, & ctl);
    ctl &= ~1;
    pci_write_config_dword (dev, 0x90, ctl);
}

{
    struct agp_kern_info info;
    unsigned long iommu_start;
    unsigned long aper_size;
    unsigned long scratch;
    long i;
    if (cache_k8_northbridges () < 0 || num_k8_northbridges == 0) {
        printk (KERN_INFO "PCI-GART: No AMD northbridge found.\n");
        return;
    }
    no_agp = 1;
    if (swiotlb)
        return;
    if (iommu_detected && !gart_iommu_aperture)
        return;
    if (no_iommu || (!force_iommu && end_pfn <= MAX_DMA32_PFN) || !gart_iommu_aperture || (no_agp && init_k8_gatt (&info) < 0)) {
        if (end_pfn > MAX_DMA32_PFN) {
            printk (KERN_WARNING "More than 4GB of memory " "but GART IOMMU not available.\n" KERN_WARNING "falling back to iommu=soft.\n");
        }
        return;
    }
    printk (KERN_INFO "PCI-DMA: using GART IOMMU.\n");
    aper_size = info.aper_size * 1024 * 1024;
    iommu_size = check_iommu_size (info.aper_base, aper_size);
    iommu_pages = iommu_size >> PAGE_SHIFT;
    iommu_gart_bitmap = (void *) __get_free_pages (GFP_KERNEL, get_order (iommu_pages / 8));
    if (!iommu_gart_bitmap)
        panic ("Cannot allocate iommu bitmap\n");
    memset (iommu_gart_bitmap, 0, iommu_pages / 8);
    set_bit_string (iommu_gart_bitmap, 0, EMERGENCY_PAGES);
    agp_memory_reserved = iommu_size;
    printk (KERN_INFO "PCI-DMA: Reserving %luMB of IOMMU area in the AGP aperture\n", iommu_size >> 20);
    iommu_start = aper_size - iommu_size;
    iommu_bus_base = info.aper_base + iommu_start;
    bad_dma_address = iommu_bus_base;
    iommu_gatt_base = agp_gatt_table + (iommu_start >> PAGE_SHIFT);
    set_memory_np ((unsigned long) __va (iommu_bus_base), iommu_size >> PAGE_SHIFT);
    wbinvd ();
    scratch = get_zeroed_page (GFP_KERNEL);
    if (!scratch)
        panic ("Cannot allocate iommu scratch page");
    gart_unmapped_entry = GPTE_ENCODE (__pa (scratch));
    for (i = EMERGENCY_PAGES; i < iommu_pages; i++)
        iommu_gatt_base[i] = gart_unmapped_entry;
    flush_gart ();
    dma_ops = &gart_dma_ops;
}

{
    printk (KERN_INFO "PCI-GART: No AMD northbridge found.\n");
    return;
}

{
    if (end_pfn > MAX_DMA32_PFN) {
        printk (KERN_WARNING "More than 4GB of memory " "but GART IOMMU not available.\n" KERN_WARNING "falling back to iommu=soft.\n");
    }
    return;
}

{
    printk (KERN_WARNING "More than 4GB of memory " "but GART IOMMU not available.\n" KERN_WARNING "falling back to iommu=soft.\n");
}

{
    int arg;
    if (isdigit (*p) && get_option (&p, &arg))
        iommu_size = arg;
    if (!strncmp (p, "fullflush", 8))
        iommu_fullflush = 1;
    if (!strncmp (p, "nofullflush", 11))
        iommu_fullflush = 0;
    if (!strncmp (p, "noagp", 5))
        no_agp = 1;
    if (!strncmp (p, "noaperture", 10))
        fix_aperture = 0;
    if (!strncmp (p, "force", 5))
        gart_iommu_aperture_allowed = 1;
    if (!strncmp (p, "allowed", 7))
        gart_iommu_aperture_allowed = 1;
    if (!strncmp (p, "memaper", 7)) {
        fallback_aper_force = 1;
        p += 7;
        if (*p == '=') {
            ++p;
            if (get_option (&p, &arg))
                fallback_aper_order = arg;
        }
    }
}

{
    fallback_aper_force = 1;
    p += 7;
    if (*p == '=') {
        ++p;
        if (get_option (&p, &arg))
            fallback_aper_order = arg;
    }
}

{
    ++p;
    if (get_option (&p, &arg))
        fallback_aper_order = arg;
}

{
    unsigned long mask = 0;
    clts ();
    if (cpu_has_fxsr) {
        memset (& fx_scratch, 0, sizeof (struct i387_fxsave_struct));
        asm volatile ("fxsave %0"
            :
            : "m" (fx_scratch)
        ) mask = fx_scratch.mxcsr_mask;
        if (mask == 0)
            mask = 0x0000ffbf;
    }
    mxcsr_feature_mask &= mask;
    stts ();
}

{
    memset (& fx_scratch, 0, sizeof (struct i387_fxsave_struct));
    asm volatile ("fxsave %0"
        :
        : "m" (fx_scratch)
    ) mask = fx_scratch.mxcsr_mask;
    if (mask == 0)
        mask = 0x0000ffbf;
}

{
    if (!HAVE_HWFP) {
        xstate_size = sizeof (struct i387_soft_struct);
        return;
    }
    if (cpu_has_fxsr)
        xstate_size = sizeof (struct i387_fxsave_struct);
}

{
    xstate_size = sizeof (struct i387_soft_struct);
    return;
}

{
    if (tsk_used_math (tsk)) {
        if (HAVE_HWFP && tsk == current)
            unlazy_fpu (tsk);
        return 0;
    }
    if (!tsk->thread.xstate) {
        tsk->thread.xstate = kmem_cache_alloc (task_xstate_cachep, GFP_KERNEL);
        if (!tsk->thread.xstate)
            return -ENOMEM;
    }
    if (cpu_has_fxsr) {
        struct i387_fxsave_struct *fx = &tsk->thread.xstate->fxsave;
        memset (fx, 0, xstate_size);
        fx->cwd = 0x37f;
        if (cpu_has_xmm)
            fx->mxcsr = MXCSR_DEFAULT;
    }
    else {
        struct i387_fsave_struct *fp = &tsk->thread.xstate->fsave;
        memset (fp, 0, xstate_size);
        fp->cwd = 0xffff037fu;
        fp->swd = 0xffff0000u;
        fp->twd = 0xffffffffu;
        fp->fos = 0xffff0000u;
    }
    set_stopped_child_used_math (tsk);
    return 0;
}

{
    if (HAVE_HWFP && tsk == current)
        unlazy_fpu (tsk);
    return 0;
}

{
    tsk->thread.xstate = kmem_cache_alloc (task_xstate_cachep, GFP_KERNEL);
    if (!tsk->thread.xstate)
        return -ENOMEM;
}

{
    struct i387_fxsave_struct *fx = &tsk->thread.xstate->fxsave;
    memset (fx, 0, xstate_size);
    fx->cwd = 0x37f;
    if (cpu_has_xmm)
        fx->mxcsr = MXCSR_DEFAULT;
}

{
    struct i387_fsave_struct *fp = &tsk->thread.xstate->fsave;
    memset (fp, 0, xstate_size);
    fp->cwd = 0xffff037fu;
    fp->swd = 0xffff0000u;
    fp->twd = 0xffffffffu;
    fp->fos = 0xffff0000u;
}

{
    return tsk_used_math (target) ? regset->n : 0;
}

{
    return (cpu_has_fxsr && tsk_used_math (target)) ? regset->n : 0;
}

{
    int ret;
    if (!cpu_has_fxsr)
        return -EIO;
    ret = init_fpu (target);
    if (ret)
        return ret;
    return user_regset_copyout (&pos, &count, &kbuf, &ubuf, &target->thread.xstate->fxsave, 0, -1);
}

{
    int ret;
    if (!cpu_has_fxsr)
        return -EIO;
    ret = init_fpu (target);
    if (ret)
        return ret;
    set_stopped_child_used_math (target);
    ret = user_regset_copyin (&pos, &count, &kbuf, &ubuf, &target->thread.xstate->fxsave, 0, -1);
    target->thread.xstate->fxsave.mxcsr &= mxcsr_feature_mask;
    return ret;
}

{
    return 0;
}

{
    detect_vsmp_box ();
    if (!is_vsmp_box ())
        return;
    set_vsmp_pv_ops ();
    return;
}

{
    unsigned int mask = 1 << irq;
    unsigned long flags;
    spin_lock_irqsave (& i8259A_lock, flags);
    cached_irq_mask |= mask;
    if (irq & 8)
        outb (cached_slave_mask, PIC_SLAVE_IMR);
    else
        outb (cached_master_mask, PIC_MASTER_IMR);
    spin_unlock_irqrestore (& i8259A_lock, flags);
}

{
    unsigned int mask = ~(1 << irq);
    unsigned long flags;
    spin_lock_irqsave (& i8259A_lock, flags);
    cached_irq_mask &= mask;
    if (irq & 8)
        outb (cached_slave_mask, PIC_SLAVE_IMR);
    else
        outb (cached_master_mask, PIC_MASTER_IMR);
    spin_unlock_irqrestore (& i8259A_lock, flags);
}

{
    unsigned int mask = 1 << irq;
    unsigned long flags;
    int ret;
    spin_lock_irqsave (& i8259A_lock, flags);
    if (irq < 8)
        ret = inb (PIC_MASTER_CMD) & mask;
    else
        ret = inb (PIC_SLAVE_CMD) & (mask >> 8);
    spin_unlock_irqrestore (& i8259A_lock, flags);
    return ret;
}

{
    disable_irq_nosync (irq);
    io_apic_irqs &= ~(1 << irq);
    set_irq_chip_and_handler_name (irq, & i8259A_chip, handle_level_irq, "XT");
    enable_irq (irq);
}

{
    int value;
    int irqmask = 1 << irq;
    if (irq < 8) {
        outb (0x0B, PIC_MASTER_CMD);
        value = inb (PIC_MASTER_CMD) & irqmask;
        outb (0x0A, PIC_MASTER_CMD);
        return value;
    }
    outb (0x0B, PIC_SLAVE_CMD);
    value = inb (PIC_SLAVE_CMD) & (irqmask >> 8);
    outb (0x0A, PIC_SLAVE_CMD);
    return value;
}

{
    outb (0x0B, PIC_MASTER_CMD);
    value = inb (PIC_MASTER_CMD) & irqmask;
    outb (0x0A, PIC_MASTER_CMD);
    return value;
}

{
    unsigned int irqmask = 1 << irq;
    unsigned long flags;
    spin_lock_irqsave (& i8259A_lock, flags);
    if (cached_irq_mask & irqmask)
        goto spurious_8259A_irq;
    cached_irq_mask |= irqmask;
handle_real_irq :
    if (irq & 8) {
        inb (PIC_SLAVE_IMR);
        outb (cached_slave_mask, PIC_SLAVE_IMR);
        outb (0x60 + (irq & 7), PIC_SLAVE_CMD);
        outb (0x60 + PIC_CASCADE_IR, PIC_MASTER_CMD);
    }
    else {
        inb (PIC_MASTER_IMR);
        outb (cached_master_mask, PIC_MASTER_IMR);
        outb (0x60 + irq, PIC_MASTER_CMD);
    }
    spin_unlock_irqrestore (& i8259A_lock, flags);
    return;
spurious_8259A_irq :
    if (i8259A_irq_real (irq))
        goto handle_real_irq;
    {
        static int spurious_irq_mask;
        if (!(spurious_irq_mask & irqmask)) {
            printk (KERN_DEBUG "spurious 8259A interrupt: IRQ%d.\n", irq);
            spurious_irq_mask |= irqmask;
        }
        atomic_inc (& irq_err_count);
        goto handle_real_irq;
    }
}

{
    inb (PIC_SLAVE_IMR);
    outb (cached_slave_mask, PIC_SLAVE_IMR);
    outb (0x60 + (irq & 7), PIC_SLAVE_CMD);
    outb (0x60 + PIC_CASCADE_IR, PIC_MASTER_CMD);
}

{
    inb (PIC_MASTER_IMR);
    outb (cached_master_mask, PIC_MASTER_IMR);
    outb (0x60 + irq, PIC_MASTER_CMD);
}

{
    static int spurious_irq_mask;
    if (!(spurious_irq_mask & irqmask)) {
        printk (KERN_DEBUG "spurious 8259A interrupt: IRQ%d.\n", irq);
        spurious_irq_mask |= irqmask;
    }
    atomic_inc (& irq_err_count);
    goto handle_real_irq;
}

{
    printk (KERN_DEBUG "spurious 8259A interrupt: IRQ%d.\n", irq);
    spurious_irq_mask |= irqmask;
}

{
    outb (trigger [0], 0x4d0);
    outb (trigger [1], 0x4d1);
}

{
    trigger[0] = inb (0x4d0) & 0xF8;
    trigger[1] = inb (0x4d1) & 0xDE;
}

{
    init_8259A (i8259A_auto_eoi);
    restore_ELCR (irq_trigger);
    return 0;
}

{
    save_ELCR (irq_trigger);
    return 0;
}

{
    outb (0xff, PIC_MASTER_IMR);
    outb (0xff, PIC_SLAVE_IMR);
    return 0;
}

{
    int error = sysdev_class_register (&i8259_sysdev_class);
    if (!error)
        error = sysdev_register (&device_i8259A);
    return error;
}

{
    unsigned long flags;
    i8259A_auto_eoi = auto_eoi;
    spin_lock_irqsave (& i8259A_lock, flags);
    outb (0xff, PIC_MASTER_IMR);
    outb (0xff, PIC_SLAVE_IMR);
    outb_pic (0x11, PIC_MASTER_CMD);
    outb_pic (IRQ0_VECTOR, PIC_MASTER_IMR);
    outb_pic (0x04, PIC_MASTER_IMR);
    if (auto_eoi)
        outb_pic (MASTER_ICW4_DEFAULT | PIC_ICW4_AEOI, PIC_MASTER_IMR);
    else
        outb_pic (MASTER_ICW4_DEFAULT, PIC_MASTER_IMR);
    outb_pic (0x11, PIC_SLAVE_CMD);
    outb_pic (IRQ8_VECTOR, PIC_SLAVE_IMR);
    outb_pic (PIC_CASCADE_IR, PIC_SLAVE_IMR);
    outb_pic (SLAVE_ICW4_DEFAULT, PIC_SLAVE_IMR);
    if (auto_eoi)
        i8259A_chip.mask_ack = disable_8259A_irq;
    else
        i8259A_chip.mask_ack = mask_and_ack_8259A;
    udelay (100);
    outb (cached_master_mask, PIC_MASTER_IMR);
    outb (cached_slave_mask, PIC_SLAVE_IMR);
    spin_unlock_irqrestore (& i8259A_lock, flags);
}

{
    int i;
    init_bsp_APIC ();
    init_8259A (0);
    for (i = 0; i < NR_IRQS; i++) {
        irq_desc[i].status = IRQ_DISABLED;
        irq_desc[i].action = NULL;
        irq_desc[i].depth = 1;
        if (i < 16) {
            set_irq_chip_and_handler_name (i, & i8259A_chip, handle_level_irq, "XT");
        }
        else {
            irq_desc[i].chip = &no_irq_chip;
        }
    }
}

{
    irq_desc[i].status = IRQ_DISABLED;
    irq_desc[i].action = NULL;
    irq_desc[i].depth = 1;
    if (i < 16) {
        set_irq_chip_and_handler_name (i, & i8259A_chip, handle_level_irq, "XT");
    }
    else {
        irq_desc[i].chip = &no_irq_chip;
    }
}

{
    set_irq_chip_and_handler_name (i, & i8259A_chip, handle_level_irq, "XT");
}

{
    irq_desc[i].chip = &no_irq_chip;
}

{
    int i;
    init_ISA_irqs ();
    for (i = 0; i < (NR_VECTORS - FIRST_EXTERNAL_VECTOR); i++) {
        int vector = FIRST_EXTERNAL_VECTOR + i;
        if (vector != IA32_SYSCALL_VECTOR)
            set_intr_gate (vector, interrupt[i]);
    }
    set_intr_gate (THERMAL_APIC_VECTOR, thermal_interrupt);
    set_intr_gate (THRESHOLD_APIC_VECTOR, threshold_interrupt);
    set_intr_gate (LOCAL_TIMER_VECTOR, apic_timer_interrupt);
    set_intr_gate (SPURIOUS_APIC_VECTOR, spurious_interrupt);
    set_intr_gate (ERROR_APIC_VECTOR, error_interrupt);
    if (!acpi_ioapic)
        setup_irq (2, &irq2);
}

{
    int vector = FIRST_EXTERNAL_VECTOR + i;
    if (vector != IA32_SYSCALL_VECTOR)
        set_intr_gate (vector, interrupt[i]);
}

{
    const struct exception_table_entry *fixup;
    fixup = search_exception_tables (regs->ip);
    if (fixup) {
        regs->ip = fixup->fixup;
        return 1;
    }
    return 0;
}

{
    regs->ip = fixup->fixup;
    return 1;
}

{
    return acpi_map_pxm_to_node (pxm);
}

{
    int i;
    for (i = 0; i < num_node_memblks; i++) {
        struct bootnode *nd = &node_memblk_range[i];
        if (nd->start == nd->end)
            continue;
        if (nd->end > start && nd->start < end)
            return memblk_nodeid[i];
        if (nd->end == end && nd->start == start)
            return memblk_nodeid[i];
    }
    return -1;
}

{
    struct bootnode *nd = &node_memblk_range[i];
    if (nd->start == nd->end)
        continue;
    if (nd->end > start && nd->start < end)
        return memblk_nodeid[i];
    if (nd->end == end && nd->start == start)
        return memblk_nodeid[i];
}

{
    struct bootnode *nd = &nodes[i];
    if (found_add_area)
        return;
    if (nd->start < start) {
        nd->start = start;
        if (nd->end < nd->start)
            nd->start = nd->end;
    }
    if (nd->end > end) {
        nd->end = end;
        if (nd->start > nd->end)
            nd->start = nd->end;
    }
}

{
    nd->start = start;
    if (nd->end < nd->start)
        nd->start = nd->end;
}

{
    nd->end = end;
    if (nd->start > nd->end)
        nd->start = nd->end;
}

{
    int i;
    printk (KERN_ERR "SRAT: SRAT not used.\n");
    acpi_numa = -1;
    found_add_area = 0;
    for (i = 0; i < MAX_LOCAL_APIC; i++)
        apicid_to_node[i] = NUMA_NO_NODE;
    for (i = 0; i < MAX_NUMNODES; i++)
        nodes_add[i].start = nodes[i].end = 0;
    remove_all_active_ranges ();
}

{
    return numa_off || acpi_numa < 0;
}

{
    acpi_slit = slit;
}

{
    int pxm, node;
    int apic_id;
    if (srat_disabled ())
        return;
    if (pa->header.length != sizeof (struct acpi_srat_cpu_affinity)) {
        bad_srat ();
        return;
    }
    if ((pa->flags & ACPI_SRAT_CPU_ENABLED) == 0)
        return;
    pxm = pa->proximity_domain_lo;
    node = setup_node (pxm);
    if (node < 0) {
        printk (KERN_ERR "SRAT: Too many proximity domains %x\n", pxm);
        bad_srat ();
        return;
    }
    if (is_uv_system ())
        apic_id = (pa->apic_id << 8) | pa->local_sapic_eid;
    else
        apic_id = pa->apic_id;
    apicid_to_node[apic_id] = node;
    acpi_numa = 1;
    printk (KERN_INFO "SRAT: PXM %u -> APIC %u -> Node %u\n", pxm, apic_id, node);
}

{
    bad_srat ();
    return;
}

{
    printk (KERN_ERR "SRAT: Too many proximity domains %x\n", pxm);
    bad_srat ();
    return;
}

{
    return -1;
}

{
    return 1;
}

{
    return 0;
}

{
    unsigned long s_pfn = start >> PAGE_SHIFT;
    unsigned long e_pfn = end >> PAGE_SHIFT;
    int ret = 0, changed = 0;
    struct bootnode *nd = &nodes_add[node];
    if ((signed long) (end - start) < NODE_MIN_SIZE) {
        printk (KERN_ERR "SRAT: Hotplug area too small\n");
        return -1;
    }
    if (absent_pages_in_range (s_pfn, e_pfn) != e_pfn - s_pfn) {
        printk (KERN_ERR "SRAT: Hotplug area %lu -> %lu has existing memory\n", s_pfn, e_pfn);
        return -1;
    }
    if (!hotadd_enough_memory (&nodes_add[node])) {
        printk (KERN_ERR "SRAT: Hotplug area too large\n");
        return -1;
    }
    if (nd->start == nd->end) {
        nd->start = start;
        nd->end = end;
        changed = 1;
    }
    else {
        if (nd->start == end) {
            nd->start = start;
            changed = 1;
        }
        if (nd->end == start) {
            nd->end = end;
            changed = 1;
        }
        if (!changed)
            printk (KERN_ERR "SRAT: Hotplug zone not continuous. Partly ignored\n");
    }
    ret = update_end_of_memory (nd->end);
    if (changed)
        printk (KERN_INFO "SRAT: hot plug zone found %Lx - %Lx\n", nd->start, nd->end);
    return ret;
}

{
    printk (KERN_ERR "SRAT: Hotplug area too small\n");
    return -1;
}

{
    printk (KERN_ERR "SRAT: Hotplug area %lu -> %lu has existing memory\n", s_pfn, e_pfn);
    return -1;
}

{
    printk (KERN_ERR "SRAT: Hotplug area too large\n");
    return -1;
}

{
    nd->start = start;
    nd->end = end;
    changed = 1;
}

{
    if (nd->start == end) {
        nd->start = start;
        changed = 1;
    }
    if (nd->end == start) {
        nd->end = end;
        changed = 1;
    }
    if (!changed)
        printk (KERN_ERR "SRAT: Hotplug zone not continuous. Partly ignored\n");
}

{
    nd->start = start;
    changed = 1;
}

{
    nd->end = end;
    changed = 1;
}

{
    struct bootnode *nd, oldnode;
    unsigned long start, end;
    int node, pxm;
    int i;
    if (srat_disabled ())
        return;
    if (ma->header.length != sizeof (struct acpi_srat_mem_affinity)) {
        bad_srat ();
        return;
    }
    if ((ma->flags & ACPI_SRAT_MEM_ENABLED) == 0)
        return;
    if ((ma->flags & ACPI_SRAT_MEM_HOT_PLUGGABLE) && !save_add_info ())
        return;
    start = ma->base_address;
    end = start + ma->length;
    pxm = ma->proximity_domain;
    node = setup_node (pxm);
    if (node < 0) {
        printk (KERN_ERR "SRAT: Too many proximity domains.\n");
        bad_srat ();
        return;
    }
    i = conflicting_memblks (start, end);
    if (i == node) {
        printk (KERN_WARNING "SRAT: Warning: PXM %d (%lx-%lx) overlaps with itself (%Lx-%Lx)\n", pxm, start, end, nodes [i].start, nodes [i].end);
    }
    else if (i >= 0) {
        printk (KERN_ERR "SRAT: PXM %d (%lx-%lx) overlaps with PXM %d (%Lx-%Lx)\n", pxm, start, end, node_to_pxm (i), nodes [i].start, nodes [i].end);
        bad_srat ();
        return;
    }
    nd = &nodes[node];
    oldnode = *nd;
    if (!node_test_and_set (node, nodes_parsed)) {
        nd->start = start;
        nd->end = end;
    }
    else {
        if (start < nd->start)
            nd->start = start;
        if (nd->end < end)
            nd->end = end;
    }
    printk (KERN_INFO "SRAT: Node %u PXM %u %lx-%lx\n", node, pxm, start, end);
    e820_register_active_regions (node, start >> PAGE_SHIFT, end >> PAGE_SHIFT);
    push_node_boundaries (node, nd -> start >> PAGE_SHIFT, nd -> end >> PAGE_SHIFT);
    if ((ma->flags & ACPI_SRAT_MEM_HOT_PLUGGABLE) && (reserve_hotadd (node, start, end) < 0)) {
        printk (KERN_NOTICE "SRAT: Hotplug region ignored\n");
        *nd = oldnode;
        if ((nd->start | nd->end) == 0)
            node_clear (node, nodes_parsed);
    }
    node_memblk_range[num_node_memblks].start = start;
    node_memblk_range[num_node_memblks].end = end;
    memblk_nodeid[num_node_memblks] = node;
    num_node_memblks++;
}

{
    bad_srat ();
    return;
}

{
    printk (KERN_ERR "SRAT: Too many proximity domains.\n");
    bad_srat ();
    return;
}

{
    printk (KERN_WARNING "SRAT: Warning: PXM %d (%lx-%lx) overlaps with itself (%Lx-%Lx)\n", pxm, start, end, nodes [i].start, nodes [i].end);
}

{
    printk (KERN_ERR "SRAT: PXM %d (%lx-%lx) overlaps with PXM %d (%Lx-%Lx)\n", pxm, start, end, node_to_pxm (i), nodes [i].start, nodes [i].end);
    bad_srat ();
    return;
}

{
    nd->start = start;
    nd->end = end;
}

{
    if (start < nd->start)
        nd->start = start;
    if (nd->end < end)
        nd->end = end;
}

{
    printk (KERN_NOTICE "SRAT: Hotplug region ignored\n");
    *nd = oldnode;
    if ((nd->start | nd->end) == 0)
        node_clear (node, nodes_parsed);
}

{
    int i;
    unsigned long pxmram, e820ram;
    pxmram = 0;

    for_each_node_mask (i, nodes_parsed) {
        unsigned long s = nodes[i].start >> PAGE_SHIFT;
        unsigned long e = nodes[i].end >> PAGE_SHIFT;
        pxmram += e - s;
        pxmram -= absent_pages_in_range (s, e);
        if ((long) pxmram < 0)
            pxmram = 0;
    }

    e820ram = end_pfn - absent_pages_in_range (0, end_pfn);
    if ((long) (e820ram - pxmram) >= 1 * 1024 * 1024) {
        printk (KERN_ERR "SRAT: PXMs only cover %luMB of your %luMB e820 RAM. Not used.\n", (pxmram << PAGE_SHIFT) >> 20, (e820ram << PAGE_SHIFT) >> 20);
        return 0;
    }
    return 1;
}

{
    unsigned long s = nodes[i].start >> PAGE_SHIFT;
    unsigned long e = nodes[i].end >> PAGE_SHIFT;
    pxmram += e - s;
    pxmram -= absent_pages_in_range (s, e);
    if ((long) pxmram < 0)
        pxmram = 0;
}

{
    printk (KERN_ERR "SRAT: PXMs only cover %luMB of your %luMB e820 RAM. Not used.\n", (pxmram << PAGE_SHIFT) >> 20, (e820ram << PAGE_SHIFT) >> 20);
    return 0;
}

{
    int i;
    node_clear (node, nodes_parsed);
    for (i = 0; i < MAX_LOCAL_APIC; i++) {
        if (apicid_to_node[i] == node)
            apicid_to_node[i] = NUMA_NO_NODE;
    }
}

{
    if (apicid_to_node[i] == node)
        apicid_to_node[i] = NUMA_NO_NODE;
}

{
    int i;
    if (acpi_numa <= 0)
        return -1;
    for (i = 0; i < MAX_NUMNODES; i++) {
        cutoff_node (i, start, end);
        if (nodes[i].end && (nodes[i].end - nodes[i].start) < NODE_MIN_SIZE) {
            unparse_node (i);
            node_set_offline (i);
        }
    }
    if (!nodes_cover_memory (nodes)) {
        bad_srat ();
        return -1;
    }
    memnode_shift = compute_hash_shift (node_memblk_range, num_node_memblks, memblk_nodeid);
    if (memnode_shift < 0) {
        printk (KERN_ERR "SRAT: No NUMA node hash function found. Contact maintainer\n");
        bad_srat ();
        return -1;
    }
    node_possible_map = nodes_parsed;
    for_each_node_mask (i, node_possible_map)
    setup_node_bootmem (i, nodes [i].start, nodes [i].end);
    for_each_node_mask (i, node_possible_map)
    if (!node_online (i))
        setup_node_bootmem (i, nodes[i].start, nodes[i].end);
    for (i = 0; i < NR_CPUS; i++) {
        int node = early_cpu_to_node (i);
        if (node == NUMA_NO_NODE)
            continue;
        if (!node_isset (node, node_possible_map))
            numa_set_node (i, NUMA_NO_NODE);
    }
    numa_init_array ();
    return 0;
}

{
    cutoff_node (i, start, end);
    if (nodes[i].end && (nodes[i].end - nodes[i].start) < NODE_MIN_SIZE) {
        unparse_node (i);
        node_set_offline (i);
    }
}

{
    unparse_node (i);
    node_set_offline (i);
}

{
    bad_srat ();
    return -1;
}

{
    printk (KERN_ERR "SRAT: No NUMA node hash function found. Contact maintainer\n");
    bad_srat ();
    return -1;
}

{
    int node = early_cpu_to_node (i);
    if (node == NUMA_NO_NODE)
        continue;
    if (!node_isset (node, node_possible_map))
        numa_set_node (i, NUMA_NO_NODE);
}

{
    return a == b;
}

{
    if (found_add_area && nodes_add[nodeid].end) {
        u64 total_mb;
        printk (KERN_INFO "SRAT: Reserving hot-add memory space " "for node %d at %Lx-%Lx\n", nodeid, nodes_add [nodeid].start, nodes_add [nodeid].end);
        total_mb = (nodes_add[nodeid].end - nodes_add[nodeid].start) >> PAGE_SHIFT;
        total_mb *= sizeof (struct page);
        total_mb >>= 20;
        printk (KERN_INFO "SRAT: This will cost you %Lu MB of " "pre-allocated memory.\n", (unsigned long long) total_mb);
        reserve_bootmem_node (NODE_DATA (nodeid), nodes_add [nodeid].start, nodes_add [nodeid].end - nodes_add [nodeid].start, BOOTMEM_DEFAULT);
    }
}

{
    u64 total_mb;
    printk (KERN_INFO "SRAT: Reserving hot-add memory space " "for node %d at %Lx-%Lx\n", nodeid, nodes_add [nodeid].start, nodes_add [nodeid].end);
    total_mb = (nodes_add[nodeid].end - nodes_add[nodeid].start) >> PAGE_SHIFT;
    total_mb *= sizeof (struct page);
    total_mb >>= 20;
    printk (KERN_INFO "SRAT: This will cost you %Lu MB of " "pre-allocated memory.\n", (unsigned long long) total_mb);
    reserve_bootmem_node (NODE_DATA (nodeid), nodes_add [nodeid].start, nodes_add [nodeid].end - nodes_add [nodeid].start, BOOTMEM_DEFAULT);
}

{
    int index;
    if (!acpi_slit)
        return null_slit_node_compare (a, b) ? LOCAL_DISTANCE : REMOTE_DISTANCE;
    index = acpi_slit->locality_count * node_to_pxm (a);
    return acpi_slit->entry[index + node_to_pxm (b)];
}

{
    int i, ret = 0;
    for_each_node (i)
    if (nodes_add[i].start <= start && nodes_add[i].end > start)
        ret = i;
    return ret;
}

{
    u64 pat;
    if (!pat_wc_enabled)
        return;
    if (!cpu_has_pat) {
        printk (KERN_ERR "PAT enabled, but CPU feature cleared\n");
        BUG_ON (boot_pat_state);
    }
    pat = PAT (0, WB) | PAT (1, WC) | PAT (2, UC_MINUS) | PAT (3, UC) | PAT (4, WB) | PAT (5, WC) | PAT (6, UC_MINUS) | PAT (7, UC);
    if (!boot_pat_state)
        rdmsrl (MSR_IA32_CR_PAT, boot_pat_state);
    wrmsrl (MSR_IA32_CR_PAT, pat);
    printk (KERN_INFO "x86 PAT enabled: cpu %d, old 0x%Lx, new 0x%Lx\n", smp_processor_id (), boot_pat_state, pat);
}

{
    printk (KERN_ERR "PAT enabled, but CPU feature cleared\n");
    BUG_ON (boot_pat_state);
}

{
    switch (flags & _PAGE_CACHE_MASK) {
    case _PAGE_CACHE_UC :
        return "uncached";
    case _PAGE_CACHE_UC_MINUS :
        return "uncached-minus";
    case _PAGE_CACHE_WB :
        return "write-back";
    case _PAGE_CACHE_WC :
        return "write-combining";
    default :
        return "broken";
    }
}

{
case _PAGE_CACHE_UC :
    return "uncached";
case _PAGE_CACHE_UC_MINUS :
    return "uncached-minus";
case _PAGE_CACHE_WB :
    return "write-back";
case _PAGE_CACHE_WC :
    return "write-combining";
default :
    return "broken";
}

{
    unsigned long pat_type;
    u8 mtrr_type;
    pat_type = prot & _PAGE_CACHE_MASK;
    prot &= (~_PAGE_CACHE_MASK);
    if (pat_type == _PAGE_CACHE_WC) {
        *ret_prot = prot | _PAGE_CACHE_WC;
        return 0;
    }
    else if (pat_type == _PAGE_CACHE_UC_MINUS) {
        *ret_prot = prot | _PAGE_CACHE_UC_MINUS;
        return 0;
    }
    else if (pat_type == _PAGE_CACHE_UC) {
        *ret_prot = prot | _PAGE_CACHE_UC;
        return 0;
    }
    mtrr_type = mtrr_type_lookup (start, end);
    if (mtrr_type == MTRR_TYPE_UNCACHABLE) {
        *ret_prot = prot | _PAGE_CACHE_UC;
    }
    else if (mtrr_type == MTRR_TYPE_WRCOMB) {
        *ret_prot = prot | _PAGE_CACHE_WC;
    }
    else {
        *ret_prot = prot | _PAGE_CACHE_WB;
    }
    return 0;
}

{
    *ret_prot = prot | _PAGE_CACHE_WC;
    return 0;
}

{
    *ret_prot = prot | _PAGE_CACHE_UC_MINUS;
    return 0;
}

{
    *ret_prot = prot | _PAGE_CACHE_UC;
    return 0;
}

{
    *ret_prot = prot | _PAGE_CACHE_UC;
}

{
    *ret_prot = prot | _PAGE_CACHE_WC;
}

{
    *ret_prot = prot | _PAGE_CACHE_WB;
}

{
    struct memtype *new_entry = NULL;
    struct memtype *parse;
    unsigned long actual_type;
    int err = 0;
    if (!pat_wc_enabled) {
        if (ret_type) {
            if (req_type == -1) {
                *ret_type = _PAGE_CACHE_WB;
            }
            else {
                *ret_type = req_type;
            }
        }
        return 0;
    }
    if (start >= ISA_START_ADDRESS && (end - 1) <= ISA_END_ADDRESS) {
        if (ret_type)
            *ret_type = _PAGE_CACHE_WB;
        return 0;
    }
    if (req_type == -1) {
        u8 mtrr_type = mtrr_type_lookup (start, end);
        if (mtrr_type == MTRR_TYPE_WRBACK) {
            req_type = _PAGE_CACHE_WB;
            actual_type = _PAGE_CACHE_WB;
        }
        else {
            req_type = _PAGE_CACHE_UC_MINUS;
            actual_type = _PAGE_CACHE_UC_MINUS;
        }
    }
    else {
        req_type &= _PAGE_CACHE_MASK;
        err = pat_x_mtrr_type (start, end, req_type, &actual_type);
    }
    if (err) {
        if (ret_type)
            *ret_type = actual_type;
        return -EINVAL;
    }
    new_entry = kmalloc (sizeof (struct memtype), GFP_KERNEL);
    if (!new_entry)
        return -ENOMEM;
    new_entry->start = start;
    new_entry->end = end;
    new_entry->type = actual_type;
    if (ret_type)
        *ret_type = actual_type;
    spin_lock (& memtype_lock);
    list_for_each_entry (parse, &memtype_list, nd) {
        struct memtype *saved_ptr;
        if (parse->start >= end) {
            pr_debug ("New Entry\n");
            list_add (& new_entry -> nd, parse -> nd.prev);
            new_entry = NULL;
            break;
        }
        if (start <= parse->start && end >= parse->start) {
            if (actual_type != parse->type && ret_type) {
                actual_type = parse->type;
                *ret_type = actual_type;
                new_entry->type = actual_type;
            }
            if (actual_type != parse->type) {
                printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
                err = -EBUSY;
                break;
            }
            saved_ptr = parse;
            list_for_each_entry_continue (parse, &memtype_list, nd) {
                if (end <= parse->start) {
                    break;
                }
                if (actual_type != parse->type) {
                    printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
                    err = -EBUSY;
                    break;
                }
            }
            if (err) {
                break;
            }
            pr_debug ("Overlap at 0x%Lx-0x%Lx\n", saved_ptr -> start, saved_ptr -> end);
            list_add (& new_entry -> nd, saved_ptr -> nd.prev);
            new_entry = NULL;
            break;
        }
        if (start < parse->end) {
            if (actual_type != parse->type && ret_type) {
                actual_type = parse->type;
                *ret_type = actual_type;
                new_entry->type = actual_type;
            }
            if (actual_type != parse->type) {
                printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
                err = -EBUSY;
                break;
            }
            saved_ptr = parse;
            list_for_each_entry_continue (parse, &memtype_list, nd) {
                if (end <= parse->start) {
                    break;
                }
                if (actual_type != parse->type) {
                    printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
                    err = -EBUSY;
                    break;
                }
            }
            if (err) {
                break;
            }
            pr_debug (KERN_INFO "Overlap at 0x%Lx-0x%Lx\n", saved_ptr -> start, saved_ptr -> end);
            list_add (& new_entry -> nd, & saved_ptr -> nd);
            new_entry = NULL;
            break;
        }
    }
    if (err) {
        printk (KERN_INFO "reserve_memtype failed 0x%Lx-0x%Lx, track %s, req %s\n", start, end, cattr_name (new_entry -> type), cattr_name (req_type));
        kfree (new_entry);
        spin_unlock (& memtype_lock);
        return err;
    }
    if (new_entry) {
        list_add_tail (& new_entry -> nd, & memtype_list);
        pr_debug ("New Entry\n");
    }
    if (ret_type) {
        pr_debug ("reserve_memtype added 0x%Lx-0x%Lx, track %s, req %s, ret %s\n", start, end, cattr_name (actual_type), cattr_name (req_type), cattr_name (* ret_type));
    }
    else {
        pr_debug ("reserve_memtype added 0x%Lx-0x%Lx, track %s, req %s\n", start, end, cattr_name (actual_type), cattr_name (req_type));
    }
    spin_unlock (& memtype_lock);
    return err;
}

{
    if (ret_type) {
        if (req_type == -1) {
            *ret_type = _PAGE_CACHE_WB;
        }
        else {
            *ret_type = req_type;
        }
    }
    return 0;
}

{
    if (req_type == -1) {
        *ret_type = _PAGE_CACHE_WB;
    }
    else {
        *ret_type = req_type;
    }
}

{
    *ret_type = _PAGE_CACHE_WB;
}

{
    *ret_type = req_type;
}

{
    if (ret_type)
        *ret_type = _PAGE_CACHE_WB;
    return 0;
}

{
    u8 mtrr_type = mtrr_type_lookup (start, end);
    if (mtrr_type == MTRR_TYPE_WRBACK) {
        req_type = _PAGE_CACHE_WB;
        actual_type = _PAGE_CACHE_WB;
    }
    else {
        req_type = _PAGE_CACHE_UC_MINUS;
        actual_type = _PAGE_CACHE_UC_MINUS;
    }
}

{
    req_type = _PAGE_CACHE_WB;
    actual_type = _PAGE_CACHE_WB;
}

{
    req_type = _PAGE_CACHE_UC_MINUS;
    actual_type = _PAGE_CACHE_UC_MINUS;
}

{
    req_type &= _PAGE_CACHE_MASK;
    err = pat_x_mtrr_type (start, end, req_type, &actual_type);
}

{
    if (ret_type)
        *ret_type = actual_type;
    return -EINVAL;
}

{
    struct memtype *saved_ptr;
    if (parse->start >= end) {
        pr_debug ("New Entry\n");
        list_add (& new_entry -> nd, parse -> nd.prev);
        new_entry = NULL;
        break;
    }
    if (start <= parse->start && end >= parse->start) {
        if (actual_type != parse->type && ret_type) {
            actual_type = parse->type;
            *ret_type = actual_type;
            new_entry->type = actual_type;
        }
        if (actual_type != parse->type) {
            printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
            err = -EBUSY;
            break;
        }
        saved_ptr = parse;
        list_for_each_entry_continue (parse, &memtype_list, nd) {
            if (end <= parse->start) {
                break;
            }
            if (actual_type != parse->type) {
                printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
                err = -EBUSY;
                break;
            }
        }
        if (err) {
            break;
        }
        pr_debug ("Overlap at 0x%Lx-0x%Lx\n", saved_ptr -> start, saved_ptr -> end);
        list_add (& new_entry -> nd, saved_ptr -> nd.prev);
        new_entry = NULL;
        break;
    }
    if (start < parse->end) {
        if (actual_type != parse->type && ret_type) {
            actual_type = parse->type;
            *ret_type = actual_type;
            new_entry->type = actual_type;
        }
        if (actual_type != parse->type) {
            printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
            err = -EBUSY;
            break;
        }
        saved_ptr = parse;
        list_for_each_entry_continue (parse, &memtype_list, nd) {
            if (end <= parse->start) {
                break;
            }
            if (actual_type != parse->type) {
                printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
                err = -EBUSY;
                break;
            }
        }
        if (err) {
            break;
        }
        pr_debug (KERN_INFO "Overlap at 0x%Lx-0x%Lx\n", saved_ptr -> start, saved_ptr -> end);
        list_add (& new_entry -> nd, & saved_ptr -> nd);
        new_entry = NULL;
        break;
    }
}

{
    pr_debug ("New Entry\n");
    list_add (& new_entry -> nd, parse -> nd.prev);
    new_entry = NULL;
    break;
}

{
    if (actual_type != parse->type && ret_type) {
        actual_type = parse->type;
        *ret_type = actual_type;
        new_entry->type = actual_type;
    }
    if (actual_type != parse->type) {
        printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
        err = -EBUSY;
