    printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
    err = -EBUSY;
    break;
}

{
    if (end <= parse->start) {
        break;
    }
    if (actual_type != parse->type) {
        printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
        err = -EBUSY;
        break;
    }
}

{
    break;
}

{
    printk (KERN_INFO "%s:%d conflicting memory types %Lx-%Lx %s<->%s\n", current -> comm, current -> pid, start, end, cattr_name (actual_type), cattr_name (parse -> type));
    err = -EBUSY;
    break;
}

{
    break;
}

{
    printk (KERN_INFO "reserve_memtype failed 0x%Lx-0x%Lx, track %s, req %s\n", start, end, cattr_name (new_entry -> type), cattr_name (req_type));
    kfree (new_entry);
    spin_unlock (& memtype_lock);
    return err;
}

{
    list_add_tail (& new_entry -> nd, & memtype_list);
    pr_debug ("New Entry\n");
}

{
    pr_debug ("reserve_memtype added 0x%Lx-0x%Lx, track %s, req %s, ret %s\n", start, end, cattr_name (actual_type), cattr_name (req_type), cattr_name (* ret_type));
}

{
    pr_debug ("reserve_memtype added 0x%Lx-0x%Lx, track %s, req %s\n", start, end, cattr_name (actual_type), cattr_name (req_type));
}

{
    struct memtype *ml;
    int err = -EINVAL;
    if (!pat_wc_enabled) {
        return 0;
    }
    if (start >= ISA_START_ADDRESS && end <= ISA_END_ADDRESS) {
        return 0;
    }
    spin_lock (& memtype_lock);
    list_for_each_entry (ml, &memtype_list, nd) {
        if (ml->start == start && ml->end == end) {
            list_del (& ml -> nd);
            kfree (ml);
            err = 0;
            break;
        }
    }
    spin_unlock (& memtype_lock);
    if (err) {
        printk (KERN_INFO "%s:%d freeing invalid memtype %Lx-%Lx\n", current -> comm, current -> pid, start, end);
    }
    pr_debug ("free_memtype request 0x%Lx-0x%Lx\n", start, end);
    return err;
}

{
    return 0;
}

{
    return 0;
}

{
    if (ml->start == start && ml->end == end) {
        list_del (& ml -> nd);
        kfree (ml);
        err = 0;
        break;
    }
}

{
    list_del (& ml -> nd);
    kfree (ml);
    err = 0;
    break;
}

{
    printk (KERN_INFO "%s:%d freeing invalid memtype %Lx-%Lx\n", current -> comm, current -> pid, start, end);
}

{
    return vma_prot;
}

{
    u64 from = ((u64) pfn) << PAGE_SHIFT;
    u64 to = from + size;
    u64 cursor = from;
    while (cursor < to) {
        if (!devmem_is_allowed (pfn)) {
            printk (KERN_INFO "Program %s tried to access /dev/mem between %Lx->%Lx.\n", current -> comm, from, to);
            return 0;
        }
        cursor += PAGE_SIZE;
        pfn++;
    }
    return 1;
}

{
    if (!devmem_is_allowed (pfn)) {
        printk (KERN_INFO "Program %s tried to access /dev/mem between %Lx->%Lx.\n", current -> comm, from, to);
        return 0;
    }
    cursor += PAGE_SIZE;
    pfn++;
}

{
    printk (KERN_INFO "Program %s tried to access /dev/mem between %Lx->%Lx.\n", current -> comm, from, to);
    return 0;
}

{
    u64 offset = ((u64) pfn) << PAGE_SHIFT;
    unsigned long flags = _PAGE_CACHE_UC_MINUS;
    int retval;
    if (!range_is_allowed (pfn, size))
        return 0;
    if (file->f_flags & O_SYNC) {
        flags = _PAGE_CACHE_UC;
    }
    if (flags != _PAGE_CACHE_UC_MINUS) {
        retval = reserve_memtype (offset, offset +size, flags, NULL);
    }
    else {
        retval = reserve_memtype (offset, offset +size, -1, &flags);
    }
    if (retval < 0)
        return 0;
    if (pfn <= max_pfn_mapped && ioremap_change_attr ((unsigned long) __va (offset), size, flags) < 0) {
        free_memtype (offset, offset + size);
        printk (KERN_INFO "%s:%d /dev/mem ioremap_change_attr failed %s for %Lx-%Lx\n", current -> comm, current -> pid, cattr_name (flags), offset, (unsigned long long) (offset + size));
        return 0;
    }
    *vma_prot = __pgprot ((pgprot_val (*vma_prot) & ~_PAGE_CACHE_MASK) | flags);
    return 1;
}

{
    flags = _PAGE_CACHE_UC;
}

{
    retval = reserve_memtype (offset, offset +size, flags, NULL);
}

{
    retval = reserve_memtype (offset, offset +size, -1, &flags);
}

{
    free_memtype (offset, offset + size);
    printk (KERN_INFO "%s:%d /dev/mem ioremap_change_attr failed %s for %Lx-%Lx\n", current -> comm, current -> pid, cattr_name (flags), offset, (unsigned long long) (offset + size));
    return 0;
}

{
    u64 addr = (u64) pfn << PAGE_SHIFT;
    unsigned long flags;
    unsigned long want_flags = (pgprot_val (vma_prot) & _PAGE_CACHE_MASK);
    reserve_memtype (addr, addr + size, want_flags, & flags);
    if (flags != want_flags) {
        printk (KERN_INFO "%s:%d /dev/mem expected mapping type %s for %Lx-%Lx, got %s\n", current -> comm, current -> pid, cattr_name (want_flags), addr, (unsigned long long) (addr + size), cattr_name (flags));
    }
}

{
    printk (KERN_INFO "%s:%d /dev/mem expected mapping type %s for %Lx-%Lx, got %s\n", current -> comm, current -> pid, cattr_name (want_flags), addr, (unsigned long long) (addr + size), cattr_name (flags));
}

{
    u64 addr = (u64) pfn << PAGE_SHIFT;
    free_memtype (addr, addr + size);
}

{
    unsigned long addr, end;
    int i, res = -1;
    memset (memnodemap, 0xff, sizeof (s16) * memnodemapsize);
    for (i = 0; i < numnodes; i++) {
        addr = nodes[i].start;
        end = nodes[i].end;
        if (addr >= end)
            continue;
        if ((end >> shift) >= memnodemapsize)
            return 0;
        do {
            if (memnodemap[addr >> shift] != NUMA_NO_NODE)
                return -1;
            if (!nodeids)
                memnodemap[addr >> shift] = i;
            else
                memnodemap[addr >> shift] = nodeids[i];
            addr += (1UL << shift);
        }
        while (addr < end);
        res = 1;
    }
    return res;
}

{
    addr = nodes[i].start;
    end = nodes[i].end;
    if (addr >= end)
        continue;
    if ((end >> shift) >= memnodemapsize)
        return 0;
    do {
        if (memnodemap[addr >> shift] != NUMA_NO_NODE)
            return -1;
        if (!nodeids)
            memnodemap[addr >> shift] = i;
        else
            memnodemap[addr >> shift] = nodeids[i];
        addr += (1UL << shift);
    }
    while (addr < end);
    res = 1;
}

{
    if (memnodemap[addr >> shift] != NUMA_NO_NODE)
        return -1;
    if (!nodeids)
        memnodemap[addr >> shift] = i;
    else
        memnodemap[addr >> shift] = nodeids[i];
    addr += (1UL << shift);
}

{
    unsigned long addr;
    memnodemap = memnode.embedded_map;
    if (memnodemapsize <= ARRAY_SIZE (memnode.embedded_map))
        return 0;
    addr = 0x8000;
    nodemap_size = round_up (sizeof (s16) * memnodemapsize, L1_CACHE_BYTES);
    nodemap_addr = find_e820_area (addr, end_pfn << PAGE_SHIFT, nodemap_size, L1_CACHE_BYTES);
    if (nodemap_addr == -1UL) {
        printk (KERN_ERR "NUMA: Unable to allocate Memory to Node hash map\n");
        nodemap_addr = nodemap_size = 0;
        return -1;
    }
    memnodemap = phys_to_virt (nodemap_addr);
    reserve_early (nodemap_addr, nodemap_addr + nodemap_size, "MEMNODEMAP");
    printk (KERN_DEBUG "NUMA: Allocated memnodemap from %lx - %lx\n", nodemap_addr, nodemap_addr + nodemap_size);
    return 0;
}

{
    printk (KERN_ERR "NUMA: Unable to allocate Memory to Node hash map\n");
    nodemap_addr = nodemap_size = 0;
    return -1;
}

{
    int i, nodes_used = 0;
    unsigned long start, end;
    unsigned long bitfield = 0, memtop = 0;
    for (i = 0; i < numnodes; i++) {
        start = nodes[i].start;
        end = nodes[i].end;
        if (start >= end)
            continue;
        bitfield |= start;
        nodes_used++;
        if (end > memtop)
            memtop = end;
    }
    if (nodes_used <= 1)
        i = 63;
    else
        i = find_first_bit (&bitfield, sizeof (unsigned long) * 8);
    memnodemapsize = (memtop >> i) + 1;
    return i;
}

{
    start = nodes[i].start;
    end = nodes[i].end;
    if (start >= end)
        continue;
    bitfield |= start;
    nodes_used++;
    if (end > memtop)
        memtop = end;
}

{
    int shift;
    shift = extract_lsb_from_nodes (nodes, numnodes);
    if (allocate_cachealigned_memnodemap ())
        return -1;
    printk (KERN_DEBUG "NUMA: Using %d for the hash shift.\n", shift);
    if (populate_memnodemap (nodes, numnodes, shift, nodeids) != 1) {
        printk (KERN_INFO "Your memory is not aligned you need to " "rebuild your kernel with a bigger NODEMAPSIZE " "shift=%d\n", shift);
        return -1;
    }
    return shift;
}

{
    printk (KERN_INFO "Your memory is not aligned you need to " "rebuild your kernel with a bigger NODEMAPSIZE " "shift=%d\n", shift);
    return -1;
}

{
    return phys_to_nid (pfn << PAGE_SHIFT);
}

{
    unsigned long mem = find_e820_area (start, end, size, align);
    void *ptr;
    if (mem != -1L)
        return __va (mem);
    ptr = __alloc_bootmem_nopanic (size, align, __pa (MAX_DMA_ADDRESS));
    if (ptr == NULL) {
        printk (KERN_ERR "Cannot find %lu bytes in node %d\n", size, nodeid);
        return NULL;
    }
    return ptr;
}

{
    printk (KERN_ERR "Cannot find %lu bytes in node %d\n", size, nodeid);
    return NULL;
}

{
    unsigned long start_pfn, end_pfn, bootmap_pages, bootmap_size;
    unsigned long bootmap_start, nodedata_phys;
    void *bootmap;
    const int pgdat_size = round_up (sizeof (pg_data_t), PAGE_SIZE);
    int nid;
    start = round_up (start, ZONE_ALIGN);
    printk (KERN_INFO "Bootmem setup node %d %016lx-%016lx\n", nodeid, start, end);
    start_pfn = start >> PAGE_SHIFT;
    end_pfn = end >> PAGE_SHIFT;
    node_data[nodeid] = early_node_mem (nodeid, start, end, pgdat_size, SMP_CACHE_BYTES);
    if (node_data[nodeid] == NULL)
        return;
    nodedata_phys = __pa (node_data[nodeid]);
    printk (KERN_INFO "  NODE_DATA [%016lx - %016lx]\n", nodedata_phys, nodedata_phys + pgdat_size - 1);
    memset (NODE_DATA (nodeid), 0, sizeof (pg_data_t));
    NODE_DATA (nodeid)->bdata = &plat_node_bdata[nodeid];
    NODE_DATA (nodeid)->node_start_pfn = start_pfn;
    NODE_DATA (nodeid)->node_spanned_pages = end_pfn - start_pfn;
    bootmap_pages = bootmem_bootmap_pages (end_pfn -start_pfn);
    nid = phys_to_nid (nodedata_phys);
    if (nid == nodeid)
        bootmap_start = round_up (nodedata_phys +pgdat_size, PAGE_SIZE);
    else
        bootmap_start = round_up (start, PAGE_SIZE);
    bootmap = early_node_mem (nodeid, bootmap_start, end, bootmap_pages << PAGE_SHIFT, PAGE_SIZE);
    if (bootmap == NULL) {
        if (nodedata_phys < start || nodedata_phys >= end)
            free_bootmem (nodedata_phys, pgdat_size);
        node_data[nodeid] = NULL;
        return;
    }
    bootmap_start = __pa (bootmap);
    bootmap_size = init_bootmem_node (NODE_DATA (nodeid), bootmap_start >> PAGE_SHIFT, start_pfn, end_pfn);
    printk (KERN_INFO "  bootmap [%016lx -  %016lx] pages %lx\n", bootmap_start, bootmap_start + bootmap_size - 1, bootmap_pages);
    free_bootmem_with_active_regions (nodeid, end);
    early_res_to_bootmem (start, end);
    if (nid != nodeid)
        printk (KERN_INFO "    NODE_DATA(%d) on node %d\n", nodeid, nid);
    else
        reserve_bootmem_node (NODE_DATA (nodeid), nodedata_phys, pgdat_size, BOOTMEM_DEFAULT);
    nid = phys_to_nid (bootmap_start);
    if (nid != nodeid)
        printk (KERN_INFO "    bootmap(%d) on node %d\n", nodeid, nid);
    else
        reserve_bootmem_node (NODE_DATA (nodeid), bootmap_start, bootmap_pages << PAGE_SHIFT, BOOTMEM_DEFAULT);
    node_set_online (nodeid);
}

{
    if (nodedata_phys < start || nodedata_phys >= end)
        free_bootmem (nodedata_phys, pgdat_size);
    node_data[nodeid] = NULL;
    return;
}

{
    int rr, i;
    rr = first_node (node_online_map);
    for (i = 0; i < NR_CPUS; i++) {
        if (early_cpu_to_node (i) != NUMA_NO_NODE)
            continue;
        numa_set_node (i, rr);
        rr = next_node (rr, node_online_map);
        if (rr == MAX_NUMNODES)
            rr = first_node (node_online_map);
    }
}

{
    if (early_cpu_to_node (i) != NUMA_NO_NODE)
        continue;
    numa_set_node (i, rr);
    rr = next_node (rr, node_online_map);
    if (rr == MAX_NUMNODES)
        rr = first_node (node_online_map);
}

{
    int i;
    nodes_clear (node_possible_map);
    nodes_clear (node_online_map);
    printk (KERN_INFO "%s\n", numa_off ? "NUMA turned off" : "No NUMA configuration found");
    printk (KERN_INFO "Faking a node at %016lx-%016lx\n", start_pfn << PAGE_SHIFT, end_pfn << PAGE_SHIFT);
    memnode_shift = 63;
    memnodemap = memnode.embedded_map;
    memnodemap[0] = 0;
    node_set_online (0);
    node_set (0, node_possible_map);
    for (i = 0; i < NR_CPUS; i++)
        numa_set_node (i, 0);
    memset (& node_to_cpumask_map [0], 0, sizeof (node_to_cpumask_map [0]));
    cpu_set (0, node_to_cpumask_map [0]);
    e820_register_active_regions (0, start_pfn, end_pfn);
    setup_node_bootmem (0, start_pfn << PAGE_SHIFT, end_pfn << PAGE_SHIFT);
}

{
    set_bit (cpu, (unsigned long *) & node_to_cpumask_map [early_cpu_to_node (cpu)]);
}

{
    int *cpu_to_node_map = x86_cpu_to_node_map_early_ptr;
    if (cpu_to_node_map)
        cpu_to_node_map[cpu] = node;
    else if (per_cpu_offset (cpu))
        per_cpu (x86_cpu_to_node_map, cpu) = node;
    else
        Dprintk (KERN_INFO "Setting node for non-present cpu %d\n", cpu);
}

{
    unsigned long pages = 0;
    int i;
    for_each_online_node (i)
    pages += free_all_bootmem_node (NODE_DATA (i));
    return pages;
}

{
    unsigned long max_zone_pfns [MAX_NR_ZONES];
    memset (max_zone_pfns, 0, sizeof (max_zone_pfns));
    max_zone_pfns[ZONE_DMA] = MAX_DMA_PFN;
    max_zone_pfns[ZONE_DMA32] = MAX_DMA32_PFN;
    max_zone_pfns[ZONE_NORMAL] = end_pfn;
    sparse_memory_present_with_active_regions (MAX_NUMNODES);
    sparse_init ();
    free_area_init_nodes (max_zone_pfns);
}

{
    if (!opt)
        return -EINVAL;
    if (!strncmp (opt, "off", 3))
        numa_off = 1;
    return 0;
}

{
    int i;
    for (i = 0; i < NR_CPUS; i++) {
        int node;
        u16 apicid = x86_cpu_to_apicid_init[i];
        if (apicid == BAD_APICID)
            continue;
        node = apicid_to_node[apicid];
        if (node == NUMA_NO_NODE)
            continue;
        if (!node_online (node))
            continue;
        numa_set_node (i, node);
    }
}

{
    int node;
    u16 apicid = x86_cpu_to_apicid_init[i];
    if (apicid == BAD_APICID)
        continue;
    node = apicid_to_node[apicid];
    if (node == NUMA_NO_NODE)
        continue;
    if (!node_online (node))
        continue;
    numa_set_node (i, node);
}

{
    int num;
    for (num = 0; num < 32; num++) {
        u32 header;
        header = read_pci_config (0, num, 0, 0x00);
        if (header != (PCI_VENDOR_ID_AMD | (0x1100 << 16)) && header != (PCI_VENDOR_ID_AMD | (0x1200 << 16)) && header != (PCI_VENDOR_ID_AMD | (0x1300 << 16)))
            continue;
        header = read_pci_config (0, num, 1, 0x00);
        if (header != (PCI_VENDOR_ID_AMD | (0x1101 << 16)) && header != (PCI_VENDOR_ID_AMD | (0x1201 << 16)) && header != (PCI_VENDOR_ID_AMD | (0x1301 << 16)))
            continue;
        return num;
    }
    return -1;
}

{
    u32 header;
    header = read_pci_config (0, num, 0, 0x00);
    if (header != (PCI_VENDOR_ID_AMD | (0x1100 << 16)) && header != (PCI_VENDOR_ID_AMD | (0x1200 << 16)) && header != (PCI_VENDOR_ID_AMD | (0x1300 << 16)))
        continue;
    header = read_pci_config (0, num, 1, 0x00);
    if (header != (PCI_VENDOR_ID_AMD | (0x1101 << 16)) && header != (PCI_VENDOR_ID_AMD | (0x1201 << 16)) && header != (PCI_VENDOR_ID_AMD | (0x1301 << 16)))
        continue;
    return num;
}

{
    early_find_smp_config ();
    if (smp_found_config)
        early_get_smp_config ();
    early_init_lapic_mapping ();
}

{
    unsigned long prevbase;
    struct bootnode nodes [8];
    int nodeid, i, nb;
    unsigned char nodeids [8];
    int found = 0;
    u32 reg;
    unsigned numnodes;
    unsigned cores;
    unsigned bits;
    int j;
    unsigned apicid_base;
    if (!early_pci_allowed ())
        return -1;
    nb = find_northbridge ();
    if (nb < 0)
        return nb;
    printk (KERN_INFO "Scanning NUMA topology in Northbridge %d\n", nb);
    reg = read_pci_config (0, nb, 0, 0x60);
    numnodes = ((reg >> 4) & 0xF) + 1;
    if (numnodes <= 1)
        return -1;
    printk (KERN_INFO "Number of nodes %d\n", numnodes);
    memset (& nodes, 0, sizeof (nodes));
    prevbase = 0;
    for (i = 0; i < 8; i++) {
        unsigned long base, limit;
        u32 nodeid;
        base = read_pci_config (0, nb, 1, 0x40 + i * 8);
        limit = read_pci_config (0, nb, 1, 0x44 + i * 8);
        nodeid = limit & 7;
        nodeids[i] = nodeid;
        if ((base & 3) == 0) {
            if (i < numnodes)
                printk ("Skipping disabled node %d\n", i);
            continue;
        }
        if (nodeid >= numnodes) {
            printk ("Ignoring excess node %d (%lx:%lx)\n", nodeid, base, limit);
            continue;
        }
        if (!limit) {
            printk (KERN_INFO "Skipping node entry %d (base %lx)\n", i, base);
            continue;
        }
        if ((base >> 8) & 3 || (limit >> 8) & 3) {
            printk (KERN_ERR "Node %d using interleaving mode %lx/%lx\n", nodeid, (base >> 8) & 3, (limit >> 8) & 3);
            return -1;
        }
        if (node_isset (nodeid, node_possible_map)) {
            printk (KERN_INFO "Node %d already present. Skipping\n", nodeid);
            continue;
        }
        limit >>= 16;
        limit <<= 24;
        limit |= (1 << 24) - 1;
        limit++;
        if (limit > end_pfn << PAGE_SHIFT)
            limit = end_pfn << PAGE_SHIFT;
        if (limit <= base)
            continue;
        base >>= 16;
        base <<= 24;
        if (base < start)
            base = start;
        if (limit > end)
            limit = end;
        if (limit == base) {
            printk (KERN_ERR "Empty node %d\n", nodeid);
            continue;
        }
        if (limit < base) {
            printk (KERN_ERR "Node %d bogus settings %lx-%lx.\n", nodeid, base, limit);
            continue;
        }
        if (prevbase > base) {
            printk (KERN_ERR "Node map not sorted %lx,%lx\n", prevbase, base);
            return -1;
        }
        printk (KERN_INFO "Node %d MemBase %016lx Limit %016lx\n", nodeid, base, limit);
        found++;
        nodes[nodeid].start = base;
        nodes[nodeid].end = limit;
        e820_register_active_regions (nodeid, nodes [nodeid].start >> PAGE_SHIFT, nodes [nodeid].end >> PAGE_SHIFT);
        prevbase = base;
        node_set (nodeid, node_possible_map);
    }
    if (!found)
        return -1;
    memnode_shift = compute_hash_shift (nodes, 8, NULL);
    if (memnode_shift < 0) {
        printk (KERN_ERR "No NUMA node hash function found. Contact maintainer\n");
        return -1;
    }
    printk (KERN_INFO "Using node hash shift of %d\n", memnode_shift);
    bits = boot_cpu_data.x86_coreid_bits;
    cores = (1 << bits);
    apicid_base = 0;
    early_get_boot_cpu_id ();
    if (boot_cpu_physical_apicid > 0) {
        printk (KERN_INFO "BSP APIC ID: %02x\n", boot_cpu_physical_apicid);
        apicid_base = boot_cpu_physical_apicid;
    }
    for (i = 0; i < 8; i++) {
        if (nodes[i].start != nodes[i].end) {
            nodeid = nodeids[i];
            for (j = apicid_base; j < cores + apicid_base; j++)
                apicid_to_node[(nodeid << bits) + j] = i;
            setup_node_bootmem (i, nodes [i].start, nodes [i].end);
        }
    }
    numa_init_array ();
    return 0;
}

{
    unsigned long base, limit;
    u32 nodeid;
    base = read_pci_config (0, nb, 1, 0x40 + i * 8);
    limit = read_pci_config (0, nb, 1, 0x44 + i * 8);
    nodeid = limit & 7;
    nodeids[i] = nodeid;
    if ((base & 3) == 0) {
        if (i < numnodes)
            printk ("Skipping disabled node %d\n", i);
        continue;
    }
    if (nodeid >= numnodes) {
        printk ("Ignoring excess node %d (%lx:%lx)\n", nodeid, base, limit);
        continue;
    }
    if (!limit) {
        printk (KERN_INFO "Skipping node entry %d (base %lx)\n", i, base);
        continue;
    }
    if ((base >> 8) & 3 || (limit >> 8) & 3) {
        printk (KERN_ERR "Node %d using interleaving mode %lx/%lx\n", nodeid, (base >> 8) & 3, (limit >> 8) & 3);
        return -1;
    }
    if (node_isset (nodeid, node_possible_map)) {
        printk (KERN_INFO "Node %d already present. Skipping\n", nodeid);
        continue;
    }
    limit >>= 16;
    limit <<= 24;
    limit |= (1 << 24) - 1;
    limit++;
    if (limit > end_pfn << PAGE_SHIFT)
        limit = end_pfn << PAGE_SHIFT;
    if (limit <= base)
        continue;
    base >>= 16;
    base <<= 24;
    if (base < start)
        base = start;
    if (limit > end)
        limit = end;
    if (limit == base) {
        printk (KERN_ERR "Empty node %d\n", nodeid);
        continue;
    }
    if (limit < base) {
        printk (KERN_ERR "Node %d bogus settings %lx-%lx.\n", nodeid, base, limit);
        continue;
    }
    if (prevbase > base) {
        printk (KERN_ERR "Node map not sorted %lx,%lx\n", prevbase, base);
        return -1;
    }
    printk (KERN_INFO "Node %d MemBase %016lx Limit %016lx\n", nodeid, base, limit);
    found++;
    nodes[nodeid].start = base;
    nodes[nodeid].end = limit;
    e820_register_active_regions (nodeid, nodes [nodeid].start >> PAGE_SHIFT, nodes [nodeid].end >> PAGE_SHIFT);
    prevbase = base;
    node_set (nodeid, node_possible_map);
}

{
    if (i < numnodes)
        printk ("Skipping disabled node %d\n", i);
    continue;
}

{
    printk ("Ignoring excess node %d (%lx:%lx)\n", nodeid, base, limit);
    continue;
}

{
    printk (KERN_INFO "Skipping node entry %d (base %lx)\n", i, base);
    continue;
}

{
    printk (KERN_ERR "Node %d using interleaving mode %lx/%lx\n", nodeid, (base >> 8) & 3, (limit >> 8) & 3);
    return -1;
}

{
    printk (KERN_INFO "Node %d already present. Skipping\n", nodeid);
    continue;
}

{
    printk (KERN_ERR "Empty node %d\n", nodeid);
    continue;
}

{
    printk (KERN_ERR "Node %d bogus settings %lx-%lx.\n", nodeid, base, limit);
    continue;
}

{
    printk (KERN_ERR "Node map not sorted %lx,%lx\n", prevbase, base);
    return -1;
}

{
    printk (KERN_ERR "No NUMA node hash function found. Contact maintainer\n");
    return -1;
}

{
    printk (KERN_INFO "BSP APIC ID: %02x\n", boot_cpu_physical_apicid);
    apicid_base = boot_cpu_physical_apicid;
}

{
    if (nodes[i].start != nodes[i].end) {
        nodeid = nodeids[i];
        for (j = apicid_base; j < cores + apicid_base; j++)
            apicid_to_node[(nodeid << bits) + j] = i;
        setup_node_bootmem (i, nodes [i].start, nodes [i].end);
    }
}

{
    nodeid = nodeids[i];
    for (j = apicid_base; j < cores + apicid_base; j++)
        apicid_to_node[(nodeid << bits) + j] = i;
    setup_node_bootmem (i, nodes [i].start, nodes [i].end);
}

{
    direct_gbpages = 0;
    return 0;
}

{
    direct_gbpages = 1;
    return 0;
}

{
    long i, total = 0, reserved = 0;
    long shared = 0, cached = 0;
    struct page *page;
    pg_data_t *pgdat;
    printk (KERN_INFO "Mem-info:\n");
    show_free_areas ();

    for_each_online_pgdat (pgdat) {
        for (i = 0; i < pgdat->node_spanned_pages; ++i) {
            if (unlikely (i % MAX_ORDER_NR_PAGES == 0))
                touch_nmi_watchdog ();
            if (!pfn_valid (pgdat->node_start_pfn + i))
                continue;
            page = pfn_to_page (pgdat->node_start_pfn + i);
            total++;
            if (PageReserved (page))
                reserved++;
            else if (PageSwapCache (page))
                cached++;
            else if (page_count (page))
                shared += page_count (page) - 1;
        }
    }

    printk (KERN_INFO "%lu pages of RAM\n", total);
    printk (KERN_INFO "%lu reserved pages\n", reserved);
    printk (KERN_INFO "%lu pages shared\n", shared);
    printk (KERN_INFO "%lu pages swap cached\n", cached);
}

{
    for (i = 0; i < pgdat->node_spanned_pages; ++i) {
        if (unlikely (i % MAX_ORDER_NR_PAGES == 0))
            touch_nmi_watchdog ();
        if (!pfn_valid (pgdat->node_start_pfn + i))
            continue;
        page = pfn_to_page (pgdat->node_start_pfn + i);
        total++;
        if (PageReserved (page))
            reserved++;
        else if (PageSwapCache (page))
            cached++;
        else if (page_count (page))
            shared += page_count (page) - 1;
    }
}

{
    if (unlikely (i % MAX_ORDER_NR_PAGES == 0))
        touch_nmi_watchdog ();
    if (!pfn_valid (pgdat->node_start_pfn + i))
        continue;
    page = pfn_to_page (pgdat->node_start_pfn + i);
    total++;
    if (PageReserved (page))
        reserved++;
    else if (PageSwapCache (page))
        cached++;
    else if (page_count (page))
        shared += page_count (page) - 1;
}

{
    void *ptr;
    if (after_bootmem)
        ptr = (void *) get_zeroed_page (GFP_ATOMIC);
    else
        ptr = alloc_bootmem_pages (PAGE_SIZE);
    if (!ptr || ((unsigned long) ptr & ~PAGE_MASK)) {
        panic ("set_pte_phys: cannot allocate page data %s\n", after_bootmem ? "after bootmem" : "");
    }
    pr_debug ("spp_getpage %p\n", ptr);
    return ptr;
}

{
    panic ("set_pte_phys: cannot allocate page data %s\n", after_bootmem ? "after bootmem" : "");
}

{
    pgd_t *pgd;
    pud_t *pud;
    pmd_t *pmd;
    pte_t *pte, new_pte;
    pr_debug ("set_pte_phys %lx to %lx\n", vaddr, phys);
    pgd = pgd_offset_k (vaddr);
    if (pgd_none (*pgd)) {
        printk (KERN_ERR "PGD FIXMAP MISSING, it should be setup in head.S!\n");
        return;
    }
    pud = pud_offset (pgd, vaddr);
    if (pud_none (*pud)) {
        pmd = (pmd_t *) spp_getpage ();
        set_pud (pud, __pud (__pa (pmd) | _KERNPG_TABLE | _PAGE_USER));
        if (pmd != pmd_offset (pud, 0)) {
            printk (KERN_ERR "PAGETABLE BUG #01! %p <-> %p\n", pmd, pmd_offset (pud, 0));
            return;
        }
    }
    pmd = pmd_offset (pud, vaddr);
    if (pmd_none (*pmd)) {
        pte = (pte_t *) spp_getpage ();
        set_pmd (pmd, __pmd (__pa (pte) | _KERNPG_TABLE | _PAGE_USER));
        if (pte != pte_offset_kernel (pmd, 0)) {
            printk (KERN_ERR "PAGETABLE BUG #02!\n");
            return;
        }
    }
    new_pte = pfn_pte (phys >> PAGE_SHIFT, prot);
    pte = pte_offset_kernel (pmd, vaddr);
    if (!pte_none (*pte) && pte_val (new_pte) && pte_val (*pte) != (pte_val (new_pte) & __supported_pte_mask))
        pte_ERROR (*pte);
    set_pte (pte, new_pte);
    __flush_tlb_one (vaddr);
}

{
    printk (KERN_ERR "PGD FIXMAP MISSING, it should be setup in head.S!\n");
    return;
}

{
    pmd = (pmd_t *) spp_getpage ();
    set_pud (pud, __pud (__pa (pmd) | _KERNPG_TABLE | _PAGE_USER));
    if (pmd != pmd_offset (pud, 0)) {
        printk (KERN_ERR "PAGETABLE BUG #01! %p <-> %p\n", pmd, pmd_offset (pud, 0));
        return;
    }
}

{
    printk (KERN_ERR "PAGETABLE BUG #01! %p <-> %p\n", pmd, pmd_offset (pud, 0));
    return;
}

{
    pte = (pte_t *) spp_getpage ();
    set_pmd (pmd, __pmd (__pa (pte) | _KERNPG_TABLE | _PAGE_USER));
    if (pte != pte_offset_kernel (pmd, 0)) {
        printk (KERN_ERR "PAGETABLE BUG #02!\n");
        return;
    }
}

{
    printk (KERN_ERR "PAGETABLE BUG #02!\n");
    return;
}

{
    unsigned long vaddr = __START_KERNEL_map;
    unsigned long end = round_up ((unsigned long) _end, PMD_SIZE) - 1;
    pmd_t *pmd = level2_kernel_pgt;
    pmd_t *last_pmd = pmd + PTRS_PER_PMD;
    for (; pmd < last_pmd; pmd++, vaddr += PMD_SIZE) {
        if (pmd_none (*pmd))
            continue;
        if (vaddr < (unsigned long) _text || vaddr > end)
            set_pmd (pmd, __pmd (0));
    }
}

{
    if (pmd_none (*pmd))
        continue;
    if (vaddr < (unsigned long) _text || vaddr > end)
        set_pmd (pmd, __pmd (0));
}

{
    unsigned long address = __fix_to_virt (idx);
    if (idx >= __end_of_fixed_addresses) {
        printk (KERN_ERR "Invalid __set_fixmap\n");
        return;
    }
    set_pte_phys (address, phys, prot);
}

{
    printk (KERN_ERR "Invalid __set_fixmap\n");
    return;
}

{
    unsigned long pfn = table_end++;
    void *adr;
    if (after_bootmem) {
        adr = (void *) get_zeroed_page (GFP_ATOMIC);
        *phys = __pa (adr);
        return adr;
    }
    if (pfn >= end_pfn)
        panic ("alloc_low_page: ran out of memory");
    adr = early_ioremap (pfn *PAGE_SIZE, PAGE_SIZE);
    memset (adr, 0, PAGE_SIZE);
    *phys = pfn * PAGE_SIZE;
    return adr;
}

{
    adr = (void *) get_zeroed_page (GFP_ATOMIC);
    *phys = __pa (adr);
    return adr;
}

{
    if (after_bootmem)
        return;
    early_iounmap (adr, PAGE_SIZE);
}

{
    pmd_t *pmd, *last_pmd;
    unsigned long vaddr;
    int i, pmds;
    pmds = ((addr & ~PMD_MASK) + size + ~PMD_MASK) / PMD_SIZE;
    vaddr = __START_KERNEL_map;
    pmd = level2_kernel_pgt;
    last_pmd = level2_kernel_pgt + PTRS_PER_PMD - 1;
    for (; pmd <= last_pmd; pmd++, vaddr += PMD_SIZE) {
        for (i = 0; i < pmds; i++) {
            if (pmd_present (pmd[i]))
                goto continue_outer_loop;
        }
        vaddr += addr & ~PMD_MASK;
        addr &= PMD_MASK;
        for (i = 0; i < pmds; i++, addr += PMD_SIZE)
            set_pmd (pmd +i, __pmd (addr | __PAGE_KERNEL_LARGE_EXEC));
        __flush_tlb_all ();
        return (void *) vaddr;
    continue_outer_loop :
        ;
    }
    printk (KERN_ERR "early_ioremap(0x%lx, %lu) failed\n", addr, size);
    return NULL;
}

{
    for (i = 0; i < pmds; i++) {
        if (pmd_present (pmd[i]))
            goto continue_outer_loop;
    }
    vaddr += addr & ~PMD_MASK;
    addr &= PMD_MASK;
    for (i = 0; i < pmds; i++, addr += PMD_SIZE)
        set_pmd (pmd +i, __pmd (addr | __PAGE_KERNEL_LARGE_EXEC));
    __flush_tlb_all ();
    return (void *) vaddr;
continue_outer_loop :
    ;
}

{
    if (pmd_present (pmd[i]))
        goto continue_outer_loop;
}

{
    unsigned long vaddr;
    pmd_t *pmd;
    int i, pmds;
    vaddr = (unsigned long) addr;
    pmds = ((vaddr & ~PMD_MASK) + size + ~PMD_MASK) / PMD_SIZE;
    pmd = level2_kernel_pgt + pmd_index (vaddr);
    for (i = 0; i < pmds; i++)
        pmd_clear (pmd +i);
    __flush_tlb_all ();
}

{
    int i = pmd_index (address);
    for (; i < PTRS_PER_PMD; i++, address += PMD_SIZE) {
        pmd_t *pmd = pmd_page + pmd_index (address);
        if (address >= end) {
            if (!after_bootmem) {
                for (; i < PTRS_PER_PMD; i++, pmd++)
                    set_pmd (pmd, __pmd (0));
            }
            break;
        }
        if (pmd_val (*pmd))
            continue;
        set_pte ((pte_t *) pmd, pfn_pte (address >> PAGE_SHIFT, PAGE_KERNEL_LARGE));
    }
    return address;
}

{
    pmd_t *pmd = pmd_page + pmd_index (address);
    if (address >= end) {
        if (!after_bootmem) {
            for (; i < PTRS_PER_PMD; i++, pmd++)
                set_pmd (pmd, __pmd (0));
        }
        break;
    }
    if (pmd_val (*pmd))
        continue;
    set_pte ((pte_t *) pmd, pfn_pte (address >> PAGE_SHIFT, PAGE_KERNEL_LARGE));
}

{
    if (!after_bootmem) {
        for (; i < PTRS_PER_PMD; i++, pmd++)
            set_pmd (pmd, __pmd (0));
    }
    break;
}

{
    for (; i < PTRS_PER_PMD; i++, pmd++)
        set_pmd (pmd, __pmd (0));
}

{
    pmd_t *pmd = pmd_offset (pud, 0);
    unsigned long last_map_addr;
    spin_lock (& init_mm.page_table_lock);
    last_map_addr = phys_pmd_init (pmd, address, end);
    spin_unlock (& init_mm.page_table_lock);
    __flush_tlb_all ();
    return last_map_addr;
}

{
    unsigned long last_map_addr = end;
    int i = pud_index (addr);
    for (; i < PTRS_PER_PUD; i++, addr = (addr & PUD_MASK) + PUD_SIZE) {
        unsigned long pmd_phys;
        pud_t *pud = pud_page + pud_index (addr);
        pmd_t *pmd;
        if (addr >= end)
            break;
        if (!after_bootmem && !e820_any_mapped (addr, addr +PUD_SIZE, 0)) {
            set_pud (pud, __pud (0));
            continue;
        }
        if (pud_val (*pud)) {
            if (!pud_large (*pud))
                last_map_addr = phys_pmd_update (pud, addr, end);
            continue;
        }
        if (direct_gbpages) {
            set_pte ((pte_t *) pud, pfn_pte (addr >> PAGE_SHIFT, PAGE_KERNEL_LARGE));
            last_map_addr = (addr & PUD_MASK) + PUD_SIZE;
            continue;
        }
        pmd = alloc_low_page (&pmd_phys);
        spin_lock (& init_mm.page_table_lock);
        set_pud (pud, __pud (pmd_phys | _KERNPG_TABLE));
        last_map_addr = phys_pmd_init (pmd, addr, end);
        spin_unlock (& init_mm.page_table_lock);
        unmap_low_page (pmd);
    }
    __flush_tlb_all ();
    return last_map_addr >> PAGE_SHIFT;
}

{
    unsigned long pmd_phys;
    pud_t *pud = pud_page + pud_index (addr);
    pmd_t *pmd;
    if (addr >= end)
        break;
    if (!after_bootmem && !e820_any_mapped (addr, addr +PUD_SIZE, 0)) {
        set_pud (pud, __pud (0));
        continue;
    }
    if (pud_val (*pud)) {
        if (!pud_large (*pud))
            last_map_addr = phys_pmd_update (pud, addr, end);
        continue;
    }
    if (direct_gbpages) {
        set_pte ((pte_t *) pud, pfn_pte (addr >> PAGE_SHIFT, PAGE_KERNEL_LARGE));
        last_map_addr = (addr & PUD_MASK) + PUD_SIZE;
        continue;
    }
    pmd = alloc_low_page (&pmd_phys);
    spin_lock (& init_mm.page_table_lock);
    set_pud (pud, __pud (pmd_phys | _KERNPG_TABLE));
    last_map_addr = phys_pmd_init (pmd, addr, end);
    spin_unlock (& init_mm.page_table_lock);
    unmap_low_page (pmd);
}

{
    set_pud (pud, __pud (0));
    continue;
}

{
    if (!pud_large (*pud))
        last_map_addr = phys_pmd_update (pud, addr, end);
    continue;
}

{
    set_pte ((pte_t *) pud, pfn_pte (addr >> PAGE_SHIFT, PAGE_KERNEL_LARGE));
    last_map_addr = (addr & PUD_MASK) + PUD_SIZE;
    continue;
}

{
    unsigned long puds, pmds, tables, start;
    puds = (end + PUD_SIZE - 1) >> PUD_SHIFT;
    tables = round_up (puds * sizeof (pud_t), PAGE_SIZE);
    if (!direct_gbpages) {
        pmds = (end + PMD_SIZE - 1) >> PMD_SHIFT;
        tables += round_up (pmds * sizeof (pmd_t), PAGE_SIZE);
    }
    start = 0x8000;
    table_start = find_e820_area (start, end, tables, PAGE_SIZE);
    if (table_start == -1UL)
        panic ("Cannot find space for the kernel page tables");
    table_start >>= PAGE_SHIFT;
    table_end = table_start;
    early_printk ("kernel direct mapping tables up to %lx @ %lx-%lx\n", end, table_start << PAGE_SHIFT, (table_start << PAGE_SHIFT) + tables);
}

{
    pmds = (end + PMD_SIZE - 1) >> PMD_SHIFT;
    tables += round_up (pmds * sizeof (pmd_t), PAGE_SIZE);
}

{
    if (direct_gbpages && cpu_has_gbpages)
        printk (KERN_INFO "Using GB pages for direct mapping\n");
    else
        direct_gbpages = 0;
}

{
    unsigned long next, last_map_addr = end;
    unsigned long start_phys = start, end_phys = end;
    printk (KERN_INFO "init_memory_mapping\n");
    if (!after_bootmem) {
        init_gbpages ();
        find_early_table_space (end);
    }
    start = (unsigned long) __va (start);
    end = (unsigned long) __va (end);
    for (; start < end; start = next) {
        pgd_t *pgd = pgd_offset_k (start);
        unsigned long pud_phys;
        pud_t *pud;
        if (after_bootmem)
            pud = pud_offset (pgd, start &PGDIR_MASK);
        else
            pud = alloc_low_page (&pud_phys);
        next = start + PGDIR_SIZE;
        if (next > end)
            next = end;
        last_map_addr = phys_pud_init (pud, __pa (start), __pa (next));
        if (!after_bootmem)
            set_pgd (pgd_offset_k (start), mk_kernel_pgd (pud_phys));
        unmap_low_page (pud);
    }
    if (!after_bootmem)
        mmu_cr4_features = read_cr4 ();
    __flush_tlb_all ();
    if (!after_bootmem)
        reserve_early (table_start << PAGE_SHIFT, table_end << PAGE_SHIFT, "PGTABLE");
    if (!after_bootmem)
        early_memtest (start_phys, end_phys);
    return last_map_addr;
}

{
    init_gbpages ();
    find_early_table_space (end);
}

{
    pgd_t *pgd = pgd_offset_k (start);
    unsigned long pud_phys;
    pud_t *pud;
    if (after_bootmem)
        pud = pud_offset (pgd, start &PGDIR_MASK);
    else
        pud = alloc_low_page (&pud_phys);
    next = start + PGDIR_SIZE;
    if (next > end)
        next = end;
    last_map_addr = phys_pud_init (pud, __pa (start), __pa (next));
    if (!after_bootmem)
        set_pgd (pgd_offset_k (start), mk_kernel_pgd (pud_phys));
    unmap_low_page (pud);
}

{
    unsigned long max_zone_pfns [MAX_NR_ZONES];
    memset (max_zone_pfns, 0, sizeof (max_zone_pfns));
    max_zone_pfns[ZONE_DMA] = MAX_DMA_PFN;
    max_zone_pfns[ZONE_DMA32] = MAX_DMA32_PFN;
    max_zone_pfns[ZONE_NORMAL] = end_pfn;
    memory_present (0, 0, end_pfn);
    sparse_init ();
    free_area_init_nodes (max_zone_pfns);
}

{
    if (pagenr <= 256)
        return 1;
    if (!page_is_ram (pagenr))
        return 1;
    return 0;
}

{
    long codesize, reservedpages, datasize, initsize;
    pci_iommu_alloc ();
    reservedpages = 0;
    totalram_pages = free_all_bootmem ();
    reservedpages = end_pfn - totalram_pages - absent_pages_in_range (0, end_pfn);
    after_bootmem = 1;
    codesize = (unsigned long) &_etext - (unsigned long) &_text;
    datasize = (unsigned long) &_edata - (unsigned long) &_etext;
    initsize = (unsigned long) &__init_end - (unsigned long) &__init_begin;
    kclist_add (& kcore_mem, __va (0), max_low_pfn << PAGE_SHIFT);
    kclist_add (& kcore_vmalloc, (void *) VMALLOC_START, VMALLOC_END - VMALLOC_START);
    kclist_add (& kcore_kernel, & _stext, _end - _stext);
    kclist_add (& kcore_modules, (void *) MODULES_VADDR, MODULES_LEN);
    kclist_add (& kcore_vsyscall, (void *) VSYSCALL_START, VSYSCALL_END - VSYSCALL_START);
    printk (KERN_INFO "Memory: %luk/%luk available (%ldk kernel code, " "%ldk reserved, %ldk data, %ldk init)\n", (unsigned long) nr_free_pages () << (PAGE_SHIFT - 10), end_pfn << (PAGE_SHIFT - 10), codesize >> 10, reservedpages << (PAGE_SHIFT - 10), datasize >> 10, initsize >> 10);
    cpa_init ();
}

{
    unsigned long addr = begin;
    if (addr >= end)
        return;
    printk (KERN_INFO "Freeing %s: %luk freed\n", what, (end - begin) >> 10);
    for (; addr < end; addr += PAGE_SIZE) {
        ClearPageReserved (virt_to_page (addr));
        init_page_count (virt_to_page (addr));
        memset ((void *) (addr & ~ (PAGE_SIZE - 1)), POISON_FREE_INITMEM, PAGE_SIZE);
        free_page (addr);
        totalram_pages++;
    }
}

{
    ClearPageReserved (virt_to_page (addr));
    init_page_count (virt_to_page (addr));
    memset ((void *) (addr & ~ (PAGE_SIZE - 1)), POISON_FREE_INITMEM, PAGE_SIZE);
    free_page (addr);
    totalram_pages++;
}

{
    free_init_pages ("unused kernel memory", (unsigned long) (& __init_begin), (unsigned long) (& __init_end));
}

{
    unsigned long pfn = phys >> PAGE_SHIFT;
    if (pfn >= end_pfn) {
        if (pfn < max_pfn_mapped)
            return;
        printk (KERN_ERR "reserve_bootmem: illegal reserve %lx %u\n", phys, len);
        return;
    }
    reserve_bootmem (phys, len, BOOTMEM_DEFAULT);
    if (phys + len <= MAX_DMA_PFN * PAGE_SIZE) {
        dma_reserve += len / PAGE_SIZE;
        set_dma_reserve (dma_reserve);
    }
}

{
    if (pfn < max_pfn_mapped)
        return;
    printk (KERN_ERR "reserve_bootmem: illegal reserve %lx %u\n", phys, len);
    return;
}

{
    dma_reserve += len / PAGE_SIZE;
    set_dma_reserve (dma_reserve);
}

{
    unsigned long above = ((long) addr) >> __VIRTUAL_MASK_SHIFT;
    pgd_t *pgd;
    pud_t *pud;
    pmd_t *pmd;
    pte_t *pte;
    if (above != 0 && above != -1UL)
        return 0;
    pgd = pgd_offset_k (addr);
    if (pgd_none (*pgd))
        return 0;
    pud = pud_offset (pgd, addr);
    if (pud_none (*pud))
        return 0;
    pmd = pmd_offset (pud, addr);
    if (pmd_none (*pmd))
        return 0;
    if (pmd_large (*pmd))
        return pfn_valid (pmd_pfn (*pmd));
    pte = pte_offset_kernel (pmd, addr);
    if (pte_none (*pte))
        return 0;
    return pfn_valid (pte_pfn (*pte));
}

{
    return &gate_vma;
}

{
    struct vm_area_struct *vma = get_gate_vma (task);
    if (!vma)
        return 0;
    return (addr >= vma->vm_start) && (addr < vma->vm_end);
}

{
    return (addr >= VSYSCALL_START) && (addr < VSYSCALL_END);
}

{
    if (vma->vm_mm && vma->vm_start == (long) vma->vm_mm->context.vdso)
        return "[vdso]";
    if (vma == &gate_vma)
        return "[vsyscall]";
    return NULL;
}

{
    return 1;
}

{
    resource_size_t addr, end;
    int i;
    if (pagenr == 0)
        return 0;
    if (pagenr >= (BIOS_BEGIN >> PAGE_SHIFT) && pagenr < (BIOS_END >> PAGE_SHIFT))
        return 0;
    for (i = 0; i < e820.nr_map; i++) {
        if (e820.map[i].type != E820_RAM)
            continue;
        addr = (e820.map[i].addr + PAGE_SIZE - 1) >> PAGE_SHIFT;
        end = (e820.map[i].addr + e820.map[i].size) >> PAGE_SHIFT;
        if ((pagenr >= addr) && (pagenr < end))
            return 1;
    }
    return 0;
}

{
    if (e820.map[i].type != E820_RAM)
        continue;
    addr = (e820.map[i].addr + PAGE_SIZE - 1) >> PAGE_SHIFT;
    end = (e820.map[i].addr + e820.map[i].size) >> PAGE_SHIFT;
    if ((pagenr >= addr) && (pagenr < end))
        return 1;
}

{
    unsigned long nrpages = size >> PAGE_SHIFT;
    int err;
    switch (prot_val) {
    case _PAGE_CACHE_UC :
    default :
        err = _set_memory_uc (vaddr, nrpages);
        break;
    case _PAGE_CACHE_WC :
        err = _set_memory_wc (vaddr, nrpages);
        break;
    case _PAGE_CACHE_WB :
        err = _set_memory_wb (vaddr, nrpages);
        break;
    }
    return err;
}

{
case _PAGE_CACHE_UC :
default :
    err = _set_memory_uc (vaddr, nrpages);
    break;
case _PAGE_CACHE_WC :
    err = _set_memory_wc (vaddr, nrpages);
    break;
case _PAGE_CACHE_WB :
    err = _set_memory_wb (vaddr, nrpages);
    break;
}

{
    unsigned long pfn, offset, vaddr;
    resource_size_t last_addr;
    struct vm_struct *area;
    unsigned long new_prot_val;
    pgprot_t prot;
    int retval;
    last_addr = phys_addr + size - 1;
    if (!size || last_addr < phys_addr)
        return NULL;
    if (!phys_addr_valid (phys_addr)) {
        printk (KERN_WARNING "ioremap: invalid physical address %llx\n", (unsigned long long) phys_addr);
        WARN_ON_ONCE (1);
        return NULL;
    }
    if (phys_addr >= ISA_START_ADDRESS && last_addr < ISA_END_ADDRESS)
        return (__force void __iomem *) phys_to_virt (phys_addr);
    for (pfn = phys_addr >> PAGE_SHIFT; (pfn << PAGE_SHIFT) < (last_addr & PAGE_MASK); pfn++) {
        int is_ram = page_is_ram (pfn);
        if (is_ram && pfn_valid (pfn) && !PageReserved (pfn_to_page (pfn)))
            return NULL;
        WARN_ON_ONCE (is_ram);
    }
    offset = phys_addr & ~PAGE_MASK;
    phys_addr &= PAGE_MASK;
    size = PAGE_ALIGN (last_addr +1) - phys_addr;
    retval = reserve_memtype (phys_addr, phys_addr +size, prot_val, &new_prot_val);
    if (retval) {
        pr_debug ("Warning: reserve_memtype returned %d\n", retval);
        return NULL;
    }
    if (prot_val != new_prot_val) {
        if ((prot_val == _PAGE_CACHE_UC_MINUS && (new_prot_val == _PAGE_CACHE_WB || new_prot_val == _PAGE_CACHE_WC)) || (prot_val == _PAGE_CACHE_WC && new_prot_val == _PAGE_CACHE_WB)) {
            pr_debug ("ioremap error for 0x%llx-0x%llx, requested 0x%lx, got 0x%lx\n", (unsigned long long) phys_addr, (unsigned long long) (phys_addr + size), prot_val, new_prot_val);
            free_memtype (phys_addr, phys_addr + size);
            return NULL;
        }
        prot_val = new_prot_val;
    }
    switch (prot_val) {
    case _PAGE_CACHE_UC :
    default :
        prot = PAGE_KERNEL_NOCACHE;
        break;
    case _PAGE_CACHE_UC_MINUS :
        prot = PAGE_KERNEL_UC_MINUS;
        break;
    case _PAGE_CACHE_WC :
        prot = PAGE_KERNEL_WC;
        break;
    case _PAGE_CACHE_WB :
        prot = PAGE_KERNEL;
        break;
    }
    area = get_vm_area_caller (size, VM_IOREMAP, caller);
    if (!area)
        return NULL;
    area->phys_addr = phys_addr;
    vaddr = (unsigned long) area->addr;
    if (ioremap_page_range (vaddr, vaddr +size, phys_addr, prot)) {
        free_memtype (phys_addr, phys_addr + size);
        free_vm_area (area);
        return NULL;
    }
    if (ioremap_change_attr (vaddr, size, prot_val) < 0) {
        free_memtype (phys_addr, phys_addr + size);
        vunmap (area -> addr);
        return NULL;
    }
    return (void __iomem *) (vaddr + offset);
}

{
    printk (KERN_WARNING "ioremap: invalid physical address %llx\n", (unsigned long long) phys_addr);
    WARN_ON_ONCE (1);
    return NULL;
}

{
    int is_ram = page_is_ram (pfn);
    if (is_ram && pfn_valid (pfn) && !PageReserved (pfn_to_page (pfn)))
        return NULL;
    WARN_ON_ONCE (is_ram);
}

{
    pr_debug ("Warning: reserve_memtype returned %d\n", retval);
    return NULL;
}

{
    if ((prot_val == _PAGE_CACHE_UC_MINUS && (new_prot_val == _PAGE_CACHE_WB || new_prot_val == _PAGE_CACHE_WC)) || (prot_val == _PAGE_CACHE_WC && new_prot_val == _PAGE_CACHE_WB)) {
        pr_debug ("ioremap error for 0x%llx-0x%llx, requested 0x%lx, got 0x%lx\n", (unsigned long long) phys_addr, (unsigned long long) (phys_addr + size), prot_val, new_prot_val);
        free_memtype (phys_addr, phys_addr + size);
        return NULL;
    }
    prot_val = new_prot_val;
}

{
    pr_debug ("ioremap error for 0x%llx-0x%llx, requested 0x%lx, got 0x%lx\n", (unsigned long long) phys_addr, (unsigned long long) (phys_addr + size), prot_val, new_prot_val);
    free_memtype (phys_addr, phys_addr + size);
    return NULL;
}

{
case _PAGE_CACHE_UC :
default :
    prot = PAGE_KERNEL_NOCACHE;
    break;
case _PAGE_CACHE_UC_MINUS :
    prot = PAGE_KERNEL_UC_MINUS;
    break;
case _PAGE_CACHE_WC :
    prot = PAGE_KERNEL_WC;
    break;
case _PAGE_CACHE_WB :
    prot = PAGE_KERNEL;
    break;
}

{
    free_memtype (phys_addr, phys_addr + size);
    free_vm_area (area);
    return NULL;
}

{
    free_memtype (phys_addr, phys_addr + size);
    vunmap (area -> addr);
    return NULL;
}

{
    unsigned long val = _PAGE_CACHE_UC_MINUS;
    return __ioremap_caller (phys_addr, size, val, __builtin_return_address (0));
}

{
    if (pat_wc_enabled)
        return __ioremap_caller (phys_addr, size, _PAGE_CACHE_WC, __builtin_return_address (0));
    else
        return ioremap_nocache (phys_addr, size);
}

{
    return __ioremap_caller (phys_addr, size, _PAGE_CACHE_WB, __builtin_return_address (0));
}

{
    unsigned long flags;
    void *ret;
    int err;
    err = reserve_memtype (phys_addr, phys_addr +size, -1, &flags);
    if (err < 0)
        return NULL;
    ret = (void *) __ioremap_caller (phys_addr, size, flags, __builtin_return_address (0));
    free_memtype (phys_addr, phys_addr + size);
    return (void __iomem *) ret;
}

{
    struct vm_struct *p, *o;
    if ((void __force *) addr <= high_memory)
        return;
    if (addr >= phys_to_virt (ISA_START_ADDRESS) && addr < phys_to_virt (ISA_END_ADDRESS))
        return;
    addr = (volatile void __iomem *) (PAGE_MASK & (unsigned long __force) addr);
    read_lock (& vmlist_lock);
    for (p = vmlist; p; p = p->next) {
        if (p->addr == addr)
            break;
    }
    read_unlock (& vmlist_lock);
    if (!p) {
        printk (KERN_ERR "iounmap: bad address %p\n", addr);
        dump_stack ();
        return;
    }
    free_memtype (p -> phys_addr, p -> phys_addr + get_vm_area_size (p));
    o = remove_vm_area ((void *) addr);
    BUG_ON (p != o || o == NULL);
    kfree (p);
}

{
    if (p->addr == addr)
        break;
}

{
    printk (KERN_ERR "iounmap: bad address %p\n", addr);
    dump_stack ();
    return;
}

{
    void *addr;
    unsigned long start = phys & PAGE_MASK;
    if (page_is_ram (start >> PAGE_SHIFT))
        return __va (phys);
    addr = (void *) ioremap_default (start, PAGE_SIZE);
    if (addr)
        addr = (void *) ((unsigned long) addr | (phys & ~PAGE_MASK));
    return addr;
}

{
    if (page_is_ram (phys >> PAGE_SHIFT))
        return;
    iounmap ((void __iomem *) ((unsigned long) addr & PAGE_MASK));
    return;
}

{
    return 0;
}

{
    unsigned char *instr;
    int scan_more = 1;
    int prefetch = 0;
    unsigned char *max_instr;
    if (error_code & PF_INSTR)
        return 0;
    instr = (unsigned char *) convert_ip_to_linear (current, regs);
    max_instr = instr + 15;
    if (user_mode (regs) && instr >= (unsigned char *) TASK_SIZE)
        return 0;
    while (scan_more && instr < max_instr) {
        unsigned char opcode;
        unsigned char instr_hi;
        unsigned char instr_lo;
        if (probe_kernel_address (instr, opcode))
            break;
        instr_hi = opcode & 0xf0;
        instr_lo = opcode & 0x0f;
        instr++;
        switch (instr_hi) {
        case 0x20 :
        case 0x30 :
            scan_more = ((instr_lo & 7) == 0x6);
            break;
        case 0x60 :
            scan_more = (instr_lo & 0xC) == 0x4;
            break;
        case 0xF0 :
            scan_more = !instr_lo || (instr_lo >> 1) == 1;
            break;
        case 0x00 :
            scan_more = 0;
            if (probe_kernel_address (instr, opcode))
                break;
            prefetch = (instr_lo == 0xF) && (opcode == 0x0D || opcode == 0x18);
            break;
        default :
            scan_more = 0;
            break;
        }
    }
    return prefetch;
}

{
    unsigned char opcode;
    unsigned char instr_hi;
    unsigned char instr_lo;
    if (probe_kernel_address (instr, opcode))
        break;
    instr_hi = opcode & 0xf0;
    instr_lo = opcode & 0x0f;
    instr++;
    switch (instr_hi) {
    case 0x20 :
    case 0x30 :
        scan_more = ((instr_lo & 7) == 0x6);
        break;
    case 0x60 :
        scan_more = (instr_lo & 0xC) == 0x4;
        break;
    case 0xF0 :
        scan_more = !instr_lo || (instr_lo >> 1) == 1;
        break;
    case 0x00 :
        scan_more = 0;
        if (probe_kernel_address (instr, opcode))
            break;
        prefetch = (instr_lo == 0xF) && (opcode == 0x0D || opcode == 0x18);
        break;
    default :
        scan_more = 0;
        break;
    }
}

{
case 0x20 :
case 0x30 :
    scan_more = ((instr_lo & 7) == 0x6);
    break;
case 0x60 :
    scan_more = (instr_lo & 0xC) == 0x4;
    break;
case 0xF0 :
    scan_more = !instr_lo || (instr_lo >> 1) == 1;
    break;
case 0x00 :
    scan_more = 0;
    if (probe_kernel_address (instr, opcode))
        break;
    prefetch = (instr_lo == 0xF) && (opcode == 0x0D || opcode == 0x18);
    break;
default :
    scan_more = 0;
    break;
}

{
    siginfo_t info;
    info.si_signo = si_signo;
    info.si_errno = 0;
    info.si_code = si_code;
    info.si_addr = (void __user *) address;
    force_sig_info (si_signo, & info, tsk);
}

{
    pgd_t *pgd;
    pud_t *pud;
    pmd_t *pmd;
    pte_t *pte;
    pgd = (pgd_t *) read_cr3 ();
    pgd = __va ((unsigned long) pgd & PHYSICAL_PAGE_MASK);
    pgd += pgd_index (address);
    if (bad_address (pgd))
        goto bad;
    printk ("PGD %lx ", pgd_val (* pgd));
    if (!pgd_present (*pgd))
        goto ret;
    pud = pud_offset (pgd, address);
    if (bad_address (pud))
        goto bad;
    printk ("PUD %lx ", pud_val (* pud));
    if (!pud_present (*pud) || pud_large (*pud))
        goto ret;
    pmd = pmd_offset (pud, address);
    if (bad_address (pmd))
        goto bad;
    printk ("PMD %lx ", pmd_val (* pmd));
    if (!pmd_present (*pmd) || pmd_large (*pmd))
        goto ret;
    pte = pte_offset_kernel (pmd, address);
    if (bad_address (pte))
        goto bad;
    printk ("PTE %lx", pte_val (* pte));
ret :
    printk ("\n");
    return;
bad :
    printk ("BAD\n");
}

{
    return 0;
}

{
    return 0;
}

{
    return 0;
}

{
    printk (KERN_ALERT "BUG: unable to handle kernel ");
    if (address < PAGE_SIZE)
        printk (KERN_CONT "NULL pointer dereference");
    else
        printk (KERN_CONT "paging request");
    printk (KERN_CONT " at %016lx\n", address);
    printk (KERN_ALERT "IP:");
    printk_address (regs -> ip, 1);
    dump_pagetable (address);
}

{
    if ((error_code & PF_WRITE) && !pte_write (*pte))
        return 0;
    if ((error_code & PF_INSTR) && !pte_exec (*pte))
        return 0;
    return 1;
}

{
    pgd_t *pgd;
    pud_t *pud;
    pmd_t *pmd;
    pte_t *pte;
    if (error_code & (PF_USER | PF_RSVD))
        return 0;
    pgd = init_mm.pgd + pgd_index (address);
    if (!pgd_present (*pgd))
        return 0;
    pud = pud_offset (pgd, address);
    if (!pud_present (*pud))
        return 0;
    if (pud_large (*pud))
        return spurious_fault_check (error_code, (pte_t *) pud);
    pmd = pmd_offset (pud, address);
    if (!pmd_present (*pmd))
        return 0;
    if (pmd_large (*pmd))
        return spurious_fault_check (error_code, (pte_t *) pmd);
    pte = pte_offset_kernel (pmd, address);
    if (!pte_present (*pte))
        return 0;
    return spurious_fault_check (error_code, pte);
}

{
    pgd_t *pgd, *pgd_ref;
    pud_t *pud, *pud_ref;
    pmd_t *pmd, *pmd_ref;
    pte_t *pte, *pte_ref;
    if (!(address >= VMALLOC_START && address < VMALLOC_END))
        return -1;
    pgd = pgd_offset (current->mm ? : &init_mm, address);
    pgd_ref = pgd_offset_k (address);
    if (pgd_none (*pgd_ref))
        return -1;
    if (pgd_none (*pgd))
        set_pgd (pgd, *pgd_ref);
    else
        BUG_ON (pgd_page_vaddr (*pgd) != pgd_page_vaddr (*pgd_ref));
    pud = pud_offset (pgd, address);
    pud_ref = pud_offset (pgd_ref, address);
    if (pud_none (*pud_ref))
        return -1;
    if (pud_none (*pud) || pud_page_vaddr (*pud) != pud_page_vaddr (*pud_ref))
        BUG ();
    pmd = pmd_offset (pud, address);
    pmd_ref = pmd_offset (pud_ref, address);
    if (pmd_none (*pmd_ref))
        return -1;
    if (pmd_none (*pmd) || pmd_page (*pmd) != pmd_page (*pmd_ref))
        BUG ();
    pte_ref = pte_offset_kernel (pmd_ref, address);
    if (!pte_present (*pte_ref))
        return -1;
    pte = pte_offset_kernel (pmd, address);
    if (!pte_present (*pte) || pte_pfn (*pte) != pte_pfn (*pte_ref))
        BUG ();
    return 0;
}

{
    struct task_struct *tsk;
    struct mm_struct *mm;
    struct vm_area_struct *vma;
    unsigned long address;
    int write, si_code;
    int fault;
    trace_hardirqs_fixup ();
    tsk = current;
    mm = tsk->mm;
    prefetchw (& mm -> mmap_sem);
    address = read_cr2 ();
    si_code = SEGV_MAPERR;
    if (notify_page_fault (regs))
        return;
    if (unlikely (address >= TASK_SIZE64)) {
        if (!(error_code & (PF_RSVD | PF_USER | PF_PROT)) && vmalloc_fault (address) >= 0)
            return;
        if (spurious_fault (address, error_code))
            return;
        goto bad_area_nosemaphore;
    }
    if (likely (regs->flags & X86_EFLAGS_IF))
        local_irq_enable ();
    if (unlikely (error_code &PF_RSVD))
        pgtable_bad (address, regs, error_code);
    if (unlikely (in_atomic () || !mm))
        goto bad_area_nosemaphore;
    if (user_mode_vm (regs))
        error_code |= PF_USER;
again :
    if (!down_read_trylock (&mm->mmap_sem)) {
        if ((error_code & PF_USER) == 0 && !search_exception_tables (regs->ip))
            goto bad_area_nosemaphore;
        down_read (& mm -> mmap_sem);
    }
    vma = find_vma (mm, address);
    if (!vma)
        goto bad_area;
    if (vma->vm_start <= address)
        goto good_area;
    if (!(vma->vm_flags & VM_GROWSDOWN))
        goto bad_area;
    if (error_code & PF_USER) {
        if (address + 65536 + 32 * sizeof (unsigned long) < regs->sp)
            goto bad_area;
    }
    if (expand_stack (vma, address))
        goto bad_area;
good_area :
    si_code = SEGV_ACCERR;
    write = 0;
    switch (error_code & (PF_PROT | PF_WRITE)) {
    default :
    case PF_WRITE :
        if (!(vma->vm_flags & VM_WRITE))
            goto bad_area;
        write++;
        break;
    case PF_PROT :
        goto bad_area;
    case 0 :
        if (!(vma->vm_flags & (VM_READ | VM_EXEC | VM_WRITE)))
            goto bad_area;
    }
    fault = handle_mm_fault (mm, vma, address, write);
    if (unlikely (fault &VM_FAULT_ERROR)) {
        if (fault & VM_FAULT_OOM)
            goto out_of_memory;
        else if (fault & VM_FAULT_SIGBUS)
            goto do_sigbus;
        BUG ();
    }
    if (fault & VM_FAULT_MAJOR)
        tsk->maj_flt++;
    else
        tsk->min_flt++;
    up_read (& mm -> mmap_sem);
    return;
bad_area :
    up_read (&mm->mmap_sem);
bad_area_nosemaphore :
    if (error_code & PF_USER) {
        local_irq_enable ();
        if (is_prefetch (regs, address, error_code))
            return;
        if (is_errata100 (regs, address))
            return;
        if (show_unhandled_signals && unhandled_signal (tsk, SIGSEGV) && printk_ratelimit ()) {
            printk ("%s%s[%d]: segfault at %lx ip %lx sp %lx error %lx", task_pid_nr (tsk) > 1 ? KERN_INFO : KERN_EMERG, tsk -> comm, task_pid_nr (tsk), address, regs -> ip, regs -> sp, error_code);
            print_vma_addr (" in ", regs -> ip);
            printk ("\n");
        }
        tsk->thread.cr2 = address;
        tsk->thread.error_code = error_code | (address >= TASK_SIZE);
        tsk->thread.trap_no = 14;
        force_sig_info_fault (SIGSEGV, si_code, address, tsk);
        return;
    }
    if (is_f00f_bug (regs, address))
        return;
no_context :
    if (fixup_exception (regs))
        return;
    if (is_prefetch (regs, address, error_code))
        return;
    if (is_errata93 (regs, address))
        return;
    flags = oops_begin ();
    show_fault_oops (regs, error_code, address);
    tsk->thread.cr2 = address;
    tsk->thread.trap_no = 14;
    tsk->thread.error_code = error_code;
    if (__die ("Oops", regs, error_code))
        regs = NULL;
    printk (KERN_EMERG "CR2: %016lx\n", address);
    oops_end (flags, regs, SIGKILL);
out_of_memory :
    up_read (&mm->mmap_sem);
    if (is_global_init (tsk)) {
        yield ();
        goto again;
    }
    printk ("VM: killing process %s\n", tsk -> comm);
    if (error_code & PF_USER)
        do_group_exit (SIGKILL);
    goto no_context;
do_sigbus :
    up_read (&mm->mmap_sem);
    if (!(error_code & PF_USER))
        goto no_context;
    tsk->thread.cr2 = address;
    tsk->thread.error_code = error_code;
    tsk->thread.trap_no = 14;
    force_sig_info_fault (SIGBUS, BUS_ADRERR, address, tsk);
}

{
    if (!(error_code & (PF_RSVD | PF_USER | PF_PROT)) && vmalloc_fault (address) >= 0)
        return;
    if (spurious_fault (address, error_code))
        return;
    goto bad_area_nosemaphore;
}

{
    if ((error_code & PF_USER) == 0 && !search_exception_tables (regs->ip))
        goto bad_area_nosemaphore;
    down_read (& mm -> mmap_sem);
}

{
    if (address + 65536 + 32 * sizeof (unsigned long) < regs->sp)
        goto bad_area;
}

{
default :
case PF_WRITE :
    if (!(vma->vm_flags & VM_WRITE))
        goto bad_area;
    write++;
    break;
case PF_PROT :
    goto bad_area;
case 0 :
    if (!(vma->vm_flags & (VM_READ | VM_EXEC | VM_WRITE)))
        goto bad_area;
}

{
    if (fault & VM_FAULT_OOM)
        goto out_of_memory;
    else if (fault & VM_FAULT_SIGBUS)
        goto do_sigbus;
    BUG ();
}

{
    local_irq_enable ();
    if (is_prefetch (regs, address, error_code))
        return;
    if (is_errata100 (regs, address))
        return;
    if (show_unhandled_signals && unhandled_signal (tsk, SIGSEGV) && printk_ratelimit ()) {
        printk ("%s%s[%d]: segfault at %lx ip %lx sp %lx error %lx", task_pid_nr (tsk) > 1 ? KERN_INFO : KERN_EMERG, tsk -> comm, task_pid_nr (tsk), address, regs -> ip, regs -> sp, error_code);
        print_vma_addr (" in ", regs -> ip);
        printk ("\n");
    }
    tsk->thread.cr2 = address;
    tsk->thread.error_code = error_code | (address >= TASK_SIZE);
    tsk->thread.trap_no = 14;
    force_sig_info_fault (SIGSEGV, si_code, address, tsk);
    return;
}

{
    printk ("%s%s[%d]: segfault at %lx ip %lx sp %lx error %lx", task_pid_nr (tsk) > 1 ? KERN_INFO : KERN_EMERG, tsk -> comm, task_pid_nr (tsk), address, regs -> ip, regs -> sp, error_code);
    print_vma_addr (" in ", regs -> ip);
    printk ("\n");
}

{
    yield ();
    goto again;
}

{
    static DECLARE_BITMAP (insync, PTRS_PER_PGD);
    static unsigned long start = VMALLOC_START & PGDIR_MASK;
    unsigned long address;
    for (address = start; address <= VMALLOC_END; address += PGDIR_SIZE) {
        if (!test_bit (pgd_index (address), insync)) {
            const pgd_t *pgd_ref = pgd_offset_k (address);
            unsigned long flags;
            struct page *page;
            if (pgd_none (*pgd_ref))
                continue;
            spin_lock_irqsave (& pgd_lock, flags);
            list_for_each_entry (page, &pgd_list, lru) {
                pgd_t *pgd;
                pgd = (pgd_t *) page_address (page) + pgd_index (address);
                if (pgd_none (*pgd))
                    set_pgd (pgd, *pgd_ref);
                else
                    BUG_ON (pgd_page_vaddr (*pgd) != pgd_page_vaddr (*pgd_ref));
            }
            spin_unlock_irqrestore (& pgd_lock, flags);
            set_bit (pgd_index (address), insync);
        }
        if (address == start)
            start = address + PGDIR_SIZE;
    }
}

{
    if (!test_bit (pgd_index (address), insync)) {
        const pgd_t *pgd_ref = pgd_offset_k (address);
        unsigned long flags;
        struct page *page;
        if (pgd_none (*pgd_ref))
            continue;
        spin_lock_irqsave (& pgd_lock, flags);
        list_for_each_entry (page, &pgd_list, lru) {
            pgd_t *pgd;
            pgd = (pgd_t *) page_address (page) + pgd_index (address);
            if (pgd_none (*pgd))
                set_pgd (pgd, *pgd_ref);
            else
                BUG_ON (pgd_page_vaddr (*pgd) != pgd_page_vaddr (*pgd_ref));
        }
        spin_unlock_irqrestore (& pgd_lock, flags);
        set_bit (pgd_index (address), insync);
    }
    if (address == start)
        start = address + PGDIR_SIZE;
}

{
    const pgd_t *pgd_ref = pgd_offset_k (address);
    unsigned long flags;
    struct page *page;
    if (pgd_none (*pgd_ref))
        continue;
    spin_lock_irqsave (& pgd_lock, flags);
    list_for_each_entry (page, &pgd_list, lru) {
        pgd_t *pgd;
        pgd = (pgd_t *) page_address (page) + pgd_index (address);
        if (pgd_none (*pgd))
            set_pgd (pgd, *pgd_ref);
        else
            BUG_ON (pgd_page_vaddr (*pgd) != pgd_page_vaddr (*pgd_ref));
    }
    spin_unlock_irqrestore (& pgd_lock, flags);
    set_bit (pgd_index (address), insync);
}

{
    pgd_t *pgd;
    pgd = (pgd_t *) page_address (page) + pgd_index (address);
    if (pgd_none (*pgd))
        set_pgd (pgd, *pgd_ref);
    else
        BUG_ON (pgd_page_vaddr (*pgd) != pgd_page_vaddr (*pgd_ref));
}

{
    return (pte_t *) __get_free_page (GFP_KERNEL | __GFP_REPEAT | __GFP_ZERO);
}

{
    struct page *pte;
    pte = alloc_pages (GFP_KERNEL | __GFP_REPEAT | __GFP_ZERO, 0);
    if (pte)
        pgtable_page_ctor (pte);
    return pte;
}

{
    pgtable_page_dtor (pte);
    paravirt_release_pte (page_to_pfn (pte));
    tlb_remove_page (tlb, pte);
}

{
    struct page *page = virt_to_page (pgd);
    list_add (& page -> lru, & pgd_list);
}

{
    struct page *page = virt_to_page (pgd);
    list_del (& page -> lru);
}

{
    pgd_t *pgd = p;
    unsigned long flags;
    memset (pgd, 0, KERNEL_PGD_BOUNDARY * sizeof (pgd_t));
    spin_lock_irqsave (& pgd_lock, flags);
    if (PAGETABLE_LEVELS == 2 || (PAGETABLE_LEVELS == 3 && SHARED_KERNEL_PMD) || PAGETABLE_LEVELS == 4) {
        clone_pgd_range (pgd + KERNEL_PGD_BOUNDARY, swapper_pg_dir + KERNEL_PGD_BOUNDARY, KERNEL_PGD_PTRS);
        paravirt_alloc_pmd_clone (__pa (pgd) >> PAGE_SHIFT, __pa (swapper_pg_dir) >> PAGE_SHIFT, KERNEL_PGD_BOUNDARY, KERNEL_PGD_PTRS);
    }
    if (!SHARED_KERNEL_PMD)
        pgd_list_add (pgd);
    spin_unlock_irqrestore (& pgd_lock, flags);
}

{
    clone_pgd_range (pgd + KERNEL_PGD_BOUNDARY, swapper_pg_dir + KERNEL_PGD_BOUNDARY, KERNEL_PGD_PTRS);
    paravirt_alloc_pmd_clone (__pa (pgd) >> PAGE_SHIFT, __pa (swapper_pg_dir) >> PAGE_SHIFT, KERNEL_PGD_BOUNDARY, KERNEL_PGD_PTRS);
}

{
    unsigned long flags;
    if (SHARED_KERNEL_PMD)
        return;
    spin_lock_irqsave (& pgd_lock, flags);
    pgd_list_del (pgd);
    spin_unlock_irqrestore (& pgd_lock, flags);
}

{
    return 1;
}

{
    pgd_t *pgd = (pgd_t *) __get_free_page (GFP_KERNEL | __GFP_ZERO);
    mm->pgd = pgd;
    if (pgd)
        pgd_ctor (pgd);
    if (pgd && !pgd_prepopulate_pmd (mm, pgd)) {
        pgd_dtor (pgd);
        free_page ((unsigned long) pgd);
        pgd = NULL;
    }
    return pgd;
}

{
    pgd_dtor (pgd);
    free_page ((unsigned long) pgd);
    pgd = NULL;
}

{
    pgd_mop_up_pmds (mm, pgd);
    pgd_dtor (pgd);
    free_page ((unsigned long) pgd);
}

{
    int changed = !pte_same (*ptep, entry);
    if (changed && dirty) {
        *ptep = entry;
        pte_update_defer (vma -> vm_mm, address, ptep);
        flush_tlb_page (vma, address);
    }
    return changed;
}

{
    *ptep = entry;
    pte_update_defer (vma -> vm_mm, address, ptep);
    flush_tlb_page (vma, address);
}

{
    int ret = 0;
    if (pte_young (*ptep))
        ret = test_and_clear_bit (_PAGE_BIT_ACCESSED, &ptep->pte);
    if (ret)
        pte_update (vma->vm_mm, addr, ptep);
    return ret;
}

{
    int young;
    young = ptep_test_and_clear_young (vma, address, ptep);
    if (young)
        flush_tlb_page (vma, address);
    return young;
}

{
    return addr >= start && addr < end;
}

{
    void *vend = vaddr + size - 1;
    mb ();
    for (; vaddr < vend; vaddr += boot_cpu_data.x86_clflush_size)
        clflush (vaddr);
    clflush (vend);
    mb ();
}

{
    unsigned long cache = (unsigned long) arg;
    __flush_tlb_all ();
    if (cache && boot_cpu_data.x86_model >= 4)
        wbinvd ();
}

{
    BUG_ON (irqs_disabled ());
    on_each_cpu (__cpa_flush_all, (void *) cache, 1, 1);
}

{
    __flush_tlb_all ();
}

{
    unsigned int i, level;
    unsigned long addr;
    BUG_ON (irqs_disabled ());
    WARN_ON (PAGE_ALIGN (start) != start);
    on_each_cpu (__cpa_flush_range, NULL, 1, 1);
    if (!cache)
        return;
    for (i = 0, addr = start; i < numpages; i++, addr += PAGE_SIZE) {
        pte_t *pte = lookup_address (addr, &level);
        if (pte && (pte_val (*pte) & _PAGE_PRESENT))
            clflush_cache_range ((void *) addr, PAGE_SIZE);
    }
}

{
    pte_t *pte = lookup_address (addr, &level);
    if (pte && (pte_val (*pte) & _PAGE_PRESENT))
        clflush_cache_range ((void *) addr, PAGE_SIZE);
}

{
    pgprot_t forbidden = __pgprot (0);
    if (within (pfn, BIOS_BEGIN >> PAGE_SHIFT, BIOS_END >> PAGE_SHIFT))
        pgprot_val (forbidden) |= _PAGE_NX;
    if (within (address, (unsigned long) _text, (unsigned long) _etext))
        pgprot_val (forbidden) |= _PAGE_NX;
    if (within (pfn, __pa ((unsigned long) __start_rodata) >> PAGE_SHIFT, __pa ((unsigned long) __end_rodata) >> PAGE_SHIFT))
        pgprot_val (forbidden) |= _PAGE_RW;
    prot = __pgprot (pgprot_val (prot) &~pgprot_val (forbidden));
    return prot;
}

{
    pgd_t *pgd = pgd_offset_k (address);
    pud_t *pud;
    pmd_t *pmd;
    *level = PG_LEVEL_NONE;
    if (pgd_none (*pgd))
        return NULL;
    pud = pud_offset (pgd, address);
    if (pud_none (*pud))
        return NULL;
    *level = PG_LEVEL_1G;
    if (pud_large (*pud) || !pud_present (*pud))
        return (pte_t *) pud;
    pmd = pmd_offset (pud, address);
    if (pmd_none (*pmd))
        return NULL;
    *level = PG_LEVEL_2M;
    if (pmd_large (*pmd) || !pmd_present (*pmd))
        return (pte_t *) pmd;
    *level = PG_LEVEL_4K;
    return pte_offset_kernel (pmd, address);
}

{
    set_pte_atomic (kpte, pte);
}

{
    unsigned long nextpage_addr, numpages, pmask, psize, flags, addr, pfn;
    pte_t new_pte, old_pte, *tmp;
    pgprot_t old_prot, new_prot;
    int i, do_split = 1;
    unsigned int level;
    if (cpa->force_split)
        return 1;
    spin_lock_irqsave (& pgd_lock, flags);
    tmp = lookup_address (address, &level);
    if (tmp != kpte)
        goto out_unlock;
    switch (level) {
    case PG_LEVEL_2M :
        psize = PMD_PAGE_SIZE;
        pmask = PMD_PAGE_MASK;
        break;
    default :
        do_split = -EINVAL;
        goto out_unlock;
    }
    nextpage_addr = (address + psize) & pmask;
    numpages = (nextpage_addr - address) >> PAGE_SHIFT;
    if (numpages < cpa->numpages)
        cpa->numpages = numpages;
    old_pte = *kpte;
    old_prot = new_prot = pte_pgprot (old_pte);
    pgprot_val (new_prot) &= ~pgprot_val (cpa->mask_clr);
    pgprot_val (new_prot) |= pgprot_val (cpa->mask_set);
    pfn = pte_pfn (old_pte) + ((address & (psize - 1)) >> PAGE_SHIFT);
    cpa->pfn = pfn;
    new_prot = static_protections (new_prot, address, pfn);
    addr = address + PAGE_SIZE;
    pfn++;
    for (i = 1; i < cpa->numpages; i++, addr += PAGE_SIZE, pfn++) {
        pgprot_t chk_prot = static_protections (new_prot, addr, pfn);
        if (pgprot_val (chk_prot) != pgprot_val (new_prot))
            goto out_unlock;
    }
    if (pgprot_val (new_prot) == pgprot_val (old_prot)) {
        do_split = 0;
        goto out_unlock;
    }
    if (address == (nextpage_addr - psize) && cpa->numpages == numpages) {
        new_pte = pfn_pte (pte_pfn (old_pte), canon_pgprot (new_prot));
        __set_pmd_pte (kpte, address, new_pte);
        cpa->flushtlb = 1;
        do_split = 0;
    }
out_unlock :
    spin_unlock_irqrestore (&pgd_lock, flags);
    return do_split;
}

{
case PG_LEVEL_2M :
    psize = PMD_PAGE_SIZE;
    pmask = PMD_PAGE_MASK;
    break;
default :
    do_split = -EINVAL;
    goto out_unlock;
}

{
    pgprot_t chk_prot = static_protections (new_prot, addr, pfn);
    if (pgprot_val (chk_prot) != pgprot_val (new_prot))
        goto out_unlock;
}

{
    do_split = 0;
    goto out_unlock;
}

{
    new_pte = pfn_pte (pte_pfn (old_pte), canon_pgprot (new_prot));
    __set_pmd_pte (kpte, address, new_pte);
    cpa->flushtlb = 1;
    do_split = 0;
}

{
    gfp_t gfp = GFP_KERNEL;
    unsigned long flags;
    struct page *p;
    if (current->flags & PF_MEMALLOC)
        return;
    current->flags |= PF_MEMALLOC;
    if (in_atomic () || irqs_disabled () || debug_pagealloc)
        gfp = GFP_ATOMIC | __GFP_NORETRY | __GFP_NOWARN;
    while (pool_pages < pool_size || (ret && !*ret)) {
        p = alloc_pages (gfp, 0);
        if (!p) {
            pool_failed++;
            break;
        }
        if (ret && !*ret) {
            *ret = p;
            continue;
        }
        spin_lock_irqsave (& pgd_lock, flags);
        list_add (& p -> lru, & page_pool);
        pool_pages++;
        spin_unlock_irqrestore (& pgd_lock, flags);
    }
    current->flags &= ~PF_MEMALLOC;
}

{
    p = alloc_pages (gfp, 0);
    if (!p) {
        pool_failed++;
        break;
    }
    if (ret && !*ret) {
        *ret = p;
        continue;
    }
    spin_lock_irqsave (& pgd_lock, flags);
    list_add (& p -> lru, & page_pool);
    pool_pages++;
    spin_unlock_irqrestore (& pgd_lock, flags);
}

{
    pool_failed++;
    break;
}

{
    *ret = p;
    continue;
}

{
    struct sysinfo si;
    unsigned long gb;
    si_meminfo (& si);
    if (debug_pagealloc) {
        gb = ((si.totalram >> SHIFT_MB) + ROUND_MB_GB) >> SHIFT_MB_GB;
        pool_size = POOL_PAGES_PER_GB * gb;
    }
    else {
        pool_size = 1;
    }
    pool_low = pool_size;
    cpa_fill_pool (NULL);
    printk (KERN_DEBUG "CPA: page pool initialized %lu of %lu pages preallocated\n", pool_pages, pool_size);
}

{
    gb = ((si.totalram >> SHIFT_MB) + ROUND_MB_GB) >> SHIFT_MB_GB;
    pool_size = POOL_PAGES_PER_GB * gb;
}

{
    pool_size = 1;
}

{
    unsigned long flags, pfn, pfninc = 1;
    unsigned int i, level;
    pte_t *pbase, *tmp;
    pgprot_t ref_prot;
    struct page *base;
    spin_lock_irqsave (& pgd_lock, flags);
    if (list_empty (&page_pool)) {
        spin_unlock_irqrestore (& pgd_lock, flags);
        base = NULL;
        cpa_fill_pool (& base);
        if (!base)
            return -ENOMEM;
        spin_lock_irqsave (& pgd_lock, flags);
    }
    else {
        base = list_first_entry (&page_pool, struct page, lru);
        list_del (& base -> lru);
        pool_pages--;
        if (pool_pages < pool_low)
            pool_low = pool_pages;
    }
    tmp = lookup_address (address, &level);
    if (tmp != kpte)
        goto out_unlock;
    pbase = (pte_t *) page_address (base);
    paravirt_alloc_pte (& init_mm, page_to_pfn (base));
    ref_prot = pte_pgprot (pte_clrhuge (*kpte));
    pfn = pte_pfn (*kpte);
    for (i = 0; i < PTRS_PER_PTE; i++, pfn += pfninc)
        set_pte (&pbase[i], pfn_pte (pfn, ref_prot));
    ref_prot = pte_pgprot (pte_mkexec (pte_clrhuge (*kpte)));
    pgprot_val (ref_prot) |= _PAGE_PRESENT;
    __set_pmd_pte (kpte, address, mk_pte (base, ref_prot));
    base = NULL;
out_unlock :
    if (base) {
        list_add (& base -> lru, & page_pool);
        pool_pages++;
    }
    else
        pool_used++;
    spin_unlock_irqrestore (& pgd_lock, flags);
    return 0;
}

{
    spin_unlock_irqrestore (& pgd_lock, flags);
    base = NULL;
    cpa_fill_pool (& base);
    if (!base)
        return -ENOMEM;
    spin_lock_irqsave (& pgd_lock, flags);
}

{
    base = list_first_entry (&page_pool, struct page, lru);
    list_del (& base -> lru);
    pool_pages--;
    if (pool_pages < pool_low)
        pool_low = pool_pages;
}

{
    list_add (& base -> lru, & page_pool);
    pool_pages++;
}

{
    unsigned long address = cpa->vaddr;
    int do_split, err;
    unsigned int level;
    pte_t *kpte, old_pte;
repeat :
    kpte = lookup_address (address, &level);
    if (!kpte)
        return 0;
    old_pte = *kpte;
    if (!pte_val (old_pte)) {
        if (!primary)
            return 0;
        printk (KERN_WARNING "CPA: called for zero pte. " "vaddr = %lx cpa->vaddr = %lx\n", address, cpa -> vaddr);
        WARN_ON (1);
        return -EINVAL;
    }
    if (level == PG_LEVEL_4K) {
        pte_t new_pte;
        pgprot_t new_prot = pte_pgprot (old_pte);
        unsigned long pfn = pte_pfn (old_pte);
        pgprot_val (new_prot) &= ~pgprot_val (cpa->mask_clr);
        pgprot_val (new_prot) |= pgprot_val (cpa->mask_set);
        new_prot = static_protections (new_prot, address, pfn);
        new_pte = pfn_pte (pfn, canon_pgprot (new_prot));
        cpa->pfn = pfn;
        if (pte_val (old_pte) != pte_val (new_pte)) {
            set_pte_atomic (kpte, new_pte);
            cpa->flushtlb = 1;
        }
        cpa->numpages = 1;
        return 0;
    }
    do_split = try_preserve_large_page (kpte, address, cpa);
    if (do_split <= 0)
        return do_split;
    err = split_large_page (kpte, address);
    if (!err) {
        cpa->flushtlb = 1;
        goto repeat;
    }
    return err;
}

{
    if (!primary)
        return 0;
    printk (KERN_WARNING "CPA: called for zero pte. " "vaddr = %lx cpa->vaddr = %lx\n", address, cpa -> vaddr);
    WARN_ON (1);
    return -EINVAL;
}

{
    pte_t new_pte;
    pgprot_t new_prot = pte_pgprot (old_pte);
    unsigned long pfn = pte_pfn (old_pte);
    pgprot_val (new_prot) &= ~pgprot_val (cpa->mask_clr);
    pgprot_val (new_prot) |= pgprot_val (cpa->mask_set);
    new_prot = static_protections (new_prot, address, pfn);
    new_pte = pfn_pte (pfn, canon_pgprot (new_prot));
    cpa->pfn = pfn;
    if (pte_val (old_pte) != pte_val (new_pte)) {
        set_pte_atomic (kpte, new_pte);
        cpa->flushtlb = 1;
    }
    cpa->numpages = 1;
    return 0;
}

{
    set_pte_atomic (kpte, new_pte);
    cpa->flushtlb = 1;
}

{
    cpa->flushtlb = 1;
    goto repeat;
}

{
    struct cpa_data alias_cpa;
    int ret = 0;
    if (cpa->pfn > max_pfn_mapped)
        return 0;
    if (!within (cpa->vaddr, PAGE_OFFSET, PAGE_OFFSET +(max_pfn_mapped << PAGE_SHIFT))) {
        alias_cpa = *cpa;
        alias_cpa.vaddr = (unsigned long) __va (cpa->pfn << PAGE_SHIFT);
        ret = __change_page_attr_set_clr (&alias_cpa, 0);
    }
    return ret;
}

{
    alias_cpa = *cpa;
    alias_cpa.vaddr = (unsigned long) __va (cpa->pfn << PAGE_SHIFT);
    ret = __change_page_attr_set_clr (&alias_cpa, 0);
}

{
    int ret, numpages = cpa->numpages;
    while (numpages) {
        cpa->numpages = numpages;
        ret = __change_page_attr (cpa, checkalias);
        if (ret)
            return ret;
        if (checkalias) {
            ret = cpa_process_alias (cpa);
            if (ret)
                return ret;
        }
        BUG_ON (cpa -> numpages > numpages);
        numpages -= cpa->numpages;
        cpa->vaddr += cpa->numpages * PAGE_SIZE;
    }
    return 0;
}

{
    cpa->numpages = numpages;
    ret = __change_page_attr (cpa, checkalias);
    if (ret)
        return ret;
    if (checkalias) {
        ret = cpa_process_alias (cpa);
        if (ret)
            return ret;
    }
    BUG_ON (cpa -> numpages > numpages);
    numpages -= cpa->numpages;
    cpa->vaddr += cpa->numpages * PAGE_SIZE;
}

{
    ret = cpa_process_alias (cpa);
    if (ret)
        return ret;
}

{
    return pgprot_val (attr) & (_PAGE_PAT | _PAGE_PAT_LARGE | _PAGE_PWT | _PAGE_PCD);
}

{
    struct cpa_data cpa;
    int ret, cache, checkalias;
    mask_set = canon_pgprot (mask_set);
    mask_clr = canon_pgprot (mask_clr);
    if (!pgprot_val (mask_set) && !pgprot_val (mask_clr) && !force_split)
        return 0;
    if (addr & ~PAGE_MASK) {
        addr &= PAGE_MASK;
        WARN_ON_ONCE (1);
    }
    cpa.vaddr = addr;
    cpa.numpages = numpages;
    cpa.mask_set = mask_set;
    cpa.mask_clr = mask_clr;
    cpa.flushtlb = 0;
    cpa.force_split = force_split;
    checkalias = (pgprot_val (mask_set) | pgprot_val (mask_clr)) != _PAGE_NX;
    ret = __change_page_attr_set_clr (&cpa, checkalias);
    if (!cpa.flushtlb)
        goto out;
    cache = cache_attr (mask_set);
    if (!ret && cpu_has_clflush)
        cpa_flush_range (addr, numpages, cache);
    else
        cpa_flush_all (cache);
out :
    cpa_fill_pool (NULL);
    return ret;
}

{
    addr &= PAGE_MASK;
    WARN_ON_ONCE (1);
}

{
    return change_page_attr_set_clr (addr, numpages, mask, __pgprot (0), 0);
}

{
    return change_page_attr_set_clr (addr, numpages, __pgprot (0), mask, 0);
}

{
    return change_page_attr_set (addr, numpages, __pgprot (_PAGE_CACHE_UC_MINUS));
}

{
    if (reserve_memtype (addr, addr +numpages * PAGE_SIZE, _PAGE_CACHE_UC_MINUS, NULL))
        return -EINVAL;
    return _set_memory_uc (addr, numpages);
}

{
    return change_page_attr_set (addr, numpages, __pgprot (_PAGE_CACHE_WC));
}

{
    if (!pat_wc_enabled)
        return set_memory_uc (addr, numpages);
    if (reserve_memtype (addr, addr +numpages * PAGE_SIZE, _PAGE_CACHE_WC, NULL))
        return -EINVAL;
    return _set_memory_wc (addr, numpages);
}

{
    return change_page_attr_clear (addr, numpages, __pgprot (_PAGE_CACHE_MASK));
}

{
    free_memtype (addr, addr + numpages * PAGE_SIZE);
    return _set_memory_wb (addr, numpages);
}

{
    return change_page_attr_clear (addr, numpages, __pgprot (_PAGE_NX));
}

{
    return change_page_attr_set (addr, numpages, __pgprot (_PAGE_NX));
}

{
    return change_page_attr_clear (addr, numpages, __pgprot (_PAGE_RW));
}

{
    return change_page_attr_set (addr, numpages, __pgprot (_PAGE_RW));
}

{
    return change_page_attr_clear (addr, numpages, __pgprot (_PAGE_PRESENT));
}

{
    return change_page_attr_set_clr (addr, numpages, __pgprot (0), __pgprot (0), 1);
}

{
    unsigned long addr = (unsigned long) page_address (page);
    return set_memory_uc (addr, numpages);
}

{
    unsigned long addr = (unsigned long) page_address (page);
    return set_memory_wb (addr, numpages);
}

{
    unsigned long addr = (unsigned long) page_address (page);
    return set_memory_x (addr, numpages);
}

{
    unsigned long addr = (unsigned long) page_address (page);
    return set_memory_nx (addr, numpages);
}

{
    unsigned long addr = (unsigned long) page_address (page);
    return set_memory_ro (addr, numpages);
}

{
    unsigned long addr = (unsigned long) page_address (page);
    return set_memory_rw (addr, numpages);
}

{
    unsigned long saddr = ((idx - svma->vm_pgoff) << PAGE_SHIFT) + svma->vm_start;
    unsigned long sbase = saddr & PUD_MASK;
    unsigned long s_end = sbase + PUD_SIZE;
    if (pmd_index (addr) != pmd_index (saddr) || vma->vm_flags != svma->vm_flags || sbase < svma->vm_start || svma->vm_end < s_end)
        return 0;
    return saddr;
}

{
    unsigned long base = addr & PUD_MASK;
    unsigned long end = base + PUD_SIZE;
    if (vma->vm_flags & VM_MAYSHARE && vma->vm_start <= base && end <= vma->vm_end)
        return 1;
    return 0;
}

{
    struct vm_area_struct *vma = find_vma (mm, addr);
    struct address_space *mapping = vma->vm_file->f_mapping;
    pgoff_t idx = ((addr - vma->vm_start) >> PAGE_SHIFT) + vma->vm_pgoff;
    struct prio_tree_iter iter;
    struct vm_area_struct *svma;
    unsigned long saddr;
    pte_t *spte = NULL;
    if (!vma_shareable (vma, addr))
        return;
    spin_lock (& mapping -> i_mmap_lock);
    vma_prio_tree_foreach (svma, & iter, & mapping -> i_mmap, idx, idx)
    {
        if (svma == vma)
            continue;
        saddr = page_table_shareable (svma, vma, addr, idx);
        if (saddr) {
            spte = huge_pte_offset (svma->vm_mm, saddr);
            if (spte) {
                get_page (virt_to_page (spte));
                break;
            }
        }
    }
    if (!spte)
        goto out;
    spin_lock (& mm -> page_table_lock);
    if (pud_none (*pud))
        pud_populate (mm, pud, (pmd_t *) ((unsigned long) spte & PAGE_MASK));
    else
        put_page (virt_to_page (spte));
    spin_unlock (& mm -> page_table_lock);
out :
    spin_unlock (&mapping->i_mmap_lock);
}

{
    if (svma == vma)
        continue;
    saddr = page_table_shareable (svma, vma, addr, idx);
    if (saddr) {
        spte = huge_pte_offset (svma->vm_mm, saddr);
        if (spte) {
            get_page (virt_to_page (spte));
            break;
        }
    }
}

{
    spte = huge_pte_offset (svma->vm_mm, saddr);
    if (spte) {
        get_page (virt_to_page (spte));
        break;
    }
}

{
    get_page (virt_to_page (spte));
    break;
}

{
    pgd_t *pgd = pgd_offset (mm, *addr);
    pud_t *pud = pud_offset (pgd, *addr);
    BUG_ON (page_count (virt_to_page (ptep)) == 0);
    if (page_count (virt_to_page (ptep)) == 1)
        return 0;
    pud_clear (pud);
    put_page (virt_to_page (ptep));
    *addr = ALIGN (*addr, HPAGE_SIZE *PTRS_PER_PTE) - HPAGE_SIZE;
    return 1;
}

{
    pgd_t *pgd;
    pud_t *pud;
    pte_t *pte = NULL;
    pgd = pgd_offset (mm, addr);
    pud = pud_alloc (mm, pgd, addr);
    if (pud) {
        if (pud_none (*pud))
            huge_pmd_share (mm, addr, pud);
        pte = (pte_t *) pmd_alloc (mm, pud, addr);
    }
    BUG_ON (pte && ! pte_none (* pte) && ! pte_huge (* pte));
    return pte;
}

{
    if (pud_none (*pud))
        huge_pmd_share (mm, addr, pud);
    pte = (pte_t *) pmd_alloc (mm, pud, addr);
}

{
    pgd_t *pgd;
    pud_t *pud;
    pmd_t *pmd = NULL;
    pgd = pgd_offset (mm, addr);
    if (pgd_present (*pgd)) {
        pud = pud_offset (pgd, addr);
        if (pud_present (*pud))
            pmd = pmd_offset (pud, addr);
    }
    return (pte_t *) pmd;
}

{
    pud = pud_offset (pgd, addr);
    if (pud_present (*pud))
        pmd = pmd_offset (pud, addr);
}

{
    return ERR_PTR (-EINVAL);
}

{
    return !!(pmd_val (pmd) & _PAGE_PSE);
}

{
    struct page *page;
    page = pte_page (*(pte_t*) pmd);
    if (page)
        page += ((address & ~HPAGE_MASK) >> PAGE_SHIFT);
    return page;
}

{
    return 0;
}

{
    if (current->personality & ADDR_COMPAT_LAYOUT)
        return 1;
    if (current->signal->rlim[RLIMIT_STACK].rlim_cur == RLIM_INFINITY)
        return 1;
    return sysctl_legacy_va_layout;
}

{
    unsigned long rnd = 0;
    if (current->flags & PF_RANDOMIZE) {
        if (mmap_is_ia32 ())
            rnd = (long) get_random_int () % (1 << 8);
        else
            rnd = (long) (get_random_int () % (1 << 28));
    }
    return rnd << PAGE_SHIFT;
}

{
    if (mmap_is_ia32 ())
        rnd = (long) get_random_int () % (1 << 8);
    else
        rnd = (long) (get_random_int () % (1 << 28));
}

{
    unsigned long gap = current->signal->rlim[RLIMIT_STACK].rlim_cur;
    if (gap < MIN_GAP)
        gap = MIN_GAP;
    else if (gap > MAX_GAP)
        gap = MAX_GAP;
    return PAGE_ALIGN (TASK_SIZE -gap - mmap_rnd ());
}

{
    if (mmap_is_ia32 ())
        return TASK_UNMAPPED_BASE;
    else
        return TASK_UNMAPPED_BASE + mmap_rnd ();
}

{
    if (mmap_is_legacy ()) {
        mm->mmap_base = mmap_legacy_base ();
        mm->get_unmapped_area = arch_get_unmapped_area;
        mm->unmap_area = arch_unmap_area;
    }
    else {
        mm->mmap_base = mmap_base ();
        mm->get_unmapped_area = arch_get_unmapped_area_topdown;
        mm->unmap_area = arch_unmap_area_topdown;
    }
}

{
    mm->mmap_base = mmap_legacy_base ();
    mm->get_unmapped_area = arch_get_unmapped_area;
    mm->unmap_area = arch_unmap_area;
}

{
    mm->mmap_base = mmap_base ();
    mm->get_unmapped_area = arch_get_unmapped_area_topdown;
    mm->unmap_area = arch_unmap_area_topdown;
}
